import React, { useEffect, useRef } from 'react';
import { useToolStore } from '../../stores/toolStore';
import { useCanvasStore } from '../../stores/canvasStore';
import { useAnimationStore } from '../../stores/animationStore';
import { useCanvasContext } from '../../contexts/CanvasContext';
import { useCanvasState } from '../../hooks/useCanvasState';
import { useCanvasMouseHandlers } from '../../hooks/useCanvasMouseHandlers';
import { useCanvasRenderer } from '../../hooks/useCanvasRenderer';
import { useToolBehavior } from '../../hooks/useToolBehavior';
import { useHoverPreview } from '../../hooks/useHoverPreview';
import { ToolManager } from './ToolManager';
import { ToolStatusManager } from './ToolStatusManager';
import { CanvasActionButtons } from './CanvasActionButtons';
import { CanvasOverlay } from './CanvasOverlay';

interface CanvasGridProps {
  className?: string;
}

export const CanvasGrid: React.FC<CanvasGridProps> = ({ className = '' }) => {
  // Use our new context and state management
  const { canvasRef, setMouseButtonDown, setShiftKeyDown, setAltKeyDown, altKeyDown } = useCanvasContext();

  // Get active tool and tool behavior
  const { activeTool, textToolState, isPlaybackMode } = useToolStore();
  const { isPlaying } = useAnimationStore();
  const { getToolCursor } = useToolBehavior();

  // Track previous tool for cleanup on tool changes
  const prevToolRef = useRef(activeTool);

  // Calculate effective tool (Alt key overrides with eyedropper for drawing tools, except gradientfill)
  const drawingTools = ['pencil', 'eraser', 'paintbucket', 'rectangle', 'ellipse'] as const;
  const shouldAllowEyedropperOverride = drawingTools.includes(activeTool as any);

  const effectiveTool = (altKeyDown && shouldAllowEyedropperOverride) ? 'eyedropper' : activeTool;

  // Canvas dimensions hooks already provide computed values
  const {
    moveState,
    commitMove,
    cancelMove,
    setSelectionMode,
    setMoveState,
    setPendingSelectionStart,
    setJustCommittedMove,
  } = useCanvasState();

  // Use our new mouse handlers
  const {
    handleMouseDown,
    handleMouseMove, 
    handleMouseUp,
    handleMouseLeave,
    handleContextMenu
  } = useCanvasMouseHandlers();

  // Use the new renderer hook that handles both grid and overlay rendering
  useCanvasRenderer();

  // Enable hover preview for tools (brush outline, etc.)
  useHoverPreview();

  const { 
    selection, 
    lassoSelection, 
    magicWandSelection, 
    clearSelection, 
    clearLassoSelection, 
    clearMagicWandSelection 
  } = useToolStore();

  // Handle arrow key movement for selections
  const handleArrowKeyMovement = (arrowOffset: { x: number; y: number }) => {
    // Determine which selection is active and handle accordingly
    if (activeTool === 'select' && selection.active) {
      handleRectangularSelectionArrowMovement(arrowOffset);
    } else if (activeTool === 'lasso' && lassoSelection.active) {
      handleLassoSelectionArrowMovement(arrowOffset);
    } else if (activeTool === 'magicwand' && magicWandSelection.active) {
      handleMagicWandSelectionArrowMovement(arrowOffset);
    }
  };

  // Handle arrow movement for rectangular selection
  const handleRectangularSelectionArrowMovement = (arrowOffset: { x: number; y: number }) => {
    if (!selection.active) return;

    // Ensure we're not blocked by justCommittedMove state
    setJustCommittedMove(false);

    if (moveState) {
      // Already in move mode - update the current offset
      setMoveState({
        ...moveState,
        currentOffset: {
          x: moveState.currentOffset.x + arrowOffset.x,
          y: moveState.currentOffset.y + arrowOffset.y
        }
      });
    } else {
      // Enter move mode for the first time
      const { getCell } = useCanvasStore.getState();
  const startX = Math.min(selection.start.x, selection.end.x);
  const startY = Math.min(selection.start.y, selection.end.y);

      const originalData = new Map();
      const originalPositions = new Set<string>();

      selection.selectedCells.forEach((cellKey) => {
        originalPositions.add(cellKey);
        const [cx, cy] = cellKey.split(',').map(Number);
        const cell = getCell(cx, cy);
        // Only store non-empty cells (not spaces or empty cells)
        if (cell && cell.char !== ' ') {
          originalData.set(cellKey, cell);
        }
      });

      setMoveState({
        originalData,
        originalPositions,
        startPos: { x: startX, y: startY }, // Use selection start as reference point
        baseOffset: { x: 0, y: 0 },
        currentOffset: arrowOffset // Start with the arrow offset
      });
      setSelectionMode('moving');
    }
  };

  // Handle arrow movement for lasso selection
  const handleLassoSelectionArrowMovement = (arrowOffset: { x: number; y: number }) => {
    if (!lassoSelection.active) return;

    // Ensure we're not blocked by justCommittedMove state
    setJustCommittedMove(false);

    if (moveState) {
      // Already in move mode - update the current offset
      setMoveState({
        ...moveState,
        currentOffset: {
          x: moveState.currentOffset.x + arrowOffset.x,
          y: moveState.currentOffset.y + arrowOffset.y
        }
      });
    } else {
      // Enter move mode for the first time
      const { getCell } = useCanvasStore.getState();

      // Store only the non-empty cells from the lasso selection
      const originalData = new Map();
      lassoSelection.selectedCells.forEach((cellKey) => {
        const [cx, cy] = cellKey.split(',').map(Number);
        const cell = getCell(cx, cy);
        // Only store non-empty cells (not spaces or empty cells)
        if (cell && cell.char !== ' ') {
          originalData.set(cellKey, cell);
        }
      });

      // Use center of selection as reference point
      const cellCoords = Array.from(lassoSelection.selectedCells).map(key => {
        const [x, y] = key.split(',').map(Number);
        return { x, y };
      });
      const centerX = Math.floor(cellCoords.reduce((sum, c) => sum + c.x, 0) / cellCoords.length);
      const centerY = Math.floor(cellCoords.reduce((sum, c) => sum + c.y, 0) / cellCoords.length);

      setMoveState({
        originalData,
        originalPositions: new Set(originalData.keys()),
        startPos: { x: centerX, y: centerY }, // Use selection center as reference point
        baseOffset: { x: 0, y: 0 },
        currentOffset: arrowOffset // Start with the arrow offset
      });
      setSelectionMode('moving');
    }
  };

  // Handle arrow movement for magic wand selection
  const handleMagicWandSelectionArrowMovement = (arrowOffset: { x: number; y: number }) => {
    if (!magicWandSelection.active) return;

    // Ensure we're not blocked by justCommittedMove state
    setJustCommittedMove(false);

    if (moveState) {
      // Already in move mode - update the current offset
      setMoveState({
        ...moveState,
        currentOffset: {
          x: moveState.currentOffset.x + arrowOffset.x,
          y: moveState.currentOffset.y + arrowOffset.y
        }
      });
    } else {
      // Enter move mode for the first time
      const { getCell } = useCanvasStore.getState();

      // Store only the non-empty cells from the magic wand selection
      const originalData = new Map();
      magicWandSelection.selectedCells.forEach((cellKey) => {
        const [cx, cy] = cellKey.split(',').map(Number);
        const cell = getCell(cx, cy);
        // Only store non-empty cells (not spaces or empty cells)
        if (cell && cell.char !== ' ') {
          originalData.set(cellKey, cell);
        }
      });

      // Use center of selection as reference point
      const cellCoords = Array.from(magicWandSelection.selectedCells).map(key => {
        const [x, y] = key.split(',').map(Number);
        return { x, y };
      });
      const centerX = Math.floor(cellCoords.reduce((sum, c) => sum + c.x, 0) / cellCoords.length);
      const centerY = Math.floor(cellCoords.reduce((sum, c) => sum + c.y, 0) / cellCoords.length);

      setMoveState({
        originalData,
        originalPositions: new Set(originalData.keys()),
        startPos: { x: centerX, y: centerY }, // Use selection center as reference point
        baseOffset: { x: 0, y: 0 },
        currentOffset: arrowOffset // Start with the arrow offset
      });
      setSelectionMode('moving');
    }
  };

  // Handle keyboard events for Escape and Shift keys
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      // Handle Shift key for aspect ratio locking
      if (event.key === 'Shift') {
        setShiftKeyDown(true);
      }

      // Handle Alt key for temporary eyedropper tool (only for drawing tools)
      // Don't override Alt key if text tool is actively typing
      if (event.key === 'Alt' && shouldAllowEyedropperOverride) {
        event.preventDefault(); // Prevent browser menu activation
        setAltKeyDown(true);
      }

      // Handle Escape key for canceling moves and clearing selections
      if (event.key === 'Escape') {
        if ((selection.active && activeTool === 'select') || 
            (lassoSelection.active && activeTool === 'lasso') ||
            (magicWandSelection.active && activeTool === 'magicwand')) {
          event.preventDefault();
          event.stopPropagation();

          if (moveState) {
            // Cancel move without committing changes
            cancelMove();
          }

          // Clear the appropriate selection
          if (selection.active) {
            clearSelection();
          }
          if (lassoSelection.active) {
            clearLassoSelection();
          }
          if (magicWandSelection.active) {
            clearMagicWandSelection();
          }
        }
      }

      // Handle Enter key for committing moves
      if (event.key === 'Enter' && moveState && (activeTool === 'select' || activeTool === 'lasso' || activeTool === 'magicwand')) {
        event.preventDefault();
        event.stopPropagation();
        commitMove();

        // Clear the appropriate selection based on active tool
        if (activeTool === 'select') {
          clearSelection();
        } else if (activeTool === 'lasso') {
          clearLassoSelection();
        } else if (activeTool === 'magicwand') {
          clearMagicWandSelection();
        }
      }

      // Handle arrow keys for selection movement
      if ((event.key === 'ArrowUp' || event.key === 'ArrowDown' || event.key === 'ArrowLeft' || event.key === 'ArrowRight')) {
        // Only handle arrow keys when a selection tool is active and has an active selection
        if ((activeTool === 'select' && selection.active) || 
            (activeTool === 'lasso' && lassoSelection.active) ||
            (activeTool === 'magicwand' && magicWandSelection.active)) {
          event.preventDefault();
          event.stopPropagation();

          // Calculate arrow direction offset
          const arrowOffset = {
            x: event.key === 'ArrowLeft' ? -1 : event.key === 'ArrowRight' ? 1 : 0,
            y: event.key === 'ArrowUp' ? -1 : event.key === 'ArrowDown' ? 1 : 0
          };

          // Call the new arrow movement handler
          handleArrowKeyMovement(arrowOffset);
        }
      }
    };

    const handleKeyUp = (event: KeyboardEvent) => {
      // Handle Shift key release
      if (event.key === 'Shift') {
        setShiftKeyDown(false);
        // Clear line preview when shift is released
        const { clearLinePreview } = useToolStore.getState();
        clearLinePreview();
      }

      // Handle Alt key release
      if (event.key === 'Alt') {
        setAltKeyDown(false);
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    document.addEventListener('keyup', handleKeyUp);
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('keyup', handleKeyUp);
    };
  }, [
    moveState, 
    activeTool, 
    commitMove, 
    cancelMove, 
    selection.active,
    lassoSelection.active, 
    magicWandSelection.active,
    handleArrowKeyMovement,
    clearSelection,
    clearLassoSelection,
    clearMagicWandSelection,
    textToolState.isTyping,
    shouldAllowEyedropperOverride
  ]);

  // Reset selection mode when tool changes
  useEffect(() => {
    const prevTool = prevToolRef.current;

    // If tool actually changed, handle cleanup
    if (prevTool !== activeTool) {
      // Always commit any pending move when switching tools
      if (moveState) {
        commitMove();
      }

      // Clear selection-related state when switching away from selection tools
      if (activeTool !== 'select' && activeTool !== 'lasso') {
        setSelectionMode('none');
        setMouseButtonDown(false);
        setPendingSelectionStart(null);
        setMoveState(null);
      }

      // Update the ref for next time
      prevToolRef.current = activeTool;
    }
  }, [activeTool, moveState, commitMove, setSelectionMode, setMouseButtonDown, setPendingSelectionStart, setMoveState]);

  return (
    <div className={`canvas-grid-container ${className} h-full flex flex-col relative`}>
      {/* Tool Manager - handles tool-specific behavior */}
      <ToolManager />

      <div className={`canvas-wrapper border rounded-lg overflow-auto flex-1 relative ${
        isPlaying 
          ? 'border-white border-2' 
          : isPlaybackMode 
            ? 'border-orange-500 border-2'
            : 'border-border border'
      }`}>
        <canvas
          ref={canvasRef}
          className={`canvas-grid border border-border ${getToolCursor(effectiveTool)}`}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseLeave}
          onContextMenu={handleContextMenu}
          style={{
            display: 'block'
            // Width and height are set by canvas resize logic in useCanvasRenderer
          }}
        />
        <CanvasOverlay />
      </div>

      {/* Action buttons and status info positioned outside canvas */}
      <div className="my-2 flex justify-between items-center">
        <CanvasActionButtons />
        <ToolStatusManager />
      </div>
    </div>
  );
};

import React, { useCallback, useEffect, useRef } from 'react';
import { useToolStore } from '../../stores/toolStore';
import { useGradientStore } from '../../stores/gradientStore';
import { useCanvasContext } from '../../contexts/CanvasContext';
import { useCanvasStore } from '../../stores/canvasStore';
import { useTheme } from '../../contexts/ThemeContext';
import { useCanvasState } from '../../hooks/useCanvasState';
import { InteractiveGradientOverlay } from './InteractiveGradientOverlay';

export const CanvasOverlay: React.FC = () => {
  // Create a separate canvas ref for overlay
  const overlayCanvasRef = useRef<HTMLCanvasElement>(null);

  // Canvas context and state  
  const { canvasRef, pasteMode, cellWidth, cellHeight, zoom, panOffset, fontMetrics, hoverPreview, selectionPreview } = useCanvasContext();
  const {
    moveState,
    getTotalOffset,
  } = useCanvasState();

  const { selection, lassoSelection, magicWandSelection, linePreview, activeTool } = useToolStore();
  const { 
    isApplying: gradientApplying, 
    startPoint: gradientStart, 
    endPoint: gradientEnd,
    definition: gradientDefinition,
    previewData: gradientPreview
  } = useGradientStore();
  const { canvasBackgroundColor, width, height } = useCanvasStore();
  const { theme } = useTheme();

  // Calculate effective dimensions with zoom and aspect ratio
  const effectiveCellWidth = cellWidth * zoom;
  const effectiveCellHeight = cellHeight * zoom;

  // Render selection overlay
  const renderOverlay = useCallback(() => {
    const overlayCanvas = overlayCanvasRef.current;
    const mainCanvas = canvasRef.current;
    if (!overlayCanvas || !mainCanvas) return;

    const ctx = overlayCanvas.getContext('2d');
    if (!ctx) return;

    // Match the overlay canvas size to the main canvas
    if (overlayCanvas.width !== mainCanvas.width || overlayCanvas.height !== mainCanvas.height) {
      overlayCanvas.width = mainCanvas.width;
      overlayCanvas.height = mainCanvas.height;
      overlayCanvas.style.width = mainCanvas.style.width;
      overlayCanvas.style.height = mainCanvas.style.height;

      // Apply the same high-DPI scaling as the main canvas
      const devicePixelRatio = window.devicePixelRatio || 1;
      ctx.scale(devicePixelRatio, devicePixelRatio);
    }

    // Clear previous overlay
    ctx.clearRect(0, 0, overlayCanvas.width / (window.devicePixelRatio || 1), overlayCanvas.height / (window.devicePixelRatio || 1));

    const totalOffset = moveState ? getTotalOffset(moveState) : { x: 0, y: 0 };

    const drawCells = (cells: Set<string>, options: { fillStyle?: string; strokeStyle?: string; lineDash?: number[] }) => {
      if (!cells || cells.size === 0) return;

      const { fillStyle, strokeStyle, lineDash } = options;
      if (lineDash) {
        ctx.setLineDash(lineDash);
      }

      cells.forEach((cellKey) => {
        const [rawX, rawY] = cellKey.split(',').map(Number);
        let cellX = rawX + totalOffset.x;
        let cellY = rawY + totalOffset.y;

        if (cellX < 0 || cellY < 0 || cellX >= width || cellY >= height) {
          return;
        }

        const pixelX = cellX * effectiveCellWidth + panOffset.x;
        const pixelY = cellY * effectiveCellHeight + panOffset.y;

        if (fillStyle) {
          ctx.fillStyle = fillStyle;
          ctx.fillRect(pixelX, pixelY, effectiveCellWidth, effectiveCellHeight);
        }

        if (strokeStyle) {
          ctx.strokeStyle = strokeStyle;
          ctx.lineWidth = 2;
          ctx.strokeRect(pixelX, pixelY, effectiveCellWidth, effectiveCellHeight);
        }
      });

      if (lineDash) {
        ctx.setLineDash([]);
      }
    };

    const selectionFillColor = 'rgba(192, 132, 252, 0.18)';
    const selectionOutlineColor = '#C084FC';
    const selectionLineDash: [number, number] = [6, 4];

    const adjustCellsForOffset = (cells: Set<string>): Set<string> => {
      const adjusted = new Set<string>();
      cells.forEach((cellKey) => {
        const [rawX, rawY] = cellKey.split(',').map(Number);
        const adjX = rawX + totalOffset.x;
        const adjY = rawY + totalOffset.y;

        if (adjX < 0 || adjY < 0 || adjX >= width || adjY >= height) {
          return;
        }

        adjusted.add(`${adjX},${adjY}`);
      });
      return adjusted;
    };

    const buildIslands = (cells: Set<string>): Set<string>[] => {
      const visited = new Set<string>();
      const islands: Set<string>[] = [];
      const neighborOffsets = [
        [1, 0],
        [-1, 0],
        [0, 1],
        [0, -1]
      ];

      cells.forEach((cellKey) => {
        if (visited.has(cellKey)) {
          return;
        }

        const island = new Set<string>();
        const stack: string[] = [cellKey];
        visited.add(cellKey);

        while (stack.length > 0) {
          const currentKey = stack.pop()!;
          island.add(currentKey);
          const [cx, cy] = currentKey.split(',').map(Number);

          neighborOffsets.forEach(([dx, dy]) => {
            const neighborKey = `${cx + dx},${cy + dy}`;
            if (!visited.has(neighborKey) && cells.has(neighborKey)) {
              visited.add(neighborKey);
              stack.push(neighborKey);
            }
          });
        }

        islands.push(island);
      });

      return islands;
    };

    const buildOutlineLoops = (island: Set<string>): { x: number; y: number }[][] => {
      const edgeMap = new Map<string, Map<string, { x: number; y: number }>>();
      const edgeKeys = new Set<string>();

      const pointKey = (point: { x: number; y: number }) => `${point.x},${point.y}`;

      const addEdge = (from: { x: number; y: number }, to: { x: number; y: number }) => {
        const fromKey = pointKey(from);
        const toKey = pointKey(to);

        if (!edgeMap.has(fromKey)) {
          edgeMap.set(fromKey, new Map());
        }

        const fromEdges = edgeMap.get(fromKey)!;
        if (!fromEdges.has(toKey)) {
          fromEdges.set(toKey, to);
          edgeKeys.add(`${fromKey}|${toKey}`);
        }
      };

      island.forEach((cellKey) => {
        const [x, y] = cellKey.split(',').map(Number);

        const neighbors = [
          { dx: 0, dy: -1, from: { x, y }, to: { x: x + 1, y } }, // top edge
          { dx: 1, dy: 0, from: { x: x + 1, y }, to: { x: x + 1, y: y + 1 } }, // right edge
          { dx: 0, dy: 1, from: { x: x + 1, y: y + 1 }, to: { x, y: y + 1 } }, // bottom edge
          { dx: -1, dy: 0, from: { x, y: y + 1 }, to: { x, y } } // left edge
        ];

        neighbors.forEach(({ dx, dy, from, to }) => {
          const neighborKey = `${x + dx},${y + dy}`;
          if (!island.has(neighborKey)) {
            addEdge(from, to);
          }
        });
      });

      const parsePoint = (key: string): { x: number; y: number } => {
        const [px, py] = key.split(',').map(Number);
        return { x: px, y: py };
      };

      const loops: { x: number; y: number }[][] = [];

      while (edgeKeys.size > 0) {
        const firstKey = edgeKeys.values().next().value as string;
        const [startFromKey, startToKey] = firstKey.split('|');
        const loop: { x: number; y: number }[] = [];

        let currentFromKey = startFromKey;
        let currentToKey = startToKey;
        loop.push(parsePoint(currentFromKey));

        while (true) {
          const fromEdges = edgeMap.get(currentFromKey);
          if (!fromEdges) {
            break;
          }

          const nextPoint = fromEdges.get(currentToKey);
          if (!nextPoint) {
            break;
          }

          loop.push(nextPoint);

          fromEdges.delete(currentToKey);
          edgeKeys.delete(`${currentFromKey}|${currentToKey}`);
          if (fromEdges.size === 0) {
            edgeMap.delete(currentFromKey);
          }

          if (currentToKey === startFromKey) {
            break;
          }

          const nextEdges = edgeMap.get(currentToKey);
          if (!nextEdges || nextEdges.size === 0) {
            break;
          }

          const nextToEntry = nextEdges.entries().next().value as [string, { x: number; y: number }];
          currentFromKey = currentToKey;
          currentToKey = nextToEntry[0];
        }

        loops.push(loop);
      }

      return loops;
    };

    const drawSelectionIslands = (cells: Set<string>) => {
      if (!cells || cells.size === 0) {
        return;
      }

      const adjustedCells = adjustCellsForOffset(cells);
      if (adjustedCells.size === 0) {
        return;
      }

      ctx.fillStyle = selectionFillColor;
      adjustedCells.forEach((cellKey) => {
        const [x, y] = cellKey.split(',').map(Number);
        const pixelX = x * effectiveCellWidth + panOffset.x;
        const pixelY = y * effectiveCellHeight + panOffset.y;
        ctx.fillRect(pixelX, pixelY, effectiveCellWidth, effectiveCellHeight);
      });

      const islands = buildIslands(adjustedCells);

      ctx.strokeStyle = selectionOutlineColor;
      ctx.lineWidth = 2;
      ctx.lineJoin = 'round';
      ctx.lineCap = 'round';
      ctx.setLineDash(selectionLineDash);

      islands.forEach((island) => {
        const loops = buildOutlineLoops(island);
        loops.forEach((loop) => {
          if (loop.length < 2) {
            return;
          }

          ctx.beginPath();
          loop.forEach((point, index) => {
            const pixelX = point.x * effectiveCellWidth + panOffset.x;
            const pixelY = point.y * effectiveCellHeight + panOffset.y;
            if (index === 0) {
              ctx.moveTo(pixelX, pixelY);
            } else {
              ctx.lineTo(pixelX, pixelY);
            }
          });
          ctx.closePath();
          ctx.stroke();
        });
      });

      ctx.setLineDash([]);
    };

    const previewActive = selectionPreview.active && selectionPreview.tool !== null && selectionPreview.modifier !== 'replace';
    if (previewActive) {
      const baseCells = new Set(selectionPreview.baseCells);
      const gestureCells = new Set(selectionPreview.gestureCells);
      const modifier = selectionPreview.modifier;
      const toolKey = selectionPreview.tool ?? 'select';

      const previewStyleMap = {
        select: {
          baseFill: 'rgba(59, 130, 246, 0.2)',
          addFill: 'rgba(34, 197, 94, 0.25)',
          addStroke: undefined,
          subtractFill: 'rgba(239, 68, 68, 0.22)',
          subtractStroke: '#EF4444'
        },
        lasso: {
          baseFill: 'rgba(168, 85, 247, 0.25)',
          addFill: 'rgba(34, 197, 94, 0.25)',
          addStroke: undefined,
          subtractFill: 'rgba(239, 68, 68, 0.22)',
          subtractStroke: '#EF4444'
        },
        magicwand: {
          baseFill: 'rgba(255, 165, 0, 0.28)',
          addFill: 'rgba(56, 189, 248, 0.25)',
          addStroke: undefined,
          subtractFill: 'rgba(239, 68, 68, 0.22)',
          subtractStroke: undefined
        }
      } as const;

      const styles = previewStyleMap[toolKey as keyof typeof previewStyleMap] ?? previewStyleMap.select;

      if (modifier === 'add') {
        drawCells(baseCells, { fillStyle: styles.baseFill });
        drawCells(gestureCells, { fillStyle: styles.addFill, strokeStyle: styles.addStroke });
      } else if (modifier === 'subtract') {
        const remainingCells = new Set<string>();
        baseCells.forEach((cell) => {
          if (!gestureCells.has(cell)) {
            remainingCells.add(cell);
          }
        });

        drawCells(remainingCells, { fillStyle: styles.baseFill });
        if (gestureCells.size > 0) {
          drawCells(gestureCells, { fillStyle: styles.subtractFill, strokeStyle: styles.subtractStroke, lineDash: styles.subtractStroke ? [4, 3] : undefined });
        }
      }
    }

    // Draw selection overlay
    if (selection.active && selection.selectedCells.size > 0) {
      drawSelectionIslands(selection.selectedCells);
    }

    if (lassoSelection.active && lassoSelection.selectedCells.size > 0) {
      drawSelectionIslands(lassoSelection.selectedCells);
    }

    if (magicWandSelection.active && magicWandSelection.selectedCells.size > 0) {
      drawSelectionIslands(magicWandSelection.selectedCells);
    }

    // Draw shift+click line preview
    if (linePreview.active && linePreview.points.length > 0) {
      ctx.fillStyle = 'rgba(168, 85, 247, 0.1)'; // Same purple as lasso selection

      linePreview.points.forEach(({ x, y }) => {
        ctx.fillRect(
          x * effectiveCellWidth + panOffset.x,
          y * effectiveCellHeight + panOffset.y,
          effectiveCellWidth,
          effectiveCellHeight
        );
      });
    }

    // Draw paste preview overlay
    if (pasteMode.isActive && pasteMode.preview) {
      const { position, data, bounds } = pasteMode.preview;

      // Calculate preview rectangle
      const previewStartX = position.x + bounds.minX;
      const previewStartY = position.y + bounds.minY;
      const previewWidth = bounds.maxX - bounds.minX + 1;
      const previewHeight = bounds.maxY - bounds.minY + 1;

      // Draw paste preview marquee
      ctx.strokeStyle = '#A855F7'; // Purple color
      ctx.lineWidth = 2;
      ctx.setLineDash([8, 4]);
      ctx.strokeRect(
        previewStartX * effectiveCellWidth + panOffset.x,
        previewStartY * effectiveCellHeight + panOffset.y,
        previewWidth * effectiveCellWidth,
        previewHeight * effectiveCellHeight
      );

      // Add semi-transparent background
      ctx.fillStyle = 'rgba(168, 85, 247, 0.1)';
      ctx.fillRect(
        previewStartX * effectiveCellWidth + panOffset.x,
        previewStartY * effectiveCellHeight + panOffset.y,
        previewWidth * effectiveCellWidth,
        previewHeight * effectiveCellHeight
      );

      ctx.setLineDash([]);

      // Draw paste content preview with transparency
      ctx.globalAlpha = 0.7;
      data.forEach((cell, key) => {
        const [relX, relY] = key.split(',').map(Number);
        const absoluteX = position.x + relX;
        const absoluteY = position.y + relY;

        const pixelX = absoluteX * effectiveCellWidth + panOffset.x;
        const pixelY = absoluteY * effectiveCellHeight + panOffset.y;

        // Draw cell background
        if (cell.backgroundColor && cell.backgroundColor !== 'transparent') {
          ctx.fillStyle = cell.backgroundColor;
          ctx.fillRect(pixelX, pixelY, effectiveCellWidth, effectiveCellHeight);
        }

        // Draw character
        if (cell.character && cell.character !== ' ') {
          ctx.fillStyle = cell.color || '#000000';
          ctx.font = `${Math.floor(effectiveCellHeight - 2)}px 'Courier New', monospace`;
          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';
          ctx.fillText(
            cell.character, 
            pixelX + effectiveCellWidth / 2, 
            pixelY + effectiveCellHeight / 2
          );
        }
      });
      ctx.globalAlpha = 1.0;
    }

    // Draw gradient fill overlay
    if (activeTool === 'gradientfill' && gradientApplying) {
      // Draw gradient start point
      if (gradientStart) {
        ctx.strokeStyle = '#22c55e'; // Green for start
        ctx.fillStyle = '#22c55e';
        ctx.lineWidth = 2;
        ctx.setLineDash([]);

        const startPixelX = gradientStart.x * effectiveCellWidth + panOffset.x + effectiveCellWidth / 2;
        const startPixelY = gradientStart.y * effectiveCellHeight + panOffset.y + effectiveCellHeight / 2;

        // Draw start point circle
        ctx.beginPath();
        ctx.arc(startPixelX, startPixelY, 6, 0, 2 * Math.PI);
        ctx.fill();
        ctx.strokeStyle = 'white';
        ctx.lineWidth = 2;
        ctx.stroke();

        // Draw "START" label
        ctx.fillStyle = 'white';
        ctx.font = '12px monospace';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        ctx.fillText('START', startPixelX, startPixelY - 18);
      }

      // Draw gradient end point and line
      if (gradientStart && gradientEnd) {
        const startPixelX = gradientStart.x * effectiveCellWidth + panOffset.x + effectiveCellWidth / 2;
        const startPixelY = gradientStart.y * effectiveCellHeight + panOffset.y + effectiveCellHeight / 2;
        const endPixelX = gradientEnd.x * effectiveCellWidth + panOffset.x + effectiveCellWidth / 2;
        const endPixelY = gradientEnd.y * effectiveCellHeight + panOffset.y + effectiveCellHeight / 2;

        // Draw gradient line
        ctx.strokeStyle = '#6b7280'; // Gray
        ctx.lineWidth = 2;
        ctx.setLineDash([5, 5]);
        ctx.beginPath();
        ctx.moveTo(startPixelX, startPixelY);
        ctx.lineTo(endPixelX, endPixelY);
        ctx.stroke();
        ctx.setLineDash([]);

        // Draw end point circle
        ctx.fillStyle = '#ef4444'; // Red for end
        ctx.strokeStyle = 'white';
        ctx.lineWidth = 2;

        ctx.beginPath();
        ctx.arc(endPixelX, endPixelY, 6, 0, 2 * Math.PI);
        ctx.fill();
        ctx.stroke();

        // Draw "END" label
        ctx.fillStyle = 'white';
        ctx.font = '12px monospace';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        ctx.fillText('END', endPixelX, endPixelY - 18);

        // Draw gradient stops along the line
        const enabledProperties = [];
        if (gradientDefinition.character.enabled) enabledProperties.push('character');
        if (gradientDefinition.textColor.enabled) enabledProperties.push('textColor');
        if (gradientDefinition.backgroundColor.enabled) enabledProperties.push('backgroundColor');

        enabledProperties.forEach((property, propIndex) => {
          const gradientProp = gradientDefinition[property as keyof typeof gradientDefinition] as any;
          if (gradientProp.stops) {
            gradientProp.stops.forEach((stop: any) => {
              if (stop.position >= 0 && stop.position <= 1) {
                // Calculate position along the line
                const lineX = startPixelX + (endPixelX - startPixelX) * stop.position;
                const lineY = startPixelY + (endPixelY - startPixelY) * stop.position;

                // Offset perpendicular to line based on property type
                const lineAngle = Math.atan2(endPixelY - startPixelY, endPixelX - startPixelX);
                const perpAngle = lineAngle + Math.PI / 2;
                const offsetDistance = propIndex * 20; // Stack properties

                const stopX = lineX + Math.cos(perpAngle) * offsetDistance;
                const stopY = lineY + Math.sin(perpAngle) * offsetDistance;

                // Draw connection line to main line
                if (offsetDistance > 0) {
                  ctx.strokeStyle = '#9ca3af';
                  ctx.lineWidth = 1;
                  ctx.setLineDash([2, 2]);
                  ctx.beginPath();
                  ctx.moveTo(lineX, lineY);
                  ctx.lineTo(stopX, stopY);
                  ctx.stroke();
                  ctx.setLineDash([]);
                }

                // Draw stop marker
                const stopColor = property === 'character' ? '#8b5cf6' : 
                                property === 'textColor' ? '#3b82f6' : '#f59e0b';
                ctx.fillStyle = stopColor;
                ctx.strokeStyle = 'white';
                ctx.lineWidth = 1;

                ctx.beginPath();
                ctx.rect(stopX - 6, stopY - 6, 12, 12);
                ctx.fill();
                ctx.stroke();

                // Draw stop value
                ctx.fillStyle = 'white';
                ctx.font = '10px monospace';
                ctx.textAlign = 'center';
                ctx.textBaseline = 'middle';

                const displayValue = property === 'character' ? stop.value : 
                                   property === 'textColor' ? 'â—' : 'â– ';
                if (property !== 'character') {
                  ctx.fillStyle = stop.value;
                }
                ctx.fillText(displayValue, stopX, stopY);
              }
            });
          }
        });
      }

      // Draw gradient preview overlay with full opacity (shows exactly what the final result will be)
      if (gradientPreview && gradientPreview.size > 0) {
        ctx.globalAlpha = 1.0;

        gradientPreview.forEach((cell, key) => {
          const [x, y] = key.split(',').map(Number);
          const pixelX = x * effectiveCellWidth + panOffset.x;
          const pixelY = y * effectiveCellHeight + panOffset.y;

          // First, clear the background to hide original canvas content
          // Use actual canvas background, or app background when canvas is transparent
          if (canvasBackgroundColor === 'transparent') {
            ctx.fillStyle = theme === 'dark' ? '#000000' : '#ffffff';
          } else {
            ctx.fillStyle = canvasBackgroundColor;
          }
          ctx.fillRect(pixelX, pixelY, effectiveCellWidth, effectiveCellHeight);

          // Draw cell background (gradient background color)
          if (cell.bgColor && cell.bgColor !== 'transparent') {
            ctx.fillStyle = cell.bgColor;
            ctx.fillRect(pixelX, pixelY, effectiveCellWidth, effectiveCellHeight);
          }

          // Draw character
          if (cell.char && cell.char !== ' ') {
            ctx.fillStyle = cell.color || '#000000';
            // Use the same font as the main canvas for 1:1 preview
            const scaledFontSize = fontMetrics.fontSize * zoom;
            ctx.font = `${scaledFontSize}px '${fontMetrics.fontFamily}', monospace`;
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            ctx.fillText(
              cell.char, 
              pixelX + effectiveCellWidth / 2, 
              pixelY + effectiveCellHeight / 2
            );
          }
        });

        ctx.globalAlpha = 1.0;
      }
    }

    // Draw hover preview (for brush and other tool-specific previews)
    // Rendered last so it appears on top of all other overlays
    if (hoverPreview.active && hoverPreview.cells.length > 0) {
      // Visual style based on preview mode
      const getPreviewStyle = (mode: string) => {
        switch (mode) {
          case 'brush':
            return {
              fillStyle: 'rgba(168, 85, 247, 0.15)', // Subtle purple fill
              strokeStyle: 'rgba(168, 85, 247, 0.5)', // Purple outline
              lineWidth: 1
            };
          case 'eraser-brush':
            return {
              fillStyle: 'rgba(248, 250, 252, 0.25)', // Soft neutral fill
              strokeStyle: 'rgba(226, 232, 240, 0.7)', // Light gray outline
              lineWidth: 1,
              lineDash: [4, 2] as [number, number]
            };
          // Future modes can have different visual styles
          case 'rectangle':
          case 'ellipse':
            return {
              fillStyle: 'rgba(59, 130, 246, 0.15)', // Blue fill
              strokeStyle: 'rgba(59, 130, 246, 0.5)', // Blue outline
              lineWidth: 1
            };
          default:
            return {
              fillStyle: 'rgba(255, 255, 255, 0.1)', // White fill
              strokeStyle: 'rgba(255, 255, 255, 0.3)', // White outline
              lineWidth: 1
            };
        }
      };

      const style = getPreviewStyle(hoverPreview.mode);
      ctx.fillStyle = style.fillStyle;
      ctx.strokeStyle = style.strokeStyle;
      ctx.lineWidth = style.lineWidth;
      if ('lineDash' in style && style.lineDash) {
        ctx.setLineDash(style.lineDash);
      } else {
        ctx.setLineDash([]);
      }

      // Draw each cell in the preview pattern
      hoverPreview.cells.forEach(({ x, y }) => {
        const pixelX = x * effectiveCellWidth + panOffset.x;
        const pixelY = y * effectiveCellHeight + panOffset.y;

        // Fill cell with semi-transparent color
        ctx.fillRect(
          pixelX,
          pixelY,
          effectiveCellWidth,
          effectiveCellHeight
        );

        // Outline cell for better visibility
        ctx.strokeRect(
          pixelX,
          pixelY,
          effectiveCellWidth,
          effectiveCellHeight
        );
      });
    }
  }, [selection, lassoSelection, linePreview, effectiveCellWidth, effectiveCellHeight, panOffset, moveState, getTotalOffset, canvasRef, pasteMode, activeTool, gradientApplying, gradientStart, gradientEnd, gradientDefinition, gradientPreview, hoverPreview]);

  // Re-render overlay when dependencies change
  useEffect(() => {
    renderOverlay();
  }, [renderOverlay]);

  return (
    <>
      <canvas
        ref={overlayCanvasRef}
        className="absolute inset-0 pointer-events-none"
        style={{
          zIndex: 10, // Ensure overlay appears above main canvas
        }}
      />
      <InteractiveGradientOverlay />
    </>
  );
};

import React, { useCallback, useEffect } from 'react';
import { useCanvasStore } from '../../stores/canvasStore';
import { useCanvasContext } from '../../contexts/CanvasContext';
import { useCanvasState } from '../../hooks/useCanvasState';
import type { Cell } from '../../types';

export const CanvasRenderer: React.FC = () => {
  // Canvas context and state
  const { canvasRef } = useCanvasContext();
  const {
    cellSize,
    moveState,
    canvasWidth,
    canvasHeight,
    getTotalOffset,
  } = useCanvasState();

  // Canvas store data
  const { 
    width, 
    height, 
    canvasBackgroundColor,
    showGrid,
    getCell
  } = useCanvasStore();

  // Draw a single cell on the canvas
  const drawCell = useCallback((ctx: CanvasRenderingContext2D, x: number, y: number, cell?: Cell) => {
    const pixelX = x * cellSize;
    const pixelY = y * cellSize;

    // Only draw cell background if it's not the default transparent background
    if (cell && cell.bgColor !== 'transparent' && cell.bgColor !== '#FFFFFF') {
      ctx.fillStyle = cell.bgColor;
      ctx.fillRect(pixelX, pixelY, cellSize, cellSize);
    }

    // Draw character if present
    if (cell?.char && cell.char !== ' ') {
      ctx.fillStyle = cell.color || '#FFFFFF';
      ctx.font = `${cellSize - 2}px 'Courier New', monospace`;
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      ctx.fillText(
        cell.char, 
        pixelX + cellSize / 2, 
        pixelY + cellSize / 2
      );
    }

    // Draw grid lines only if grid is visible
    if (showGrid) {
      ctx.strokeStyle = canvasBackgroundColor === '#000000' ? '#333333' : '#E5E7EB';
      ctx.lineWidth = 0.5;
      ctx.strokeRect(pixelX, pixelY, cellSize, cellSize);
    }
  }, [cellSize, canvasBackgroundColor, showGrid]);

  // Render the entire grid
  const renderGrid = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Clear canvas and fill with background color
    ctx.fillStyle = canvasBackgroundColor;
    ctx.fillRect(0, 0, canvasWidth, canvasHeight);

    // Create a set of coordinates that are being moved (to skip in normal rendering)
    const movingCells = new Set<string>();
    if (moveState) {
      const originalKeys = moveState.originalPositions ?? new Set(moveState.originalData.keys());
      originalKeys.forEach(key => {
        movingCells.add(key);
      });
    }

    // Draw all cells (excluding cells that are being moved)
    for (let y = 0; y < height; y++) {
      for (let x = 0; x < width; x++) {
        const key = `${x},${y}`;

        // Skip cells that are being moved during preview
        if (movingCells.has(key)) {
          // Draw empty cell in original position during move (use default black background)
          drawCell(ctx, x, y, { char: ' ', color: '#FFFFFF', bgColor: '#000000' });
        } else {
          const cell = getCell(x, y);
          drawCell(ctx, x, y, cell);
        }
      }
    }

    // Draw moved cells at their new positions during preview
    if (moveState && moveState.originalData.size > 0) {
      const totalOffset = getTotalOffset(moveState);
      moveState.originalData.forEach((cell, key) => {
        const [origX, origY] = key.split(',').map(Number);
        const newX = origX + totalOffset.x;
        const newY = origY + totalOffset.y;

        // Only draw if within bounds
        if (newX >= 0 && newX < width && newY >= 0 && newY < height) {
          drawCell(ctx, newX, newY, cell);
        }
      });
    }
  }, [width, height, getCell, drawCell, canvasWidth, canvasHeight, moveState, getTotalOffset, canvasBackgroundColor, showGrid]);

  // Re-render when dependencies change
  useEffect(() => {
    renderGrid();
  }, [renderGrid]);

  // Handle canvas resize
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    // Set canvas size
    canvas.width = canvasWidth;
    canvas.height = canvasHeight;

    // Re-render after resize
    renderGrid();
  }, [canvasWidth, canvasHeight, renderGrid]);

  return null; // This component only handles rendering logic, no UI
};

