                      >
                        <ChevronLeft className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Move color left{!activePalette?.isCustom ? ' (will create custom copy)' : ''}</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>

                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => selectedColorId && moveColorRight(activePaletteId, selectedColorId)}
                        disabled={!selectedColorId || !activePalette?.isCustom}
                      >
                        <ChevronRight className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Move color right{!activePalette?.isCustom ? ' (will create custom copy)' : ''}</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>

                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => addColor(activePaletteId, getCurrentActiveColor())}
                      >
                        <Plus className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Add color{!activePalette?.isCustom ? ' (will create custom copy)' : ''}</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>

                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => selectedColorId && removeColor(activePaletteId, selectedColorId)}
                        disabled={!selectedColorId || activeColors.length <= 1}
                      >
                        <Trash2 className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Remove color{!activePalette?.isCustom ? ' (will create custom copy)' : ''}</p>
                    </TooltipContent>
                    </Tooltip>
                  </TooltipProvider>
                </div>

              {/* Import/Export buttons (always visible) */}
              <div className="flex gap-0.5">
                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => setIsImportDialogOpen(true)}
                      >
                        <Upload className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Import palette</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>

                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => setIsExportDialogOpen(true)}
                      >
                        <Download className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Export palette</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>
              </div>
            </div>
          )}

          {/* Status text */}
          {activePalette && (
            <div className="text-xs text-muted-foreground text-center">
              {activePalette.name} â€¢ {activeColors.length} colors
              {selectedColorId && (
                <span className="ml-2">
                  â€¢ Selected: {activeColors.find(c => c.id === selectedColorId)?.name || 'Color'}
                </span>
              )}
            </div>
          )}
        </CollapsibleContent>
      </Collapsible>

      {/* Divider between Color Palette and Effects */}
      <PanelSeparator />

      {/* Effects Section */}
      <EffectsSection />

      {/* Divider after Effects */}
      <PanelSeparator />

      {/* Color Picker Modal */}
      <ColorPickerOverlay
        isOpen={isColorPickerOpen}
        onOpenChange={(open) => {
          setIsColorPickerOpen(open);
          // Reset editing state when closing
          if (!open && colorPickerMode === 'palette') {
            setEditingColorId(null);
            setColorPickerMode('foreground');
            setColorPickerTriggerRef(undefined);
          }
        }}
        onColorSelect={handleColorPickerSelect}
        onColorChange={colorPickerMode !== 'palette' ? handleColorPickerChange : undefined}
        initialColor={colorPickerInitialColor}
        title={
          colorPickerMode === 'palette' 
            ? 'Edit Palette Color' 
            : `Edit ${colorPickerMode === 'foreground' ? 'Foreground' : 'Background'} Color`
        }
        showTransparentOption={colorPickerMode === 'background'}
        triggerRef={colorPickerTriggerRef}
        anchorPosition="bottom-right"
      />

      {/* Import/Export Dialogs */}
      <ImportPaletteDialog
        isOpen={isImportDialogOpen}
        onOpenChange={setIsImportDialogOpen}
      />

      <ExportPaletteDialog
        isOpen={isExportDialogOpen}
        onOpenChange={setIsExportDialogOpen}
      />

      {/* Manage Palettes Dialog */}
      <ManagePalettesDialog
        isOpen={isManagePalettesOpen}
        onOpenChange={setIsManagePalettesOpen}
      />
    </div>
  );
};

// Advanced color picker overlay with improved HSV/RGB/HEX controls and interactive color wheel

import React, { useState, useCallback, useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Slider } from '@/components/ui/slider';
import { Label } from '@/components/ui/label';
import { Separator } from '@/components/ui/separator';
import { Card, CardContent } from '@/components/ui/card';
import { Pipette, RotateCcw, Check, X } from 'lucide-react';
import { DraggableDialogBar } from '@/components/common/DraggableDialogBar';
import { usePaletteStore } from '../../stores/paletteStore';
import type { HSVColor, RGBColor } from '../../types/palette';
import { 
  hexToRgb, 
  rgbToHex, 
  hexToHsv, 
  hsvToHex, 
  hsvToRgb, 
  rgbToHsv,
  normalizeHexColor
} from '../../utils/colorConversion';
import { ANSI_COLORS } from '../../constants/colors';

interface ColorPickerOverlayProps {
  isOpen: boolean;
  onOpenChange: (open: boolean) => void;
  onColorSelect: (color: string) => void;
  onColorChange?: (color: string) => void; // For real-time updates
  onCancel?: () => void; // For canceling changes
  initialColor?: string;
  title?: string;
  showTransparentOption?: boolean; // whether to show the transparent quick-set button
  triggerRef?: React.RefObject<HTMLElement | null>; // Reference to trigger element for positioning
  anchorPosition?: 'left-slide' | 'right-slide' | 'bottom-left' | 'bottom-right' | 'gradient-panel' | 'import-media-panel';
}

export const ColorPickerOverlay: React.FC<ColorPickerOverlayProps> = ({
  isOpen,
  onOpenChange,
  onColorSelect,
  onColorChange,
  onCancel,
  initialColor = '#000000',
  title = 'Color Picker',
  showTransparentOption = false,
  triggerRef,
  anchorPosition = 'left-slide'
}) => {
  const { updatePreviewColor, addRecentColor, recentColors } = usePaletteStore();
  const pickerRef = useRef<HTMLDivElement>(null);
  const previousColorRef = useRef<string | null>(null);

  // Check if initial color is transparent
  const isTransparent = initialColor === 'transparent' || initialColor === ANSI_COLORS.transparent;

  // Color state in different formats
  const [currentColor, setCurrentColor] = useState(initialColor);
  const [previewColor, setPreviewColor] = useState(isTransparent ? 'transparent' : initialColor);
  const [hexInput, setHexInput] = useState(isTransparent ? '' : initialColor);
  const [rgbValues, setRgbValues] = useState<RGBColor>({ r: 0, g: 0, b: 0 });
  // Initialize HSV values from the initial color
  const getInitialHSV = () => {
    if (initialColor === 'transparent' || initialColor === ANSI_COLORS.transparent) {
      return { h: 0, s: 0, v: 0 };
    }
    const hsv = hexToHsv(normalizeHexColor(initialColor));
    return hsv || { h: 120, s: 100, v: 80 }; // Default to green if parsing fails
  };

  const [hsvValues, setHsvValues] = useState<HSVColor>(getInitialHSV());
  const [colorWheelPosition, setColorWheelPosition] = useState({ x: 80, y: 80 }); // Center of 160px wheel
  const [valueSliderValue, setValueSliderValue] = useState(() => {
    if (initialColor === 'transparent' || initialColor === ANSI_COLORS.transparent) {
      return 0;
    }
    const hsv = hexToHsv(normalizeHexColor(initialColor));
    return hsv ? hsv.v : 80; // Default to 80% if parsing fails
  });
  const [isDragging, setIsDragging] = useState(false);
  const [isTransparentColor, setIsTransparentColor] = useState(isTransparent);
  const [hasInitialized, setHasInitialized] = useState(false);
  const [positionOffset, setPositionOffset] = useState({ x: 0, y: 0 });
  const [isDraggingDialog, setIsDraggingDialog] = useState(false);
  const [hasBeenDragged, setHasBeenDragged] = useState(false);
  const dragStartOffsetRef = useRef({ x: 0, y: 0 });

  const colorWheelRef = useRef<HTMLDivElement>(null);
  // Ref for hex input to auto-focus on open
  const hexInputRef = useRef<HTMLInputElement | null>(null);

  // Initialize color values when dialog opens or initial color changes
  useEffect(() => {
    if (isOpen) {
      // Reset position offset and drag state when dialog opens
      setPositionOffset({ x: 0, y: 0 });
      setHasBeenDragged(false);

      const transparent = initialColor === 'transparent' || initialColor === ANSI_COLORS.transparent;
      setIsTransparentColor(transparent);


      if (transparent) {
        // Handle transparent color
        setCurrentColor('transparent');
        setPreviewColor('transparent');
        setHexInput('');
        setRgbValues({ r: 0, g: 0, b: 0 });
        setHsvValues({ h: 0, s: 0, v: 0 });
        setValueSliderValue(0);
        setColorWheelPosition({ x: 80, y: 80 });
      } else {
        // Handle normal color
        const color = normalizeHexColor(initialColor);
        setCurrentColor(color);
        setPreviewColor(color);
        setHexInput(color);

        const rgb = hexToRgb(color);
        const hsv = hexToHsv(color);

        if (rgb) setRgbValues(rgb);
        if (hsv) {
          setHsvValues(hsv);
          setValueSliderValue(hsv.v);
          updateColorWheelPosition(hsv);
        }
      }

      // Mark as initialized after setting all values
      setTimeout(() => setHasInitialized(true), 0);
    } else {
      setHasInitialized(false);
    }
  }, [isOpen, initialColor]);

  // Update preview color in store
  useEffect(() => {
    updatePreviewColor(previewColor);
  }, [previewColor, updatePreviewColor]);

  // Auto-focus and select hex input when dialog opens (if not transparent)
  useEffect(() => {
    if (isOpen) {
      // Use rAF to ensure the input is mounted after dialog animation/layout
      requestAnimationFrame(() => {
        if (hexInputRef.current && !isTransparentColor) {
          hexInputRef.current.focus();
          // Select existing content for quick replacement/paste
          hexInputRef.current.select();
        }
      });
    }
  }, [isOpen, isTransparentColor]);

  // Close picker when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        isOpen &&
        pickerRef.current &&
        !pickerRef.current.contains(event.target as Node) &&
        triggerRef?.current &&
        !triggerRef.current.contains(event.target as Node)
      ) {
        onOpenChange(false);
      }
    };

    if (isOpen) {
      document.addEventListener('mousedown', handleClickOutside);
      return () => document.removeEventListener('mousedown', handleClickOutside);
    }
  }, [isOpen, onOpenChange, triggerRef]);

  // Close picker on escape key
  useEffect(() => {
    const handleEscape = (event: KeyboardEvent) => {
      if (event.key === 'Escape' && isOpen) {
        onOpenChange(false);
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
      return () => document.removeEventListener('keydown', handleEscape);
    }
  }, [isOpen, onOpenChange]);

  // Position calculation
  const getPickerPosition = () => {
    const pickerWidth = 400;
    const pickerHeight = 600;

    // Special handling for gradient panel
    if (anchorPosition === 'gradient-panel') {
      // Center the picker vertically in the viewport
      const viewportHeight = window.innerHeight;
      const top = Math.max(8, (viewportHeight - pickerHeight) / 2 + window.scrollY);

      // Position to the left of the gradient panel (which is 320px wide and on the right side)
      const gradientPanelWidth = 320;
      const left = window.innerWidth - gradientPanelWidth - pickerWidth - 16; // 16px gap

      return {
        top,
        left: Math.max(8, left), // Ensure it doesn't go off-screen
        right: 'auto'
      };
    }

    // Special handling for import media panel
    if (anchorPosition === 'import-media-panel') {
      // Center the picker vertically in the viewport
      const viewportHeight = window.innerHeight;
      const top = Math.max(8, (viewportHeight - pickerHeight) / 2 + window.scrollY);

      // Position to the left of the import media panel (which is 320px wide and on the right side)
      // Align with the left edge of the import media panel
      const importPanelWidth = 320; // 80*4 = 320px as seen in the MediaImportPanel
      const left = window.innerWidth - importPanelWidth - pickerWidth - 8; // 8px gap for alignment with panel edge

      return {
        top,
        left: Math.max(8, left), // Ensure it doesn't go off-screen
        right: 'auto'
      };
    }

    // For other anchor positions, we need a triggerRef
    if (!triggerRef?.current) return { top: 100, left: 100 };

    const triggerRect = triggerRef.current.getBoundingClientRect();

    // Center the picker vertically in the viewport for other cases
    const viewportHeight = window.innerHeight;
    const top = Math.max(8, (viewportHeight - pickerHeight) / 2 + window.scrollY);

    if (anchorPosition === 'left-slide') {
      // Slide out from the left side of the trigger, but center vertically
      let right = window.innerWidth - triggerRect.left + 8; // 8px gap from trigger

      return {
        top,
        right,
        left: 'auto'
      };
    } else if (anchorPosition === 'right-slide') {
      // Slide out from the right side of the trigger, but center vertically
      let left = triggerRect.right + 8; // 8px gap from trigger

      // Ensure picker doesn't go off-screen horizontally
      if (left + pickerWidth > window.innerWidth) {
        left = window.innerWidth - pickerWidth - 8;
      }

      return {
        top,
        left,
        right: 'auto'
      };
    } else if (anchorPosition === 'bottom-left') {
      // Position below the trigger element, aligned to the left edge
      let top = triggerRect.bottom + 8; // 8px gap below trigger
      let left = triggerRect.left;

      // Ensure picker doesn't go off-screen horizontally
      if (left + pickerWidth > window.innerWidth) {
        left = window.innerWidth - pickerWidth - 8;
      }

      // Check if it would go off bottom of screen
      if (top + pickerHeight > window.innerHeight + window.scrollY) {
        // Try positioning above the trigger instead
        const topAbove = triggerRect.top + window.scrollY - pickerHeight - 8;

        // If positioning above would go off the top, use smart positioning
        if (topAbove < window.scrollY + 8) {
          // Position it in the viewport with available space
          const maxTop = Math.max(window.scrollY + 8, triggerRect.bottom + window.scrollY + 8);
          const maxBottom = window.scrollY + window.innerHeight - 8;

          // Use the position that gives us the most space
          if (triggerRect.top > window.innerHeight / 2) {
            // More space above, position above the trigger
            top = Math.max(window.scrollY + 8, triggerRect.top + window.scrollY - pickerHeight - 8);
          } else {
            // More space below, position below the trigger but constrained
            top = Math.min(maxTop, maxBottom - pickerHeight);
          }
        } else {
          top = topAbove;
        }
      }

      return {
        top,
        left,
        right: 'auto'
      };
    } else if (anchorPosition === 'bottom-right') {
      // Position below the trigger element, aligned to the right edge
      let top = triggerRect.bottom + 8; // 8px gap below trigger
      let left = triggerRect.right - pickerWidth;

      // Ensure picker doesn't go off-screen horizontally
      if (left < 8) {
        left = 8;
      }

      // Check if it would go off bottom of screen
      if (top + pickerHeight > window.innerHeight + window.scrollY) {
        // Try positioning above the trigger instead
        const topAbove = triggerRect.top + window.scrollY - pickerHeight - 8;

        // If positioning above would go off the top, use smart positioning
        if (topAbove < window.scrollY + 8) {
          // Position it in the viewport with available space
          const maxTop = Math.max(window.scrollY + 8, triggerRect.bottom + window.scrollY + 8);
          const maxBottom = window.scrollY + window.innerHeight - 8;

          // Use the position that gives us the most space
          if (triggerRect.top > window.innerHeight / 2) {
            // More space above, position above the trigger
            top = Math.max(window.scrollY + 8, triggerRect.top + window.scrollY - pickerHeight - 8);
          } else {
            // More space below, position below the trigger but constrained
            top = Math.min(maxTop, maxBottom - pickerHeight);
          }
        } else {
          top = topAbove;
        }
      }

      return {
        top,
        left,
        right: 'auto'
      };
    } else {
      // Default left-slide behavior with vertical centering
      return {
        top,
        right: window.innerWidth - triggerRect.left + 8,
        left: 'auto'
      };
    }
  };

  // Trigger real-time updates when color values change
  useEffect(() => {
    // Update preview color and trigger real-time callbacks when values change
    if (hasInitialized && isOpen) {
      let newColor: string;
      if (isTransparentColor) {
        newColor = 'transparent';
      } else {
        newColor = hsvToHex(hsvValues);
      }

      setPreviewColor(newColor);

      // Trigger real-time update if callback is provided and color actually changed
      // Using ref to track changes prevents circular dependencies in useEffect deps
      // while ensuring we only fire callbacks when the color genuinely changes
      if (onColorChange && newColor !== previousColorRef.current) {
        previousColorRef.current = newColor;
        const timeoutId = setTimeout(() => {
          onColorChange(newColor);
        }, 50); // 50ms debounce to prevent excessive calls during rapid changes

        return () => clearTimeout(timeoutId);
      }
    }
  }, [hsvValues, isTransparentColor, hasInitialized, isOpen, onColorChange]);

  // Update color wheel position based on HSV values
  const updateColorWheelPosition = useCallback((hsv: HSVColor) => {
    const centerX = 80; // Half of 160px wheel
    const centerY = 80;
    const maxRadius = 72; // Slightly less than 80 to stay within bounds
    const angle = (hsv.h * Math.PI) / 180;
    const radius = (hsv.s / 100) * maxRadius;
    // Round to mitigate sub-pixel oscillation that can cause jitter when React recalculates layout
    const x = +(centerX + radius * Math.cos(angle)).toFixed(2);
    const y = +(centerY + radius * Math.sin(angle)).toFixed(2);
    setColorWheelPosition({ x, y });
  }, []);

  // Color update handlers
  const updateFromHex = useCallback((hex: string) => {
    const normalizedHex = normalizeHexColor(hex);
    const rgb = hexToRgb(normalizedHex);
    const hsv = hexToHsv(normalizedHex);

    if (rgb && hsv) {
      setPreviewColor(normalizedHex);
      setHexInput(normalizedHex);
      setRgbValues(rgb);
      setHsvValues(hsv);
      setValueSliderValue(hsv.v);
      updateColorWheelPosition(hsv);
      setIsTransparentColor(false);

      // Trigger real-time update
      if (onColorChange) {
        onColorChange(normalizedHex);
      }
    }
  }, [updateColorWheelPosition, onColorChange]);

  const updateFromRgb = useCallback((rgb: RGBColor) => {
    const hex = rgbToHex(rgb);
    const hsv = rgbToHsv(rgb);

    setPreviewColor(hex);
    setHexInput(hex);
    setHsvValues(hsv);
    setValueSliderValue(hsv.v);
    updateColorWheelPosition(hsv);
    setIsTransparentColor(false);

    // Trigger real-time update
    if (onColorChange) {
      onColorChange(hex);
    }
  }, [updateColorWheelPosition, onColorChange]);

  const updateFromHsv = useCallback((hsv: HSVColor, skipWheelPosition: boolean = false) => {
    const rgb = hsvToRgb(hsv);
    const hex = hsvToHex(hsv);
    setPreviewColor(hex);
    setHexInput(hex);
    setRgbValues(rgb);
    setValueSliderValue(hsv.v);
    if (!skipWheelPosition) {
      updateColorWheelPosition(hsv);
    }
    setIsTransparentColor(false);

    // Trigger real-time update
    if (onColorChange) {
      onColorChange(hex);
    }
  }, [updateColorWheelPosition, onColorChange]);

  // Handle hex input change with live sanitization
  const handleHexChange = (value: string) => {
    // Remove any non-hex characters and convert to uppercase
    let sanitized = value.replace(/[^#0-9A-Fa-f]/g, '').toUpperCase();

    // Ensure it starts with # and limit length
    if (!sanitized.startsWith('#')) {
      sanitized = '#' + sanitized.replace(/#/g, ''); // Remove any # that's not at the start
    }

    // Limit to 7 characters max (#FFFFFF)
    if (sanitized.length > 7) {
      sanitized = sanitized.slice(0, 7);
    }

    setHexInput(sanitized);

    // Only update color if we have a valid 6-digit hex
    if (/^#[0-9A-F]{6}$/.test(sanitized)) {
      updateFromHex(sanitized);
    }
  };

  // Handle RGB slider changes
  const handleRgbChange = (component: 'r' | 'g' | 'b', value: number) => {
    if (isTransparentColor) {
      // Wake from transparent mode
      setIsTransparentColor(false);
    }
    const newRgb = { ...rgbValues, [component]: Math.round(value) };
    setRgbValues(newRgb);
    updateFromRgb(newRgb);
  };

  // Handle RGB input changes
  const handleRgbInputChange = (component: 'r' | 'g' | 'b', value: string) => {
    const numValue = parseInt(value);
    if (!isNaN(numValue) && numValue >= 0 && numValue <= 255) {
      handleRgbChange(component, numValue);
    }
  };

  // Handle HSV slider changes
  const handleHsvChange = (component: 'h' | 's' | 'v', value: number) => {
    if (isTransparentColor) {
      setIsTransparentColor(false);
    }
    const roundedValue = component === 'h' ? Math.round(value * 100) / 100 : Math.round(value);
    const newHsv = { ...hsvValues, [component]: roundedValue };
    setHsvValues(newHsv);
    updateFromHsv(newHsv, component === 'v');
  };

  // Handle HSV input changes
  const handleHsvInputChange = (component: 'h' | 's' | 'v', value: string) => {
    const numValue = parseFloat(value);
    const maxValues = { h: 360, s: 100, v: 100 };
    if (!isNaN(numValue) && numValue >= 0 && numValue <= maxValues[component]) {
      handleHsvChange(component, numValue);
    }
  };

  // Handle value slider change (affects the entire color wheel brightness)
  const handleValueSliderChange = (value: number) => {
    if (isTransparentColor) {
      setIsTransparentColor(false);
    }
    const newHsv = { ...hsvValues, v: value };
    setHsvValues(newHsv);
    setValueSliderValue(value);
    updateFromHsv(newHsv, true);
  };

  // Color wheel interaction with drag support
  const handleColorWheelInteraction = (event: React.MouseEvent<HTMLDivElement> | MouseEvent) => {
    if (!colorWheelRef.current) return;
    if (isTransparentColor) {
      setIsTransparentColor(false);
    }

    const rect = colorWheelRef.current.getBoundingClientRect();
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;
    const x = (event.clientX - rect.left) - centerX;
    const y = (event.clientY - rect.top) - centerY;

    const distance = Math.sqrt(x * x + y * y);
    const maxDistance = Math.min(centerX, centerY) - 10;

    // Always calculate the angle for hue
    let angle = Math.atan2(y, x);
    angle = (angle * 180 / Math.PI + 360) % 360; // Convert to 0-360 degrees

    // Calculate saturation based on distance, but clamp to maximum when outside
    let saturation: number;
    let displayX: number;
    let displayY: number;

    if (distance <= maxDistance) {
      // Inside circle - normal behavior
      saturation = (distance / maxDistance) * 100;
      displayX = centerX + x;
      displayY = centerY + y;
    } else {
      // Outside circle - snap to edge with full saturation
      saturation = 100;

      // Calculate position on the edge of the circle
      const edgeX = Math.cos(angle * Math.PI / 180) * maxDistance;
      const edgeY = Math.sin(angle * Math.PI / 180) * maxDistance;
      displayX = centerX + edgeX;
      displayY = centerY + edgeY;
    }

    const newHsv = { ...hsvValues, h: angle, s: saturation };
    setHsvValues(newHsv);
    updateFromHsv(newHsv);

    // Update position for visual feedback
    setColorWheelPosition({ x: displayX, y: displayY });
  };

  const handleColorWheelMouseDown = (event: React.MouseEvent<HTMLDivElement>) => {
    setIsDragging(true);
    handleColorWheelInteraction(event);
  };

  // Mouse move handler for dragging
  useEffect(() => {
    if (!isDragging) return;

    const handleMouseMove = (event: MouseEvent) => {
      handleColorWheelInteraction(event);
    };

    const handleMouseUp = () => {
      setIsDragging(false);
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging]);

  // Eyedropper functionality
  const handleEyedropper = async () => {
    if ('EyeDropper' in window) {
      try {
        // @ts-ignore - EyeDropper API is experimental
        const eyeDropper = new window.EyeDropper();
        const result = await eyeDropper.open();
        updateFromHex(result.sRGBHex);
      } catch (error) {
        // Eyedropper cancelled or not supported - silently handle
      }
    }
  };

  // Recent color selection
  const handleRecentColorSelect = (color: string) => {
    updateFromHex(color);
  };

  // Reset to current color
  const handleReset = () => {
    if (currentColor === 'transparent' || currentColor === ANSI_COLORS.transparent) {
      setIsTransparentColor(true);
      setPreviewColor('transparent');
      setHexInput('');
      setRgbValues({ r: 0, g: 0, b: 0 });
      setHsvValues({ h: 0, s: 0, v: 0 });
      setValueSliderValue(0);
      setColorWheelPosition({ x: 80, y: 80 });
    } else {
      updateFromHex(currentColor);
    }
  };

  // Confirm color selection
  const handleConfirm = () => {
    addRecentColor(previewColor);
    onColorSelect(previewColor);
    onOpenChange(false);
  };

  // Cancel selection
  const handleCancel = () => {
    if (onCancel) {
      onCancel();
    } else {
      onOpenChange(false);
    }
  };

  // Set preview state to transparent (without committing until confirm)
  const handleSetTransparent = () => {
    setIsTransparentColor(true);
    setPreviewColor('transparent');
    setHexInput('');
    setRgbValues({ r: 0, g: 0, b: 0 });
    setHsvValues({ h: 0, s: 0, v: 0 });
    setValueSliderValue(0);
    setColorWheelPosition({ x: 80, y: 80 });
  };

  // Handler for dragging the dialog
  const handleDrag = useCallback((deltaX: number, deltaY: number) => {
    // Add the drag delta to the stored offset from when drag started
    setPositionOffset({
      x: dragStartOffsetRef.current.x + deltaX,
      y: dragStartOffsetRef.current.y + deltaY
    });
  }, []);

  // Track dialog drag state for animation control
  const handleDragStart = useCallback(() => {
    setIsDraggingDialog(true);
    setHasBeenDragged(true);
    // Store the current offset when drag starts
    dragStartOffsetRef.current = { ...positionOffset };
  }, [positionOffset]);

  const handleDragEnd = useCallback(() => {
    setIsDraggingDialog(false);
    // Update the ref with the final position
    dragStartOffsetRef.current = { ...positionOffset };
  }, [positionOffset]);

  // Create canvas-based HSV color wheel
  const canvasRef = useRef<HTMLCanvasElement>(null);

  // Generate base HSV color wheel once (at full brightness)
  const renderingRef = useRef(false);
  const baseRendered = useRef(false);
  // Cache original wheel pixel data at V=100 for brightness adjustments
  const baseImageDataRef = useRef<ImageData | null>(null);
  const lastAppliedValueRef = useRef<number | null>(null);
  const dprRef = useRef<number>(typeof window !== 'undefined' ? window.devicePixelRatio || 1 : 1);

  useEffect(() => {
    if (!isOpen || !hasInitialized || baseRendered.current) return;

    const canvas = canvasRef.current;
    if (!canvas || renderingRef.current) return;

    renderingRef.current = true;

    const ctx = canvas.getContext('2d');
    if (!ctx) {
      renderingRef.current = false;
      return;
    }

      const logicalSize = 160; // CSS pixels
      const dpr = dprRef.current;
      const size = Math.round(logicalSize * dpr); // backing store size
      const center = size / 2;
      const radius = center - 4 * dpr; // scale border inset with DPR

      // Set dimensions only once to prevent layout recalculation
      if (canvas.width !== size || canvas.height !== size) {
        canvas.style.width = `${logicalSize}px`;
        canvas.style.height = `${logicalSize}px`;
        canvas.width = size;
        canvas.height = size;
      }

      // Enable canvas smoothing
      ctx.imageSmoothingEnabled = true;
      ctx.imageSmoothingQuality = 'high';
      ctx.setTransform(1,0,0,1,0,0); // reset
      ctx.scale(dpr, dpr); // scale drawing operations to logical pixels

      const imageData = ctx.createImageData(size, size); // we draw in backing pixel space manually
      const data = imageData.data;

      for (let y = 0; y < size; y++) {
        for (let x = 0; x < size; x++) {
          const dx = x - center;
          const dy = y - center;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance <= radius) {
            // Calculate hue from angle
            let angle = Math.atan2(dy, dx);
            angle = (angle * 180 / Math.PI + 360) % 360;

            // Calculate saturation from distance
            const saturation = Math.min(100, (distance / radius) * 100);

          // Use full brightness for base canvas
          const rgb = hsvToRgb({ h: angle, s: saturation, v: 100 });            // Anti-aliasing for smooth edges
            let alpha = 255;
            if (distance > radius - 1) {
              alpha = Math.round(255 * (radius - distance));
            }

            const index = (y * size + x) * 4;
            data[index] = rgb.r;
            data[index + 1] = rgb.g;
            data[index + 2] = rgb.b;
            data[index + 3] = alpha;
          } else {
            // Outside circle - transparent
            const index = (y * size + x) * 4;
            data[index + 3] = 0;
          }
        }
      }

    ctx.putImageData(imageData, 0, 0);
    baseImageDataRef.current = imageData; // store base pixels
    baseRendered.current = true;
    lastAppliedValueRef.current = 100; // initial brightness
    renderingRef.current = false;
  }, [isOpen, hasInitialized]);

  // Reset base canvas when dialog closes
  useEffect(() => {
    if (!isOpen) {
      baseRendered.current = false;
    }
  }, [isOpen]);

  // Re-render brightness when V changes without shifting underlying wheel pixels
  useEffect(() => {
    if (!isOpen || !baseRendered.current) return;
    if (lastAppliedValueRef.current === valueSliderValue) return; // no change
    const canvas = canvasRef.current;
    const base = baseImageDataRef.current;
    if (!canvas || !base) return;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;
    // Create a copy to avoid mutating the base reference
    const copy = ctx.createImageData(base.width, base.height);
    const src = base.data;
    const dst = copy.data;
    const factor = valueSliderValue / 100; // linear scale
    for (let i = 0; i < src.length; i += 4) {
      dst[i] = Math.round(src[i] * factor);
      dst[i + 1] = Math.round(src[i + 1] * factor);
      dst[i + 2] = Math.round(src[i + 2] * factor);
      dst[i + 3] = src[i + 3]; // preserve alpha
    }
    ctx.putImageData(copy, 0, 0);
    lastAppliedValueRef.current = valueSliderValue;
  }, [valueSliderValue, isOpen]);

  if (!isOpen) return null;

  const position = getPickerPosition();

  return createPortal(
    <div
      ref={pickerRef}
      className={`fixed z-[99999] ${
        !hasBeenDragged ? `animate-in duration-200 ${
          anchorPosition === 'right-slide' ? 'slide-in-from-left-2 fade-in-0' : 
          anchorPosition === 'gradient-panel' ? 'slide-in-from-right-2 fade-in-0' : 'slide-in-from-right-2 fade-in-0'
        }` : ''
      }`}
      style={{
        top: position.top + positionOffset.y,
        right: position.right !== 'auto' && typeof position.right === 'number' ? position.right - positionOffset.x : undefined,
        left: position.left !== 'auto' && typeof position.left === 'number' ? position.left + positionOffset.x : undefined,
        maxWidth: '400px',
        width: '400px',
        transition: isDraggingDialog ? 'none' : undefined
      }}
      onMouseDown={(e) => e.stopPropagation()}
      onClick={(e) => e.stopPropagation()}
    >
      <Card className="border border-border/50 shadow-lg">
        <DraggableDialogBar 
          title={title} 
          onDrag={handleDrag}
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
          onClose={handleCancel}
        />
        <CardContent className="space-y-3 pt-3">{/* Color Preview */}
          <div className="flex items-center gap-2">
            <div className="flex-1">
              <div className="flex h-8 border border-border rounded overflow-hidden">
                {/* Current Color */}
                <div className="flex-1 relative">
                  {(currentColor === 'transparent' || currentColor === ANSI_COLORS.transparent) ? (
                    <div className="w-full h-full relative bg-white">
                      <div 
                        className="absolute inset-0"
                        style={{
                          backgroundImage: 'linear-gradient(45deg, #ccc 25%, transparent 25%), linear-gradient(-45deg, #ccc 25%, transparent 25%), linear-gradient(45deg, transparent 75%, #ccc 75%), linear-gradient(-45deg, transparent 75%, #ccc 75%)',
                          backgroundSize: '8px 8px',
                          backgroundPosition: '0 0, 0 4px, 4px -4px, -4px 0px'
                        }}
                      />
                      <svg className="absolute inset-0 w-full h-full" viewBox="0 0 48 48">
                        <line x1="4" y1="44" x2="44" y2="4" stroke="#dc2626" strokeWidth="2" strokeLinecap="round" />
                      </svg>
                    </div>
                  ) : (
                    <div 
                      className="w-full h-full"
                      style={{ backgroundColor: currentColor }}
                      title="Current Color"
                    />
                  )}
                </div>

                {/* Preview Color */}
                <div className="flex-1 relative border-l border-border">
                  {(previewColor === 'transparent' || isTransparentColor) ? (
                    <div className="w-full h-full relative bg-white">
                      <div 
                        className="absolute inset-0"
                        style={{
                          backgroundImage: 'linear-gradient(45deg, #ccc 25%, transparent 25%), linear-gradient(-45deg, #ccc 25%, transparent 25%), linear-gradient(45deg, transparent 75%, #ccc 75%), linear-gradient(-45deg, transparent 75%, #ccc 75%)',
                          backgroundSize: '8px 8px',
                          backgroundPosition: '0 0, 0 4px, 4px -4px, -4px 0px'
                        }}
                      />
                      <svg className="absolute inset-0 w-full h-full" viewBox="0 0 48 48">
                        <line x1="4" y1="44" x2="44" y2="4" stroke="#dc2626" strokeWidth="2" strokeLinecap="round" />
                      </svg>
                    </div>
                  ) : (
                    <div 
                      className="w-full h-full"
                      style={{ backgroundColor: previewColor }}
                      title="New Color"
                    />
                  )}
                </div>
              </div>
            </div>

            {/* Eyedropper Button */}
            {'EyeDropper' in window && (
              <Button
                size="sm"
                variant="outline" 
                onClick={handleEyedropper}
                className="h-8 w-8 p-0"
              >
                <Pipette className="h-4 w-4" />
              </Button>
            )}
          </div>

          {/* Color Wheel and Value Slider */}
          <div className="space-y-1">
            <div className="flex justify-center items-start gap-4">
              {/* Color Wheel */}
              <div 
                ref={colorWheelRef}
                className="rounded-full border border-border cursor-crosshair relative select-none overflow-hidden"
                onMouseDown={handleColorWheelMouseDown}
                style={{ 
                  width: '160px',
                  height: '160px',
                  clipPath: 'circle(50% at 50% 50%)',
                  borderRadius: '50%',
                  flexShrink: 0,
                  contain: 'layout style paint',
                  willChange: 'contents',
                  transform: 'translateZ(0)' // Force hardware acceleration
                }}
              >
                <canvas
                  ref={canvasRef}
                  width={160}
                  height={160}
                  style={{ 
                    width: '160px',
                    height: '160px',
                    borderRadius: '50%',
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    display: 'block',
                    backfaceVisibility: 'hidden'
                  }}
                />
                <div 
                  className="absolute w-3 h-3 bg-white border-2 border-black rounded-full transform -translate-x-1.5 -translate-y-1.5 pointer-events-none"
                  style={{
                    left: Math.round(colorWheelPosition.x),
                    top: Math.round(colorWheelPosition.y)
                  }}
                />
              </div>

              {/* Value Slider - Vertical */}
              <div className="flex flex-col items-center gap-2 ml-4 select-none w-12 flex-shrink-0">
                <Label className="text-xs font-medium select-none">V</Label>
                <div className="relative h-32 w-4 flex items-center justify-center select-none">
                  {/* Custom vertical slider track */}
                  <div className="absolute h-32 w-1 bg-muted rounded-full"></div>
                  {/* Slider handle */}
                  <div 
                    className="absolute w-3 h-3 bg-primary rounded-full cursor-pointer shadow-sm border border-background z-10"
                    style={{
                      top: `${((100 - valueSliderValue) / 100) * 128 - 6}px`,
                      left: '50%',
                      transform: 'translateX(-50%)'
                    }}
                    onMouseDown={(e) => {
                      // Wake from transparent mode if needed
                      if (isTransparentColor) {
                        setIsTransparentColor(false);
                      }
                      e.preventDefault(); // Prevent text selection
                      e.stopPropagation(); // Prevent track click handler

                      const startY = e.clientY;
                      const startValue = valueSliderValue;

                      const handleMouseMove = (moveEvent: MouseEvent) => {
                        const deltaY = moveEvent.clientY - startY;
                        const newValue = Math.max(0, Math.min(100, startValue - (deltaY / 128) * 100));
                        handleValueSliderChange(newValue);
                      };

                      const handleMouseUp = () => {
                        document.removeEventListener('mousemove', handleMouseMove);
                        document.removeEventListener('mouseup', handleMouseUp);
                      };

                      document.addEventListener('mousemove', handleMouseMove);
                      document.addEventListener('mouseup', handleMouseUp);
                    }}
                  />
                  {/* Click on track to set value */}
                  <div 
                    className="absolute inset-0 cursor-pointer select-none"
                    onMouseDown={(e) => {
                      if (isTransparentColor) {
                        setIsTransparentColor(false);
                      }
                      e.preventDefault(); // Prevent text selection

                      const rect = e.currentTarget.getBoundingClientRect();
                      const y = e.clientY - rect.top;
                      const newValue = Math.max(0, Math.min(100, 100 - (y / rect.height) * 100));
                      handleValueSliderChange(newValue);

                      // Start dragging from track click
                      const handleMouseMove = (moveEvent: MouseEvent) => {
                        const moveY = moveEvent.clientY - rect.top;
                        const moveValue = Math.max(0, Math.min(100, 100 - (moveY / rect.height) * 100));
                        handleValueSliderChange(moveValue);
                      };

                      const handleMouseUp = () => {
                        document.removeEventListener('mousemove', handleMouseMove);
                        document.removeEventListener('mouseup', handleMouseUp);
                      };

                      document.addEventListener('mousemove', handleMouseMove);
                      document.addEventListener('mouseup', handleMouseUp);
                    }}
                  />
                </div>
                <span 
                  className="text-xs text-muted-foreground inline-block text-center"
                  style={{ width: '100%', fontVariantNumeric: 'tabular-nums' }}
                >
                  {isTransparentColor ? '-' : `${Math.round(valueSliderValue)}%`}
                </span>
              </div>
            </div>
          </div>

          <Separator />

          {/* HSV Controls */}
          <div className="space-y-2">
            {/* Hue */}
            <div className="flex items-center gap-1.5">
                <Label className="w-3 text-xs">H</Label>
              <Slider
                value={hsvValues.h}
                onValueChange={(value) => handleHsvChange('h', value)}
                max={360}
                step={0.1}
                className="flex-1"
              />
              <Input
                value={hsvValues.h.toFixed(2)}
                onChange={(e) => handleHsvInputChange('h', e.target.value)}
                className="w-16 h-7 text-xs select-text"
              />
              <span className="text-xs text-muted-foreground w-2">Â°</span>
            </div>

            {/* Saturation */}
            <div className="flex items-center gap-1.5">
              <Label className="w-3 text-xs">S</Label>
              <Slider
                value={hsvValues.s}
                onValueChange={(value) => handleHsvChange('s', value)}
                max={100}
                step={1}
                className="flex-1"
              />
              <Input
                value={Math.round(hsvValues.s).toString()}
                onChange={(e) => handleHsvInputChange('s', e.target.value)}
                className="w-16 h-7 text-xs"
              />
              <span className="text-xs text-muted-foreground w-2">%</span>
            </div>

            {/* Value */}
            <div className="flex items-center gap-1.5">
              <Label className="w-3 text-xs">V</Label>
              <Slider
                value={hsvValues.v}
                onValueChange={(value) => handleHsvChange('v', value)}
                max={100}
                step={1}
                className="flex-1"
              />
              <Input
                value={Math.round(hsvValues.v).toString()}
                onChange={(e) => handleHsvInputChange('v', e.target.value)}
                className="w-16 h-7 text-xs"
              />
              <span className="text-xs text-muted-foreground w-2">%</span>
            </div>
          </div>

          <Separator />

          {/* RGB Controls */}
          <div className="space-y-2">
            {/* Red */}
            <div className="flex items-center gap-1.5">
              <Label className="w-3 text-xs">R</Label>
              <Slider
                value={rgbValues.r}
                onValueChange={(value) => handleRgbChange('r', value)}
                max={255}
                step={1}
                className="flex-1"
              />
              <Input
                value={rgbValues.r.toString()}
                onChange={(e) => handleRgbInputChange('r', e.target.value)}
                className="w-14 h-7 text-xs"
              />
            </div>

            {/* Green */}
            <div className="flex items-center gap-1.5">
              <Label className="w-3 text-xs">G</Label>
              <Slider
                value={rgbValues.g}
                onValueChange={(value) => handleRgbChange('g', value)}
                max={255}
                step={1}
                className="flex-1"
              />
              <Input
                value={rgbValues.g.toString()}
                onChange={(e) => handleRgbInputChange('g', e.target.value)}
                className="w-14 h-7 text-xs"
              />
            </div>

            {/* Blue */}
            <div className="flex items-center gap-1.5">
              <Label className="w-3 text-xs">B</Label>
              <Slider
                value={rgbValues.b}
                onValueChange={(value) => handleRgbChange('b', value)}
                max={255}
                step={1}
                className="flex-1"
              />
              <Input
                value={rgbValues.b.toString()}
                onChange={(e) => handleRgbInputChange('b', e.target.value)}
                className="w-14 h-7 text-xs"
              />
            </div>
          </div>

          <Separator />

          {/* Hex Input (and optional Transparent Button) */}
          <div className={`flex items-center ${showTransparentOption ? 'gap-2' : ''}`}>
            <Input
              ref={hexInputRef}
              value={hexInput}
              onChange={(e) => {
                if (isTransparentColor) {
                  setIsTransparentColor(false);
                }
                handleHexChange(e.target.value);
              }}
              placeholder={isTransparentColor ? 'Transparent' : '#000000'}
              className={`font-mono h-7 text-xs ${showTransparentOption ? 'flex-1' : 'w-full'}`}
            />
            {showTransparentOption && (
              <Button
                type="button"
                variant="outline"
                size="sm"
                onClick={handleSetTransparent}
                title="Set Transparent"
                aria-label="Set Transparent"
                className={`h-7 w-7 p-0 overflow-hidden ${isTransparentColor ? 'ring-2 ring-primary ring-offset-1' : ''}`}
              >
                <div className="w-full h-full relative bg-white">
                  <div
                    className="absolute inset-0"
                    style={{
                      backgroundImage: 'linear-gradient(45deg, #ccc 25%, transparent 25%), linear-gradient(-45deg, #ccc 25%, transparent 25%), linear-gradient(45deg, transparent 75%, #ccc 75%), linear-gradient(-45deg, transparent 75%, #ccc 75%)',
                      backgroundSize: '8px 8px',
                      backgroundPosition: '0 0, 0 4px, 4px -4px, -4px 0px'
                    }}
                  />
                  <svg className="absolute inset-0 w-full h-full" viewBox="0 0 48 48">
                    <line x1="4" y1="44" x2="44" y2="4" stroke="#dc2626" strokeWidth="2" strokeLinecap="round" />
                  </svg>
                </div>
              </Button>
            )}
          </div>

          {/* Recent Colors */}
          {recentColors.length > 0 && (
            <div className="space-y-1">
              <Label className="text-sm font-medium">Recent Colors</Label>
              <div className="flex gap-1 flex-wrap">
                {recentColors.slice(0, 10).map((color, index) => (
                  <button
                    key={index}
                    className="w-5 h-5 rounded border border-border hover:scale-110 transition-transform"
                    style={{ backgroundColor: color }}
                    onClick={() => handleRecentColorSelect(color)}
                    title={color}
                  />
                ))}
              </div>
            </div>
          )}

        {/* Dialog Footer */} 
        <div className="flex justify-between pt-3">
          <Button
            variant="outline"
            onClick={handleReset}
            className="gap-2"
          >
            <RotateCcw className="h-4 w-4" />
            Reset
          </Button>

          <div className="flex gap-2">
            <Button
              variant="outline"
              onClick={handleCancel}
              className="gap-2"
            >
              <X className="h-4 w-4" />
              Cancel
            </Button>

            <Button
              onClick={handleConfirm}
              className="gap-2"
            >
              <Check className="h-4 w-4" />
              OK
            </Button>
          </div>
        </div>
        </CardContent>
      </Card>
    </div>,
    document.body
  );
};

// Advanced color picker overlay with improved HSV/RGB/HEX controls and interactive color wheel

import React, { useState, useCallback, useEffect, useRef } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Slider } from '@/components/ui/slider';
import { Label } from '@/components/ui/label';
import { Separator } from '@/components/ui/separator';
import { Pipette, RotateCcw, Check, X } from 'lucide-react';
import { usePaletteStore } from '../../stores/paletteStore';
import type { HSVColor, RGBColor } from '../../types/palette';
import { 
  hexToRgb, 
  rgbToHex, 
  hexToHsv, 
  hsvToHex, 
  hsvToRgb, 
  rgbToHsv,
  normalizeHexColor
} from '../../utils/colorConversion';
import { ANSI_COLORS } from '../../constants/colors';

interface ColorPickerOverlayProps {
  isOpen: boolean;
  onOpenChange: (open: boolean) => void;
  onColorSelect: (color: string) => void;
  initialColor?: string;
  title?: string;
}

export const ColorPickerOverlay: React.FC<ColorPickerOverlayProps> = ({
  isOpen,
  onOpenChange,
  onColorSelect,
  initialColor = '#000000',
  title = 'Color Picker'
}) => {
  const { updatePreviewColor, addRecentColor, recentColors } = usePaletteStore();

  // Check if initial color is transparent
  const isTransparent = initialColor === 'transparent' || initialColor === ANSI_COLORS.transparent;

  // Color state in different formats
  const [currentColor, setCurrentColor] = useState(initialColor);
  const [previewColor, setPreviewColor] = useState(isTransparent ? 'transparent' : initialColor);
  const [hexInput, setHexInput] = useState(isTransparent ? '' : initialColor);
  const [rgbValues, setRgbValues] = useState<RGBColor>({ r: 0, g: 0, b: 0 });
  const [hsvValues, setHsvValues] = useState<HSVColor>({ h: 0, s: 0, v: 0 });
  const [colorWheelPosition, setColorWheelPosition] = useState({ x: 64, y: 64 }); // Center of 128px wheel
  const [valueSliderValue, setValueSliderValue] = useState(100);
  const [isDragging, setIsDragging] = useState(false);
  const [isTransparentColor, setIsTransparentColor] = useState(isTransparent);

  const colorWheelRef = useRef<HTMLDivElement>(null);

  // Initialize color values when dialog opens or initial color changes
  useEffect(() => {
    if (isOpen) {
      const transparent = initialColor === 'transparent' || initialColor === ANSI_COLORS.transparent;
      setIsTransparentColor(transparent);

      if (transparent) {
        // Handle transparent color
        setCurrentColor('transparent');
        setPreviewColor('transparent');
        setHexInput('');
        setRgbValues({ r: 0, g: 0, b: 0 });
        setHsvValues({ h: 0, s: 0, v: 0 });
        setValueSliderValue(0);
        setColorWheelPosition({ x: 64, y: 64 });
      } else {
        // Handle normal color
        const color = normalizeHexColor(initialColor);
        setCurrentColor(color);
        setPreviewColor(color);
        setHexInput(color);

        const rgb = hexToRgb(color);
        const hsv = hexToHsv(color);

        if (rgb) setRgbValues(rgb);
        if (hsv) {
          setHsvValues(hsv);
          setValueSliderValue(hsv.v);
          updateColorWheelPosition(hsv);
        }
      }
    }
  }, [isOpen, initialColor]);

  // Update preview color in store
  useEffect(() => {
    updatePreviewColor(previewColor);
  }, [previewColor, updatePreviewColor]);

  // Update color wheel position based on HSV values
  const updateColorWheelPosition = useCallback((hsv: HSVColor) => {
    const centerX = 64; // Half of 128px wheel
    const centerY = 64;
    const maxRadius = 54; // Slightly less than 64 to stay within bounds

    const angle = (hsv.h * Math.PI) / 180;
    const radius = (hsv.s / 100) * maxRadius;

    const x = centerX + radius * Math.cos(angle);
    const y = centerY + radius * Math.sin(angle);

    setColorWheelPosition({ x, y });
  }, []);

  // Color update handlers
  const updateFromHex = useCallback((hex: string) => {
    const normalizedHex = normalizeHexColor(hex);
    const rgb = hexToRgb(normalizedHex);
    const hsv = hexToHsv(normalizedHex);

    if (rgb && hsv) {
      setPreviewColor(normalizedHex);
      setRgbValues(rgb);
      setHsvValues(hsv);
      setValueSliderValue(hsv.v);
      updateColorWheelPosition(hsv);
      setIsTransparentColor(false);
    }
  }, [updateColorWheelPosition]);

  const updateFromRgb = useCallback((rgb: RGBColor) => {
    const hex = rgbToHex(rgb);
    const hsv = rgbToHsv(rgb);

    setPreviewColor(hex);
    setHexInput(hex);
    setHsvValues(hsv);
    setValueSliderValue(hsv.v);
    updateColorWheelPosition(hsv);
    setIsTransparentColor(false);
  }, [updateColorWheelPosition]);

  const updateFromHsv = useCallback((hsv: HSVColor) => {
    const rgb = hsvToRgb(hsv);
    const hex = hsvToHex(hsv);

    setPreviewColor(hex);
    setHexInput(hex);
    setRgbValues(rgb);
    setValueSliderValue(hsv.v);
    updateColorWheelPosition(hsv);
    setIsTransparentColor(false);
  }, [updateColorWheelPosition]);

  // Handle hex input change
  const handleHexChange = (value: string) => {
    setHexInput(value);
    if (/^#[0-9A-Fa-f]{6}$/.test(value)) {
      updateFromHex(value);
    }
  };

  // Handle RGB slider changes
  const handleRgbChange = (component: 'r' | 'g' | 'b', value: number) => {
    const newRgb = { ...rgbValues, [component]: Math.round(value) };
    setRgbValues(newRgb);
    updateFromRgb(newRgb);
  };

  // Handle RGB input changes
  const handleRgbInputChange = (component: 'r' | 'g' | 'b', value: string) => {
    const numValue = parseInt(value);
    if (!isNaN(numValue) && numValue >= 0 && numValue <= 255) {
      handleRgbChange(component, numValue);
    }
  };

  // Handle HSV slider changes
  const handleHsvChange = (component: 'h' | 's' | 'v', value: number) => {
    const roundedValue = component === 'h' ? Math.round(value * 100) / 100 : Math.round(value);
    const newHsv = { ...hsvValues, [component]: roundedValue };
    setHsvValues(newHsv);
    updateFromHsv(newHsv);
  };

  // Handle HSV input changes
  const handleHsvInputChange = (component: 'h' | 's' | 'v', value: string) => {
    const numValue = parseFloat(value);
    const maxValues = { h: 360, s: 100, v: 100 };
    if (!isNaN(numValue) && numValue >= 0 && numValue <= maxValues[component]) {
      handleHsvChange(component, numValue);
    }
  };

  // Handle value slider change (affects the entire color wheel brightness)
  const handleValueSliderChange = (value: number) => {
    const newHsv = { ...hsvValues, v: value };
    setHsvValues(newHsv);
    setValueSliderValue(value);
    updateFromHsv(newHsv);
  };

  // Color wheel interaction with drag support
  const handleColorWheelInteraction = (event: React.MouseEvent<HTMLDivElement> | MouseEvent) => {
    if (!colorWheelRef.current) return;

    const rect = colorWheelRef.current.getBoundingClientRect();
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;
    const x = (event.clientX - rect.left) - centerX;
    const y = (event.clientY - rect.top) - centerY;

    const distance = Math.sqrt(x * x + y * y);
    const maxDistance = Math.min(centerX, centerY) - 10;

    if (distance <= maxDistance) {
      let angle = Math.atan2(y, x);
      angle = (angle * 180 / Math.PI + 360) % 360; // Convert to 0-360 degrees

      const saturation = Math.min(100, (distance / maxDistance) * 100);

      const newHsv = { ...hsvValues, h: angle, s: saturation };
      setHsvValues(newHsv);
      updateFromHsv(newHsv);

      // Update position for visual feedback
      const displayX = centerX + x;
      const displayY = centerY + y;
      setColorWheelPosition({ x: displayX, y: displayY });
    }
  };

  const handleColorWheelMouseDown = (event: React.MouseEvent<HTMLDivElement>) => {
    setIsDragging(true);
    handleColorWheelInteraction(event);
  };

  // Mouse move handler for dragging
  useEffect(() => {
    if (!isDragging) return;

    const handleMouseMove = (event: MouseEvent) => {
      handleColorWheelInteraction(event);
    };

    const handleMouseUp = () => {
      setIsDragging(false);
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging]);

  // Eyedropper functionality
  const handleEyedropper = async () => {
    if ('EyeDropper' in window) {
      try {
        // @ts-ignore - EyeDropper API is experimental
        const eyeDropper = new window.EyeDropper();
        const result = await eyeDropper.open();
        updateFromHex(result.sRGBHex);
      } catch (error) {
        // Eyedropper cancelled or not supported
      }
    }
  };

  // Recent color selection
  const handleRecentColorSelect = (color: string) => {
    updateFromHex(color);
  };

  // Reset to current color
  const handleReset = () => {
    if (currentColor === 'transparent' || currentColor === ANSI_COLORS.transparent) {
      setIsTransparentColor(true);
      setPreviewColor('transparent');
      setHexInput('');
      setRgbValues({ r: 0, g: 0, b: 0 });
      setHsvValues({ h: 0, s: 0, v: 0 });
      setValueSliderValue(0);
      setColorWheelPosition({ x: 64, y: 64 });
    } else {
      updateFromHex(currentColor);
    }
  };

  // Confirm color selection
  const handleConfirm = () => {
    addRecentColor(previewColor);
    onColorSelect(previewColor);
    onOpenChange(false);
  };

  // Cancel selection
  const handleCancel = () => {
    onOpenChange(false);
  };

  // Create radial gradient for saturation in color wheel
  const createColorWheelStyle = () => {
    const lightness = 50 + (valueSliderValue - 50) * 0.5; // Adjust lightness based on value
    return {
      background: `conic-gradient(
        hsl(0, 100%, ${lightness}%),
        hsl(60, 100%, ${lightness}%),
        hsl(120, 100%, ${lightness}%),
        hsl(180, 100%, ${lightness}%),
        hsl(240, 100%, ${lightness}%),
        hsl(300, 100%, ${lightness}%),
        hsl(0, 100%, ${lightness}%)
      ), radial-gradient(circle, transparent 0%, rgba(255,255,255,0.8) 70%)`
    };
  };

  return (
    <Dialog open={isOpen} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>{title}</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Color Preview */}
          <div className="flex items-center gap-3">
            <div className="flex-1">
              <Label className="text-sm font-medium">Preview</Label>
              <div className="flex h-12 border border-border rounded overflow-hidden">
                {/* Current Color */}
                <div className="flex-1 relative">
                  {(currentColor === 'transparent' || currentColor === ANSI_COLORS.transparent) ? (
                    <div className="w-full h-full relative bg-white">
                      <div 
                        className="absolute inset-0"
                        style={{
                          backgroundImage: 'linear-gradient(45deg, #ccc 25%, transparent 25%), linear-gradient(-45deg, #ccc 25%, transparent 25%), linear-gradient(45deg, transparent 75%, #ccc 75%), linear-gradient(-45deg, transparent 75%, #ccc 75%)',
                          backgroundSize: '8px 8px',
                          backgroundPosition: '0 0, 0 4px, 4px -4px, -4px 0px'
                        }}
                      />
                      <svg className="absolute inset-0 w-full h-full" viewBox="0 0 48 48">
                        <line x1="4" y1="44" x2="44" y2="4" stroke="#dc2626" strokeWidth="2" strokeLinecap="round" />
                      </svg>
                    </div>
                  ) : (
                    <div 
                      className="w-full h-full"
                      style={{ backgroundColor: currentColor }}
                      title="Current Color"
                    />
                  )}
                </div>

                {/* Preview Color */}
                <div className="flex-1 relative border-l border-border">
                  {(previewColor === 'transparent' || isTransparentColor) ? (
                    <div className="w-full h-full relative bg-white">
                      <div 
                        className="absolute inset-0"
                        style={{
                          backgroundImage: 'linear-gradient(45deg, #ccc 25%, transparent 25%), linear-gradient(-45deg, #ccc 25%, transparent 25%), linear-gradient(45deg, transparent 75%, #ccc 75%), linear-gradient(-45deg, transparent 75%, #ccc 75%)',
                          backgroundSize: '8px 8px',
                          backgroundPosition: '0 0, 0 4px, 4px -4px, -4px 0px'
                        }}
                      />
                      <svg className="absolute inset-0 w-full h-full" viewBox="0 0 48 48">
                        <line x1="4" y1="44" x2="44" y2="4" stroke="#dc2626" strokeWidth="2" strokeLinecap="round" />
                      </svg>
                    </div>
                  ) : (
                    <div 
                      className="w-full h-full"
                      style={{ backgroundColor: previewColor }}
                      title="New Color"
                    />
                  )}
                </div>
              </div>
            </div>

            {/* Eyedropper Button */}
            {'EyeDropper' in window && (
              <Button
                size="sm"
                variant="outline" 
                onClick={handleEyedropper}
                className="h-8 w-8 p-0"
              >
                <Pipette className="h-4 w-4" />
              </Button>
            )}
          </div>

          {/* Color Wheel and Value Slider */}
          <div className="space-y-2">
            <Label className="text-sm font-medium">Color Wheel</Label>
            <div className="flex justify-center items-center gap-4">
              {/* Color Wheel */}
              <div 
                ref={colorWheelRef}
                className="w-32 h-32 rounded-full border border-border cursor-crosshair relative select-none"
                style={createColorWheelStyle()}
                onMouseDown={handleColorWheelMouseDown}
              >
                <div 
                  className="absolute w-3 h-3 bg-white border-2 border-black rounded-full transform -translate-x-1.5 -translate-y-1.5 pointer-events-none"
                  style={{
                    left: colorWheelPosition.x,
                    top: colorWheelPosition.y
                  }}
                />
              </div>

              {/* Value Slider */}
              <div className="flex flex-col items-center gap-2">
                <Label className="text-xs font-medium">V</Label>
                <div className="w-6 flex flex-col items-center">
                  <input
                    type="range"
                    value={valueSliderValue}
                    onChange={(e) => handleValueSliderChange(parseFloat(e.target.value))}
                    max={100}
                    step={1}
                    className="h-32 w-2 bg-muted rounded-lg appearance-none cursor-pointer transform -rotate-90 origin-center"
                    style={{ writingMode: 'bt-lr' } as any}
                    disabled={isTransparentColor}
                  />
                </div>
                <span className="text-xs text-muted-foreground">
                  {isTransparentColor ? '-' : `${Math.round(valueSliderValue)}%`}
                </span>
              </div>
            </div>
          </div>

          <Separator />

          {/* HSV Controls */}
          <div className="space-y-3">
            <Label className="text-sm font-medium">HSV</Label>

            {/* Hue */}
            <div className="flex items-center gap-2">
              <Label className="w-4 text-xs">H</Label>
              <Slider
                value={isTransparentColor ? 0 : hsvValues.h}
                onValueChange={(value) => handleHsvChange('h', value)}
                max={360}
                step={0.1}
                className="flex-1"
                disabled={isTransparentColor}
              />
              <Input
                value={isTransparentColor ? '-' : hsvValues.h.toFixed(2)}
                onChange={(e) => handleHsvInputChange('h', e.target.value)}
                className="w-20 h-8 text-xs"
                disabled={isTransparentColor}
              />
              <span className="text-xs text-muted-foreground">Â°</span>
            </div>

            {/* Saturation */}
            <div className="flex items-center gap-2">
              <Label className="w-4 text-xs">S</Label>
              <Slider
                value={isTransparentColor ? 0 : hsvValues.s}
                onValueChange={(value) => handleHsvChange('s', value)}
                max={100}
                step={1}
                className="flex-1"
                disabled={isTransparentColor}
              />
              <Input
                value={isTransparentColor ? '-' : Math.round(hsvValues.s).toString()}
                onChange={(e) => handleHsvInputChange('s', e.target.value)}
                className="w-20 h-8 text-xs"
                disabled={isTransparentColor}
              />
              <span className="text-xs text-muted-foreground">%</span>
            </div>

            {/* Value */}
            <div className="flex items-center gap-2">
              <Label className="w-4 text-xs">V</Label>
              <Slider
                value={isTransparentColor ? 0 : hsvValues.v}
                onValueChange={(value) => handleHsvChange('v', value)}
                max={100}
                step={1}
                className="flex-1"
                disabled={isTransparentColor}
              />
              <Input
                value={isTransparentColor ? '-' : Math.round(hsvValues.v).toString()}
                onChange={(e) => handleHsvInputChange('v', e.target.value)}
                className="w-20 h-8 text-xs"
                disabled={isTransparentColor}
              />
              <span className="text-xs text-muted-foreground">%</span>
            </div>
          </div>

          <Separator />

          {/* RGB Controls */}
          <div className="space-y-3">
            <Label className="text-sm font-medium">RGB</Label>

            {/* Red */}
            <div className="flex items-center gap-2">
              <Label className="w-4 text-xs">R</Label>
              <Slider
                value={isTransparentColor ? 0 : rgbValues.r}
                onValueChange={(value) => handleRgbChange('r', value)}
                max={255}
                step={1}
                className="flex-1"
                disabled={isTransparentColor}
              />
              <Input
                value={isTransparentColor ? '-' : rgbValues.r.toString()}
                onChange={(e) => handleRgbInputChange('r', e.target.value)}
                className="w-16 h-8 text-xs"
                disabled={isTransparentColor}
              />
            </div>

            {/* Green */}
            <div className="flex items-center gap-2">
              <Label className="w-4 text-xs">G</Label>
              <Slider
                value={isTransparentColor ? 0 : rgbValues.g}
                onValueChange={(value) => handleRgbChange('g', value)}
                max={255}
                step={1}
                className="flex-1"
                disabled={isTransparentColor}
              />
              <Input
                value={isTransparentColor ? '-' : rgbValues.g.toString()}
                onChange={(e) => handleRgbInputChange('g', e.target.value)}
                className="w-16 h-8 text-xs"
                disabled={isTransparentColor}
              />
            </div>

            {/* Blue */}
            <div className="flex items-center gap-2">
              <Label className="w-4 text-xs">B</Label>
              <Slider
                value={isTransparentColor ? 0 : rgbValues.b}
                onValueChange={(value) => handleRgbChange('b', value)}
                max={255}
                step={1}
                className="flex-1"
                disabled={isTransparentColor}
              />
              <Input
                value={isTransparentColor ? '-' : rgbValues.b.toString()}
                onChange={(e) => handleRgbInputChange('b', e.target.value)}
                className="w-16 h-8 text-xs"
                disabled={isTransparentColor}
              />
            </div>
          </div>

          <Separator />

          {/* Hex Input */}
          <div className="space-y-2">
            <Label className="text-sm font-medium">Hex</Label>
            <Input
              value={isTransparentColor ? '-' : hexInput}
              onChange={(e) => handleHexChange(e.target.value)}
              placeholder={isTransparentColor ? "Transparent" : "#000000"}
              className="font-mono"
              disabled={isTransparentColor}
            />
          </div>

          {/* Recent Colors */}
          {recentColors.length > 0 && (
            <div className="space-y-2">
              <Label className="text-sm font-medium">Recent Colors</Label>
              <div className="flex gap-1 flex-wrap">
                {recentColors.slice(0, 10).map((color, index) => (
                  <button
                    key={index}
                    className="w-6 h-6 rounded border border-border hover:scale-110 transition-transform"
                    style={{ backgroundColor: color }}
                    onClick={() => handleRecentColorSelect(color)}
                    title={color}
                  />
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Dialog Footer */} 
        <div className="flex justify-between pt-4">
          <Button
            variant="outline"
            onClick={handleReset}
            className="gap-2"
          >
            <RotateCcw className="h-4 w-4" />
            Reset
          </Button>

          <div className="flex gap-2">
            <Button
              variant="outline"
              onClick={handleCancel}
              className="gap-2"
            >
              <X className="h-4 w-4" />
              Cancel
            </Button>

            <Button
              onClick={handleConfirm}
              className="gap-2"
            >
              <Check className="h-4 w-4" />
              OK
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

import React, { useState, useEffect } from 'react';
import { useToolStore } from '../../stores/toolStore';
import { usePaletteStore } from '../../stores/paletteStore';
import { Card, CardContent } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectSeparator, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from '@/components/ui/collapsible';
import { Palette, Type, Settings, Plus, Trash2, ChevronLeft, ChevronRight, Upload, Download, ChevronDown, ChevronUp } from 'lucide-react';
import { ForegroundBackgroundSelector } from './ForegroundBackgroundSelector';
import { ColorPickerOverlay } from './ColorPickerOverlay';
import { ImportPaletteDialog } from './ImportPaletteDialog';
import { ExportPaletteDialog } from './ExportPaletteDialog';
import { ManagePalettesDialog } from './ManagePalettesDialog';
import { ANSI_COLORS } from '../../constants/colors';

interface ColorPickerProps {
  className?: string;
}

export const ColorPicker: React.FC<ColorPickerProps> = ({ className = '' }) => {
  const { selectedColor, selectedBgColor, setSelectedColor, setSelectedBgColor } = useToolStore();
  const { 
    palettes,
    activePaletteId,
    selectedColorId,
    getActivePalette,
    getActiveColors,
    getCustomPalettes,
    getPresetPalettes,
    setActivePalette,
    setSelectedColor: setSelectedColorId,
    addColor,
    removeColor,
    updateColor,
    moveColorLeft,
    moveColorRight,
    createCustomPalette,
    initialize,
    addRecentColor
  } = usePaletteStore();

  const [activeTab, setActiveTab] = useState("text");
  const [isColorPickerOpen, setIsColorPickerOpen] = useState(false);
  const [colorPickerMode, setColorPickerMode] = useState<'foreground' | 'background' | 'palette'>('foreground');
  const [colorPickerInitialColor, setColorPickerInitialColor] = useState('#000000');
  const [editingColorId, setEditingColorId] = useState<string | null>(null);
  const [isImportDialogOpen, setIsImportDialogOpen] = useState(false);
  const [isExportDialogOpen, setIsExportDialogOpen] = useState(false);
  const [isManagePalettesOpen, setIsManagePalettesOpen] = useState(false);

  // Collapsible section states
  const [isColorSectionOpen, setIsColorSectionOpen] = useState(true);
  const [isPaletteSectionOpen, setIsPaletteSectionOpen] = useState(true);

  // Initialize palette store on mount (ensure default palettes are loaded)
  useEffect(() => {
    if (palettes.length === 0) {
      initialize();
    }
  }, [palettes.length, initialize]);

  // Get active palette and colors
  const activePalette = getActivePalette();
  const activeColors = getActiveColors();
  const customPalettes = getCustomPalettes();
  const presetPalettes = getPresetPalettes();

  // Filter colors for foreground (no transparent) and background (always include transparent)
  const foregroundColors = activeColors.filter(color => color.value !== 'transparent' && color.value !== ANSI_COLORS.transparent);
  const backgroundColors = [
    { id: 'transparent', value: 'transparent', name: 'Transparent' }, 
    ...activeColors.filter(color => color.value !== 'transparent' && color.value !== ANSI_COLORS.transparent)
  ];

  // Handle palette selection
  const handlePaletteChange = (paletteId: string) => {
    setActivePalette(paletteId);
    setSelectedColorId(null);
  };

  // Handle color selection from palette
  const handleColorSelect = (color: string, isBackground = false) => {
    if (isBackground) {
      setSelectedBgColor(color);
    } else {
      setSelectedColor(color);
    }
    addRecentColor(color);
  };

  // Handle color selection for editing (single click)
  const handleColorPaletteSelect = (colorId: string) => {
    setSelectedColorId(selectedColorId === colorId ? null : colorId);
  };

  // Drag and drop state
  const [draggedColorId, setDraggedColorId] = useState<string | null>(null);
  const [dropIndicatorIndex, setDropIndicatorIndex] = useState<number | null>(null);

  // Handle drag start
  const handleDragStart = (e: React.DragEvent, colorId: string) => {
    if (!activePalette) {
      e.preventDefault();
      return;
    }
    setDraggedColorId(colorId);
    e.dataTransfer.effectAllowed = 'move';
  };

  // Handle drag over
  const handleDragOver = (e: React.DragEvent, targetColorId?: string) => {
    if (!activePalette || !draggedColorId) return;
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';

    if (targetColorId) {
      const targetIndex = activeColors.findIndex(c => c.id === targetColorId);
      if (targetIndex !== -1) {
        // Determine if we should show indicator before or after based on mouse position
        const rect = (e.target as HTMLElement).getBoundingClientRect();
        const mouseX = e.clientX - rect.left;
        const isAfter = mouseX > rect.width / 2;
        setDropIndicatorIndex(isAfter ? targetIndex + 1 : targetIndex);
      }
    }
  };

  // Handle drop
  const handleDrop = (e: React.DragEvent, targetColorId: string) => {
    e.preventDefault();
    if (!activePalette || !draggedColorId || draggedColorId === targetColorId) {
      setDraggedColorId(null);
      setDropIndicatorIndex(null);
      return;
    }

    // Find indices of source and target colors
    const sourceIndex = activeColors.findIndex(c => c.id === draggedColorId);
    const targetIndex = activeColors.findIndex(c => c.id === targetColorId);

    if (sourceIndex === -1 || targetIndex === -1) {
      setDraggedColorId(null);
      setDropIndicatorIndex(null);
      return;
    }

    // Determine final position based on drop indicator
    let finalTargetIndex = targetIndex;
    if (dropIndicatorIndex === targetIndex + 1) {
      finalTargetIndex = targetIndex + 1;
    }

    // Move the colors
    let currentIndex = sourceIndex;
    if (currentIndex < finalTargetIndex) {
      // Moving right - use moveColorRight
      for (let i = 0; i < finalTargetIndex - sourceIndex; i++) {
        moveColorRight(activePaletteId, draggedColorId);
      }
    } else if (currentIndex > finalTargetIndex) {
      // Moving left - use moveColorLeft
      for (let i = 0; i < sourceIndex - finalTargetIndex; i++) {
        moveColorLeft(activePaletteId, draggedColorId);
      }
    }

    setDraggedColorId(null);
    setDropIndicatorIndex(null);
  };

  // Handle drag leave
  const handleDragLeave = (e: React.DragEvent) => {
    // Only clear if leaving the grid container
    if (!e.currentTarget.contains(e.relatedTarget as Node)) {
      setDropIndicatorIndex(null);
    }
  };

  // Handle color double-click to edit
  const handleColorDoubleClick = (color: string) => {
    if (activePalette) {
      const colorItem = activeColors.find(c => c.value === color);
      setEditingColorId(colorItem?.id || null);
      setColorPickerMode('palette');
      setColorPickerInitialColor(color);
      setIsColorPickerOpen(true);
    }
  };

  // Handle opening color picker from foreground/background selector
  const handleOpenColorPicker = (mode: 'foreground' | 'background', currentColor: string) => {
    setColorPickerMode(mode);
    setColorPickerInitialColor(currentColor);
    setIsColorPickerOpen(true);
  };

  // Handle color picker selection
  const handleColorPickerSelect = (newColor: string) => {
    if (colorPickerMode === 'palette' && editingColorId) {
      // Update the color in the palette
      updateColor(activePaletteId, editingColorId, newColor);
      // Clear editing state
      setEditingColorId(null);
      setColorPickerMode('foreground');
    } else if (colorPickerMode === 'foreground') {
      setSelectedColor(newColor);
    } else if (colorPickerMode === 'background') {
      setSelectedBgColor(newColor);
    }
  };

  // Check if color is currently selected 
  const isColorSelected = (color: string, isBackground = false) => {
    return isBackground ? selectedBgColor === color : selectedColor === color;
  };

  return (
    <div className={`space-y-3 ${className}`}>
      {/* Color Section */}
      <Collapsible open={isColorSectionOpen} onOpenChange={setIsColorSectionOpen}>
        <CollapsibleTrigger asChild>
          <Button variant="ghost" className="w-full justify-between p-2 h-auto font-medium text-sm">
            Color
            {isColorSectionOpen ? (
              <ChevronUp className="h-4 w-4" />
            ) : (
              <ChevronDown className="h-4 w-4" />
            )}
          </Button>
        </CollapsibleTrigger>
        <CollapsibleContent className="space-y-3">
          {/* Photoshop-style foreground/background selector */}
          <ForegroundBackgroundSelector onOpenColorPicker={handleOpenColorPicker} />
        </CollapsibleContent>
      </Collapsible>

      {/* Palette Section */}
      <Collapsible open={isPaletteSectionOpen} onOpenChange={setIsPaletteSectionOpen}>
        <CollapsibleTrigger asChild>
          <Button variant="ghost" className="w-full justify-between p-2 h-auto font-medium text-sm">
            Palette
            {isPaletteSectionOpen ? (
              <ChevronUp className="h-4 w-4" />
            ) : (
              <ChevronDown className="h-4 w-4" />
            )}
          </Button>
        </CollapsibleTrigger>
        <CollapsibleContent className="space-y-3">
          {/* Palette selector */}
          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <label className="text-sm font-medium text-muted-foreground">Active Palette</label>
              <div className="flex gap-1">
                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => {
                          const newPaletteId = createCustomPalette('New Palette');
                          setActivePalette(newPaletteId);
                        }}
                      >
                        <Plus className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Create new palette</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>

                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => setIsManagePalettesOpen(true)}
                      >
                        <Settings className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Manage palettes</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>
              </div>
            </div>

            <Select value={activePaletteId || ''} onValueChange={handlePaletteChange}>
              <SelectTrigger className="w-full h-8 text-xs">
                <SelectValue placeholder="Select palette..." />
              </SelectTrigger>
              <SelectContent>
                {customPalettes.length > 0 && (
                  <>
                    {customPalettes.map((palette) => (
                      <SelectItem key={palette.id} value={palette.id} className="text-xs">
                        <span>{palette.name}</span>
                      </SelectItem>
                    ))}
                    <SelectSeparator />
                  </>
                )}
                {presetPalettes.map((palette) => (
                  <SelectItem key={palette.id} value={palette.id} className="text-xs">
                    <span>{palette.name}</span>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Color palette tabs */}
          <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
            <TabsList className="grid w-full grid-cols-2 h-8">
              <TabsTrigger value="text" className="text-xs h-full flex items-center justify-center gap-1">
                <Type className="w-3 h-3" />
                Text
              </TabsTrigger>
              <TabsTrigger value="bg" className="text-xs h-full flex items-center justify-center gap-1">
                <Palette className="w-3 h-3" />
                BG
              </TabsTrigger>
            </TabsList>

            {/* Foreground colors */}
            <TabsContent value="text" className="mt-2">
              <Card className="bg-card/50 border-border/50">
                <CardContent className="p-2">
                  <div className="grid grid-cols-6 gap-0.5 relative flex items-center justify-center" onDragLeave={handleDragLeave}>
                    {foregroundColors.map((color, index) => (
                      <div key={`text-${color.id}`} className="relative flex items-center justify-center">
                        {/* Drop indicator line */}
                        {dropIndicatorIndex === index && (
                          <div className="absolute -left-0.5 top-0 bottom-0 w-0.5 bg-primary z-10 rounded-full"></div>
                        )}

                        <button
                          draggable={!!activePalette}
                          className={`w-6 h-6 rounded border-2 transition-all hover:scale-105 relative ${
                            draggedColorId === color.id ? 'opacity-50 scale-95' : ''
                          } ${
                            selectedColorId === color.id
                              ? 'border-primary ring-2 ring-primary/20 shadow-lg' 
                              : isColorSelected(color.value, false)
                              ? 'border-primary ring-1 ring-primary/20' 
                              : 'border-border'
                          } cursor-move`}
                          style={{ backgroundColor: color.value }}
                          onClick={() => {
                            // Single click sets drawing color and selects for editing
                            handleColorSelect(color.value, false);
                            handleColorPaletteSelect(color.id);
                          }}
                          onDoubleClick={() => handleColorDoubleClick(color.value)}
                          onDragStart={(e) => handleDragStart(e, color.id)}
                          onDragOver={(e) => handleDragOver(e, color.id)}
                          onDrop={(e) => handleDrop(e, color.id)}
                          title={
                            activePalette?.isCustom 
                              ? `${color.name ? `${color.name}: ${color.value}` : color.value} (drag to reorder)` 
                              : color.name ? `${color.name}: ${color.value}` : color.value
                          }
                        />

                        {/* Drop indicator line after last item */}
                        {dropIndicatorIndex === index + 1 && (
                          <div className="absolute -right-0.5 top-0 bottom-0 w-0.5 bg-primary z-10 rounded-full"></div>
                        )}
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            {/* Background colors */}
            <TabsContent value="bg" className="mt-2">
              <Card className="bg-card/50 border-border/50">
                <CardContent className="p-2">
                  <div className="grid grid-cols-6 gap-0.5 relative flex items-center justify-center" onDragLeave={handleDragLeave}>
                    {backgroundColors.map((color, index) => {
                      const isTransparent = color.value === 'transparent';
                      return (
                        <div key={`bg-${color.id}`} className="relative flex items-center justify-center">
                          {/* Drop indicator line */}
                          {dropIndicatorIndex === index && (
                            <div className="absolute -left-0.5 top-0 bottom-0 w-0.5 bg-primary z-10 rounded-full"></div>
                          )}

                          <button
                            draggable={!!activePalette && !isTransparent}
                            className={`w-6 h-6 rounded border-2 transition-all hover:scale-105 relative overflow-hidden ${
                              draggedColorId === color.id ? 'opacity-50 scale-95' : ''
                            } ${
                              selectedColorId === color.id
                                ? 'border-primary ring-2 ring-primary/20 shadow-lg' 
                                : isColorSelected(color.value, true)
                                ? 'border-primary ring-1 ring-primary/20' 
                                : 'border-border'
                            } ${!isTransparent ? 'cursor-move' : 'cursor-pointer'}`}
                            style={{
                              backgroundColor: isTransparent ? '#ffffff' : color.value,
                              backgroundImage: isTransparent 
                                ? 'linear-gradient(45deg, #ccc 25%, transparent 25%), linear-gradient(-45deg, #ccc 25%, transparent 25%), linear-gradient(45deg, transparent 75%, #ccc 75%), linear-gradient(-45deg, transparent 75%, #ccc 75%)'
                                : 'none',
                              backgroundSize: isTransparent ? '8px 8px' : 'auto',
                              backgroundPosition: isTransparent ? '0 0, 0 4px, 4px -4px, -4px 0px' : 'auto'
                            }}
                            onClick={() => {
                              handleColorSelect(color.value, true);
                              handleColorPaletteSelect(color.id);
                            }}
                            onDoubleClick={() => !isTransparent && handleColorDoubleClick(color.value)}
                            onDragStart={(e) => handleDragStart(e, color.id)}
                            onDragOver={(e) => handleDragOver(e, color.id)}
                            onDrop={(e) => handleDrop(e, color.id)}
                            title={
                              isTransparent 
                                ? 'Transparent background' 
                                : activePalette?.isCustom 
                                ? `${color.name ? `${color.name}: ${color.value}` : color.value} (drag to reorder)` 
                                : color.name ? `${color.name}: ${color.value}` : color.value
                            }
                          >
                            {isTransparent && (
                              <svg 
                                className="absolute inset-0 w-full h-full pointer-events-none" 
                                viewBox="0 0 24 24"
                                style={{ borderRadius: 'inherit' }}
                              >
                                <line 
                                  x1="2" y1="2" 
                                  x2="22" y2="22" 
                                  stroke="#ef4444" 
                                  strokeWidth="2"
                                />
                              </svg>
                            )}
                          </button>

                          {/* Drop indicator line after last item */}
                          {dropIndicatorIndex === index + 1 && (
                            <div className="absolute -right-0.5 top-0 bottom-0 w-0.5 bg-primary z-10 rounded-full"></div>
                          )}
                        </div>
                      );
                    })}
                  </div>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>

          {/* Palette controls */}
          {activePalette && (
            <div className="flex items-center justify-between">
              {/* Editing controls (only for custom palettes) */}
              <div className="flex gap-0.5">
                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => selectedColorId && moveColorLeft(activePaletteId, selectedColorId)}
                        disabled={!selectedColorId || !activePalette?.isCustom}
                      >
                        <ChevronLeft className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Move color left{!activePalette?.isCustom ? ' (will create custom copy)' : ''}</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>

                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => selectedColorId && moveColorRight(activePaletteId, selectedColorId)}
                        disabled={!selectedColorId || !activePalette?.isCustom}
                      >
                        <ChevronRight className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Move color right{!activePalette?.isCustom ? ' (will create custom copy)' : ''}</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>

                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => addColor(activePaletteId, '#808080')}
                      >
                        <Plus className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Add color{!activePalette?.isCustom ? ' (will create custom copy)' : ''}</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>

                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => selectedColorId && removeColor(activePaletteId, selectedColorId)}
                        disabled={!selectedColorId || activeColors.length <= 1}
                      >
                        <Trash2 className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Remove color{!activePalette?.isCustom ? ' (will create custom copy)' : ''}</p>
                    </TooltipContent>
                    </Tooltip>
                  </TooltipProvider>
                </div>

              {/* Import/Export buttons (always visible) */}
              <div className="flex gap-0.5">
                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => setIsImportDialogOpen(true)}
                      >
                        <Upload className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Import palette</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>

                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        size="sm"
                        variant="outline"
                        className="h-6 w-6 p-0"
                        onClick={() => setIsExportDialogOpen(true)}
                      >
                        <Download className="h-3 w-3" />
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Export palette</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>
              </div>
            </div>
          )}

          {/* Status text */}
          {activePalette && (
            <div className="text-xs text-muted-foreground text-center">
              {activePalette.name} â€¢ {activeColors.length} colors
              {selectedColorId && (
                <span className="ml-2">
                  â€¢ Selected: {activeColors.find(c => c.id === selectedColorId)?.name || 'Color'}
                </span>
              )}
            </div>
          )}
        </CollapsibleContent>
      </Collapsible>

      {/* Color Picker Modal */}
      <ColorPickerOverlay
        isOpen={isColorPickerOpen}
        onOpenChange={(open) => {
          setIsColorPickerOpen(open);
          // Reset editing state when closing
          if (!open && colorPickerMode === 'palette') {
            setEditingColorId(null);
            setColorPickerMode('foreground');
          }
        }}
        onColorSelect={handleColorPickerSelect}
        initialColor={colorPickerInitialColor}
        title={
          colorPickerMode === 'palette' 
            ? 'Edit Palette Color' 
            : `Edit ${colorPickerMode === 'foreground' ? 'Foreground' : 'Background'} Color`
        }
        showTransparentOption={colorPickerMode === 'background'}
      />

      {/* Import/Export Dialogs */}
      <ImportPaletteDialog
        isOpen={isImportDialogOpen}
        onOpenChange={setIsImportDialogOpen}
      />

      <ExportPaletteDialog
        isOpen={isExportDialogOpen}
        onOpenChange={setIsExportDialogOpen}
      />

      {/* Manage Palettes Dialog */}
      <ManagePalettesDialog
        isOpen={isManagePalettesOpen}
        onOpenChange={setIsManagePalettesOpen}
      />
    </div>
  );
};

/**
 * EffectsPanel - Main overlay panel for effects system
 * 
 * Features:
 * - Fixed right-side overlay with slide animation
 * - Header with effect name and close button
 * - Scrollable content area for effect-specific controls
 * - Footer with Apply to Timeline toggle and Apply/Cancel buttons
 * - Follows MediaImportPanel and GradientPanel patterns exactly
 */

import { useEffect, useMemo, useState } from 'react';
import { Button } from '../ui/button';
import { ScrollArea } from '../ui/scroll-area';
import { Separator } from '../ui/separator';
import { Switch } from '../ui/switch';
import { useEffectsStore } from '../../stores/effectsStore';
import { useEffectsHistory } from '../../hooks/useEffectsHistory';
import { EFFECT_DEFINITIONS } from '../../constants/effectsDefaults';
import { PANEL_ANIMATION } from '../../constants';
import { cn } from '../../lib/utils';
import { LevelsEffectPanel } from './effects/LevelsEffectPanel';
import { HueSaturationEffectPanel } from './effects/HueSaturationEffectPanel';
import { RemapColorsEffectPanel } from './effects/RemapColorsEffectPanel';
import { RemapCharactersEffectPanel } from './effects/RemapCharactersEffectPanel';
import {
  X,
  BarChart3,
  Palette,
  RefreshCcw,
  Type
} from 'lucide-react';

// Icon mapping for effect headers
const EFFECT_ICONS = {
  'BarChart3': BarChart3,
  'Palette': Palette,
  'RefreshCcw': RefreshCcw,
  'Type': Type
} as const;

// Parse Tailwind duration for animation timing
const parseTailwindDuration = (token: string): number | null => {
  const match = token.match(/duration-(\d+)/);
  return match ? Number(match[1]) : null;
};

interface EffectsPanelProps {
  // No props needed - uses store state
}

export function EffectsPanel({}: EffectsPanelProps) {
  const { 
    isOpen, 
    activeEffect, 
    applyToTimeline,
    setApplyToTimeline,
    closeEffectPanel,
    isAnalyzing
  } = useEffectsStore();

  // Use history-aware effects hook
  const { 
    applyEffectWithHistory, 
    canApplyEffect,
    getEffectDescription 
  } = useEffectsHistory();

  const animationDurationMs = useMemo(
    () => parseTailwindDuration(PANEL_ANIMATION.DURATION) ?? 300,
    []
  );

  // Animation state to handle transitions properly (from GradientPanel pattern)
  const [shouldRender, setShouldRender] = useState(isOpen);
  const [isAnimating, setIsAnimating] = useState(isOpen);

  // Handle panel animation states
  useEffect(() => {
    if (isOpen) {
      setShouldRender(true);
      // Trigger animation on next frame to ensure DOM is ready
      requestAnimationFrame(() => {
        setIsAnimating(true);
      });
    } else if (shouldRender) {
      // Only start exit animation if panel was previously rendered
      setIsAnimating(false);
      // Wait for animation to complete before removing from DOM
      const timer = setTimeout(() => {
        setShouldRender(false);
      }, animationDurationMs);
      return () => clearTimeout(timer);
    }
  }, [isOpen, shouldRender, animationDurationMs]);

  // Get current effect definition
  const currentEffectDef = activeEffect 
    ? EFFECT_DEFINITIONS.find(def => def.id === activeEffect)
    : null;

  // Get effect icon component
  const EffectIconComponent = currentEffectDef 
    ? EFFECT_ICONS[currentEffectDef.icon as keyof typeof EFFECT_ICONS]
    : null;

  // Handle cancel - stop preview and close panel
  const handleCancel = async () => {
    const { stopPreview } = useEffectsStore.getState();
    stopPreview();
    closeEffectPanel();
  };

  // Handle apply effect
  const handleApplyEffect = async () => {
    if (!activeEffect) return;

    const success = await applyEffectWithHistory(activeEffect);
    if (success) {
      // Panel will close automatically via store
      console.log(`${activeEffect} effect applied successfully with history tracking`);
    } else {
      console.error(`Failed to apply ${activeEffect} effect`);
    }
  };

  // Don't render if panel should not be visible
  if (!shouldRender) return null;

  return (
    <div className={cn(
      "fixed inset-y-0 right-0 w-80 bg-background border-l border-border shadow-lg z-50",
      "flex flex-col overflow-hidden",
      PANEL_ANIMATION.TRANSITION,
      isAnimating ? "translate-x-0" : "translate-x-full"
    )}>
      {/* Header */}
      <div className="flex items-center justify-between p-3 border-b border-border">
        <h2 className="text-sm font-medium flex items-center gap-2">
          {EffectIconComponent && <EffectIconComponent className="w-3 h-3" />}
          {currentEffectDef?.name || 'Effect'}
        </h2>
        <Button
          onClick={closeEffectPanel}
          variant="ghost"
          size="sm"
          className="h-6 w-6 p-0"
        >
          <X className="w-3 h-3" />
        </Button>
      </div>

      {/* Scrollable Content Area */}
      <ScrollArea className="flex-1">
        <div className="p-3 space-y-3" style={{ width: '296px', maxWidth: '296px' }}>

          {/* Effect-specific content will be rendered here */}
          {activeEffect === 'levels' && (
            <LevelsEffectPanel />
          )}

          {activeEffect === 'hue-saturation' && (
            <HueSaturationEffectPanel />
          )}

          {activeEffect === 'remap-colors' && (
            <RemapColorsEffectPanel />
          )}

          {activeEffect === 'remap-characters' && (
            <RemapCharactersEffectPanel />
          )}

          {/* Analysis status */}
          {isAnalyzing && (
            <div className="flex items-center gap-2 text-xs text-muted-foreground animate-pulse">
              <div className="w-2 h-2 bg-primary rounded-full animate-pulse" />
              Analyzing canvas...
            </div>
          )}

        </div>
      </ScrollArea>

      {/* Footer */}
      <div className="border-t border-border p-3 space-y-3">
        {/* Apply to Timeline Toggle */}
        <div className="flex items-center justify-between">
          <label className="flex items-center gap-2 text-xs cursor-pointer">
            <Switch
              checked={applyToTimeline}
              onCheckedChange={setApplyToTimeline}
            />
            <span>Apply to entire timeline</span>
          </label>
        </div>

        <div className="text-xs text-muted-foreground">
          {applyToTimeline 
            ? 'Effect will be applied to all frames' 
            : 'Effect will be applied to current canvas only'
          }
        </div>

        <Separator className="-mx-3" />

        {/* Action Buttons */}
        <div className="flex gap-2">
          <Button
            variant="outline"
            size="sm"
            onClick={handleCancel}
            className="flex-1 h-8"
          >
            Cancel
          </Button>
          <Button
            variant="default"
            size="sm"
            onClick={handleApplyEffect}
            disabled={!activeEffect || isAnalyzing || !canApplyEffect()}
            className="flex-1 h-8"
            title={
              !canApplyEffect() 
                ? 'No canvas data to apply effect to' 
                : `Apply ${getEffectDescription(activeEffect || 'levels')} effect`
            }
          >
            Apply
          </Button>
        </div>
      </div>
    </div>
  );
}

/**
 * EffectsSection - Collapsible effects section for the right panel
 * 
 * Features:
 * - Collapsible section header with effects icon
 * - Effect buttons with icons and names
 * - Timeline toggle for applying effects to entire timeline
 * - Follows MainCharacterPaletteSection patterns exactly
 */

import { useState } from 'react';
import { Button } from '../ui/button';
import { 
  Collapsible,
  CollapsibleContent,
} from '../ui/collapsible';
import { CollapsibleHeader } from '../common/CollapsibleHeader';
import { useEffectsStore } from '../../stores/effectsStore';
import { EFFECT_DEFINITIONS } from '../../constants/effectsDefaults';
import { 
  Wand2,
  BarChart3,
  Palette,
  RefreshCcw,
  Type
} from 'lucide-react';

// Icon mapping for effect buttons
const EFFECT_ICONS = {
  'BarChart3': BarChart3,
  'Palette': Palette,
  'RefreshCcw': RefreshCcw,
  'Type': Type
} as const;

interface EffectsSectionProps {
  className?: string;
}

export function EffectsSection({ className = '' }: EffectsSectionProps) {
  const [isOpen, setIsOpen] = useState(false);

  const { 
    openEffectPanel, 
    isAnalyzing
  } = useEffectsStore();

  const handleEffectClick = (effectId: string) => {
    openEffectPanel(effectId as any);
  };

  return (
    <div className={`space-y-3 ${className}`}>
      <Collapsible open={isOpen} onOpenChange={setIsOpen}>
        <CollapsibleHeader isOpen={isOpen}>
          <div className="flex items-center gap-2">
            <Wand2 className="w-4 h-4" />
            Effects
          </div>
        </CollapsibleHeader>

        <CollapsibleContent className="collapsible-content mt-2">
          <div className="space-y-3">
            {/* Effect Buttons */}
            <div className="space-y-2">
              {EFFECT_DEFINITIONS.map(effect => {
                const IconComponent = EFFECT_ICONS[effect.icon as keyof typeof EFFECT_ICONS];

                return (
                  <Button
                    key={effect.id}
                    variant="outline"
                    size="sm"
                    onClick={() => handleEffectClick(effect.id)}
                    disabled={isAnalyzing}
                    className="w-full justify-start gap-2 h-8 text-xs"
                    title={effect.description}
                  >
                    {IconComponent && <IconComponent className="w-3 h-3" />}
                    {effect.name}
                  </Button>
                );
              })}
            </div>

            {/* Analysis Status */}
            {isAnalyzing && (
              <div className="text-xs text-muted-foreground animate-pulse">
                Analyzing canvas...
              </div>
            )}
          </div>
        </CollapsibleContent>
      </Collapsible>
    </div>
  );
}

import React, { useState, useRef, useEffect, useCallback } from 'react';
import { createPortal } from 'react-dom';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { DraggableDialogBar } from '@/components/common/DraggableDialogBar';
import { CHARACTER_CATEGORIES } from '@/constants';
import { 
  Type, 
  Hash, 
  Grid3X3, 
  Square, 
  Navigation, 
  Triangle, 
  Sparkles,
  Minus
} from 'lucide-react';

interface EnhancedCharacterPickerProps {
  isOpen: boolean;
  onClose: () => void;
  onSelectCharacter: (character: string) => void;
  triggerRef: React.RefObject<HTMLElement | null>;
  anchorPosition?: 'bottom-right' | 'left-slide' | 'left-bottom' | 'left-bottom-aligned' | 'gradient-panel';
  initialValue?: string;
  title?: string;
}

const CATEGORY_ICONS = {
  "Basic Text": Type,
  "Punctuation": Minus,
  "Math/Symbols": Hash,
  "Lines/Borders": Grid3X3,
  "Blocks/Shading": Square,
  "Arrows": Navigation,
  "Geometric": Triangle,
  "Special": Sparkles
};

export const EnhancedCharacterPicker: React.FC<EnhancedCharacterPickerProps> = ({
  isOpen,
  onClose,
  onSelectCharacter,
  triggerRef,
  anchorPosition = 'left-slide',
  initialValue = '',
  title = 'Select Character'
}) => {
  const [selectedCategory, setSelectedCategory] = useState("Basic Text");
  const [positionOffset, setPositionOffset] = useState({ x: 0, y: 0 });
  const [isDraggingDialog, setIsDraggingDialog] = useState(false);
  const [hasBeenDragged, setHasBeenDragged] = useState(false);
  const dragStartOffsetRef = useRef({ x: 0, y: 0 });
  const pickerRef = useRef<HTMLDivElement>(null);

  // Reset position offset and drag state when dialog opens
  useEffect(() => {
    if (isOpen) {
      setPositionOffset({ x: 0, y: 0 });
      setHasBeenDragged(false);
    }
  }, [isOpen]);

  // Close picker when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        isOpen &&
        pickerRef.current &&
        !pickerRef.current.contains(event.target as Node) &&
        triggerRef.current &&
        !triggerRef.current.contains(event.target as Node)
      ) {
        onClose();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [isOpen, onClose, triggerRef]);

  // Close picker on escape key
  useEffect(() => {
    const handleEscape = (event: KeyboardEvent) => {
      if (event.key === 'Escape' && isOpen) {
        onClose();
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [isOpen, onClose]);

  const handleCharacterSelect = (character: string) => {
    onSelectCharacter(character);
    onClose();
  };

  // Handler for dragging the dialog
  const handleDrag = useCallback((deltaX: number, deltaY: number) => {
    // Add the drag delta to the stored offset from when drag started
    setPositionOffset({
      x: dragStartOffsetRef.current.x + deltaX,
      y: dragStartOffsetRef.current.y + deltaY
    });
  }, []);

  // Track dialog drag state for animation control
  const handleDragStart = useCallback(() => {
    setIsDraggingDialog(true);
    setHasBeenDragged(true);
    // Store the current offset when drag starts
    dragStartOffsetRef.current = { ...positionOffset };
  }, [positionOffset]);

  const handleDragEnd = useCallback(() => {
    setIsDraggingDialog(false);
    // Update the ref with the final position
    dragStartOffsetRef.current = { ...positionOffset };
  }, [positionOffset]);

  // Position calculation with support for all existing anchor positions
  const getPickerPosition = () => {
    if (!triggerRef.current) return { top: 0, right: 0, left: 0 };

    const triggerRect = triggerRef.current.getBoundingClientRect();
    const pickerWidth = 400; // Enhanced width for better breathing room
    const pickerHeight = 500; // Enhanced height for better visual hierarchy

    if (anchorPosition === 'gradient-panel') {
      // Center the picker vertically in the viewport (from GradientStopPicker)
      const viewportHeight = window.innerHeight;
      const top = Math.max(8, (viewportHeight - pickerHeight) / 2 + window.scrollY);

      // Position to the left of the gradient panel (which is 320px wide and on the right side)
      const gradientPanelWidth = 320;
      const left = window.innerWidth - gradientPanelWidth - pickerWidth - 16; // 16px gap

      return {
        top,
        left: Math.max(8, left), // Ensure it doesn't go off-screen
        right: 'auto'
      };
    } else if (anchorPosition === 'bottom-right') {
      // Anchor bottom-right corner of picker to the trigger element
      let top = triggerRect.bottom + window.scrollY - pickerHeight - 8; // 8px gap above trigger
      let left = triggerRect.right - pickerWidth + window.scrollX;

      // Ensure picker doesn't go off-screen
      if (left < 0) left = 8; // 8px margin from left edge
      if (top < window.scrollY) top = triggerRect.bottom + window.scrollY + 8; // Show below if no room above

      return {
        top,
        left,
        right: 'auto'
      };
    } else if (anchorPosition === 'left-bottom') {
      // Anchor bottom-right corner of picker to the left side of trigger, with bottom alignment
      let top = triggerRect.bottom + window.scrollY - pickerHeight;
      let right = window.innerWidth - triggerRect.left + 8; // 8px gap from trigger

      // Ensure picker doesn't go off-screen vertically
      if (top < window.scrollY) top = window.scrollY + 8; // 8px margin from top

      return {
        top,
        right,
        left: 'auto'
      };
    } else if (anchorPosition === 'left-bottom-aligned') {
      // Align bottom of picker with bottom of trigger element, positioned to the left
      let top = triggerRect.bottom + window.scrollY - pickerHeight;
      let right = window.innerWidth - triggerRect.left + 8; // 8px gap from trigger

      // Ensure picker doesn't go off-screen vertically
      if (top < window.scrollY) top = window.scrollY + 8; // 8px margin from top

      return {
        top,
        right,
        left: 'auto'
      };
    } else {
      // Default left-slide behavior (for edit button from palette container)
      return {
        top: triggerRect.top + window.scrollY,
        right: window.innerWidth - triggerRect.left + 8, // 8px gap from trigger
        left: 'auto'
      };
    }
  };

  if (!isOpen) return null;

  const position = getPickerPosition();

  return createPortal(
    <div
      ref={pickerRef}
      className={`fixed z-[99999] ${
        !hasBeenDragged ? `animate-in duration-200 ${
          anchorPosition === 'bottom-right' ? 'slide-in-from-bottom-2 fade-in-0' : 'slide-in-from-right-2 fade-in-0'
        }` : ''
      }`}
      style={{
        top: position.top + positionOffset.y,
        right: position.right !== 'auto' && typeof position.right === 'number' ? position.right - positionOffset.x : undefined,
        left: position.left !== 'auto' && typeof position.left === 'number' ? position.left + positionOffset.x : undefined,
        maxWidth: '400px',
        width: '400px',
        transition: isDraggingDialog ? 'none' : undefined
      }}
      onMouseDown={(e) => e.stopPropagation()}
      onClick={(e) => e.stopPropagation()}
    >
      <Card className="border border-border/50 shadow-lg">
        <DraggableDialogBar 
          title={title} 
          onDrag={handleDrag}
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
          onClose={onClose}
        />
        <div className="p-4 pt-2">

          <div className="space-y-4">
            {/* Category Selection - Enhanced 4-column grid with icons */}
            <div className="grid grid-cols-4 gap-2">
              {Object.entries(CHARACTER_CATEGORIES).map(([category]) => {
                const Icon = CATEGORY_ICONS[category as keyof typeof CATEGORY_ICONS] || Type;
                return (
                  <Button
                    key={category}
                    variant={selectedCategory === category ? "default" : "outline"}
                    className="h-12 flex flex-col items-center gap-1 text-xs"
                    onClick={() => setSelectedCategory(category)}
                  >
                    <Icon className="w-4 h-4" />
                    <span className="text-xs leading-none">{category.split('/')[0]}</span>
                  </Button>
                );
              })}
            </div>

            {/* Character Grid - Enhanced 8-column grid for better spacing */}
            <div className="max-h-60 overflow-y-auto">
              <div className="grid grid-cols-8 gap-1 p-2 border border-border rounded bg-muted/30">
                <TooltipProvider>
                  {CHARACTER_CATEGORIES[selectedCategory as keyof typeof CHARACTER_CATEGORIES]?.map((char, index) => (
                    <Tooltip key={index}>
                      <TooltipTrigger asChild>
                        <Button
                          variant={initialValue === char ? "default" : "ghost"}
                          className="h-8 w-8 p-0 font-mono text-sm hover:bg-accent hover:text-accent-foreground flex items-center justify-center"
                          onClick={() => handleCharacterSelect(char)}
                        >
                          {char}
                        </Button>
                      </TooltipTrigger>
                      <TooltipContent>
                        <p>Insert "{char}"</p>
                      </TooltipContent>
                    </Tooltip>
                  ))}
                </TooltipProvider>
              </div>
            </div>
          </div>
        </div>
      </Card>
    </div>,
    document.body
  );
};

import React, { useState, useMemo } from 'react';
import { Badge } from '../ui/badge';
import { Button } from '../ui/button';
import { Card } from '../ui/card';
import type { Frame } from '../../types';
import { X, Copy } from 'lucide-react';
import { useCanvasStore } from '../../stores/canvasStore';

interface FrameThumbnailProps {
  frame: Frame;
  frameIndex: number;
  isActive: boolean;      // Is this the active frame (displayed on canvas)?
  isSelected: boolean;    // Is this frame in the selection?
  canvasWidth: number;
  canvasHeight: number;
  scaleZoom?: number; // Timeline zoom scaling (0.5 to 1.0)
  onSelect: (event: React.MouseEvent) => void; // Now passes event for shift-click detection
  onDuplicate: () => void;
  onDelete: () => void;
  onDurationChange: (duration: number) => void;
  isDragging?: boolean;
  dragHandleProps?: any;
  // Onion skin props
  isOnionSkinPrevious?: boolean;
  isOnionSkinNext?: boolean;
  onionSkinDistance?: number;
}

/**
 * Individual frame thumbnail component with ASCII preview
 * Renders a miniaturized version of the frame's ASCII art
 */
export const FrameThumbnail: React.FC<FrameThumbnailProps> = ({
  frame,
  frameIndex,
  isActive,
  isSelected,
  canvasWidth,
  canvasHeight,
  scaleZoom = 1.0,
  onSelect,
  onDuplicate,
  onDelete,
  onDurationChange,
  isDragging = false,
  dragHandleProps,
  isOnionSkinPrevious = false,
  isOnionSkinNext = false,
  onionSkinDistance = 0
}) => {
  // Local state for duration input to allow free typing
  const [durationInput, setDurationInput] = useState(frame.duration.toString());
  const [isEditingDuration, setIsEditingDuration] = useState(false);

  // Get canvas background color from store
  const { canvasBackgroundColor } = useCanvasStore();

  // Calculate scaled dimensions based on timeline zoom
  const baseCardSize = 144; // w-36 = 144px
  const scaledCardSize = Math.round(baseCardSize * scaleZoom);



  // Scale thumbnail proportionally but ensure it fits in the card
  const baseThumbnailWidth = 120;
  const baseThumbnailHeight = 60;
  const maxThumbnailWidth = scaledCardSize - 16; // account for padding

  // Scale thumbnail to fit within the available space
  let scaledThumbnailWidth = Math.round(baseThumbnailWidth * scaleZoom);
  let scaledThumbnailHeight = Math.round(baseThumbnailHeight * scaleZoom);

  // Ensure thumbnail fits within the card
  if (scaledThumbnailWidth > maxThumbnailWidth) {
    const scale = maxThumbnailWidth / scaledThumbnailWidth;
    scaledThumbnailWidth = maxThumbnailWidth;
    scaledThumbnailHeight = Math.round(scaledThumbnailHeight * scale);
  }

  // Ensure minimum thumbnail size for readability
  if (scaledThumbnailHeight < 30) {
    scaledThumbnailHeight = 30; 
    scaledThumbnailWidth = Math.round(scaledThumbnailHeight * (baseThumbnailWidth / baseThumbnailHeight));
  }



  // Update local input when frame duration changes externally
  React.useEffect(() => {
    if (!isEditingDuration) {
      setDurationInput(frame.duration.toString());
    }
  }, [frame.duration, isEditingDuration]);

  // Generate pixel-based thumbnail canvas
  const thumbnailCanvas = useMemo(() => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    if (!ctx) return null;

    // Set thumbnail dimensions (scaled based on timeline zoom)
    const thumbnailWidth = scaledThumbnailWidth;
    const thumbnailHeight = scaledThumbnailHeight;
    canvas.width = thumbnailWidth;
    canvas.height = thumbnailHeight;

    // Calculate scaling factors
    const scaleX = thumbnailWidth / canvasWidth;
    const scaleY = thumbnailHeight / canvasHeight;
    const cellWidth = Math.max(1, scaleX);
    const cellHeight = Math.max(1, scaleY);

    // Use canvas background color instead of hardcoded color
    ctx.fillStyle = canvasBackgroundColor || '#1a1a1a';
    ctx.fillRect(0, 0, thumbnailWidth, thumbnailHeight);

    // If frame is empty, show a subtle grid pattern
    if (frame.data.size === 0) {
      ctx.strokeStyle = '#333333';
      ctx.lineWidth = 0.5;
      const gridSpacing = 8;

      // Draw vertical lines
      for (let x = 0; x < thumbnailWidth; x += gridSpacing) {
        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, thumbnailHeight);
        ctx.stroke();
      }

      // Draw horizontal lines
      for (let y = 0; y < thumbnailHeight; y += gridSpacing) {
        ctx.beginPath();
        ctx.moveTo(0, y);
        ctx.lineTo(thumbnailWidth, y);
        ctx.stroke();
      }

      return canvas.toDataURL();
    }

    // Render each cell as a colored rectangle
    for (const [key, cell] of frame.data) {
      const coords = key.split(',').map(Number);
      const x = coords[0];
      const y = coords[1];

      if (x >= 0 && x < canvasWidth && y >= 0 && y < canvasHeight) {
        // Calculate pixel position in thumbnail
        const pixelX = Math.floor(x * scaleX);
        const pixelY = Math.floor(y * scaleY);

        // Use character color (foreground) primarily, fallback to background, then white
        const color = cell.color || cell.bgColor || '#ffffff';
        ctx.fillStyle = color;
        ctx.fillRect(pixelX, pixelY, Math.ceil(cellWidth), Math.ceil(cellHeight));
      }
    }

    return canvas.toDataURL();
  }, [frame.data, canvasWidth, canvasHeight, canvasBackgroundColor, scaledThumbnailWidth, scaledThumbnailHeight]);

  // Handle duration input change (allow free typing)
  const handleDurationInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setDurationInput(event.target.value);
  };

  // Handle duration input focus (start editing)
  const handleDurationFocus = () => {
    setIsEditingDuration(true);
  };

  // Handle duration input blur (validate and commit)
  const handleDurationBlur = () => {
    setIsEditingDuration(false);
    const parsedDuration = parseInt(durationInput);

    if (isNaN(parsedDuration) || parsedDuration < 50) {
      // Invalid input, reset to minimum and update store
      setDurationInput('50');
      onDurationChange(50);
    } else if (parsedDuration > 10000) {
      // Exceeds maximum, clamp and update store
      setDurationInput('10000');
      onDurationChange(10000);
    } else {
      // Valid input, update store
      onDurationChange(parsedDuration);
    }
  };

  // Handle Enter and Tab keys to commit changes
  const handleDurationKeyDown = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === 'Enter' || event.key === 'Tab') {
      // For Tab, we let the default behavior happen but ensure blur occurs first
      if (event.key === 'Enter') {
        event.preventDefault(); // Prevent form submission
      }
      event.currentTarget.blur(); // Trigger blur event to commit changes
    }
  };

  // Handle mouse down on duration input to prevent drag initiation
  const handleDurationMouseDown = (event: React.MouseEvent<HTMLInputElement>) => {
    // Stop propagation to prevent drag handlers from being triggered
    event.stopPropagation();
  };

  // Handle mouse enter on duration input to disable dragging
  const handleDurationMouseEnter = () => {
    // Disable dragging on the parent card when mouse is over the input
    const parentCard = document.querySelector(`[data-frame-index="${frameIndex}"]`) as HTMLElement;
    if (parentCard) {
      parentCard.draggable = false;
    }
  };

  // Handle mouse leave on duration input to re-enable dragging
  const handleDurationMouseLeave = () => {
    // Re-enable dragging on the parent card when mouse leaves the input
    const parentCard = document.querySelector(`[data-frame-index="${frameIndex}"]`) as HTMLElement;
    if (parentCard) {
      parentCard.draggable = true;
    }
  };

  // Calculate frame card border styling based on state
  const getBorderStyle = () => {
    if (isDragging) {
      return 'border-primary/50 bg-primary/10';
    }

    if (isActive) {
      // Active frame: Full white border
      return 'border-white bg-white/5';
    }

    if (isSelected) {
      // Selected but not active: Dimmer white border (60% opacity)
      return 'border-white/60 bg-white/5';
    }

    // Unselected: Default border
    return 'border-border hover:border-primary/50';
  };

  // Onion skin indicator on canvas preview box (not card border)
  const getCanvasPreviewBorder = () => {
    if (isOnionSkinPrevious) {
      return 'border-purple-500/60';
    }
    if (isOnionSkinNext) {
      return 'border-red-500/60';
    }
    return 'border-border';
  };

  return (
    <Card
      className={`
        relative flex-shrink-0 cursor-pointer transition-all duration-150 ease-out select-none overflow-hidden flex flex-col
        ${getBorderStyle()}
        ${isDragging ? 'opacity-50 scale-95' : ''}
      `}
      onClick={(e) => onSelect(e)} // Pass event to parent
      {...dragHandleProps}
      data-frame-index={frameIndex}
      style={{
        width: `${scaledCardSize}px`,
        height: `${scaledCardSize}px`,
        maxWidth: `${scaledCardSize}px`,
        maxHeight: `${scaledCardSize}px`,
        padding: '8px', // Fixed padding
        userSelect: 'none',
        WebkitUserSelect: 'none',
        MozUserSelect: 'none',
        msUserSelect: 'none',
        ...dragHandleProps?.style
      }}
    >
      {/* Frame number and controls - pinned to top */}
      <div className="flex items-center justify-between mb-1.5 flex-shrink-0">
        <div className="flex items-center gap-1">
          <Badge variant="outline" className="text-xs px-1 py-0">
            {frameIndex + 1}
          </Badge>

          {/* Onion skin distance indicator */}
          {(isOnionSkinPrevious || isOnionSkinNext) && onionSkinDistance > 0 && (
            <Badge 
              variant="outline" 
              className={`text-xs px-1 py-0 ${
                isOnionSkinPrevious 
                  ? 'border-purple-500 text-purple-600' 
                  : 'border-red-500 text-red-600'
              }`}
            >
              -{onionSkinDistance}
            </Badge>
          )}
        </div>

        <div className="flex gap-1">
          <Button
            size="sm"
            variant="ghost"
            className="h-5 w-5 p-0"
            tabIndex={-1} // Remove from tab order - tab should skip to next frame's duration input
            onClick={(e) => {
              e.stopPropagation();
              onDuplicate();
            }}
          >
            <Copy className="h-3 w-3" />
          </Button>

          <Button
            size="sm"
            variant="ghost"
            className="h-5 w-5 p-0 text-destructive hover:text-destructive"
            tabIndex={-1} // Remove from tab order - tab should skip to next frame's duration input
            onClick={(e) => {
              e.stopPropagation();
              onDelete();
            }}
          >
            <X className="h-3 w-3" />
          </Button>
        </div>
      </div>

      {/* Frame preview - flexible space between header and duration */}
      <div className="flex-1 mb-1 overflow-hidden min-h-0">
        <div className={`bg-muted/30 p-1 rounded border h-full flex items-center justify-center ${getCanvasPreviewBorder()}`}>
          {thumbnailCanvas ? (
            <img 
              src={thumbnailCanvas} 
              alt={`Frame ${frameIndex} preview`}
              className="max-w-full max-h-full object-contain rounded-sm pointer-events-none"
              style={{ imageRendering: 'pixelated' }}
            />
          ) : (
            <div className="text-muted-foreground italic text-center text-xs pointer-events-none">
              Empty
            </div>
          )}
        </div>
      </div>

      {/* Duration control - pinned to bottom with fixed height */}
      <div className="flex items-center gap-1 flex-shrink-0" style={{ height: '24px' }}>
        <input
          type="number"
          value={durationInput}
          onChange={handleDurationInputChange}
          onFocus={handleDurationFocus}
          onBlur={handleDurationBlur}
          onKeyDown={handleDurationKeyDown}
          onMouseDown={handleDurationMouseDown}
          onMouseEnter={handleDurationMouseEnter}
          onMouseLeave={handleDurationMouseLeave}
          onClick={(e) => e.stopPropagation()}
          tabIndex={frameIndex + 1} // Sequential tab order: frame 0 = tabIndex 1, frame 1 = tabIndex 2, etc.
          className="flex-1 text-xs px-1 py-0.5 border border-border rounded w-12 bg-background"
          min="50"
          max="10000"
          step="10"
        />
        <span className="text-xs text-muted-foreground">ms</span>
      </div>
    </Card>
  );
};

import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '../ui/dialog';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Label } from '../ui/label';
import { Card, CardContent } from '../ui/card';
import { Badge } from '../ui/badge';
import { Switch } from '../ui/switch';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { Globe, Download, Loader2, Palette, Type } from 'lucide-react';
import { useExportStore } from '../../stores/exportStore';
import { useExportDataCollector } from '../../utils/exportDataCollector';
import { ExportRenderer } from '../../utils/exportRenderer';

/**
 * HTML Export Dialog
 * Handles HTML animation export with customization options
 */
export const HtmlExportDialog: React.FC = () => {
  const activeFormat = useExportStore(state => state.activeFormat);
  const showExportModal = useExportStore(state => state.showExportModal);
  const setShowExportModal = useExportStore(state => state.setShowExportModal);
  const htmlSettings = useExportStore(state => state.htmlSettings);
  const setHtmlSettings = useExportStore(state => state.setHtmlSettings);
  const setProgress = useExportStore(state => state.setProgress);
  const setIsExporting = useExportStore(state => state.setIsExporting);
  const isExporting = useExportStore(state => state.isExporting);

  const exportData = useExportDataCollector();

  const [filename, setFilename] = useState('ascii-motion-animation');

  const isOpen = showExportModal && activeFormat === 'html';

  const handleClose = () => {
    setShowExportModal(false);
  };

  const handleExport = async () => {
    if (!exportData) {
      console.error('No export data available');
      return;
    }

    try {
      setIsExporting(true);

      // Create renderer with progress callback
      const renderer = new ExportRenderer((progress) => {
        setProgress(progress);
      });

      // Perform the export
      await renderer.exportHtml(exportData, htmlSettings, filename);

      // Close dialog on success
      handleClose();
    } catch (error) {
      console.error('HTML export failed:', error);
    } finally {
      setIsExporting(false);
      setProgress(null);
    }
  };

  const handleSettingChange = (key: keyof typeof htmlSettings, value: any) => {
    setHtmlSettings({ [key]: value });
  };

  return (
