// Import palette dialog with JSON validation and preview

import React, { useState, useCallback } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Separator } from '@/components/ui/separator';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { Upload, FileText, AlertCircle, CheckCircle, X } from 'lucide-react';
import { usePaletteStore } from '../../stores/paletteStore';
import { validatePaletteJSON, getValidationSummary } from '../../utils/paletteValidation';
import type { PaletteExportFormat } from '../../types/palette';

interface ImportPaletteDialogProps {
  isOpen: boolean;
  onOpenChange: (open: boolean) => void;
}

export const ImportPaletteDialog: React.FC<ImportPaletteDialogProps> = ({
  isOpen,
  onOpenChange
}) => {
  const { importPalette } = usePaletteStore();

  const [importMethod, setImportMethod] = useState<'file' | 'text'>('file');
  const [jsonText, setJsonText] = useState('');
  const [validationResult, setValidationResult] = useState<ReturnType<typeof validatePaletteJSON> | null>(null);
  const [previewData, setPreviewData] = useState<PaletteExportFormat | null>(null);
  const [isImporting, setIsImporting] = useState(false);
  const [importSuccess, setImportSuccess] = useState<string | null>(null);

  // Handle file upload
  const handleFileUpload = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const content = e.target?.result as string;
      setJsonText(content);
      validateAndPreview(content);
    };
    reader.readAsText(file);
  }, []);

  // Handle text input change
  const handleTextChange = (value: string) => {
    setJsonText(value);
    if (value.trim()) {
      validateAndPreview(value);
    } else {
      setValidationResult(null);
      setPreviewData(null);
    }
  };

  // Validate JSON and show preview
  const validateAndPreview = (jsonString: string) => {
    const result = validatePaletteJSON(jsonString);
    setValidationResult(result);
    setPreviewData(result.isValid ? result.data || null : null);
  };

  // Handle import confirmation
  const handleImport = async () => {
    if (!jsonText.trim() || !validationResult?.isValid) return;

    setIsImporting(true);
    try {
      const result = importPalette(jsonText);
      if (result.success) {
        setImportSuccess(result.message);
        // Clear form after successful import
        setTimeout(() => {
          handleReset();
          onOpenChange(false);
        }, 2000);
      } else {
        setValidationResult({
          isValid: false,
          errors: [result.message],
          warnings: []
        });
      }
    } catch (error) {
      setValidationResult({
        isValid: false,
        errors: ['An unexpected error occurred during import.'],
        warnings: []
      });
    } finally {
      setIsImporting(false);
    }
  };

  // Reset form
  const handleReset = () => {
    setJsonText('');
    setValidationResult(null);
    setPreviewData(null);
    setImportSuccess(null);
    setImportMethod('file');
  };

  // Handle dialog close
  const handleClose = () => {
    handleReset();
    onOpenChange(false);
  };

  return (
    <Dialog open={isOpen} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-lg">
        <DialogHeader>
          <DialogTitle>Import Color Palette</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Import method selection */}
          <div className="space-y-2">
            <Label className="text-sm font-medium">Import Method</Label>
            <div className="flex gap-2">
              <Button
                size="sm"
                variant={importMethod === 'file' ? 'default' : 'outline'}
                onClick={() => setImportMethod('file')}
                className="gap-2"
              >
                <Upload className="h-4 w-4" />
                File Upload
              </Button>
              <Button
                size="sm"
                variant={importMethod === 'text' ? 'default' : 'outline'}
                onClick={() => setImportMethod('text')}
                className="gap-2"
              >
                <FileText className="h-4 w-4" />
                Paste JSON
              </Button>
            </div>
          </div>

          <Separator />

          {/* File upload */}
          {importMethod === 'file' && (
            <div className="space-y-2">
              <Label className="text-sm font-medium">Select Palette File</Label>
              <Input
                type="file"
                accept=".json,.palette,.pal"
                onChange={handleFileUpload}
                className="cursor-pointer"
              />
              <p className="text-xs text-muted-foreground">
                Supported formats: .json, .palette, .pal
              </p>
            </div>
          )}

          {/* Text input */}
          {importMethod === 'text' && (
            <div className="space-y-2">
              <Label className="text-sm font-medium">Paste JSON Data</Label>
              <textarea
                value={jsonText}
                onChange={(e) => handleTextChange(e.target.value)}
                placeholder='{"name": "My Palette", "colors": ["#ff0000", "#00ff00", "#0000ff"]}'
                className="w-full h-24 px-3 py-2 text-sm border border-border rounded-md bg-background resize-none focus:outline-none focus:ring-1 focus:ring-ring"
              />
            </div>
          )}

          {/* Validation results */}
          {validationResult && (
            <div className="space-y-2">
              <Alert className={validationResult.isValid ? 'border-green-200' : 'border-red-200'}>
                <div className="flex items-start gap-2">
                  {validationResult.isValid ? (
                    <CheckCircle className="h-4 w-4 text-green-600 mt-0.5" />
                  ) : (
                    <AlertCircle className="h-4 w-4 text-red-600 mt-0.5" />
                  )}
                  <div className="flex-1">
                    <AlertDescription>
                      <div className="font-medium mb-1">
                        {getValidationSummary(validationResult)}
                      </div>

                      {validationResult.errors.length > 0 && (
                        <div className="space-y-1">
                          <div className="text-xs font-medium text-red-600">Errors:</div>
                          {validationResult.errors.map((error, index) => (
                            <div key={index} className="text-xs text-red-600">â€¢ {error}</div>
                          ))}
                        </div>
                      )}

                      {validationResult.warnings.length > 0 && (
                        <div className="space-y-1 mt-2">
                          <div className="text-xs font-medium text-yellow-600">Warnings:</div>
                          {validationResult.warnings.map((warning, index) => (
                            <div key={index} className="text-xs text-yellow-600">â€¢ {warning}</div>
                          ))}
                        </div>
                      )}
                    </AlertDescription>
                  </div>
                </div>
              </Alert>
            </div>
          )}

          {/* Preview */}
          {previewData && (
            <div className="space-y-3 p-4 bg-muted/50 rounded-lg">
              <div className="flex items-center gap-2">
                <CheckCircle className="h-4 w-4 text-green-600" />
                <span className="font-medium">Palette Preview</span>
              </div>

              <div className="space-y-2">
                <div>
                  <span className="text-sm font-medium">Name:</span>{' '}
                  <span className="text-sm">{previewData.name}</span>
                </div>
                <div>
                  <span className="text-sm font-medium">Colors:</span>{' '}
                  <span className="text-sm">{previewData.colors.length}</span>
                </div>

                {/* Color Preview */}
                <div className="space-y-1">
                  <span className="text-sm font-medium">Preview:</span>
                  <TooltipProvider>
                    <div className="flex flex-wrap gap-1">
                      {previewData.colors.slice(0, 16).map((color, index) => (
                        <Tooltip key={index}>
                          <TooltipTrigger asChild>
                            <div
                              className="w-6 h-6 rounded border border-border"
                              style={{ backgroundColor: color }}
                            />
                          </TooltipTrigger>
                          <TooltipContent>
                            <p>{color}</p>
                          </TooltipContent>
                        </Tooltip>
                      ))}
                      {previewData.colors.length > 16 && (
                        <div className="flex items-center justify-center w-6 h-6 text-xs text-muted-foreground border border-border rounded">
                          +{previewData.colors.length - 16}
                        </div>
                      )}
                    </div>
                  </TooltipProvider>
                </div>
              </div>
            </div>
          )}

          {/* Success message */}
          {importSuccess && (
            <Alert className="border-green-200">
              <CheckCircle className="h-4 w-4 text-green-600" />
              <AlertDescription className="text-green-700">
                {importSuccess}
              </AlertDescription>
            </Alert>
          )}
        </div>

        <DialogFooter>
          <div className="flex gap-2 w-full justify-between">
            <Button
              variant="outline"
              onClick={handleReset}
              disabled={isImporting || importSuccess !== null}
            >
              Reset
            </Button>

            <div className="flex gap-2">
              <Button
                variant="outline"
                onClick={handleClose}
                disabled={isImporting}
                className="gap-2"
              >
                <X className="h-4 w-4" />
                Cancel
              </Button>

              <Button
                onClick={handleImport}
                disabled={!validationResult?.isValid || isImporting || importSuccess !== null}
                className="gap-2"
              >
                {isImporting ? (
                  <>
                    <div className="animate-spin h-4 w-4 border-2 border-current border-t-transparent rounded-full" />
                    Importing...
                  </>
                ) : (
                  <>
                    <Upload className="h-4 w-4" />
                    Import Palette
                  </>
                )}
              </Button>
            </div>
          </div>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
};

import React, { useCallback, useRef, useEffect } from 'react';
import { useGradientStore } from '../../stores/gradientStore';
import { useCanvasContext } from '../../contexts/CanvasContext';
import { useToolStore } from '../../stores/toolStore';
import { GradientStopPicker } from './GradientStopPicker';

export const InteractiveGradientOverlay: React.FC = () => {
  const overlayRef = useRef<HTMLDivElement>(null);
  const { activeTool } = useToolStore();
  const { cellWidth, cellHeight, zoom, panOffset, altKeyDown } = useCanvasContext();
  const { 
    isApplying, 
    startPoint, 
    endPoint, 
    ellipsePoint,
    hoverEndPoint,
    definition,
    dragState,
    editingStop,
    startDrag,
    updateDrag,
    endDrag,
    startEditingStop,
    updateEditingStopValue,
    closeStopEditor,
    cancelStopEdit,
    duplicateStop
  } = useGradientStore();

  const stopOffsetBase = 24;
  const stopSize = 18;
  const stopHitRadius = stopSize / 2 + 2;
  const controlBorderColor = '#1f2937';
  const controlOuterStrokeColor = '#ffffff';
  const displayEndPoint = endPoint ?? hoverEndPoint;

  const effectiveCellWidth = cellWidth * zoom;
  const effectiveCellHeight = cellHeight * zoom;

  // Hit testing function to determine what element is clicked
  const hitTest = useCallback((mouseX: number, mouseY: number) => {
    if (!startPoint) return null;

    const startPixelX = startPoint.x * effectiveCellWidth + panOffset.x + effectiveCellWidth / 2;
    const startPixelY = startPoint.y * effectiveCellHeight + panOffset.y + effectiveCellHeight / 2;

    // Test start point (6px radius)
    const startDist = Math.sqrt(Math.pow(mouseX - startPixelX, 2) + Math.pow(mouseY - startPixelY, 2));

    if (displayEndPoint) {
      const endPixelX = displayEndPoint.x * effectiveCellWidth + panOffset.x + effectiveCellWidth / 2;
      const endPixelY = displayEndPoint.y * effectiveCellHeight + panOffset.y + effectiveCellHeight / 2;

      // Test end point (6px radius)
      const endDist = Math.sqrt(Math.pow(mouseX - endPixelX, 2) + Math.pow(mouseY - endPixelY, 2));

      // Check stops first (they take precedence)
      const enabledProperties: Array<'character' | 'textColor' | 'backgroundColor'> = [];
      if (definition.character.enabled) enabledProperties.push('character');
      if (definition.textColor.enabled) enabledProperties.push('textColor');
      if (definition.backgroundColor.enabled) enabledProperties.push('backgroundColor');

      for (let propIndex = 0; propIndex < enabledProperties.length; propIndex++) {
        const property = enabledProperties[propIndex];
        const stops = definition[property].stops;

        for (let stopIndex = 0; stopIndex < stops.length; stopIndex++) {
          const stop = stops[stopIndex];
          if (stop.position < 0 || stop.position > 1) continue;

          // Calculate stop position
          const lineX = startPixelX + (endPixelX - startPixelX) * stop.position;
          const lineY = startPixelY + (endPixelY - startPixelY) * stop.position;

          // Apply perpendicular offset
          const lineAngle = Math.atan2(endPixelY - startPixelY, endPixelX - startPixelX);
          const perpAngle = lineAngle + Math.PI / 2;
          const offsetDistance = (propIndex + 1) * stopOffsetBase;

          const stopX = lineX + Math.cos(perpAngle) * offsetDistance;
          const stopY = lineY + Math.sin(perpAngle) * offsetDistance;

          // Test stop hit (6px radius)
          const stopDist = Math.sqrt(Math.pow(mouseX - stopX, 2) + Math.pow(mouseY - stopY, 2));
          if (stopDist <= stopHitRadius) {
            return {
              type: 'stop' as const,
              property,
              stopIndex
            };
          }
        }
      }

      // Test end point after stops (stops have precedence)
      if (endDist <= 8 && endPoint) {
        return { type: 'end' as const };
      }

      // Test ellipse point for radial gradients
      if (definition.type === 'radial' && ellipsePoint) {
        const ellipsePixelX = ellipsePoint.x * effectiveCellWidth + panOffset.x + effectiveCellWidth / 2;
        const ellipsePixelY = ellipsePoint.y * effectiveCellHeight + panOffset.y + effectiveCellHeight / 2;
        const ellipseDist = Math.sqrt(Math.pow(mouseX - ellipsePixelX, 2) + Math.pow(mouseY - ellipsePixelY, 2));

        if (ellipseDist <= 8) {
          return { type: 'ellipse' as const };
        }
      }
    }

    // Test start point last
    if (startDist <= 8) {
      return { type: 'start' as const };
    }

    return null;
  }, [startPoint, endPoint, ellipsePoint, hoverEndPoint, definition, effectiveCellWidth, effectiveCellHeight, panOffset]);

  // Mouse event handlers
  const handleMouseDown = useCallback((event: React.MouseEvent) => {
    const rect = overlayRef.current?.getBoundingClientRect();
    if (!rect) return;

    const mouseX = event.clientX - rect.left;
    const mouseY = event.clientY - rect.top;

    const hit = hitTest(mouseX, mouseY);

    if (!hit) {
      // No control hit - let the event bubble to canvas for end point placement
      return;
    }

    // We hit a control - prevent default and handle drag
    event.preventDefault();
    event.stopPropagation();

    if (hit.type === 'start' || hit.type === 'end' || hit.type === 'ellipse') {
      startDrag(hit.type, { x: mouseX, y: mouseY });
    } else if (hit.type === 'stop') {
      // Check for alt+click to duplicate stop (use event.altKey for immediate detection)
      if (event.altKey) {
        // Duplicate the stop and start dragging the new one
        const newStopIndex = duplicateStop(hit.property, hit.stopIndex);
        startDrag('stop', { x: mouseX, y: mouseY }, {
          property: hit.property,
          stopIndex: newStopIndex
        });
      } else {
        // Normal drag
        startDrag('stop', { x: mouseX, y: mouseY }, {
          property: hit.property,
          stopIndex: hit.stopIndex
        });
      }
    }
  }, [hitTest, startDrag, altKeyDown, duplicateStop]);

  // Handle double-click on overlay to detect stop clicks
  const handleOverlayDoubleClick = useCallback((event: React.MouseEvent) => {
    const rect = overlayRef.current?.getBoundingClientRect();
    if (!rect) return;

    const mouseX = event.clientX - rect.left;
    const mouseY = event.clientY - rect.top;

    const hit = hitTest(mouseX, mouseY);

    if (hit && hit.type === 'stop') {
      event.preventDefault();
      event.stopPropagation();
      startEditingStop(hit.property, hit.stopIndex);
    }
  }, [hitTest, startEditingStop]);

  const handleMouseMove = useCallback((event: React.MouseEvent) => {
    if (!dragState?.isDragging) return;

    const rect = overlayRef.current?.getBoundingClientRect();
    if (!rect) return;

    const mouseX = event.clientX - rect.left;
    const mouseY = event.clientY - rect.top;

    // Pass canvas context for accurate coordinate conversion
    updateDrag({ x: mouseX, y: mouseY }, {
      cellWidth,
      cellHeight,
      zoom,
      panOffset
    });
  }, [dragState?.isDragging, updateDrag, cellWidth, cellHeight, zoom, panOffset]);

  const handleMouseUp = useCallback((event: React.MouseEvent) => {
    event.preventDefault();
    event.stopPropagation();

    if (dragState?.isDragging) {
      endDrag();
    }
  }, [dragState?.isDragging, endDrag]);

  // Global mouse up handler for when mouse leaves the overlay
  useEffect(() => {
    const handleGlobalMouseUp = () => {
      if (dragState?.isDragging) {
        endDrag();
      }
    };

    if (dragState?.isDragging) {
      window.addEventListener('mouseup', handleGlobalMouseUp);
      return () => window.removeEventListener('mouseup', handleGlobalMouseUp);
    }
  }, [dragState?.isDragging, endDrag]);

  // Don't render if not in gradient tool mode or not applying
  if (activeTool !== 'gradientfill' || !isApplying) return null;

  // Render interactive controls
  const renderControls = () => {
    if (!startPoint) return null;

    const startPixelX = startPoint.x * effectiveCellWidth + panOffset.x + effectiveCellWidth / 2;
    const startPixelY = startPoint.y * effectiveCellHeight + panOffset.y + effectiveCellHeight / 2;

    const elements: React.ReactNode[] = [];

    // Start point
    elements.push(
      <div
        key="start-point"
        className="absolute w-3 h-3 -translate-x-1/2 -translate-y-1/2 rounded-full border-2 cursor-move"
        style={{
          left: startPixelX,
          top: startPixelY,
          backgroundColor: '#ffffff',
          borderColor: controlBorderColor,
          boxShadow: `0 0 0 1px ${controlOuterStrokeColor}`,
          pointerEvents: dragState?.isDragging ? 'none' : 'auto'
        }}
        onMouseDown={(e) => {
          // Handle start point dragging even when container has pointer-events: none
          e.preventDefault();
          e.stopPropagation();
          startDrag('start', {
            x: e.clientX - (overlayRef.current?.getBoundingClientRect()?.left || 0),
            y: e.clientY - (overlayRef.current?.getBoundingClientRect()?.top || 0)
          });
        }}
      />
    );

    const enabledProperties: Array<'character' | 'textColor' | 'backgroundColor'> = [];
    if (definition.character.enabled) enabledProperties.push('character');
    if (definition.textColor.enabled) enabledProperties.push('textColor');
    if (definition.backgroundColor.enabled) enabledProperties.push('backgroundColor');

    const hasDisplayEndPoint = Boolean(displayEndPoint);
    let endPixelX = 0;
    let endPixelY = 0;

    if (hasDisplayEndPoint) {
      endPixelX = displayEndPoint!.x * effectiveCellWidth + panOffset.x + effectiveCellWidth / 2;
      endPixelY = displayEndPoint!.y * effectiveCellHeight + panOffset.y + effectiveCellHeight / 2;

      elements.push(
        <svg
          key="gradient-line"
          className="absolute inset-0 pointer-events-none"
          style={{ overflow: 'visible' }}
        >
          <line
            x1={startPixelX}
            y1={startPixelY}
            x2={endPixelX}
            y2={endPixelY}
            stroke="#6b7280"
            strokeWidth={2}
            strokeDasharray="5,5"
          />
        </svg>
      );

      elements.push(
        <div
          key="end-point"
          className="absolute w-3 h-3 -translate-x-1/2 -translate-y-1/2 rounded-full border-2 cursor-move"
          style={{
            left: endPixelX,
            top: endPixelY,
            backgroundColor: '#ffffff',
            borderColor: controlBorderColor,
            boxShadow: `0 0 0 1px ${controlOuterStrokeColor}`,
            pointerEvents: dragState?.isDragging || !endPoint ? 'none' : 'auto'
          }}
        />
      );
    }

    // Ellipse point for radial gradients
    if (definition.type === 'radial' && ellipsePoint) {
      const ellipsePixelX = ellipsePoint.x * effectiveCellWidth + panOffset.x + effectiveCellWidth / 2;
      const ellipsePixelY = ellipsePoint.y * effectiveCellHeight + panOffset.y + effectiveCellHeight / 2;

      elements.push(
        <div
          key="ellipse-point"
          className="absolute w-3 h-3 -translate-x-1/2 -translate-y-1/2 rounded-full border-2 cursor-move"
          style={{
            left: ellipsePixelX,
            top: ellipsePixelY,
            backgroundColor: '#ffffff',
            borderColor: '#dc2626', // Different color to distinguish from end point
            boxShadow: `0 0 0 1px ${controlOuterStrokeColor}`,
            pointerEvents: dragState?.isDragging ? 'none' : 'auto'
          }}
          onMouseDown={(e) => {
            e.preventDefault();
            e.stopPropagation();
            startDrag('ellipse', {
              x: e.clientX - (overlayRef.current?.getBoundingClientRect()?.left || 0),
              y: e.clientY - (overlayRef.current?.getBoundingClientRect()?.top || 0)
            });
          }}
        />
      );
    }

    enabledProperties.forEach((property, propIndex) => {
      const stops = definition[property].stops;

      stops.forEach((stop, stopIndex) => {
        if (stop.position < 0 || stop.position > 1) return;

        let stopX: number;
        let stopY: number;

        if (hasDisplayEndPoint) {
          const lineX = startPixelX + (endPixelX - startPixelX) * stop.position;
          const lineY = startPixelY + (endPixelY - startPixelY) * stop.position;

          const lineAngle = Math.atan2(endPixelY - startPixelY, endPixelX - startPixelX);
          const perpAngle = lineAngle + Math.PI / 2;
          const offsetDistance = (propIndex + 1) * stopOffsetBase;

          stopX = lineX + Math.cos(perpAngle) * offsetDistance;
          stopY = lineY + Math.sin(perpAngle) * offsetDistance;

          if (offsetDistance > 0) {
            elements.push(
              <svg
                key={`connection-${property}-${stopIndex}`}
                className="absolute inset-0 pointer-events-none"
                style={{ overflow: 'visible' }}
              >
                <line
                  x1={lineX}
                  y1={lineY}
                  x2={stopX}
                  y2={stopY}
                  stroke="#9ca3af"
                  strokeWidth={1}
                  strokeDasharray="2,2"
                />
              </svg>
            );
          }
        } else {
          stopX = startPixelX + propIndex * 25 - (enabledProperties.length - 1) * 12.5;
          stopY = startPixelY + 40 + stopIndex * 25;
        }

        const isColorStop = property === 'textColor' || property === 'backgroundColor';
        const markerBackground = isColorStop ? stop.value : '#111827';

        // Check if this stop is being edited
        const isEditing = editingStop?.property === property && editingStop?.stopIndex === stopIndex;

        elements.push(
          <div
            key={`stop-${property}-${stopIndex}`}
            className="absolute -translate-x-1/2 -translate-y-1/2 border cursor-move"
            style={{
              left: stopX,
              top: stopY,
              width: stopSize,
              height: stopSize,
              backgroundColor: markerBackground,
              borderColor: isEditing ? '#3b82f6' : controlBorderColor, // Blue border when editing
              borderRadius: 4,
              borderWidth: isEditing ? 3 : 2, // Thicker border when editing
              boxShadow: isEditing 
                ? `0 0 0 2px #3b82f6, 0 0 12px rgba(59, 130, 246, 0.5)` // Blue glow when editing
                : `0 0 0 1px ${controlOuterStrokeColor}`,
              pointerEvents: dragState?.isDragging || editingStop ? 'none' : 'auto',
              transition: 'border-color 0.2s ease, border-width 0.2s ease, box-shadow 0.2s ease' // Only transition visual properties, not position
            }}
          />
        );

        if (property === 'character') {
          elements.push(
            <div
              key={`stop-value-${property}-${stopIndex}`}
              className="absolute font-mono text-sm -translate-x-1/2 -translate-y-1/2 pointer-events-none select-none"
              style={{
                left: stopX,
                top: stopY,
                color: '#f9fafb'
              }}
            >
              {stop.value}
            </div>
          );
        }
      });
    });

    return elements;
  };

  return (
    <div
      ref={overlayRef}
      className="absolute inset-0"
      style={{ 
        zIndex: 10,
        cursor: dragState?.isDragging ? 'grabbing' : 'auto',
        // Disable pointer events when:
        // 1. Only have start point (no end point yet)
        // 2. Stop editor is open (picker is showing)
        pointerEvents: (startPoint && endPoint && !editingStop) ? 'auto' : 'none'
      }}
      onMouseDown={!editingStop ? handleMouseDown : undefined}
      onMouseMove={!editingStop ? handleMouseMove : undefined}
      onMouseUp={!editingStop ? handleMouseUp : undefined}
      onDoubleClick={!editingStop ? handleOverlayDoubleClick : undefined}
    >
      {renderControls()}

      {/* Gradient Stop Picker */}
      {editingStop && (
        <GradientStopPicker
          isOpen={true}
          onOpenChange={(open) => {
            if (!open) {
              // User closed picker without selecting - revert changes
              cancelStopEdit();
            }
          }}
          onValueSelect={(value) => {
            // User confirmed selection - keep changes and close
            updateEditingStopValue(value);
            closeStopEditor();
          }}
          onValueChange={(value) => {
            // Live preview - temporarily update stop value
            updateEditingStopValue(value);
          }}
          initialValue={editingStop.originalValue}
          type={editingStop.property}
        />
      )}
    </div>
  );
};

/**
 * MediaImportPanel - Side panel overlay for importing images and videos to ASCII art
 * 
 * Features:
 * - Overlays existing side panel while keeping canvas visible
 * - Live preview on canvas as settings change
 * - File drop zone with format detection
 * - Size controls with real-time feedback
 * - Processing progress display
 */

import React, { useCallback, useState, useEffect } from 'react';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Label } from '../ui/label';
import { Progress } from '../ui/progress';
import { Checkbox } from '../ui/checkbox';
import { Alert, AlertDescription } from '../ui/alert';
import { ScrollArea } from '../ui/scroll-area';
import { Slider } from '../ui/slider';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '../ui/tooltip';
import { 
  Collapsible,
  CollapsibleContent,
} from '../ui/collapsible';
import { CollapsibleHeader } from '../common/CollapsibleHeader';
import { PanelSeparator } from '../common/PanelSeparator';
import { 
  Upload, 
  Image as ImageIcon, 
  Video, 
  FileX, 
  Download,
  X,
  ChevronLeft,
  ChevronRight,
  ChevronsLeft,
  ChevronsRight,
  Link,
  Eye,
  Move3D,
  ArrowUp,
  ArrowDown,
  ArrowLeft,
  ArrowRight,
  RotateCcw
} from 'lucide-react';
import { cn } from '../../lib/utils';
import { PANEL_ANIMATION } from '../../constants';
import { Spinner } from '../common/Spinner';
import { 
  useImportModal, 
  useImportFile, 
  useImportProcessing, 
  useImportSettings,
  useImportPreview,
  useImportUIState,
  useImportSessionState
} from '../../stores/importStore';
import { mediaProcessor, SUPPORTED_IMAGE_FORMATS, SUPPORTED_VIDEO_FORMATS } from '../../utils/mediaProcessor';
import { asciiConverter } from '../../utils/asciiConverter';
import { useCanvasStore } from '../../stores/canvasStore';
import { useAnimationStore } from '../../stores/animationStore';
import { usePreviewStore } from '../../stores/previewStore';
import { useCharacterPaletteStore } from '../../stores/characterPaletteStore';
import { usePaletteStore } from '../../stores/paletteStore';
import { useToolStore } from '../../stores/toolStore';
import { CharacterMappingSection } from './CharacterMappingSection';
import { TextColorMappingSection } from './TextColorMappingSection';
import { BackgroundColorMappingSection } from './BackgroundColorMappingSection';
import { PreprocessingSection } from './PreprocessingSection';
import type { MediaFile } from '../../utils/mediaProcessor';
import type { Cell } from '../../types';

export function MediaImportPanel() {
  const { isOpen, closeModal } = useImportModal();
  const { selectedFile, setSelectedFile, setProcessedFrames } = useImportFile();
  const { isProcessing, progress, error, setProcessing, setProgress, setError } = useImportProcessing();
  const { settings, updateSettings } = useImportSettings();
  const { frameIndex, setFrameIndex, frames: previewFrames } = useImportPreview();
  const { uiState, updateUIState } = useImportUIState();
  const { hasSessionSettings } = useImportSessionState();

  // Character palette integration
  const activePalette = useCharacterPaletteStore(state => state.activePalette);
  const mappingMethod = useCharacterPaletteStore(state => state.mappingMethod);
  const invertDensity = useCharacterPaletteStore(state => state.invertDensity);
  const characterSpacing = useCharacterPaletteStore(state => state.characterSpacing);

  // Tool store integration for default colors
  const selectedColor = useToolStore(state => state.selectedColor);

  // Canvas and animation stores
  const canvasWidth = useCanvasStore(state => state.width);
  const canvasHeight = useCanvasStore(state => state.height);
  const setCanvasData = useCanvasStore(state => state.setCanvasData);
  const clearCanvas = useCanvasStore(state => state.clearCanvas);
  const addFrame = useAnimationStore(state => state.addFrame);
  const setCurrentFrame = useAnimationStore(state => state.setCurrentFrame);
  const updateFrameDuration = useAnimationStore(state => state.updateFrameDuration);
  const importFramesOverwrite = useAnimationStore(state => state.importFramesOverwrite);
  const importFramesAppend = useAnimationStore(state => state.importFramesAppend);
  const currentFrameIndex = useAnimationStore(state => state.currentFrameIndex);

  // Preview store for independent preview overlay
  const { setPreviewData, clearPreview, setPreviewActive } = usePreviewStore();

  const [dragActive, setDragActive] = useState(false);
  const [isImporting, setIsImporting] = useState(false);
  const [originalImageAspectRatio, setOriginalImageAspectRatio] = useState<number | null>(null);

  // Use persistent UI state from store
  const { 
    importMode, 
    livePreviewEnabled, 
    previewSectionOpen, 
    positionSectionOpen 
  } = uiState;

  const setImportMode = (mode: 'overwrite' | 'append') => 
    updateUIState({ importMode: mode });
  const setLivePreviewEnabled = (enabled: boolean) => 
    updateUIState({ livePreviewEnabled: enabled });
  const setPreviewSectionOpen = (open: boolean) => 
    updateUIState({ previewSectionOpen: open });
  const setPositionSectionOpen = (open: boolean) => 
    updateUIState({ positionSectionOpen: open });

  // Preview state management
  const [isPreviewActive, setIsPreviewActive] = useState(false);

  // Handle linked sizing when maintain aspect ratio is enabled
  const handleWidthChange = useCallback((value: number) => {
    if (settings.maintainAspectRatio && originalImageAspectRatio) {
      // Apply character aspect ratio compensation (characters are 0.6x as wide as tall)
      // To make image appear with correct aspect ratio, we need MORE width in character count
      const CHARACTER_ASPECT_RATIO = 0.6;
      const characterCompensatedAspectRatio = originalImageAspectRatio / CHARACTER_ASPECT_RATIO;

      const newHeight = Math.ceil(value / characterCompensatedAspectRatio);
      // Don't constrain to canvas - allow larger dimensions for proper aspect ratio
      updateSettings({ 
        characterWidth: value,
        characterHeight: newHeight
      });
    } else {
      updateSettings({ characterWidth: value });
    }
  }, [settings.maintainAspectRatio, originalImageAspectRatio, updateSettings]);

  const handleHeightChange = useCallback((value: number) => {
    if (settings.maintainAspectRatio && originalImageAspectRatio) {
      // Apply character aspect ratio compensation (characters are 0.6x as wide as tall)
      // To make image appear with correct aspect ratio, we need MORE width in character count
      const CHARACTER_ASPECT_RATIO = 0.6;
      const characterCompensatedAspectRatio = originalImageAspectRatio / CHARACTER_ASPECT_RATIO;

      const newWidth = Math.ceil(value * characterCompensatedAspectRatio);
      // Don't constrain to canvas - allow larger dimensions for proper aspect ratio
      updateSettings({ 
        characterWidth: newWidth,
        characterHeight: value
      });
    } else {
      updateSettings({ characterHeight: value });
    }
  }, [settings.maintainAspectRatio, originalImageAspectRatio, updateSettings]);

  // Preview management functions
  const startPreview = useCallback(() => {
    if (!isPreviewActive) {
      setIsPreviewActive(true);
      // Activate preview overlay
      setPreviewActive(true);
    }
  }, [isPreviewActive, setPreviewActive]);

  const endPreview = useCallback(() => {
    if (isPreviewActive) {
      // Clear preview overlay
      clearPreview();
      setPreviewActive(false);
      // Clear local state
      setIsPreviewActive(false);
    }
  }, [isPreviewActive, clearPreview, setPreviewActive]);

  // Position cells on canvas based on alignment settings
  const positionCellsOnCanvas = useCallback((cells: Map<string, any>, imageWidth: number, imageHeight: number) => {


    // Calculate offset based on alignment
    let offsetX = 0;
    let offsetY = 0;

    switch (settings.cropMode) {
      case 'top-left':
        offsetX = 0;
        offsetY = 0;
        break;
      case 'top':
        offsetX = Math.floor((canvasWidth - imageWidth) / 2);
        offsetY = 0;
        break;
      case 'top-right':
        offsetX = canvasWidth - imageWidth;
        offsetY = 0;
        break;
      case 'left':
        offsetX = 0;
        offsetY = Math.floor((canvasHeight - imageHeight) / 2);
        break;
      case 'center':
        offsetX = Math.floor((canvasWidth - imageWidth) / 2);
        offsetY = Math.floor((canvasHeight - imageHeight) / 2);
        break;
      case 'right':
        offsetX = canvasWidth - imageWidth;
        offsetY = Math.floor((canvasHeight - imageHeight) / 2);
        break;
      case 'bottom-left':
        offsetX = 0;
        offsetY = canvasHeight - imageHeight;
        break;
      case 'bottom':
        offsetX = Math.floor((canvasWidth - imageWidth) / 2);
        offsetY = canvasHeight - imageHeight;
        break;
      case 'bottom-right':
        offsetX = canvasWidth - imageWidth;
        offsetY = canvasHeight - imageHeight;
        break;
    }

    // Apply nudge adjustments
    offsetX += settings.nudgeX;
    offsetY += settings.nudgeY;



    // For images larger than canvas, we need different logic
    // Don't constrain offset to positive values - allow negative offsets to show center portions
    if (imageWidth > canvasWidth || imageHeight > canvasHeight) {
      // For oversized images, positioning shows different parts of the image
      // No additional constraints needed - let the cell bounds check below handle visibility
    } else {
      // For images smaller than canvas, allow some nudging beyond bounds but keep at least part visible
      // Allow image to be nudged up to 50% off-screen while keeping some portion visible
      const maxNegativeX = Math.floor(imageWidth * -0.5);
      const maxPositiveX = canvasWidth - Math.floor(imageWidth * 0.5);
      const maxNegativeY = Math.floor(imageHeight * -0.5);
      const maxPositiveY = canvasHeight - Math.floor(imageHeight * 0.5);

      offsetX = Math.max(maxNegativeX, Math.min(offsetX, maxPositiveX));
      offsetY = Math.max(maxNegativeY, Math.min(offsetY, maxPositiveY));
    }



    const positionedCells = new Map();
    cells.forEach((cell, originalKey) => {
      // Parse original coordinates from the key (format: "x,y")
      const [origX, origY] = originalKey.split(',').map(Number);

      const newCell = {
        ...cell,
        x: origX + offsetX,
        y: origY + offsetY
      };

      // Only add cell if it's within canvas bounds
      if (newCell.x >= 0 && newCell.x < canvasWidth && newCell.y >= 0 && newCell.y < canvasHeight) {
        const newKey = `${newCell.x},${newCell.y}`;
        positionedCells.set(newKey, newCell);
      }
    });



    return positionedCells;
  }, [canvasWidth, canvasHeight, settings.cropMode, settings.nudgeX, settings.nudgeY]);

  // Get palette data for dependencies (use individual arrays to avoid getAllPalettes() caching issues)
  const palettes = usePaletteStore(state => state.palettes);
  const customPalettes = usePaletteStore(state => state.customPalettes);

  // Create conversion settings helper function
  const createConversionSettings = useCallback(() => {
    const allPalettes = [...palettes, ...customPalettes];
    const textColorPalette = settings.enableTextColorMapping && settings.textColorPaletteId 
      ? allPalettes.find((p: any) => p.id === settings.textColorPaletteId)?.colors.map((c: any) => c.value) || []
      : [];
    const backgroundColorPalette = settings.enableBackgroundColorMapping && settings.backgroundColorPaletteId 
      ? allPalettes.find((p: any) => p.id === settings.backgroundColorPaletteId)?.colors.map((c: any) => c.value) || []
      : [];

    return {
      // Character mapping
      enableCharacterMapping: settings.enableCharacterMapping,
      characterPalette: activePalette,
      mappingMethod: mappingMethod,
      invertDensity: invertDensity,

      // Text color mapping 
      enableTextColorMapping: settings.enableTextColorMapping,
      textColorPalette: textColorPalette,
      textColorMappingMode: settings.textColorMappingMode,
      defaultTextColor: selectedColor,

      // Background color mapping
      enableBackgroundColorMapping: settings.enableBackgroundColorMapping,
      backgroundColorPalette: backgroundColorPalette,
      backgroundColorMappingMode: settings.backgroundColorMappingMode,

      // Legacy settings
      useOriginalColors: settings.useOriginalColors,
      colorQuantization: settings.colorQuantization,
      paletteSize: settings.paletteSize,
      colorMappingMode: settings.colorMappingMode,

      // Preprocessing settings
      contrastEnhancement: 1 + (settings.contrast / 100), // Convert -100-100 to 0-2
      brightnessAdjustment: settings.brightness,
      saturationAdjustment: settings.saturation,
      highlightsAdjustment: settings.highlights,
      shadowsAdjustment: settings.shadows,
      midtonesAdjustment: settings.midtones,
      blurAmount: settings.blur,
      sharpenAmount: settings.sharpen,
      ditherStrength: 0.5
    };
  }, [
    settings.enableCharacterMapping,
    settings.enableTextColorMapping, 
    settings.textColorPaletteId,
    settings.textColorMappingMode,
    settings.enableBackgroundColorMapping,
    settings.backgroundColorPaletteId,
    settings.backgroundColorMappingMode,
    settings.useOriginalColors,
    settings.colorQuantization,
    settings.paletteSize,
    settings.colorMappingMode,
    settings.brightness,
    settings.contrast,
    settings.saturation,
    settings.highlights,
    settings.shadows,
    settings.midtones,
    settings.blur,
    settings.sharpen,
    activePalette,
    mappingMethod,
    invertDensity,
    selectedColor,
    palettes,
    customPalettes
  ]);

  // Auto-process file when settings change
  useEffect(() => {
    if (!selectedFile || !livePreviewEnabled) return;

    const processFileAutomatically = async () => {
      setProcessing(true);
      setProgress(0);

      try {
        const options = {
          targetWidth: settings.characterWidth,
          targetHeight: settings.characterHeight,
          maintainAspectRatio: false, // Never crop - just resize to exact dimensions
          cropMode: 'center' as const, // Not used since maintainAspectRatio is false
          quality: 'medium' as const
        };

        let result;
        if (selectedFile.type === 'image') {
          setProgress(25);
          result = await mediaProcessor.processImage(selectedFile, options);
          setProgress(100);
        } else {
          setProgress(10);
          result = await mediaProcessor.processVideo(selectedFile, options);
          setProgress(100);
        }

        if (result.success) {
          setProcessedFrames(result.frames);
          setError(null);
        } else {
          setError(result.error || 'Unknown processing error');
        }
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to process file');
      } finally {
        setProcessing(false);
      }
    };

    // Debounce the processing to avoid excessive calls
    const timeoutId = setTimeout(processFileAutomatically, 500);
    return () => clearTimeout(timeoutId);
  }, [
    selectedFile,
    livePreviewEnabled,
    settings.characterWidth,
    settings.characterHeight,
    // Removed maintainAspectRatio and cropMode since they don't affect processing anymore
    setProcessing,
    setProgress,
    setProcessedFrames,
    setError
  ]);

  // Live preview effect - update canvas when settings change
  useEffect(() => {
    if (!livePreviewEnabled || previewFrames.length === 0) return;

    const updateLivePreview = async () => {
      try {
        // Start preview mode (stores original data if not already started)
        startPreview();

        const conversionSettings = createConversionSettings();

        const result = asciiConverter.convertFrame(previewFrames[frameIndex], conversionSettings);

        // Show preview on canvas overlay (positioned based on alignment)
        const positionedCells = positionCellsOnCanvas(result.cells, settings.characterWidth, settings.characterHeight);
        setPreviewData(positionedCells);
      } catch (err) {
        console.error('Live preview error:', err);
      }
    };

    // Debounce the update to avoid excessive calls
    const timeoutId = setTimeout(updateLivePreview, 300);
    return () => clearTimeout(timeoutId);
  }, [
    livePreviewEnabled, 
    previewFrames, 
    frameIndex, 
    settings.characterWidth, 
    settings.characterHeight,
    settings.cropMode, // Added back since it affects positioning
    settings.nudgeX, // FIXED: Added nudge dependencies for position updates
    settings.nudgeY, // FIXED: Added nudge dependencies for position updates
    settings.useOriginalColors,
    settings.colorQuantization,
    settings.paletteSize,
    settings.colorMappingMode,
    settings.brightness,
    settings.contrast,
    settings.saturation,
    settings.highlights,
    settings.shadows,
    settings.midtones,
    settings.blur,
    settings.sharpen,
    // Character mapping settings
    settings.enableCharacterMapping,
    activePalette,
    mappingMethod,
    invertDensity,
    characterSpacing,
    // Color mapping settings
    settings.enableTextColorMapping,
    settings.textColorPaletteId,
    settings.textColorMappingMode,
    settings.enableBackgroundColorMapping,
    settings.backgroundColorPaletteId,
    settings.backgroundColorMappingMode,
    selectedColor,
    palettes,
    customPalettes,
    setCanvasData,
    positionCellsOnCanvas,
    startPreview
  ]);

  // End preview when live preview is disabled or component unmounts
  useEffect(() => {
    if (!livePreviewEnabled && isPreviewActive) {
      endPreview();
    }
  }, [livePreviewEnabled, isPreviewActive, endPreview]);

  // End preview when modal is closed
  useEffect(() => {
    if (!isOpen && isPreviewActive) {
      endPreview();
    }
  }, [isOpen, isPreviewActive, endPreview]);

  // End preview when modal closes
  useEffect(() => {
    if (!isOpen && isPreviewActive) {
      endPreview();
    }
  }, [isOpen, isPreviewActive, endPreview]);

  // File drop handlers
  const handleFileSelect = useCallback(async (file: File) => {
    const mediaFile = mediaProcessor.validateFile(file);

    if (!mediaFile) {
      setError(`Unsupported file format: ${file.type}`);
      return;
    }

    // Calculate optimal image size based on canvas dimensions and file dimensions
    try {
      let imageWidth = 0;
      let imageHeight = 0;

      if (mediaFile.type === 'image') {
        // Get image dimensions
        const img = new Image();
        img.src = URL.createObjectURL(file);
        await new Promise((resolve, reject) => {
          img.onload = () => {
            imageWidth = img.width;
            imageHeight = img.height;
            resolve(void 0);
          };
          img.onerror = reject;
        });
        URL.revokeObjectURL(img.src);
      } else {
        // Get video dimensions from metadata
        const video = document.createElement('video');
        video.preload = 'metadata';
        video.src = URL.createObjectURL(file);

        await new Promise((resolve) => {
          video.onloadedmetadata = () => {
            imageWidth = video.videoWidth;
            imageHeight = video.videoHeight;
            resolve(void 0);
          };
          video.onerror = () => {
            // Fallback to default dimensions if video loading fails
            imageWidth = 1920;
            imageHeight = 1080;
            resolve(void 0);
          };
        });

        URL.revokeObjectURL(video.src);
      }

      // Calculate optimal character dimensions
      const imageAspectRatio = imageWidth / imageHeight;
      setOriginalImageAspectRatio(imageAspectRatio);

      // Apply character aspect ratio compensation for initial sizing
      // Characters are 0.6x as wide as tall, so we need MORE width to maintain visual aspect ratio
      const CHARACTER_ASPECT_RATIO = 0.6;
      const characterCompensatedAspectRatio = imageAspectRatio / CHARACTER_ASPECT_RATIO;
      const canvasAspectRatio = canvasWidth / canvasHeight;

      let targetWidth: number;
      let targetHeight: number;

      if (characterCompensatedAspectRatio > canvasAspectRatio) {
        // Image is wider than canvas - fit to canvas width
        targetWidth = canvasWidth;
        targetHeight = Math.round(canvasWidth / characterCompensatedAspectRatio);
      } else {
        // Image is taller than canvas - fit to canvas height  
        targetHeight = canvasHeight;
        targetWidth = Math.round(canvasHeight * characterCompensatedAspectRatio);
      }

      // Ensure we don't exceed canvas bounds
      targetWidth = Math.min(targetWidth, canvasWidth);
      targetHeight = Math.min(targetHeight, canvasHeight);

      // Only auto-calculate dimensions if user hasn't saved preferences from previous session
      if (!hasSessionSettings) {
        updateSettings({
          characterWidth: targetWidth,
          characterHeight: targetHeight,
          maintainAspectRatio: true // Ensure aspect ratio is maintained
        });
      }

    } catch (error) {

    }

    setSelectedFile(mediaFile);
    setError(null);
    // Live preview enabled by default will trigger auto-processing
  }, [setSelectedFile, setError, canvasWidth, canvasHeight, updateSettings, setOriginalImageAspectRatio]);

