};

/**
 * RemapCharactersEffectPanel - Character remapping controls
 * 
 * Provides intuitive character remapping with automatic canvas character detection
 * and direct From/To character editing interface.
 */

import { useCallback, useEffect, useMemo, useState } from 'react';
import { Button } from '../../ui/button';
import { Label } from '../../ui/label';
import { useEffectsStore } from '../../../stores/effectsStore';
import { useCanvasStore } from '../../../stores/canvasStore';
import { EnhancedCharacterPicker } from '../EnhancedCharacterPicker';
import { RotateCcw, Eye, EyeOff, MoveRight, RotateCcwSquare } from 'lucide-react';

// Character utility functions
const sortCharactersByFrequency = (characters: string[], canvasAnalysis: any): string[] => {
  const charFrequency = canvasAnalysis?.charactersByFrequency || [];
  const frequencyMap = charFrequency.reduce((map: Record<string, number>, { char, count }: { char: string, count: number }) => {
    map[char] = count;
    return map;
  }, {});

  return characters.sort((a, b) => {
    const freqA = frequencyMap[a] || 0;
    const freqB = frequencyMap[b] || 0;
    // Sort by frequency (descending), then alphabetically
    if (freqA !== freqB) {
      return freqB - freqA;
    }
    return a.localeCompare(b);
  });
};

export function RemapCharactersEffectPanel() {
  const { 
    remapCharactersSettings,
    updateRemapCharactersSettings,
    resetEffectSettings,
    isPreviewActive,
    previewEffect,
    startPreview,
    stopPreview,
    updatePreview,
    getUniqueCharacters,
    analyzeCanvas,
    canvasAnalysis,
    isAnalyzing,
    clearAnalysisCache
  } = useEffectsStore();

  const { cells } = useCanvasStore();

  const isCurrentlyPreviewing = isPreviewActive && previewEffect === 'remap-characters';

  // Character picker state
  const [isCharacterPickerOpen, setIsCharacterPickerOpen] = useState(false);
  const [characterPickerTarget, setCharacterPickerTarget] = useState<{ 
    fromChar: string; 
    isToChar: boolean; 
    triggerRef: React.RefObject<HTMLElement | null> | null 
  }>({ fromChar: '', isToChar: false, triggerRef: null });

  // Get all unique characters from canvas analysis, sorted by frequency
  const allCanvasCharacters = useMemo(() => {
    // Only get characters if analysis is complete (not analyzing and has results)
    if (isAnalyzing || !canvasAnalysis) {
      return [];
    }
    const characters = getUniqueCharacters().filter(char => char.trim() !== ''); // Filter out empty/whitespace
    return sortCharactersByFrequency(characters, canvasAnalysis);
  }, [getUniqueCharacters, isAnalyzing, canvasAnalysis]);

  // Create refs for all character picker buttons (must be at top level)
  const characterPickerButtonRefs = useMemo(() => {
    const refs: Record<string, React.RefObject<HTMLButtonElement | null>> = {};
    allCanvasCharacters.forEach((fromChar) => {
      refs[`${fromChar}-from`] = { current: null };
      refs[`${fromChar}-to`] = { current: null };
    });
    return refs;
  }, [allCanvasCharacters]);

  // Auto-start preview when panel opens and analyze canvas
  useEffect(() => {
    // Clear cache and ensure fresh analysis every time panel opens
    clearAnalysisCache();
    analyzeCanvas();

    if (!isCurrentlyPreviewing) {
      startPreview('remap-characters');
    }

    // Cleanup on unmount
    return () => {
      if (isCurrentlyPreviewing) {
        stopPreview();
      }
    };
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  // Re-analyze when canvas data changes (e.g., edits made outside the effect)
  useEffect(() => {
    // Clear cache and re-analyze when canvas cells change
    clearAnalysisCache();
    analyzeCanvas();
  }, [cells, clearAnalysisCache, analyzeCanvas]);

  // Initialize character mappings with identity mappings when canvas characters change
  useEffect(() => {
    // Only initialize if we have characters and analysis is complete
    if (allCanvasCharacters.length > 0 && !isAnalyzing) {
      const currentMappings = remapCharactersSettings.characterMappings;
      const identityMappings: Record<string, string> = {};

      // Create identity mappings for all current canvas characters
      allCanvasCharacters.forEach(char => {
        // Preserve existing mapping if it exists, otherwise map to self
        identityMappings[char] = currentMappings[char] || char;
      });

      // Only update if the mappings have changed
      const currentKeys = Object.keys(currentMappings).sort();
      const newKeys = Object.keys(identityMappings).sort();
      const keysChanged = JSON.stringify(currentKeys) !== JSON.stringify(newKeys);

      if (keysChanged || Object.keys(currentMappings).length === 0) {
        updateRemapCharactersSettings({
          characterMappings: identityMappings
        });
      }
    }
  }, [allCanvasCharacters, remapCharactersSettings.characterMappings, updateRemapCharactersSettings, isAnalyzing]);


  // Auto-start preview when panel opens
  useEffect(() => {
    if (!isCurrentlyPreviewing) {
      startPreview('remap-characters');
    }

    // Cleanup on unmount
    return () => {
      if (isCurrentlyPreviewing) {
        stopPreview();
      }
    };
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  // Update preview when settings change
  useEffect(() => {
    if (isCurrentlyPreviewing) {
      updatePreview().catch(error => {
        console.error('Preview update failed:', error);
      });
    }
  }, [remapCharactersSettings, isCurrentlyPreviewing, updatePreview]);

  // Update preview when active frame changes
  useEffect(() => {
    if (isCurrentlyPreviewing) {
      updatePreview().catch(error => {
        console.error('Preview update failed:', error);
      });
    }
  }, [cells, isCurrentlyPreviewing, updatePreview]);

  // Toggle preview
  const handleTogglePreview = useCallback(() => {
    if (isCurrentlyPreviewing) {
      stopPreview();
    } else {
      startPreview('remap-characters');
    }
  }, [isCurrentlyPreviewing, startPreview, stopPreview]);

  // Reset to default values
  const handleReset = useCallback(() => {
    resetEffectSettings('remap-characters');
  }, [resetEffectSettings]);

  // Update character mapping
  const handleUpdateMapping = useCallback((fromChar: string, toChar: string, isToChar: boolean) => {
    const newMappings = { ...remapCharactersSettings.characterMappings };

    if (isToChar) {
      // Update the 'to' character
      newMappings[fromChar] = toChar;
    } else {
      // This shouldn't happen in our UX, but handle just in case
      console.warn('Updating from character not supported in this interface');
    }

    updateRemapCharactersSettings({
      characterMappings: newMappings
    });
  }, [remapCharactersSettings.characterMappings, updateRemapCharactersSettings]);

  // Reset individual character mapping
  const handleResetMapping = useCallback((fromChar: string) => {
    const newMappings = { ...remapCharactersSettings.characterMappings };
    newMappings[fromChar] = fromChar; // Reset to identity mapping

    updateRemapCharactersSettings({
      characterMappings: newMappings
    });
  }, [remapCharactersSettings.characterMappings, updateRemapCharactersSettings]);

  // Handle character picker
  const handleOpenCharacterPicker = useCallback((fromChar: string, isToChar: boolean, triggerElement: HTMLElement | null) => {
    setCharacterPickerTarget({
      fromChar,
      isToChar,
      triggerRef: { current: triggerElement }
    });
    setIsCharacterPickerOpen(true);
  }, []);

  const handleCharacterPick = useCallback((character: string) => {
    if (characterPickerTarget.fromChar && characterPickerTarget.isToChar) {
      handleUpdateMapping(characterPickerTarget.fromChar, character, true);
    }
    setIsCharacterPickerOpen(false);
    setCharacterPickerTarget({ fromChar: '', isToChar: false, triggerRef: null });
  }, [characterPickerTarget, handleUpdateMapping]);

  const handleCloseCharacterPicker = useCallback(() => {
    setIsCharacterPickerOpen(false);
    setCharacterPickerTarget({ fromChar: '', isToChar: false, triggerRef: null });
  }, []);

  return (
    <div className="space-y-4">

      {/* Live Preview Toggle */}
      <div className="flex items-center justify-between p-3 bg-blue-50 dark:bg-blue-950/30 rounded border border-blue-200 dark:border-blue-800">
        <div className="space-y-1">
          <Label className="text-xs font-medium text-blue-900 dark:text-blue-100">Live Preview</Label>
          <div className="text-xs text-blue-700 dark:text-blue-300">
            {isCurrentlyPreviewing ? 'Changes are shown on canvas' : 'Preview is disabled'}
          </div>
        </div>
        <Button
          onClick={handleTogglePreview}
          variant={isCurrentlyPreviewing ? "default" : "outline"}
          size="sm"
          className="h-8 gap-1"
        >
          {isCurrentlyPreviewing ? <Eye className="w-3 h-3" /> : <EyeOff className="w-3 h-3" />}
          {isCurrentlyPreviewing ? 'On' : 'Off'}
        </Button>
      </div>

      {/* Character Mappings */}
      <div className="space-y-3">
        <div className="flex items-center justify-between">
          <Label className="text-xs font-medium">
            Character Mappings ({allCanvasCharacters.length})
          </Label>
          <Button
            onClick={handleReset}
            variant="ghost"
            size="sm"
            className="h-6 w-6 p-0"
            title="Reset all mappings"
            disabled={allCanvasCharacters.length === 0}
          >
            <RotateCcw className="w-3 h-3" />
          </Button>
        </div>

        {isAnalyzing ? (
          <div className="p-4 border border-dashed border-muted-foreground/50 rounded text-center text-xs text-muted-foreground">
            Analyzing canvas characters...
          </div>
        ) : allCanvasCharacters.length === 0 ? (
          <div className="p-4 border border-dashed border-muted-foreground/50 rounded text-center text-xs text-muted-foreground">
            No characters found in canvas. Draw something to see character mappings.
          </div>
        ) : (
          <div className="space-y-2 max-h-64 overflow-y-auto">
            {/* Header */}
            <div className="grid grid-cols-[32px_32px_32px_24px_24px] gap-2 items-center text-xs font-medium text-muted-foreground px-1">
              <div>From</div>
              <div></div>
              <div>To</div>
              <div></div>
              <div></div>
            </div>

            {/* Character mappings */}
            <div className="space-y-1">
              {allCanvasCharacters.map((fromChar) => {
                const toChar = remapCharactersSettings.characterMappings[fromChar] || fromChar;
                const toButtonRef = characterPickerButtonRefs[`${fromChar}-to`];

                return (
                  <div 
                    key={fromChar} 
                    className="grid grid-cols-[32px_32px_32px_24px_24px] gap-2 items-center p-2 bg-background rounded border border-muted/30 hover:bg-muted/50 hover:border-muted/50 transition-colors text-xs"
                  >
                    {/* From Character (read-only display) */}
                    <div className="flex items-center justify-center">
                      <div className="w-6 h-6 bg-muted/50 border rounded text-center font-mono text-xs flex items-center justify-center">
                        {fromChar === ' ' ? 'â£' : fromChar}
                      </div>
                    </div>

                    {/* Arrow */}
                    <div className="flex justify-center">
                      <MoveRight className="w-4 h-4 text-muted-foreground" />
                    </div>

                    {/* To Character (editable) */}
                    <div className="flex items-center justify-center">
                      <button
                        ref={toButtonRef}
                        onClick={(e) => handleOpenCharacterPicker(fromChar, true, e.currentTarget)}
                        className="w-6 h-6 bg-background border rounded text-center font-mono text-xs flex items-center justify-center hover:border-blue-500 transition-colors cursor-pointer"
                        title={`Click to change target character for '${fromChar === ' ' ? 'space' : fromChar}'`}
                      >
                        {toChar === ' ' ? 'â£' : toChar}
                      </button>
                    </div>

                    {/* Individual Reset Button */}
                    <Button
                      onClick={() => handleResetMapping(fromChar)}
                      variant="ghost"
                      size="sm"
                      className="h-6 w-6 p-0 text-muted-foreground hover:text-blue-600"
                      title={`Reset '${fromChar === ' ' ? 'space' : fromChar}' to map to itself`}
                      disabled={fromChar === toChar}
                    >
                      <RotateCcwSquare className="w-3 h-3" />
                    </Button>

                    {/* Remove Button - Hidden for auto-populated mappings */}
                    <div className="w-6 h-6"></div>
                  </div>
                );
              })}
            </div>
          </div>
        )}
      </div>

      {/* Character Picker */}
      {isCharacterPickerOpen && characterPickerTarget.triggerRef && (
        <EnhancedCharacterPicker
          isOpen={isCharacterPickerOpen}
          onClose={handleCloseCharacterPicker}
          onSelectCharacter={handleCharacterPick}
          triggerRef={characterPickerTarget.triggerRef}
          initialValue=""
          title="Select Replacement Character"
        />
      )}

    </div>
  );
}

import React, { useState, useRef, useEffect, useCallback } from 'react';
import { createPortal } from 'react-dom';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import { DraggableDialogBar } from '@/components/common/DraggableDialogBar';
import { useTimeEffectsStore } from '@/stores/timeEffectsStore';
import { useAnimationStore } from '@/stores/animationStore';
import { useAnimationHistory } from '@/hooks/useAnimationHistory';
import { ADD_FRAMES_LIMITS } from '@/constants/timeEffects';

export const AddFramesDialog: React.FC = () => {
  const { 
    isAddFramesDialogOpen, 
    closeAddFramesDialog 
  } = useTimeEffectsStore();

  const { frames, currentFrameIndex } = useAnimationStore();
  const { addMultipleFrames } = useAnimationHistory();

  // Dialog state
  const [positionOffset, setPositionOffset] = useState({ x: 0, y: 0 });
  const [isDraggingDialog, setIsDraggingDialog] = useState(false);
  const [hasBeenDragged, setHasBeenDragged] = useState(false);
  const dragStartOffsetRef = useRef({ x: 0, y: 0 });
  const dialogRef = useRef<HTMLDivElement>(null);

  // Form state
  const [frameCount, setFrameCount] = useState(1);
  const [frameCountInput, setFrameCountInput] = useState('1');
  const [duplicateCurrentFrame, setDuplicateCurrentFrame] = useState(true);

  // Reset position when dialog opens
  useEffect(() => {
    if (isAddFramesDialogOpen) {
      setPositionOffset({ x: 0, y: 0 });
      setHasBeenDragged(false);
      // Reset form to defaults
      setFrameCount(1);
      setFrameCountInput('1');
      setDuplicateCurrentFrame(true);
    }
  }, [isAddFramesDialogOpen]);

  // Handle escape key to close
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (!isAddFramesDialogOpen) return;

      if (event.key === 'Escape') {
        event.preventDefault();
        closeAddFramesDialog();
      } else if (event.key === 'Enter' && !event.shiftKey) {
        event.preventDefault();
        handleApply();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isAddFramesDialogOpen, closeAddFramesDialog]);

  // Dragging handlers
  const handleDrag = useCallback((deltaX: number, deltaY: number) => {
    setPositionOffset({
      x: dragStartOffsetRef.current.x + deltaX,
      y: dragStartOffsetRef.current.y + deltaY
    });
  }, []);

  const handleDragStart = useCallback(() => {
    setIsDraggingDialog(true);
    setHasBeenDragged(true);
    dragStartOffsetRef.current = { ...positionOffset };
  }, [positionOffset]);

  const handleDragEnd = useCallback(() => {
    setIsDraggingDialog(false);
    dragStartOffsetRef.current = { ...positionOffset };
  }, [positionOffset]);

  // Handle input changes - allow typing without validation
  const handleFrameCountChange = (value: string) => {
    // Allow empty string or numbers only (no decimals)
    if (value === '' || /^\d+$/.test(value)) {
      setFrameCountInput(value);
      // Only update frameCount state if valid number
      if (value !== '') {
        const numValue = parseInt(value, 10);
        setFrameCount(numValue);
      }
    }
  };

  const handleFrameCountBlur = () => {
    // Validate and clamp on blur
    if (frameCountInput === '') {
      const defaultValue = ADD_FRAMES_LIMITS.MIN_COUNT;
      setFrameCountInput(defaultValue.toString());
      setFrameCount(defaultValue);
    } else {
      const numValue = parseInt(frameCountInput, 10);
      const clampedValue = Math.max(
        ADD_FRAMES_LIMITS.MIN_COUNT,
        Math.min(ADD_FRAMES_LIMITS.MAX_COUNT, numValue)
      );
      setFrameCountInput(clampedValue.toString());
      setFrameCount(clampedValue);
    }
  };

  // Apply changes
  const handleApply = () => {
    const sourceFrame = duplicateCurrentFrame ? frames[currentFrameIndex] : null;
    addMultipleFrames(frameCount, sourceFrame);
    closeAddFramesDialog();
  };

  // Calculate dialog position (lower-left corner)
  const getDialogPosition = () => {
    const dialogWidth = 400;
    const margin = 8;

    // Calculate maxHeight to ensure dialog fits on screen
    const viewportHeight = window.innerHeight;
    const maxHeight = viewportHeight - (margin * 2);

    return {
      bottom: margin,
      left: margin,
      width: dialogWidth,
      maxHeight: `${maxHeight}px`
    };
  };

  if (!isAddFramesDialogOpen) return null;

  const position = getDialogPosition();

  return createPortal(
    <div
      ref={dialogRef}
      className={`fixed z-[99999] ${
        !hasBeenDragged ? 'animate-in duration-200 slide-in-from-bottom-4 fade-in-0' : ''
      }`}
      style={{
        bottom: position.bottom - positionOffset.y,
        left: position.left + positionOffset.x,
        width: position.width,
        maxHeight: position.maxHeight,
        transition: isDraggingDialog ? 'none' : undefined
      }}
      onClick={(e) => e.stopPropagation()}
    >
      <Card className="border border-border/50 shadow-lg">
        <DraggableDialogBar 
          title="Add Frames" 
          onDrag={handleDrag}
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
          onClose={closeAddFramesDialog}
        />

        <div className="p-6 space-y-6">
          <div className="text-sm text-muted-foreground">
            Add multiple frames to your animation. New frames will be appended at the end.
          </div>

          <div className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="frame-count-input">Number of frames to add</Label>
              <Input
                id="frame-count-input"
                type="text"
                inputMode="numeric"
                value={frameCountInput}
                onChange={(e) => handleFrameCountChange(e.target.value)}
                onBlur={handleFrameCountBlur}
                className="w-full"
              />
              <div className="text-xs text-muted-foreground">
                Range: {ADD_FRAMES_LIMITS.MIN_COUNT}-{ADD_FRAMES_LIMITS.MAX_COUNT} frames
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <Checkbox
                id="duplicate-frame"
                checked={duplicateCurrentFrame}
                onCheckedChange={(checked) => setDuplicateCurrentFrame(checked === true)}
              />
              <Label 
                htmlFor="duplicate-frame" 
                className="text-sm font-normal cursor-pointer"
              >
                Duplicate current frame content
              </Label>
            </div>

            <div className="text-xs text-muted-foreground bg-muted/50 p-3 rounded space-y-1">
              <div>
                <strong>Current frame:</strong> {currentFrameIndex + 1} of {frames.length}
              </div>
              {duplicateCurrentFrame ? (
                <div>
                  âœ“ New frames will contain the same content as frame {currentFrameIndex + 1}
                </div>
              ) : (
                <div>
                  â—‹ New frames will be empty (transparent background)
                </div>
              )}
              <div>
                <strong>After adding:</strong> Total frames will be {frames.length + frameCount}
              </div>
            </div>
          </div>
        </div>

        <div className="flex justify-end gap-2 p-6 pt-0">
          <Button variant="outline" onClick={closeAddFramesDialog}>
            Cancel
          </Button>
          <Button onClick={handleApply}>
            Add {frameCount} Frame{frameCount !== 1 ? 's' : ''}
          </Button>
        </div>
      </Card>
    </div>,
    document.body
  );
};

import React, { useState, useRef, useEffect, useCallback, useMemo } from 'react';
import { createPortal } from 'react-dom';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { DraggableDialogBar } from '@/components/common/DraggableDialogBar';
import { useTimeEffectsStore } from '@/stores/timeEffectsStore';
import { useTimeEffectsHistory } from '@/hooks/useTimeEffectsHistory';
import { useAnimationStore } from '@/stores/animationStore';
import { fpsToMs, msToFps } from '@/utils/timeEffectsProcessing';
import { FRAME_DURATION_LIMITS } from '@/constants/timeEffects';
import type { FrameDurationMode } from '@/types/timeEffects';

export const SetFrameDurationDialog: React.FC = () => {
  const { 
    isSetDurationDialogOpen, 
    closeSetDurationDialog 
  } = useTimeEffectsStore();

  const { setFrameDurationsWithHistory } = useTimeEffectsHistory();
  const { frames, selectedFrameIndices } = useAnimationStore();

  const selectedIndices = useMemo(() => {
    return Array.from(selectedFrameIndices).sort((a, b) => a - b);
  }, [selectedFrameIndices]);

  const hasMultiSelection = selectedIndices.length > 1;
  const targetIndices = useMemo(() => {
    if (hasMultiSelection) {
      return selectedIndices;
    }
    return frames.map((_, index) => index);
  }, [hasMultiSelection, selectedIndices, frames]);

  // Dialog state
  const [positionOffset, setPositionOffset] = useState({ x: 0, y: 0 });
  const [isDraggingDialog, setIsDraggingDialog] = useState(false);
  const [hasBeenDragged, setHasBeenDragged] = useState(false);
  const dragStartOffsetRef = useRef({ x: 0, y: 0 });
  const dialogRef = useRef<HTMLDivElement>(null);

  // Form state
  const [mode, setMode] = useState<FrameDurationMode>('ms');
  const [milliseconds, setMilliseconds] = useState(100);
  const [fps, setFps] = useState(10);

  // Input text state (allows empty/partial input while typing)
  const [msInputValue, setMsInputValue] = useState('100');
  const [fpsInputValue, setFpsInputValue] = useState('10');

  // Calculate current average frame duration for initial values
  useEffect(() => {
    if (isSetDurationDialogOpen && targetIndices.length > 0) {
      const totalDuration = targetIndices.reduce((sum, frameIndex) => {
        const frame = frames[frameIndex];
        return frame ? sum + frame.duration : sum;
      }, 0);
      const averageDuration = totalDuration / targetIndices.length;
      const roundedMs = Math.round(averageDuration);
      const roundedFps = Math.round(msToFps(averageDuration));
      setMilliseconds(roundedMs);
      setFps(roundedFps);
      setMsInputValue(roundedMs.toString());
      setFpsInputValue(roundedFps.toString());
    }
  }, [isSetDurationDialogOpen, frames, targetIndices]);

  // Reset position when dialog opens
  useEffect(() => {
    if (isSetDurationDialogOpen) {
      setPositionOffset({ x: 0, y: 0 });
      setHasBeenDragged(false);
    }
  }, [isSetDurationDialogOpen]);

  // Handle escape key to close
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (!isSetDurationDialogOpen) return;

      if (event.key === 'Escape') {
        event.preventDefault();
        closeSetDurationDialog();
      } else if (event.key === 'Enter' && !event.shiftKey) {
        event.preventDefault();
        handleApply();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isSetDurationDialogOpen, closeSetDurationDialog]);

  // Sync display values when mode changes (not input values - those stay as user typed)
  useEffect(() => {
    if (mode === 'ms') {
      const roundedFps = Math.round(msToFps(milliseconds));
      setFps(roundedFps);
    } else {
      const roundedMs = Math.round(fpsToMs(fps));
      setMilliseconds(roundedMs);
    }
  }, [mode, milliseconds, fps]);

  // Dragging handlers
  const handleDrag = useCallback((deltaX: number, deltaY: number) => {
    setPositionOffset({
      x: dragStartOffsetRef.current.x + deltaX,
      y: dragStartOffsetRef.current.y + deltaY
    });
  }, []);

  const handleDragStart = useCallback(() => {
    setIsDraggingDialog(true);
    setHasBeenDragged(true);
    dragStartOffsetRef.current = { ...positionOffset };
  }, [positionOffset]);

  const handleDragEnd = useCallback(() => {
    setIsDraggingDialog(false);
    dragStartOffsetRef.current = { ...positionOffset };
  }, [positionOffset]);

  // Handle input changes - allow typing without validation
  const handleMillisecondsChange = (value: string) => {
    // Allow empty string or numbers only (no decimals)
    if (value === '' || /^\d+$/.test(value)) {
      setMsInputValue(value);
      // Only update milliseconds state if valid number
      if (value !== '') {
        const numValue = parseInt(value, 10);
        setMilliseconds(numValue);
      }
    }
  };

  const handleMillisecondsBlur = () => {
    // Validate and clamp on blur
    if (msInputValue === '') {
      const defaultValue = FRAME_DURATION_LIMITS.MIN_MS;
      setMsInputValue(defaultValue.toString());
      setMilliseconds(defaultValue);
    } else {
      const numValue = parseInt(msInputValue, 10);
      const clampedValue = Math.max(
        FRAME_DURATION_LIMITS.MIN_MS,
        Math.min(FRAME_DURATION_LIMITS.MAX_MS, numValue)
      );
      setMsInputValue(clampedValue.toString());
      setMilliseconds(clampedValue);
    }
  };

  const handleFpsChange = (value: string) => {
    // Allow empty string or numbers only (no decimals)
    if (value === '' || /^\d+$/.test(value)) {
      setFpsInputValue(value);
      // Only update fps state if valid number
      if (value !== '') {
        const numValue = parseInt(value, 10);
        setFps(numValue);
      }
    }
  };

  const handleFpsBlur = () => {
    // Validate and clamp on blur
    if (fpsInputValue === '') {
      const defaultValue = FRAME_DURATION_LIMITS.MIN_FPS;
      setFpsInputValue(defaultValue.toString());
      setFps(defaultValue);
    } else {
      const numValue = parseInt(fpsInputValue, 10);
      const clampedValue = Math.max(
        FRAME_DURATION_LIMITS.MIN_FPS,
        Math.min(FRAME_DURATION_LIMITS.MAX_FPS, numValue)
      );
      setFpsInputValue(clampedValue.toString());
      setFps(clampedValue);
    }
  };

  // Apply changes
  const handleApply = () => {
  const targetDuration = mode === 'ms' ? milliseconds : fpsToMs(fps);
  setFrameDurationsWithHistory(targetDuration, targetIndices);
  };

  // Calculate dialog position (lower-left corner)
  const getDialogPosition = () => {
    const dialogWidth = 400;
    const margin = 8;

    // Calculate maxHeight to ensure dialog fits on screen
    const viewportHeight = window.innerHeight;
    const maxHeight = viewportHeight - (margin * 2);

    return {
      bottom: margin,
      left: margin,
      width: dialogWidth,
      maxHeight: `${maxHeight}px`
    };
  };

  if (!isSetDurationDialogOpen) return null;

  const position = getDialogPosition();

  return createPortal(
    <div
      ref={dialogRef}
      className={`fixed z-[99999] ${
        !hasBeenDragged ? 'animate-in duration-200 slide-in-from-bottom-4 fade-in-0' : ''
      }`}
      style={{
        bottom: position.bottom - positionOffset.y,
        left: position.left + positionOffset.x,
        width: position.width,
        maxHeight: position.maxHeight,
        transition: isDraggingDialog ? 'none' : undefined
      }}
      onClick={(e) => e.stopPropagation()}
    >
      <Card className="border border-border/50 shadow-lg">
        <DraggableDialogBar 
          title="Set Frame Duration" 
          onDrag={handleDrag}
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
          onClose={closeSetDurationDialog}
        />

        <div className="p-6 space-y-6">
          <div className="text-sm text-muted-foreground">
            {hasMultiSelection ? (
              <>
                Set the duration for the <strong>{targetIndices.length}</strong> selected frame
                {targetIndices.length === 1 ? '' : 's'}. This leaves unselected frames untouched.
              </>
            ) : (
              <>Set the duration for all {frames.length} frames in the animation. This affects playback speed.</>
            )}
          </div>

          <Tabs value={mode} onValueChange={(value) => setMode(value as FrameDurationMode)}>
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="ms">Milliseconds</TabsTrigger>
              <TabsTrigger value="fps">FPS</TabsTrigger>
            </TabsList>

            <TabsContent value="ms" className="space-y-4 mt-4">
              <div className="space-y-2">
                <Label htmlFor="ms-input">Duration (ms)</Label>
                <Input
                  id="ms-input"
                  type="text"
                  inputMode="numeric"
                  value={msInputValue}
                  onChange={(e) => handleMillisecondsChange(e.target.value)}
                  onBlur={handleMillisecondsBlur}
                  className="w-full"
                />
                <div className="text-xs text-muted-foreground">
                  Range: {FRAME_DURATION_LIMITS.MIN_MS}-{FRAME_DURATION_LIMITS.MAX_MS}ms
                  {milliseconds > 0 && (
                    <> {' â€¢ '}Equivalent to {Math.round(msToFps(milliseconds))} FPS</>
                  )}
                </div>
              </div>
            </TabsContent>

            <TabsContent value="fps" className="space-y-4 mt-4">
              <div className="space-y-2">
                <Label htmlFor="fps-input">Frames per Second</Label>
                <Input
                  id="fps-input"
                  type="text"
                  inputMode="numeric"
                  value={fpsInputValue}
                  onChange={(e) => handleFpsChange(e.target.value)}
                  onBlur={handleFpsBlur}
                  className="w-full"
                />
                <div className="text-xs text-muted-foreground">
                  Range: {FRAME_DURATION_LIMITS.MIN_FPS}-{FRAME_DURATION_LIMITS.MAX_FPS} FPS
                  {fps > 0 && (
                    <> {' â€¢ '}Equivalent to {fpsToMs(fps)}ms per frame</>
                  )}
                </div>
              </div>
            </TabsContent>
          </Tabs>

          <div className="text-xs text-muted-foreground bg-muted/50 p-3 rounded">
            {hasMultiSelection ? (
              <>
                <strong>Note:</strong> Only the {targetIndices.length} selected frame
                {targetIndices.length === 1 ? '' : 's'} will be updated. Undo restores previous durations and the selection highlight.
              </>
            ) : (
              <>
                <strong>Note:</strong> This will set the same duration for all {frames.length} frames in your animation.
                You can undo this change if needed.
              </>
            )}
          </div>
        </div>

        <div className="flex justify-end gap-2 p-6 pt-0">
          <Button variant="outline" onClick={closeSetDurationDialog}>
            Cancel
          </Button>
          <Button onClick={handleApply}>
            Apply
          </Button>
        </div>
      </Card>
    </div>,
    document.body
  );
};

import React, { useState, useRef, useEffect, useCallback } from 'react';
