  WAVE_WARP_RANGES
} from '@/constants/timeEffects';
import type { WaveAxis, FrameRangeSettings } from '@/types/timeEffects';
import { Eye, EyeOff } from 'lucide-react';

export const WaveWarpDialog: React.FC = () => {
  const { 
    isWaveWarpDialogOpen, 
