import React, { createContext, useContext, useState, useRef, useMemo, useCallback } from 'react';
import type { ReactNode } from 'react';
import type { Cell } from '../types';
import { usePasteMode } from '../hooks/usePasteMode';
import { useFrameSynchronization } from '../hooks/useFrameSynchronization';
import type { PasteModeState } from '../hooks/usePasteMode';
import { calculateFontMetrics, calculateCellDimensions, DEFAULT_SPACING } from '../utils/fontMetrics';
import type { FontMetrics } from '../utils/fontMetrics';

// Canvas-specific state that doesn't belong in global stores
interface SelectionPreviewState {
  active: boolean;
  modifier: 'replace' | 'add' | 'subtract';
  tool: 'select' | 'lasso' | 'magicwand' | null;
  baseCells: string[];
  gestureCells: string[];
}

interface CanvasState {
  // Canvas display settings
  cellSize: number; // Base font size for character height
  zoom: number; // 0.2 to 4.0 (20% to 400% in 20% increments)
  panOffset: { x: number; y: number };

  // Typography settings
  characterSpacing: number; // multiplier for character width spacing
  lineSpacing: number;      // multiplier for line height spacing
  fontSize: number;         // base font size in pixels

  // Computed font metrics
  fontMetrics: FontMetrics;
  cellWidth: number;   // actual cell width including spacing
  cellHeight: number;  // actual cell height including spacing

  // Interaction state
  isDrawing: boolean;
  mouseButtonDown: boolean;
  shiftKeyDown: boolean;
  altKeyDown: boolean;  // For temporary eyedropper activation

  // Selection state
  selectionMode: 'none' | 'dragging' | 'moving';
  pendingSelectionStart: { x: number; y: number } | null;
  justCommittedMove: boolean;

  // Hover state
  hoveredCell: { x: number; y: number } | null;

  // Hover preview state (for tool-specific previews like brush outline)
  hoverPreview: {
    active: boolean;
    mode: 'none' | 'brush' | 'eraser-brush' | 'rectangle' | 'ellipse' | 'line';
    cells: Array<{ x: number; y: number }>;
  };

  // Move/drag state
  moveState: {
    originalData: Map<string, Cell>;
    originalPositions: Set<string>;
    startPos: { x: number; y: number };
    baseOffset: { x: number; y: number };
    currentOffset: { x: number; y: number };
  } | null;

  // Paste mode state
  pasteMode: PasteModeState;

  // Selection preview (for additive/subtractive gestures)
  selectionPreview: SelectionPreviewState;
}

interface CanvasActions {
  // Canvas settings
  setCellSize: (size: number) => void;
  setZoom: (zoom: number) => void;
  setPanOffset: (offset: { x: number; y: number }) => void;

    // Typography actions
  setCharacterSpacing: (spacing: number) => void;
  setLineSpacing: (spacing: number) => void;
  setFontSize: (size: number) => void;

  // Interaction actions
  setIsDrawing: (drawing: boolean) => void;
  setMouseButtonDown: (down: boolean) => void;
  setShiftKeyDown: (down: boolean) => void;
  setAltKeyDown: (down: boolean) => void;

  // Selection actions
  setSelectionMode: (mode: CanvasState['selectionMode']) => void;
  setPendingSelectionStart: (start: { x: number; y: number } | null) => void;
  setJustCommittedMove: (committed: boolean) => void;

  // Hover actions
  setHoveredCell: (cell: { x: number; y: number } | null) => void;
  setHoverPreview: (preview: {
    active: boolean;
    mode: 'none' | 'brush' | 'eraser-brush' | 'rectangle' | 'ellipse' | 'line';
    cells: Array<{ x: number; y: number }>;
  }) => void;

  // Move/drag actions
  setMoveState: (state: CanvasState['moveState']) => void;

  // Paste mode actions
  startPasteMode: (position: { x: number; y: number }) => boolean;
  updatePastePosition: (position: { x: number; y: number }) => void;
  startPasteDrag: (clickPosition: { x: number; y: number }) => void;
  stopPasteDrag: () => void;
  cancelPasteMode: () => void;
  commitPaste: () => Map<string, any> | null;

  // Canvas ref
  canvasRef: React.RefObject<HTMLCanvasElement | null>;

  // Selection preview actions
  setSelectionPreview: (preview: SelectionPreviewState) => void;
}

interface CanvasContextValue extends CanvasState, CanvasActions {}

const CanvasContext = createContext<CanvasContextValue | null>(null);

interface CanvasProviderProps {
  children: ReactNode;
  initialCellSize?: number;
}

export const CanvasProvider: React.FC<CanvasProviderProps> = ({ 
  children, 
  initialCellSize = 18 
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  // Canvas display settings
  const [cellSize, setCellSize] = useState(initialCellSize);
  const [zoom, setZoom] = useState(1.0);
  const [panOffset, setPanOffset] = useState({ x: 0, y: 0 });

  // Typography settings
  const [characterSpacing, setCharacterSpacing] = useState(DEFAULT_SPACING.characterSpacing);
  const [lineSpacing, setLineSpacing] = useState(DEFAULT_SPACING.lineSpacing);

  // Computed font metrics (recalculated when cellSize changes)
  const fontMetrics = useMemo(() => {
    return calculateFontMetrics(cellSize);
  }, [cellSize]);

  // Computed cell dimensions (recalculated when metrics or spacing changes)
  const { cellWidth, cellHeight } = useMemo(() => {
    return calculateCellDimensions(fontMetrics, { characterSpacing, lineSpacing });
  }, [fontMetrics, characterSpacing, lineSpacing]);

  // Interaction state
  const [isDrawing, setIsDrawing] = useState(false);
  const [mouseButtonDown, setMouseButtonDown] = useState(false);
  const [shiftKeyDown, setShiftKeyDown] = useState(false);
  const [altKeyDown, setAltKeyDown] = useState(false);

  // Selection state
  const [selectionMode, setSelectionMode] = useState<CanvasState['selectionMode']>('none');
  const [pendingSelectionStart, setPendingSelectionStart] = useState<{ x: number; y: number } | null>(null);
  const [justCommittedMove, setJustCommittedMove] = useState(false);

  // Hover state
  const [hoveredCell, setHoveredCell] = useState<{ x: number; y: number } | null>(null);

  // Hover preview state (for tool-specific previews like brush outline)
  const [hoverPreview, setHoverPreview] = useState<{
    active: boolean;
    mode: 'none' | 'brush' | 'eraser-brush' | 'rectangle' | 'ellipse' | 'line';
    cells: Array<{ x: number; y: number }>;
  }>({
    active: false,
    mode: 'none',
    cells: []
  });

  // Move/drag state
  const [moveState, setMoveState] = useState<CanvasState['moveState']>(null);
  const [selectionPreviewState, setSelectionPreviewState] = useState<SelectionPreviewState>({
    active: false,
    modifier: 'replace',
    tool: null,
    baseCells: [],
    gestureCells: []
  });

  const setSelectionPreview = useCallback((preview: SelectionPreviewState) => {
    setSelectionPreviewState(preview);
  }, []);

  // Paste mode integration
  const {
    pasteMode,
    startPasteMode,
    updatePastePosition,
    startPasteDrag,
    stopPasteDrag,
    cancelPasteMode,
    commitPaste
  } = usePasteMode();

  // Frame synchronization for animation
  useFrameSynchronization(moveState, setMoveState);

  const contextValue: CanvasContextValue = {
    // Display state
    cellSize,
    zoom,
    panOffset,

    // Typography state
    characterSpacing,
    lineSpacing,
    fontSize: cellSize,
    fontMetrics,
    cellWidth,
    cellHeight,

    // Interaction state
    isDrawing,
    mouseButtonDown,
    shiftKeyDown,
    altKeyDown,
    selectionMode,
    pendingSelectionStart,
    justCommittedMove,
    hoveredCell,
    hoverPreview,
    moveState,
    pasteMode,
  selectionPreview: selectionPreviewState,

    // Display actions
    setCellSize,
    setZoom,
    setPanOffset,

    // Typography actions
    setCharacterSpacing,
    setLineSpacing,
    setFontSize: setCellSize,

    // Interaction actions
    setIsDrawing,
    setMouseButtonDown,
    setShiftKeyDown,
    setAltKeyDown,
    setSelectionMode,
    setPendingSelectionStart,
    setJustCommittedMove,
    setHoveredCell,
    setHoverPreview,
    setMoveState,
    startPasteMode,
    updatePastePosition,
    startPasteDrag,
    stopPasteDrag,
    cancelPasteMode,
    commitPaste,
  setSelectionPreview,

    // Ref
    canvasRef,
  };

  return (
    <CanvasContext.Provider value={contextValue}>
      {children}
    </CanvasContext.Provider>
  );
};

export const useCanvasContext = (): CanvasContextValue => {
  const context = useContext(CanvasContext);
  if (!context) {
    throw new Error('useCanvasContext must be used within a CanvasProvider');
  }
  return context;
};

// Helper hook for canvas dimensions calculations
export const useCanvasDimensions = () => {
  const { cellWidth, cellHeight, zoom, panOffset } = useCanvasContext();

  return {
    cellWidth,
    cellHeight,
    getCanvasSize: (gridWidth: number, gridHeight: number) => ({
      width: gridWidth * cellWidth,
      height: gridHeight * cellHeight,
    }),
    getGridCoordinates: (
      mouseX: number, 
      mouseY: number, 
      canvasRect: DOMRect,
      gridWidth: number,
      gridHeight: number
    ) => {
      // Get relative position within canvas (in CSS pixels)
      const relativeX = mouseX - canvasRect.left;
      const relativeY = mouseY - canvasRect.top;

      // Note: No need to account for device pixel ratio here because:
      // - mouseX/mouseY are in CSS pixels 
      // - canvasRect is in CSS pixels
      // - Our coordinate calculations should remain in CSS pixel space
      // - The high-DPI scaling happens only in the rendering context

      // Account for pan offset - subtract pan offset to get actual grid position
      const adjustedX = relativeX - panOffset.x;
      const adjustedY = relativeY - panOffset.y;

      // Account for zoom - divide by zoomed cell size
      const effectiveCellWidth = cellWidth * zoom;
      const effectiveCellHeight = cellHeight * zoom;
      const x = Math.floor(adjustedX / effectiveCellWidth);
      const y = Math.floor(adjustedY / effectiveCellHeight);

      return {
        x: Math.max(0, Math.min(x, gridWidth - 1)),
        y: Math.max(0, Math.min(y, gridHeight - 1)),
      };
    },
    getGridCoordinatesWithCenter: (
      mouseX: number, 
      mouseY: number, 
      canvasRect: DOMRect,
      gridWidth: number,
      gridHeight: number
    ) => {
      // Get relative position within canvas (in CSS pixels)
      const relativeX = mouseX - canvasRect.left;
      const relativeY = mouseY - canvasRect.top;

      // Account for pan offset - subtract pan offset to get actual grid position
      const adjustedX = relativeX - panOffset.x;
      const adjustedY = relativeY - panOffset.y;

      // Account for zoom - divide by zoomed cell size
      const effectiveCellWidth = cellWidth * zoom;
      const effectiveCellHeight = cellHeight * zoom;
      const x = Math.floor(adjustedX / effectiveCellWidth);
      const y = Math.floor(adjustedY / effectiveCellHeight);

      // Return cell center coordinates (add 0.5 to snap to center)
      return {
        x: Math.max(0.5, Math.min(x + 0.5, gridWidth - 0.5)),
        y: Math.max(0.5, Math.min(y + 0.5, gridHeight - 0.5)),
      };
    },
  };
};

import React, { createContext, useContext, useState, useCallback } from 'react';
import type { ReactNode } from 'react';

interface ModalContextType {
  isAnyModalOpen: boolean;
  openModal: (modalId: string) => void;
  closeModal: (modalId: string) => void;
}

const ModalContext = createContext<ModalContextType | undefined>(undefined);

export const useModalContext = () => {
  const context = useContext(ModalContext);
  if (!context) {
    throw new Error('useModalContext must be used within a ModalProvider');
  }
  return context;
};

interface ModalProviderProps {
  children: ReactNode;
}

export const ModalProvider: React.FC<ModalProviderProps> = ({ children }) => {
  const [openModals, setOpenModals] = useState<Set<string>>(new Set());

  const openModal = useCallback((modalId: string) => {
    setOpenModals(prev => new Set(prev).add(modalId));
  }, []);

  const closeModal = useCallback((modalId: string) => {
    setOpenModals(prev => {
      const newSet = new Set(prev);
      newSet.delete(modalId);
      return newSet;
    });
  }, []);

  const isAnyModalOpen = openModals.size > 0;

  return (
    <ModalContext.Provider value={{ isAnyModalOpen, openModal, closeModal }}>
      {children}
    </ModalContext.Provider>
  );
};

import React, { createContext, useContext, useEffect, useState } from 'react'

type Theme = 'dark' | 'light'

interface ThemeContextType {
  theme: Theme
  setTheme: (theme: Theme) => void
  toggleTheme: () => void
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined)

export function ThemeProvider({ children }: { children: React.ReactNode }) {
  const [theme, setTheme] = useState<Theme>(() => {
    // Check localStorage first, then default to dark
    const stored = localStorage.getItem('ascii-motion-theme') as Theme
    return stored || 'dark'
  })

  useEffect(() => {
    const root = window.document.documentElement

    // Remove previous theme classes
    root.classList.remove('light', 'dark')

    // Add current theme class
    root.classList.add(theme)

    // Store in localStorage
    localStorage.setItem('ascii-motion-theme', theme)
  }, [theme])

  const toggleTheme = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark')
  }

  const value = {
    theme,
    setTheme,
    toggleTheme
  }

  return (
    <ThemeContext.Provider value={value}>
      {children}
    </ThemeContext.Provider>
  )
}

export function useTheme() {
  const context = useContext(ThemeContext)
  if (context === undefined) {
    throw new Error('useTheme must be used within a ThemeProvider')
  }
  return context
}

import { useCallback } from 'react';
import { useAnimationStore } from '../stores/animationStore';
import { useToolStore } from '../stores/toolStore';
import type { 
  Cell,
  AddFrameHistoryAction, 
  DuplicateFrameHistoryAction, 
  DeleteFrameHistoryAction, 
  ReorderFramesHistoryAction, 
  UpdateDurationHistoryAction,
  UpdateNameHistoryAction
} from '../types';
import { cloneFrame, cloneFrames } from '../utils/frameUtils';

/**
 * Custom hook that provides animation actions with integrated undo/redo history
 * This ensures all timeline operations are recorded in the history stack
 */
export const useAnimationHistory = () => {
  const { 
    frames, 
    currentFrameIndex, 
    selectedFrameIndices,
    addFrame: addFrameStore, 
    removeFrame: removeFrameStore, 
    duplicateFrame: duplicateFrameStore, 
    duplicateFrameRange: duplicateFrameRangeStore,
    updateFrameDuration: updateFrameDurationStore, 
    updateFrameName: updateFrameNameStore, 
    reorderFrames: reorderFramesStore 
  } = useAnimationStore();

  const { pushToHistory } = useToolStore();

  /**
   * Add a new blank frame with history recording
   */
  const addFrame = useCallback((atIndex?: number) => {
    const insertIndex = atIndex !== undefined ? atIndex : frames.length;
    const previousCurrentFrame = currentFrameIndex;

    // Create blank canvas data for new frame
    const blankCanvasData = new Map<string, Cell>();

    // Create history action before making the change
    const historyAction: AddFrameHistoryAction = {
      type: 'add_frame',
      timestamp: Date.now(),
      description: `Add frame at position ${insertIndex + 1}`,
      data: {
        frameIndex: insertIndex,
        frame: {
          id: `frame-${Date.now()}-${Math.random()}` as any,
          name: `Frame ${insertIndex + 1}`,
          duration: 1000, // Default duration
          data: new Map(blankCanvasData),
          thumbnail: undefined
        },
        canvasData: new Map(blankCanvasData),
        previousCurrentFrame
      }
    };

    // Execute the action
    addFrameStore(insertIndex, blankCanvasData);

    // Record in history
    pushToHistory(historyAction);
  }, [frames.length, currentFrameIndex, addFrameStore, pushToHistory]);

  /**
   * Duplicate a frame with history recording
   */
  const duplicateFrame = useCallback((index: number) => {
    const frameToDuplicate = frames[index];
    if (!frameToDuplicate) return;

    const previousCurrentFrame = currentFrameIndex;

    // Create history action
    const historyAction: DuplicateFrameHistoryAction = {
      type: 'duplicate_frame',
      timestamp: Date.now(),
      description: `Duplicate frame ${index + 1}`,
      data: {
        originalIndex: index,
        newIndex: index + 1,
        frame: cloneFrame(frameToDuplicate),
        previousCurrentFrame
      }
    };

    // Execute the action
    duplicateFrameStore(index);

    // Record in history
    pushToHistory(historyAction);
  }, [frames, currentFrameIndex, duplicateFrameStore, pushToHistory]);

  const duplicateFrameRange = useCallback((frameIndices: number[]) => {
    if (frameIndices.length === 0) return;

    const uniqueIndices = Array.from(new Set(frameIndices)).sort((a, b) => a - b);

    if (uniqueIndices.length === 1) {
      duplicateFrame(uniqueIndices[0]);
      return;
    }

    if (uniqueIndices.some(idx => idx < 0 || idx >= frames.length)) {
      return;
    }

    const previousFramesSnapshot = cloneFrames(frames);
    const previousSelection = Array.from(selectedFrameIndices).sort((a, b) => a - b);
    const previousCurrentFrame = currentFrameIndex;

    duplicateFrameRangeStore(uniqueIndices);

    const {
      frames: framesAfter,
      selectedFrameIndices: selectionAfter,
      currentFrameIndex: currentAfter
    } = useAnimationStore.getState();

    const newFramesSnapshot = cloneFrames(framesAfter);
    const newSelection = Array.from(selectionAfter).sort((a, b) => a - b);
    const previousFrameIds = new Set(previousFramesSnapshot.map(frame => frame.id));
    const insertedFrameIds = framesAfter
      .filter(frame => !previousFrameIds.has(frame.id))
      .map(frame => frame.id);

    const historyAction: import('../types').DuplicateFrameRangeHistoryAction = {
      type: 'duplicate_frame_range',
      timestamp: Date.now(),
      description: `Duplicate ${uniqueIndices.length} frame${uniqueIndices.length > 1 ? 's' : ''}`,
      data: {
        originalFrameIndices: uniqueIndices,
        insertedFrameIds,
        previousFrames: previousFramesSnapshot,
        newFrames: newFramesSnapshot,
        previousSelection,
        newSelection,
        previousCurrentFrame,
        newCurrentFrame: currentAfter
      }
    };

    pushToHistory(historyAction);
  }, [frames, currentFrameIndex, duplicateFrameRangeStore, pushToHistory, selectedFrameIndices, duplicateFrame]);

  /**
   * Remove a frame with history recording
   */
  const removeFrame = useCallback((index: number) => {
    const frameToDelete = frames[index];
    if (!frameToDelete || frames.length <= 1) return;

    const previousCurrentFrame = currentFrameIndex;
    let newCurrentFrame = currentFrameIndex;

    if (index <= currentFrameIndex && currentFrameIndex > 0) {
      newCurrentFrame = currentFrameIndex - 1;
    } else if (newCurrentFrame >= frames.length - 1) {
      newCurrentFrame = frames.length - 2;
    }

    // Create history action
    const historyAction: DeleteFrameHistoryAction = {
      type: 'delete_frame',
      timestamp: Date.now(),
      description: `Delete frame ${index + 1}`,
      data: {
        frameIndex: index,
        frame: cloneFrame(frameToDelete),
        previousCurrentFrame,
        newCurrentFrame
      }
    };

    // Execute the action
    removeFrameStore(index);

    // Record in history
    pushToHistory(historyAction);
  }, [frames, currentFrameIndex, removeFrameStore, pushToHistory]);

  /**
   * Reorder frames with history recording
   */
  const reorderFrames = useCallback((fromIndex: number, toIndex: number) => {
    if (fromIndex < 0 || fromIndex >= frames.length ||
        toIndex < 0 || toIndex > frames.length ||
        fromIndex === toIndex) {
      return;
    }

    const previousCurrentFrame = currentFrameIndex;
    let newCurrentFrame = currentFrameIndex;

    if (currentFrameIndex === fromIndex) {
      newCurrentFrame = toIndex;
    } else if (fromIndex < currentFrameIndex && toIndex >= currentFrameIndex) {
      newCurrentFrame = currentFrameIndex - 1;
    } else if (fromIndex > currentFrameIndex && toIndex <= currentFrameIndex) {
      newCurrentFrame = currentFrameIndex + 1;
    }

    // Create history action
    const historyAction: ReorderFramesHistoryAction = {
      type: 'reorder_frames',
      timestamp: Date.now(),
      description: `Move frame ${fromIndex + 1} to position ${toIndex + 1}`,
      data: {
        fromIndex,
        toIndex,
        previousCurrentFrame,
        newCurrentFrame
      }
    };

    // Execute the action
    reorderFramesStore(fromIndex, toIndex);

    // Record in history
    pushToHistory(historyAction);
  }, [frames.length, currentFrameIndex, reorderFramesStore, pushToHistory]);

  /**
   * Update frame duration with history recording
   */
  const updateFrameDuration = useCallback((index: number, duration: number) => {
    const frame = frames[index];
    if (!frame || frame.duration === duration) return;

    // Create history action
    const historyAction: UpdateDurationHistoryAction = {
      type: 'update_duration',
      timestamp: Date.now(),
      description: `Change frame ${index + 1} duration to ${duration}ms`,
      data: {
        frameIndex: index,
        oldDuration: frame.duration,
        newDuration: duration
      }
    };

    // Execute the action
    updateFrameDurationStore(index, duration);

    // Record in history
    pushToHistory(historyAction);
  }, [frames, updateFrameDurationStore, pushToHistory]);

  /**
   * Update frame name with history recording
   */
  const updateFrameName = useCallback((index: number, name: string) => {
    const frame = frames[index];
    if (!frame || frame.name === name) return;

    // Create history action
    const historyAction: UpdateNameHistoryAction = {
      type: 'update_name',
      timestamp: Date.now(),
      description: `Rename frame ${index + 1} to "${name}"`,
      data: {
        frameIndex: index,
        oldName: frame.name,
        newName: name
      }
    };

    // Execute the action
    updateFrameNameStore(index, name);

    // Record in history
    pushToHistory(historyAction);
  }, [frames, updateFrameNameStore, pushToHistory]);

  /**
   * Add multiple frames with history recording
   */
  const addMultipleFrames = useCallback((count: number, sourceFrame?: any) => {
    if (count <= 0) return;

    // For now, add frames one by one using the existing single-frame method
    // This ensures each frame is properly tracked in history
    for (let i = 0; i < count; i++) {
      if (sourceFrame) {
        // Use duplicate frame functionality
        const currentLength = frames.length + i;
        duplicateFrame(Math.min(currentFrameIndex, currentLength - 1));
      } else {
        // Add blank frame
        addFrame();
      }
    }
  }, [frames.length, currentFrameIndex, addFrame, duplicateFrame]);

  /**
   * Delete multiple frames at once with history recording
   */
  const deleteFrameRange = useCallback((frameIndices: number[]) => {
    if (frameIndices.length === 0) return;

    // Sort indices to get correct frame data before deletion
    const sortedIndices = [...frameIndices].sort((a, b) => a - b);

    // Collect frames to be deleted
    const framesToDelete = sortedIndices
      .map(idx => frames[idx])
      .filter((frame): frame is typeof frames[number] => Boolean(frame));

    if (framesToDelete.length === 0 || framesToDelete.length >= frames.length) {
      // Can't delete all frames
      return;
    }

    const previousCurrentFrame = currentFrameIndex;

    // Calculate new current frame after deletion
    const removedBeforeCurrent = sortedIndices.filter(idx => idx < currentFrameIndex).length;
    const newCurrentFrame = Math.max(0, Math.min(
      currentFrameIndex - removedBeforeCurrent,
      frames.length - framesToDelete.length - 1
    ));

    // Create history action
    const previousFramesSnapshot = cloneFrames(frames);

    const selectionBeforeDelete = Array.from(selectedFrameIndices).sort((a, b) => a - b);

    const historyAction: import('../types').DeleteFrameRangeHistoryAction = {
      type: 'delete_frame_range',
      timestamp: Date.now(),
      description: `Delete ${framesToDelete.length} frame${framesToDelete.length > 1 ? 's' : ''}`,
      data: {
        frameIndices: sortedIndices,
  frames: framesToDelete.map((frame) => cloneFrame(frame)),
        previousCurrentFrame,
        newCurrentFrame,
        previousFrames: previousFramesSnapshot,
        previousSelection: selectionBeforeDelete
      }
    };

    // Execute the action
    useAnimationStore.getState().removeFrameRange(sortedIndices);

    // Record in history
    pushToHistory(historyAction);
  }, [frames, currentFrameIndex, pushToHistory]);

  /**
   * Delete all frames and reset to single blank frame with history recording
   */
  const deleteAllFramesWithReset = useCallback(() => {
    const previousCurrentFrame = currentFrameIndex;
    const framesToSave = cloneFrames(frames);

    // Create history action
    const historyAction: import('../types').DeleteAllFramesHistoryAction = {
      type: 'delete_all_frames',
      timestamp: Date.now(),
      description: 'Delete all frames',
      data: {
        frames: framesToSave,
        previousCurrentFrame
      }
    };

    // Execute the action
    useAnimationStore.getState().clearAllFrames();

    // Record in history
    pushToHistory(historyAction);
  }, [frames, currentFrameIndex, pushToHistory, selectedFrameIndices]);

  /**
   * Reorder multiple frames as a contiguous group with history recording
   */
  const reorderFrameRange = useCallback((frameIndices: number[], targetIndex: number) => {
    if (frameIndices.length === 0) return;

    const sortedIndices = [...frameIndices].sort((a, b) => a - b);

    // Validate indices
    if (sortedIndices.some(idx => idx < 0 || idx >= frames.length)) {
      return;
    }

    const previousCurrentFrame = currentFrameIndex;

    // Calculate new current frame after reordering
    let newCurrentFrame = currentFrameIndex;

    if (sortedIndices.includes(currentFrameIndex)) {
      // Current frame is being moved
      const framesBefore = sortedIndices.filter(idx => idx < targetIndex).length;
      const adjustedTarget = Math.max(0, targetIndex - framesBefore);
      const positionInSelection = sortedIndices.indexOf(currentFrameIndex);
      newCurrentFrame = adjustedTarget + positionInSelection;
    } else {
      // Adjust current index based on movement
      const movedFromBefore = sortedIndices.filter(idx => idx < currentFrameIndex).length;
      const movedToBefore = targetIndex <= currentFrameIndex ? frameIndices.length : 0;
      newCurrentFrame = currentFrameIndex - movedFromBefore + movedToBefore;
    }

    newCurrentFrame = Math.max(0, Math.min(newCurrentFrame, frames.length - 1));

    // Create history action
    const movedFrameIds = sortedIndices
      .map(idx => frames[idx])
      .filter((frame): frame is typeof frames[number] => Boolean(frame))
      .map(frame => frame.id);

    const previousSelectionFrameIds = Array.from(selectedFrameIndices)
      .map(idx => frames[idx])
      .filter((frame): frame is typeof frames[number] => Boolean(frame))
      .map(frame => frame.id);

    const historyAction: import('../types').ReorderFrameRangeHistoryAction = {
      type: 'reorder_frame_range',
      timestamp: Date.now(),
      description: `Move ${frameIndices.length} frame${frameIndices.length > 1 ? 's' : ''} to position ${targetIndex + 1}`,
      data: {
        frameIndices: sortedIndices,
        targetIndex,
        previousCurrentFrame,
        newCurrentFrame,
        movedFrameIds,
        previousSelectionFrameIds,
        newSelectionFrameIds: movedFrameIds
      }
    };

    // Execute the action
    useAnimationStore.getState().reorderFrameRange(sortedIndices, targetIndex);

    // Record in history
    pushToHistory(historyAction);
  }, [frames, currentFrameIndex, pushToHistory, selectedFrameIndices]);

  return {
    addFrame,
    duplicateFrame,
    duplicateFrameRange,
    removeFrame,
    reorderFrames,
    updateFrameDuration,
    updateFrameName,
    addMultipleFrames,
    deleteFrameRange,
    deleteAllFramesWithReset,
    reorderFrameRange
  };
};

import { useCallback, useRef } from 'react';
import { useCanvasContext, useCanvasDimensions } from '../contexts/CanvasContext';
import { useCanvasState } from './useCanvasState';
import { useCanvasStore } from '../stores/canvasStore';
import { useAnimationStore } from '../stores/animationStore';
import { useToolStore } from '../stores/toolStore';
import type { Cell } from '../types';
import { unionSelectionMasks, subtractSelectionMask } from '../utils/selectionUtils';

/**
 * Hook for handling magic wand selection tool behavior
 * Manages character/color-based selection creation, movement, and drag operations
 */
export const useCanvasMagicWandSelection = () => {
  const { canvasRef, mouseButtonDown, setMouseButtonDown, setSelectionPreview } = useCanvasContext();
  const { getGridCoordinates } = useCanvasDimensions();
  const {
    selectionMode,
    moveState,
    justCommittedMove,
    commitMove,
    setSelectionMode,
    setMoveState,
    setJustCommittedMove,
  } = useCanvasState();

  const { currentFrameIndex } = useAnimationStore();
  const { width, height, cells, getCell } = useCanvasStore();
  const { 
    magicWandSelection, 
    magicWandContiguous,
    startMagicWandSelection,
    clearMagicWandSelection,
    pushCanvasHistory,
    magicMatchChar,
    magicMatchColor,
    magicMatchBgColor,
    setMagicWandSelectionFromMask
  } = useToolStore();

  const selectionModifierRef = useRef<'replace' | 'add' | 'subtract'>('replace');
  const baseSelectionMaskRef = useRef<Set<string>>(new Set());
  const selectionGestureActiveRef = useRef(false);
  const baseTargetCellRef = useRef<Cell | null>(null);

  const clearSelectionPreview = useCallback(() => {
    setSelectionPreview({
      active: false,
      modifier: 'replace',
      tool: null,
      baseCells: [],
      gestureCells: []
    });
  }, [setSelectionPreview]);

  const resetSelectionGesture = useCallback(() => {
    selectionModifierRef.current = 'replace';
    baseSelectionMaskRef.current = new Set();
    selectionGestureActiveRef.current = false;
    baseTargetCellRef.current = null;
    clearSelectionPreview();
  }, [clearSelectionPreview]);

  const beginSelectionPreview = useCallback((modifier: 'replace' | 'add' | 'subtract') => {
    if (modifier === 'replace') {
      clearSelectionPreview();
      return;
    }

    setSelectionPreview({
      active: true,
      modifier,
      tool: 'magicwand',
      baseCells: Array.from(baseSelectionMaskRef.current),
      gestureCells: []
    });
  }, [setSelectionPreview, clearSelectionPreview]);

  const updateSelectionPreview = useCallback((gestureCells: Set<string>) => {
    if (!selectionGestureActiveRef.current) {
      return;
    }

    if (selectionModifierRef.current === 'replace') {
      clearSelectionPreview();
      return;
    }

    setSelectionPreview({
      active: true,
      modifier: selectionModifierRef.current,
      tool: 'magicwand',
      baseCells: Array.from(baseSelectionMaskRef.current),
      gestureCells: Array.from(gestureCells)
    });
  }, [setSelectionPreview, clearSelectionPreview]);

  const finalizeSelectionGesture = useCallback(() => {
    if (!selectionGestureActiveRef.current) {
      return;
    }

    const currentMask = magicWandSelection.active ? new Set(magicWandSelection.selectedCells) : new Set<string>();
    let nextMask: Set<string>;

    switch (selectionModifierRef.current) {
      case 'add':
        nextMask = unionSelectionMasks(baseSelectionMaskRef.current, currentMask);
        break;
      case 'subtract':
        nextMask = subtractSelectionMask(baseSelectionMaskRef.current, currentMask);
        break;
      default:
        nextMask = currentMask;
        break;
    }

    const finalTarget = selectionModifierRef.current === 'replace'
      ? magicWandSelection.targetCell
      : baseTargetCellRef.current ?? magicWandSelection.targetCell;

    setMagicWandSelectionFromMask(nextMask, finalTarget ?? undefined);
    resetSelectionGesture();
  }, [magicWandSelection, setMagicWandSelectionFromMask, resetSelectionGesture]);

  // Convert mouse coordinates to grid coordinates
  const getGridCoordinatesFromEvent = useCallback((event: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas) return { x: 0, y: 0 };

    const rect = canvas.getBoundingClientRect();
    const mouseX = event.clientX;
    const mouseY = event.clientY;

    return getGridCoordinates(mouseX, mouseY, rect, width, height);
  }, [getGridCoordinates, width, height, canvasRef]);

  // Check if a cell is empty (has no character or default character)
  const isCellEmpty = useCallback((cell: Cell | undefined) => {
    if (!cell) return true;
    return !cell.char || cell.char === '' || cell.char === ' ';
  }, []);

  // Check if two cells match according to enabled criteria (AND semantics)
  const cellsMatch = useCallback((cell1: Cell | undefined, cell2: Cell | undefined) => {
    // Both empty considered match only if character matching is enabled (so they represent same absence)
    if (isCellEmpty(cell1) && isCellEmpty(cell2)) {
      return magicMatchChar; // Only match empties if char criterion is on
    }
    if (isCellEmpty(cell1) || isCellEmpty(cell2)) return false;
    if (!cell1 || !cell2) return false;
    if (magicMatchChar && cell1.char !== cell2.char) return false;
    if (magicMatchColor && cell1.color !== cell2.color) return false;
    if (magicMatchBgColor && cell1.bgColor !== cell2.bgColor) return false;
    // If all enabled criteria passed
    return true;
  }, [isCellEmpty, magicMatchChar, magicMatchColor, magicMatchBgColor]);

  // Find all matching cells using flood fill (contiguous) or scan (non-contiguous)
  const findMatchingCells = useCallback((targetX: number, targetY: number, targetCell: Cell | undefined) => {
    // If no criteria enabled, do nothing
    if (!magicMatchChar && !magicMatchColor && !magicMatchBgColor) {
      return new Set<string>();
    }
    // If target is empty and char not part of criteria -> nothing
    if (isCellEmpty(targetCell) && !magicMatchChar) {
      return new Set<string>();
    }

    const matchingCells = new Set<string>();

    if (magicWandContiguous) {
      // Contiguous selection using flood fill
      const visited = new Set<string>();
      const queue: { x: number; y: number }[] = [{ x: targetX, y: targetY }];

      while (queue.length > 0) {
        const { x, y } = queue.shift()!;
        const cellKey = `${x},${y}`;

        // Skip if out of bounds or already visited
        if (x < 0 || x >= width || y < 0 || y >= height || visited.has(cellKey)) {
          continue;
        }

        visited.add(cellKey);
        const currentCell = getCell(x, y);

        // If this cell matches the target, add it and check neighbors
        if (cellsMatch(currentCell, targetCell)) {
          matchingCells.add(cellKey);

          // Add neighbors to queue
          queue.push(
            { x: x - 1, y }, // left
            { x: x + 1, y }, // right
            { x, y: y - 1 }, // up
            { x, y: y + 1 }  // down
          );
        }
      }
    } else {
      // Non-contiguous selection - scan entire canvas
      for (let x = 0; x < width; x++) {
        for (let y = 0; y < height; y++) {
          const currentCell = getCell(x, y);
          if (cellsMatch(currentCell, targetCell)) {
            matchingCells.add(`${x},${y}`);
          }
        }
      }
    }

    return matchingCells;
  }, [width, height, getCell, cellsMatch, isCellEmpty, magicWandContiguous, magicMatchChar, magicMatchColor, magicMatchBgColor]);

  // Check if a point is inside the current magic wand selection
  const isPointInMagicWandSelection = useCallback((x: number, y: number) => {
    if (!magicWandSelection.active || magicWandSelection.selectedCells.size === 0) return false;

    // If there's a move state, we need to check against the original (non-offset) coordinates
    // because the selectedCells are stored in original coordinates
    if (moveState) {
      const totalOffset = {
        x: moveState.baseOffset.x + moveState.currentOffset.x,
        y: moveState.baseOffset.y + moveState.currentOffset.y
      };

      // Convert the click point back to original coordinates
      const originalX = x - totalOffset.x;
      const originalY = y - totalOffset.y;
      return magicWandSelection.selectedCells.has(`${originalX},${originalY}`);
    }

    // No move state, check directly
    return magicWandSelection.selectedCells.has(`${x},${y}`);
  }, [magicWandSelection, moveState]);

  // Handle magic wand selection mouse down
  const handleMagicWandMouseDown = useCallback((event: React.MouseEvent<HTMLCanvasElement>) => {
    const { x, y } = getGridCoordinatesFromEvent(event);
    const modifier: 'replace' | 'add' | 'subtract' = event.altKey ? 'subtract' : (event.shiftKey ? 'add' : 'replace');
    selectionModifierRef.current = modifier;

    const existingMask = magicWandSelection.active ? new Set(magicWandSelection.selectedCells) : new Set<string>();
    baseTargetCellRef.current = magicWandSelection.active ? magicWandSelection.targetCell : null;

    // Save current state for undo
    pushCanvasHistory(new Map(cells), currentFrameIndex);

    if (moveState && magicWandSelection.active && !isPointInMagicWandSelection(x, y) && modifier === 'replace') {
      commitMove();
      clearMagicWandSelection();
      setJustCommittedMove(true);
      resetSelectionGesture();
      return;
    }

    // Check if we clicked inside an existing selection to start move mode (only without modifiers)
    if (magicWandSelection.active && isPointInMagicWandSelection(x, y) && modifier === 'replace') {
      setSelectionMode('moving');

      if (moveState) {
        const adjustedStartPos = {
          x: x - moveState.currentOffset.x,
          y: y - moveState.currentOffset.y
        };
        setMoveState({
          ...moveState,
          startPos: adjustedStartPos
        });
      } else {
        const originalData = new Map<string, Cell>();
        magicWandSelection.selectedCells.forEach((cellKey) => {
          const [cx, cy] = cellKey.split(',').map(Number);
          const cell = getCell(cx, cy);
          if (cell && !isCellEmpty(cell)) {
            originalData.set(cellKey, cell);
          }
        });

        setMoveState({
          originalData,
          originalPositions: new Set(originalData.keys()),
          startPos: { x, y },
          baseOffset: { x: 0, y: 0 },
          currentOffset: { x: 0, y: 0 }
        });
      }
      setMouseButtonDown(true);
      resetSelectionGesture();
      return;
    }

    setJustCommittedMove(false);

    const targetCell = getCell(x, y);
    const matchingCells = findMatchingCells(x, y, targetCell);

    if (matchingCells.size === 0) {
      if (modifier === 'replace') {
        clearMagicWandSelection();
      }
      resetSelectionGesture();
      setMouseButtonDown(true);
      return;
    }

    baseSelectionMaskRef.current = existingMask;
    selectionGestureActiveRef.current = true;
    beginSelectionPreview(modifier);

    startMagicWandSelection(targetCell, matchingCells);
    setSelectionMode('none');
    updateSelectionPreview(matchingCells);
    setMouseButtonDown(true);
  }, [
    getGridCoordinatesFromEvent,
    magicWandSelection,
    pushCanvasHistory,
    cells,
    currentFrameIndex,
    moveState,
    isPointInMagicWandSelection,
    commitMove,
    clearMagicWandSelection,
    setJustCommittedMove,
    resetSelectionGesture,
    beginSelectionPreview,
    setSelectionMode,
    setMoveState,
    setMouseButtonDown,
    getCell,
    isCellEmpty,
    findMatchingCells,
    startMagicWandSelection,
    updateSelectionPreview
  ]);

  // Handle mouse move during magic wand selection
  const handleMagicWandMouseMove = useCallback((event: React.MouseEvent<HTMLCanvasElement>) => {
    const { x, y } = getGridCoordinatesFromEvent(event);

    // Handle move mode - only update position if mouse button is down (mouse-initiated move)
    // This prevents arrow key-initiated moves from jumping to follow mouse movement
    if (selectionMode === 'moving' && moveState && mouseButtonDown) {
      const newOffset = {
        x: x - moveState.startPos.x,
        y: y - moveState.startPos.y
      };

      setMoveState({
        ...moveState,
        currentOffset: newOffset
      });
    }
  }, [
    mouseButtonDown, 
    magicWandSelection, 
    getGridCoordinatesFromEvent, 
    selectionMode, 
    moveState, 
    setMoveState
  ]);

  // Handle mouse up for magic wand selection
  const handleMagicWandMouseUp = useCallback(() => {
    if (!mouseButtonDown) return;

    setMouseButtonDown(false);

    // If we were in moving mode, that's the end of the move operation
    // The move will be committed when the user clicks elsewhere or presses Enter/Escape
    if (selectionMode === 'moving' && moveState) {
      // Move operation is complete, but not committed yet
      // User can continue adjusting or commit/cancel
    }

    // Reset just committed flag after a short delay to allow for proper click detection
    if (justCommittedMove) {
      setTimeout(() => setJustCommittedMove(false), 100);
    }

    if (selectionGestureActiveRef.current) {
      finalizeSelectionGesture();
    }
  }, [
    mouseButtonDown,
    setMouseButtonDown,
    selectionMode,
    moveState,
    justCommittedMove,
    setJustCommittedMove,
    finalizeSelectionGesture
  ]);

  return {
    handleMagicWandMouseDown,
    handleMagicWandMouseMove,
    handleMagicWandMouseUp,
    isPointInMagicWandSelection,
  };
};

import { useCallback, useEffect, useMemo } from 'react';
import { useCanvasStore } from '../stores/canvasStore';
import { useToolStore } from '../stores/toolStore';
import { usePreviewStore } from '../stores/previewStore';
import { useEffectsStore } from '../stores/effectsStore';
import { useTimeEffectsStore } from '../stores/timeEffectsStore';
import { useAsciiTypeStore } from '../stores/asciiTypeStore';
import { useCanvasContext } from '../contexts/CanvasContext';
import { useTheme } from '../contexts/ThemeContext';
import { useCanvasState } from './useCanvasState';
import { useMemoizedGrid } from './useMemoizedGrid';
import { useDrawingTool } from './useDrawingTool';
import { useOnionSkinRenderer } from './useOnionSkinRenderer';
import { measureCanvasRender, finishCanvasRender } from '../utils/performance';
import { 
  setupTextRendering
} from '../utils/canvasTextRendering';
import { scheduleCanvasRender } from '../utils/renderScheduler';
import { markFullRedraw } from '../utils/dirtyTracker';
import { calculateAdaptiveGridColor } from '../utils/gridColor';
import type { Cell } from '../types';

/**
 * Setup high-DPI canvas for crisp text rendering
 * Returns scale factor for coordinate transformations
 */
const setupHighDPICanvas = (
  canvas: HTMLCanvasElement,
  displayWidth: number,
  displayHeight: number
): { ctx: CanvasRenderingContext2D; scale: number } => {
  const ctx = canvas.getContext('2d');
  if (!ctx) throw new Error('Failed to get 2D context');

  // Use device pixel ratio for crisp rendering on high-DPI displays
  const devicePixelRatio = window.devicePixelRatio || 1;

  // Set canvas internal resolution to match device pixel ratio
  canvas.width = displayWidth * devicePixelRatio;
  canvas.height = displayHeight * devicePixelRatio;

  // Set CSS size to desired display size (no transform needed)
  canvas.style.width = `${displayWidth}px`;
  canvas.style.height = `${displayHeight}px`;

  // Scale the drawing context to match the device pixel ratio
  ctx.scale(devicePixelRatio, devicePixelRatio);

  // Apply high-quality text rendering settings
  ctx.textBaseline = 'top';
  ctx.imageSmoothingEnabled = true;
  ctx.imageSmoothingQuality = 'high';

  return { ctx, scale: devicePixelRatio };
};

/**
 * Hook for optimized canvas rendering with memoization
 * Implements Step 5.1 performance optimizations:
 * - Memoized font and style calculations
 * - Grid-level change detection
 * - Performance measurement
 */
export const useCanvasRenderer = () => {
  const { canvasRef, pasteMode, panOffset, hoveredCell, fontMetrics } = useCanvasContext();
  const { theme } = useTheme();
  const {
    effectiveCellWidth,
    effectiveCellHeight,
    zoom,
    moveState,
    canvasWidth,
    canvasHeight,
    getTotalOffset,
  } = useCanvasState();

  const { 
    width, 
    height, 
    cells,
    canvasBackgroundColor,
    showGrid,
    getCell
  } = useCanvasStore();

  const { activeTool, rectangleFilled, lassoSelection, magicWandSelection, textToolState, linePreview } = useToolStore();
  const { previewData, isPreviewActive } = usePreviewStore();
  const { isPreviewActive: isEffectPreviewActive } = useEffectsStore();
  const { isPreviewActive: isTimeEffectPreviewActive } = useTimeEffectsStore();
  const { previewOrigin, previewDimensions } = useAsciiTypeStore();

  // Debug: Log preview state changes
  useEffect(() => {
    console.log('[Canvas Renderer] Preview state changed:', {
      isPreviewActive,
      previewDataSize: previewData.size,
      isEffectPreviewActive,  
      isTimeEffectPreviewActive
    });
  }, [isPreviewActive, previewData, isEffectPreviewActive, isTimeEffectPreviewActive]);
  const { getEllipsePoints } = useDrawingTool();

  // Use onion skin renderer for frame overlays
  const { renderOnionSkins } = useOnionSkinRenderer();

  // Use memoized grid for optimized rendering  
  const { selectionData } = useMemoizedGrid(
    moveState,
    getTotalOffset
  );

  // Memoize canvas dimensions and styling to reduce re-renders
  const canvasConfig = useMemo(() => ({
    width,
    height,
    canvasWidth,
    canvasHeight,
    effectiveCellWidth,
    effectiveCellHeight,
    panOffset,
    showGrid,
    canvasBackgroundColor
  }), [width, height, canvasWidth, canvasHeight, effectiveCellWidth, effectiveCellHeight, panOffset, showGrid, canvasBackgroundColor]);

  // Memoize tool state to reduce re-renders
  const toolState = useMemo(() => ({
    activeTool,
    rectangleFilled,
    lassoSelection,
    magicWandSelection,
    textToolState,
    linePreview
  }), [activeTool, rectangleFilled, lassoSelection, magicWandSelection, textToolState, linePreview]);

  // Memoize overlay state
  const overlayState = useMemo(() => ({
    moveState,
    selectionData,
    hoveredCell,
    pasteMode
  }), [moveState, selectionData, hoveredCell, pasteMode]);

  // Memoize font and style calculations
  const drawingStyles = useMemo(() => {
    // Scale font size with zoom - keep original logic
    const scaledFontSize = fontMetrics.fontSize * zoom;
    const scaledFontString = `${scaledFontSize}px '${fontMetrics.fontFamily}', monospace`;

    return {
      font: scaledFontString,
      gridLineColor: calculateAdaptiveGridColor(canvasBackgroundColor, theme),
      gridLineWidth: 1, // Use 1 pixel for crisp grid lines
      textAlign: 'center' as CanvasTextAlign,
      textBaseline: 'middle' as CanvasTextBaseline,
      defaultTextColor: '#FFFFFF',
      defaultBgColor: '#000000'
    };
  }, [fontMetrics, zoom, canvasBackgroundColor, theme]);

    // Optimized drawCell function with pixel-aligned rendering (but no coordinate changes)
  const drawCell = useCallback((ctx: CanvasRenderingContext2D, x: number, y: number, cell: Cell) => {
    // Round pixel positions to ensure crisp rendering
    const pixelX = Math.round(x * effectiveCellWidth + panOffset.x);
    const pixelY = Math.round(y * effectiveCellHeight + panOffset.y);
    const cellWidth = Math.round(effectiveCellWidth);
    const cellHeight = Math.round(effectiveCellHeight);

    // Draw background (only if different from canvas background)
    if (cell.bgColor && cell.bgColor !== 'transparent' && cell.bgColor !== canvasBackgroundColor) {
      ctx.fillStyle = cell.bgColor;
      ctx.fillRect(pixelX, pixelY, cellWidth, cellHeight);
    }

    // Draw character with pixel-perfect positioning
    if (cell.char && cell.char !== ' ') {
      ctx.fillStyle = cell.color || drawingStyles.defaultTextColor;
      ctx.font = drawingStyles.font;
      ctx.textAlign = drawingStyles.textAlign;
      ctx.textBaseline = drawingStyles.textBaseline;

      // Center text with rounded positions for crisp rendering
      const centerX = Math.round(pixelX + cellWidth / 2);
      const centerY = Math.round(pixelY + cellHeight / 2);

      ctx.fillText(cell.char, centerX, centerY);
    }
  }, [effectiveCellWidth, effectiveCellHeight, panOffset, canvasBackgroundColor, drawingStyles]);

  // Separate function to render grid background
  const drawGridBackground = useCallback((ctx: CanvasRenderingContext2D) => {
    if (!showGrid) return;

    ctx.strokeStyle = drawingStyles.gridLineColor;
    ctx.lineWidth = drawingStyles.gridLineWidth;

    // Draw vertical lines
    for (let x = 0; x <= width; x++) {
      const lineX = Math.round(x * effectiveCellWidth + panOffset.x) + 0.5;
      ctx.beginPath();
      ctx.moveTo(lineX, panOffset.y);
      ctx.lineTo(lineX, height * effectiveCellHeight + panOffset.y);
      ctx.stroke();
    }

    // Draw horizontal lines
    for (let y = 0; y <= height; y++) {
      const lineY = Math.round(y * effectiveCellHeight + panOffset.y) + 0.5;
      ctx.beginPath();
      ctx.moveTo(panOffset.x, lineY);
      ctx.lineTo(width * effectiveCellWidth + panOffset.x, lineY);
      ctx.stroke();
    }
  }, [width, height, effectiveCellWidth, effectiveCellHeight, panOffset, drawingStyles, showGrid]);

  // Optimized render function with performance measurement and subtle DPI improvements
  const renderCanvas = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Apply only text rendering optimizations without affecting canvas size/coordinates
    setupTextRendering(ctx);

    // Start performance measurement
    measureCanvasRender();

    // Clear canvas and fill with background color
    if (canvasConfig.canvasBackgroundColor === 'transparent') {
      // For transparent backgrounds, clear the canvas completely
      ctx.clearRect(0, 0, canvasConfig.canvasWidth, canvasConfig.canvasHeight);
    } else {
      // For solid backgrounds, fill with the background color
      ctx.fillStyle = canvasConfig.canvasBackgroundColor;
      ctx.fillRect(0, 0, canvasConfig.canvasWidth, canvasConfig.canvasHeight);
    }

    // Render grid background layer first (behind content)
    drawGridBackground(ctx);

    // Render onion skin layers (previous and next frames)
    renderOnionSkins();

    // Set font context once for the entire render batch
    ctx.font = drawingStyles.font;
    ctx.textAlign = drawingStyles.textAlign;
    ctx.textBaseline = drawingStyles.textBaseline;

    // Create a set of coordinates that are being moved (optimized)
    const movingCells = new Set<string>();
    if (moveState) {
      const originalKeys = moveState.originalPositions ?? new Set(moveState.originalData.keys());
      originalKeys.forEach((key: string) => {
        movingCells.add(key);
      });
    }

    // Draw static cells (excluding cells being moved)
    // Skip drawing original cells if time effects preview is active (preview will render all cells)
    if (!isTimeEffectPreviewActive) {
      for (let y = 0; y < canvasConfig.height; y++) {
        for (let x = 0; x < canvasConfig.width; x++) {
          const key = `${x},${y}`;

          if (movingCells.has(key)) {
            // Draw empty cell in original position during move
            drawCell(ctx, x, y, { 
              char: ' ', 
              color: drawingStyles.defaultTextColor, 
              bgColor: drawingStyles.defaultBgColor 
            });
          } else {
            const cell = getCell(x, y);
            if (cell) {
              drawCell(ctx, x, y, cell);
            }
          }
        }
      }
    }

    // Draw moved cells at their new positions
    if (overlayState.moveState && overlayState.moveState.originalData.size > 0) {
      const totalOffset = getTotalOffset(overlayState.moveState);
      overlayState.moveState.originalData.forEach((cell: Cell, key: string) => {
        const [origX, origY] = key.split(',').map(Number);
        const newX = origX + totalOffset.x;
        const newY = origY + totalOffset.y;

        // Only draw if within bounds
        if (newX >= 0 && newX < canvasConfig.width && newY >= 0 && newY < canvasConfig.height) {
          drawCell(ctx, newX, newY, cell);
        }
      });
    }

    // Draw selection overlay
    if (overlayState.selectionData) {
      if (toolState.activeTool === 'ellipse') {
        // Draw ellipse preview with highlighted cells
        const centerX = (overlayState.selectionData.startX + overlayState.selectionData.startX + overlayState.selectionData.width - 1) / 2;
        const centerY = (overlayState.selectionData.startY + overlayState.selectionData.startY + overlayState.selectionData.height - 1) / 2;
        const radiusX = (overlayState.selectionData.width - 1) / 2;
        const radiusY = (overlayState.selectionData.height - 1) / 2;

        // Get ellipse points to highlight exactly which cells will be affected
        const ellipsePoints = getEllipsePoints(centerX, centerY, radiusX, radiusY, toolState.rectangleFilled);

        // Highlight each cell that will be part of the ellipse
        ctx.fillStyle = 'rgba(168, 85, 247, 0.3)'; // Purple highlight
        ellipsePoints.forEach(({ x, y }) => {
          if (x >= 0 && y >= 0 && x < canvasConfig.width && y < canvasConfig.height) {
            ctx.fillRect(
              Math.round(x * canvasConfig.effectiveCellWidth + canvasConfig.panOffset.x),
              Math.round(y * canvasConfig.effectiveCellHeight + canvasConfig.panOffset.y),
              Math.round(canvasConfig.effectiveCellWidth),
              Math.round(canvasConfig.effectiveCellHeight)
            );
          }
        });

        // Draw ellipse outline
        ctx.strokeStyle = '#A855F7'; // Purple border
        ctx.lineWidth = 2;
        ctx.setLineDash([5, 5]);

        // Draw ellipse path using HTML5 Canvas ellipse method
        ctx.beginPath();
        ctx.ellipse(
          (centerX + 0.5) * canvasConfig.effectiveCellWidth + canvasConfig.panOffset.x,  // center x
          (centerY + 0.5) * canvasConfig.effectiveCellHeight + canvasConfig.panOffset.y,  // center y  
          (radiusX + 0.5) * canvasConfig.effectiveCellWidth,  // radius x
          (radiusY + 0.5) * canvasConfig.effectiveCellHeight,  // radius y
          0,                           // rotation
          0,                           // start angle
          2 * Math.PI                  // end angle
        );
        ctx.stroke();
        ctx.setLineDash([]);
      } else {
        // Default rectangle preview for rectangle tool and selection tool
        ctx.strokeStyle = '#3B82F6';
        ctx.lineWidth = 2;
        ctx.setLineDash([5, 5]);
        ctx.strokeRect(
          Math.round(overlayState.selectionData.startX * canvasConfig.effectiveCellWidth + canvasConfig.panOffset.x),
          Math.round(overlayState.selectionData.startY * canvasConfig.effectiveCellHeight + canvasConfig.panOffset.y),
          Math.round(overlayState.selectionData.width * canvasConfig.effectiveCellWidth),
          Math.round(overlayState.selectionData.height * canvasConfig.effectiveCellHeight)
        );
        ctx.setLineDash([]);
      }
    }

    // Draw lasso selection overlay
    if (toolState.lassoSelection.active) {
      // Note: Lasso path drawing removed for cleaner UX - only show filled selection

      // Highlight selected cells
      if (lassoSelection.selectedCells.size > 0) {
        ctx.fillStyle = 'rgba(168, 85, 247, 0.2)'; // Purple highlight with transparency

        lassoSelection.selectedCells.forEach(cellKey => {
          const [x, y] = cellKey.split(',').map(Number);

          // Apply move offset if in move mode
          let displayX = x;
          let displayY = y;
          if (moveState) {
            const totalOffset = getTotalOffset(moveState);
            displayX = x + totalOffset.x;
            displayY = y + totalOffset.y;
          }

          // Only draw if within canvas bounds
          if (displayX >= 0 && displayY >= 0 && displayX < width && displayY < height) {
            ctx.fillRect(
              Math.round(displayX * effectiveCellWidth + panOffset.x),
              Math.round(displayY * effectiveCellHeight + panOffset.y),
              Math.round(effectiveCellWidth),
              Math.round(effectiveCellHeight)
            );
          }
        });

        // Note: Lasso path border removed for cleaner UX - only show filled selection
      }
    }

    // Draw shift+click line preview
    if (toolState.linePreview.active && toolState.linePreview.points.length > 0) {
      ctx.fillStyle = 'rgba(168, 85, 247, 0.2)'; // Same purple as lasso selection, doubled opacity

      toolState.linePreview.points.forEach(({ x, y }) => {
        // Only draw if within canvas bounds
        if (x >= 0 && y >= 0 && x < canvasConfig.width && y < canvasConfig.height) {
          ctx.fillRect(
            Math.round(x * canvasConfig.effectiveCellWidth + canvasConfig.panOffset.x),
            Math.round(y * canvasConfig.effectiveCellHeight + canvasConfig.panOffset.y),
            Math.round(canvasConfig.effectiveCellWidth),
            Math.round(canvasConfig.effectiveCellHeight)
          );
        }
      });
    }

    // Draw paste preview overlay
    if (pasteMode.isActive && pasteMode.preview) {
      const { position, data, bounds } = pasteMode.preview;

      // Calculate preview rectangle
      const previewStartX = position.x + bounds.minX;
      const previewStartY = position.y + bounds.minY;
      const previewWidth = bounds.maxX - bounds.minX + 1;
      const previewHeight = bounds.maxY - bounds.minY + 1;

      // Draw paste preview marquee
      ctx.strokeStyle = '#A855F7'; // Purple color
      ctx.lineWidth = 2;
      ctx.setLineDash([8, 4]);
      ctx.strokeRect(
        Math.round(previewStartX * effectiveCellWidth + panOffset.x),
        Math.round(previewStartY * effectiveCellHeight + panOffset.y),
        Math.round(previewWidth * effectiveCellWidth),
        Math.round(previewHeight * effectiveCellHeight)
      );

      // Add semi-transparent background
      ctx.fillStyle = 'rgba(168, 85, 247, 0.1)';
      ctx.fillRect(
        Math.round(previewStartX * effectiveCellWidth + panOffset.x),
        Math.round(previewStartY * effectiveCellHeight + panOffset.y),
        Math.round(previewWidth * effectiveCellWidth),
        Math.round(previewHeight * effectiveCellHeight)
      );

      ctx.setLineDash([]);

      // Draw paste content preview with transparency
      ctx.globalAlpha = 0.85; // Make preview more visible
      data.forEach((cell, key) => {
        const [relX, relY] = key.split(',').map(Number);
        const absoluteX = position.x + relX;
        const absoluteY = position.y + relY;

        // Only draw if within canvas bounds
        if (absoluteX >= 0 && absoluteX < width && absoluteY >= 0 && absoluteY < height) {
          // Draw the actual cell content
          drawCell(ctx, absoluteX, absoluteY, {
            char: cell.char || ' ',
            color: cell.color || drawingStyles.defaultTextColor,
            bgColor: cell.bgColor || 'transparent'
          });

          // Add a subtle highlight border around preview cells
          ctx.strokeStyle = 'rgba(168, 85, 247, 0.4)';
          ctx.lineWidth = 1;
          ctx.strokeRect(
            Math.round(absoluteX * effectiveCellWidth + panOffset.x), 
            Math.round(absoluteY * effectiveCellHeight + panOffset.y), 
            Math.round(effectiveCellWidth), 
            Math.round(effectiveCellHeight)
          );
        }
      });
      ctx.globalAlpha = 1.0;
    }

    // Draw hover cell outline (subtle outline for current cell under cursor)
    if (hoveredCell && hoveredCell.x >= 0 && hoveredCell.x < width && hoveredCell.y >= 0 && hoveredCell.y < height) {
      ctx.strokeStyle = 'rgba(59, 130, 246, 0.5)'; // 50% opacity blue outline for screenshots
      ctx.lineWidth = 2;
      ctx.setLineDash([]);
      ctx.strokeRect(
        Math.round(hoveredCell.x * effectiveCellWidth + panOffset.x),
        Math.round(hoveredCell.y * effectiveCellHeight + panOffset.y),
        Math.round(effectiveCellWidth),
        Math.round(effectiveCellHeight)
      );
    }

    // Draw preview overlay 
    if (isPreviewActive && previewData.size > 0) {
      // Check if this is an effects preview (should be fully opaque) or other preview (semi-transparent)
      const isEffectsPreview = isEffectPreviewActive || isTimeEffectPreviewActive;
      const previewAlpha = isEffectsPreview ? 1.0 : 0.8; // Effects: full opacity, others: semi-transparent

      previewData.forEach((cell, key) => {
        const [x, y] = key.split(',').map(Number);

        // Only draw if within canvas bounds
        if (x >= 0 && x < canvasConfig.width && y >= 0 && y < canvasConfig.height) {
          ctx.save();
          ctx.globalAlpha = previewAlpha;

          // For effects previews, clear the cell area first to ensure complete replacement
          if (isEffectsPreview) {
            const pixelX = Math.round(x * effectiveCellWidth + panOffset.x);
            const pixelY = Math.round(y * effectiveCellHeight + panOffset.y);
            const cellWidth = Math.round(effectiveCellWidth);
            const cellHeight = Math.round(effectiveCellHeight);

            // Clear the cell area with canvas background
            ctx.fillStyle = canvasBackgroundColor;
            ctx.fillRect(pixelX, pixelY, cellWidth, cellHeight);
          }

          drawCell(ctx, x, y, cell);

          ctx.restore();
        }
      });

      // Draw purple dotted outline for ASCII Type preview
      if (activeTool === 'asciitype' && previewOrigin && previewDimensions) {
        ctx.strokeStyle = '#A855F7'; // Purple color matching other tool overlays
        ctx.lineWidth = 2;
        ctx.setLineDash([8, 4]); // Dotted pattern matching paste/selection overlays

        const outlineX = Math.round(previewOrigin.x * effectiveCellWidth + panOffset.x);
        const outlineY = Math.round(previewOrigin.y * effectiveCellHeight + panOffset.y);
        const outlineWidth = Math.round(previewDimensions.width * effectiveCellWidth);
        const outlineHeight = Math.round(previewDimensions.height * effectiveCellHeight);

        ctx.strokeRect(outlineX, outlineY, outlineWidth, outlineHeight);
        ctx.setLineDash([]); // Reset line dash
      }
    }

    // Draw text cursor overlay
    if (textToolState.isTyping && textToolState.cursorVisible && textToolState.cursorPosition) {
      const { x, y } = textToolState.cursorPosition;

      // Only draw cursor if within canvas bounds
      if (x >= 0 && x < width && y >= 0 && y < height) {
        ctx.fillStyle = '#A855F7'; // Purple color to match other overlays
        ctx.fillRect(
          Math.round(x * effectiveCellWidth + panOffset.x),
          Math.round(y * effectiveCellHeight + panOffset.y),
          Math.round(effectiveCellWidth),
          Math.round(effectiveCellHeight)
        );
      }
    }

    // Finish performance measurement
    const totalCells = width * height;
    finishCanvasRender(totalCells);

  }, [
    // Use memoized objects to reduce re-renders
    canvasConfig,
    toolState,
    overlayState,
    // Keep these individual dependencies for now
    cells, 
    getCell, 
    drawCell, 
    getTotalOffset,
    canvasRef,
    drawingStyles,
    getEllipsePoints,
    renderOnionSkins,
    // Preview store values
    previewData,
    isPreviewActive,
    // Effects preview state
    isEffectPreviewActive,
    isTimeEffectPreviewActive,
    // ASCII Type preview outline state
    previewOrigin,
    previewDimensions
  ]);

  // Throttled render function that uses requestAnimationFrame
  const scheduleRender = useCallback(() => {
    scheduleCanvasRender(renderCanvas);
  }, [renderCanvas]);

  // Optimized render trigger - use scheduled rendering for better performance
  const triggerRender = useCallback(() => {
    // Mark that we need a full redraw for now (we can optimize this later)
    markFullRedraw();
    scheduleRender();
  }, [scheduleRender]);

  // Re-render when dependencies change (now throttled)
  useEffect(() => {
    triggerRender();
  }, [triggerRender]);

  // Handle canvas resize with high-DPI setup
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    // Setup high-DPI canvas for crisp text rendering
    setupHighDPICanvas(canvas, canvasWidth, canvasHeight);

    // Re-render after resize (immediate for resize)
    renderCanvas();
  }, [canvasWidth, canvasHeight, renderCanvas]);

  return {
    renderCanvas,
    scheduleRender,
    triggerRender
  };
};

import { useCallback, useRef } from 'react';
import { useCanvasContext, useCanvasDimensions } from '../contexts/CanvasContext';
import { useCanvasState } from './useCanvasState';
import { useCanvasStore } from '../stores/canvasStore';
import { useAnimationStore } from '../stores/animationStore';
import { useToolStore } from '../stores/toolStore';
import type { Cell } from '../types';
import { unionSelectionMasks, subtractSelectionMask, createRectSelectionMask } from '../utils/selectionUtils';

/**
 * Hook for handling selection tool behavior
 * Manages selection creation, movement, and drag operations
 */
export const useCanvasSelection = () => {
  const { canvasRef, mouseButtonDown, setMouseButtonDown, setSelectionPreview } = useCanvasContext();
  const { getGridCoordinates } = useCanvasDimensions();
  const {
    selectionMode,
    moveState,
    pendingSelectionStart,
    justCommittedMove,
    isPointInEffectiveSelection,
    commitMove,
    setSelectionMode,
    setMoveState,
    setPendingSelectionStart,
    setJustCommittedMove,
  } = useCanvasState();

  const { width, height, cells, getCell } = useCanvasStore();
  const { currentFrameIndex } = useAnimationStore();
  const { 
    selection, 
    startSelection, 
    updateSelection, 
    clearSelection, 
    pushCanvasHistory,
    setSelectionFromMask
  } = useToolStore();

  const selectionModifierRef = useRef<'replace' | 'add' | 'subtract'>('replace');
  const baseSelectionMaskRef = useRef<Set<string>>(new Set());
  const selectionGestureActiveRef = useRef(false);
  const gestureStartRef = useRef<{ x: number; y: number } | null>(null);

  const clearSelectionPreview = useCallback(() => {
    setSelectionPreview({
      active: false,
      modifier: 'replace',
      tool: null,
      baseCells: [],
      gestureCells: []
    });
  }, [setSelectionPreview]);

  const resetSelectionGesture = useCallback(() => {
    selectionModifierRef.current = 'replace';
    baseSelectionMaskRef.current = new Set();
    selectionGestureActiveRef.current = false;
    gestureStartRef.current = null;
    clearSelectionPreview();
  }, [clearSelectionPreview]);

  const beginSelectionPreview = useCallback((modifier: 'replace' | 'add' | 'subtract', start: { x: number; y: number }) => {
    gestureStartRef.current = start;
    if (modifier === 'replace') {
      clearSelectionPreview();
      return;
    }

    setSelectionPreview({
      active: true,
      modifier,
      tool: 'select',
      baseCells: Array.from(baseSelectionMaskRef.current),
      gestureCells: []
    });
  }, [setSelectionPreview, clearSelectionPreview]);

  const updateSelectionPreview = useCallback((endX: number, endY: number) => {
    if (!selectionGestureActiveRef.current) {
      return;
    }

    if (selectionModifierRef.current === 'replace') {
      clearSelectionPreview();
      return;
    }

    const start = gestureStartRef.current;
    if (!start) {
      return;
    }

    const gestureMask = createRectSelectionMask(start, { x: endX, y: endY });
    setSelectionPreview({
      active: true,
      modifier: selectionModifierRef.current,
      tool: 'select',
      baseCells: Array.from(baseSelectionMaskRef.current),
      gestureCells: Array.from(gestureMask)
    });
  }, [setSelectionPreview, clearSelectionPreview]);

  const finalizeSelectionGesture = useCallback(() => {
    if (!selectionGestureActiveRef.current) {
      return;
    }

    const currentMask = selection.active ? new Set(selection.selectedCells) : new Set<string>();
    let nextMask: Set<string>;

    switch (selectionModifierRef.current) {
      case 'add':
        nextMask = unionSelectionMasks(baseSelectionMaskRef.current, currentMask);
        break;
      case 'subtract':
        nextMask = subtractSelectionMask(baseSelectionMaskRef.current, currentMask);
        break;
      default:
        nextMask = currentMask;
        break;
    }

    setSelectionFromMask(nextMask);
    resetSelectionGesture();
  }, [selection, setSelectionFromMask, resetSelectionGesture]);

  // Convert mouse coordinates to grid coordinates
  const getGridCoordinatesFromEvent = useCallback((event: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas) return { x: 0, y: 0 };

    const rect = canvas.getBoundingClientRect();
    const mouseX = event.clientX;
    const mouseY = event.clientY;

    return getGridCoordinates(mouseX, mouseY, rect, width, height);
  }, [getGridCoordinates, width, height, canvasRef]);

  // Handle selection tool mouse down
  const handleSelectionMouseDown = useCallback((event: React.MouseEvent<HTMLCanvasElement>) => {
    const { x, y } = getGridCoordinatesFromEvent(event);
    const modifier: 'replace' | 'add' | 'subtract' = event.altKey ? 'subtract' : (event.shiftKey ? 'add' : 'replace');
    selectionModifierRef.current = modifier;

    const existingMask = selection.active ? new Set(selection.selectedCells) : new Set<string>();

    // Save current state for undo
    pushCanvasHistory(new Map(cells), currentFrameIndex, 'Selection action');

    // If there's an uncommitted move and clicking outside selection, commit it first
    if (moveState && selection.active && !isPointInEffectiveSelection(x, y)) {
      commitMove();
      clearSelection();
      setJustCommittedMove(true);
      resetSelectionGesture();
      return;
    }

    if (justCommittedMove) {
      // Previous click committed a move, this click starts fresh
      setJustCommittedMove(false);
      baseSelectionMaskRef.current = existingMask;
      selectionGestureActiveRef.current = true;
      beginSelectionPreview(modifier, { x, y });
      startSelection(x, y);
      setPendingSelectionStart({ x, y });
      setMouseButtonDown(true);
      return;
    }

    if (selection.active && isPointInEffectiveSelection(x, y) && modifier === 'replace') {
      // Click inside existing selection - enter move mode
      setJustCommittedMove(false);
      if (moveState) {
        // Already have a moveState (continuing from arrow key movement) 
        // Adjust startPos to account for existing currentOffset so position doesn't jump
        const adjustedStartPos = {
          x: x - moveState.currentOffset.x,
          y: y - moveState.currentOffset.y
        };
        setMoveState({
          ...moveState,
          startPos: adjustedStartPos
        });
      } else {
        // First time moving - create new moveState
        const originalData = new Map<string, Cell>();
        const originalPositions = new Set<string>();

        selection.selectedCells.forEach((cellKey) => {
          originalPositions.add(cellKey);
          const [cx, cy] = cellKey.split(',').map(Number);
          const cell = getCell(cx, cy);
          if (cell && cell.char !== ' ') {
            originalData.set(cellKey, cell);
          }
        });

        setMoveState({
          originalData,
          originalPositions,
          startPos: { x, y },
          baseOffset: { x: 0, y: 0 },
          currentOffset: { x: 0, y: 0 }
        });
      }
      setSelectionMode('moving');
      setMouseButtonDown(true);
      resetSelectionGesture();
      return;
    }

    if (selection.active && !isPointInEffectiveSelection(x, y) && modifier === 'replace') {
      // Click outside existing selection without modifiers - clear selection
      setJustCommittedMove(false);
      clearSelection();
      resetSelectionGesture();
      return;
    }

    // Start new selection (add/subtract/replace)
    setJustCommittedMove(false);
    baseSelectionMaskRef.current = existingMask;
    selectionGestureActiveRef.current = true;

    if (pendingSelectionStart && modifier !== 'replace') {
      // Complete pending anchor selection for additive/subtractive mode
      beginSelectionPreview(modifier, pendingSelectionStart);
      startSelection(pendingSelectionStart.x, pendingSelectionStart.y);
      updateSelection(x, y);
      updateSelectionPreview(x, y);
      setPendingSelectionStart(null);
    } else {
      beginSelectionPreview(modifier, { x, y });
      startSelection(x, y);
      setPendingSelectionStart({ x, y });
      setMouseButtonDown(true);
    }
  }, [
    getGridCoordinatesFromEvent,
    selection,
    pushCanvasHistory,
    cells,
    currentFrameIndex,
    moveState,
    isPointInEffectiveSelection,
    commitMove,
    clearSelection,
    setJustCommittedMove,
    beginSelectionPreview,
    startSelection,
    setPendingSelectionStart,
    setMouseButtonDown,
    setMoveState,
    setSelectionMode,
    getCell,
    updateSelection,
    pendingSelectionStart,
    justCommittedMove,
    resetSelectionGesture,
    updateSelectionPreview
  ]);

  // Handle selection tool mouse move
  const handleSelectionMouseMove = useCallback((event: React.MouseEvent<HTMLCanvasElement>) => {
    const { x, y } = getGridCoordinatesFromEvent(event);

    if (selectionMode === 'moving' && moveState && mouseButtonDown) {
      // Only update move position if mouse button is down (mouse-initiated move)
      // This prevents arrow key-initiated moves from jumping to follow mouse movement
      const currentDragOffset = {
        x: x - moveState.startPos.x,
        y: y - moveState.startPos.y
      };

      // Update the current offset for preview
      setMoveState({
        ...moveState,
        currentOffset: currentDragOffset
      });
      // Note: Canvas modification happens in renderGrid for preview, actual move on mouse release
    } else if (mouseButtonDown && selection.active && pendingSelectionStart) {
      // Mouse button is down and we have a pending selection start - switch to drag mode
      if (x !== pendingSelectionStart.x || y !== pendingSelectionStart.y) {
        setSelectionMode('dragging');
        setPendingSelectionStart(null);
      }
      updateSelection(x, y);
      updateSelectionPreview(x, y);
    } else if (selectionMode === 'dragging' && selection.active) {
      // Update selection bounds while dragging
      updateSelection(x, y);
      updateSelectionPreview(x, y);
    }
  }, [
    getGridCoordinatesFromEvent, selectionMode, moveState, setMoveState, 
    selection, updateSelection, mouseButtonDown, pendingSelectionStart, setPendingSelectionStart,
    updateSelectionPreview
  ]);

  // Handle selection tool mouse up
  const handleSelectionMouseUp = useCallback(() => {
    if (selectionMode === 'moving' && moveState) {
      // Move drag completed - persist the current offset into base offset for continued editing
      setMoveState({
        ...moveState,
        baseOffset: {
          x: moveState.baseOffset.x + moveState.currentOffset.x,
          y: moveState.baseOffset.y + moveState.currentOffset.y
        },
        currentOffset: { x: 0, y: 0 }
      });
      setSelectionMode('none');
      setMouseButtonDown(false);
    } else if (selectionMode === 'dragging') {
      // Drag completed - finish the selection
      setSelectionMode('none');
      setMouseButtonDown(false);
      // Selection remains active with current bounds
    } else {
      // Single click completed - clear mouse button state but keep pending selection
      setMouseButtonDown(false);
    }

    if (selectionGestureActiveRef.current) {
      finalizeSelectionGesture();
    }
  }, [
    selectionMode,
    moveState,
    setMoveState,
    setSelectionMode,
    setMouseButtonDown,
    finalizeSelectionGesture
  ]);

  return {
    handleSelectionMouseDown,
    handleSelectionMouseMove,
    handleSelectionMouseUp,
  };
};

import { useCallback } from 'react';
import { useCanvasStore } from '../stores/canvasStore';
import { useToolStore } from '../stores/toolStore';
import { useCanvasContext } from '../contexts/CanvasContext';
import { calculateBrushCells } from '../utils/brushUtils';
import type { Cell } from '../types';

/**
 * Custom hook for handling canvas drawing operations
 */
export const useDrawingTool = () => {
  const { setCell, clearCell, getCell, fillArea } = useCanvasStore();
  const { 
    activeTool, 
    selectedChar, 
    selectedColor, 
    selectedBgColor,
    brushSettings,
    rectangleFilled,
    paintBucketContiguous,
    pickFromCell,
    pencilLastPosition,
    setPencilLastPosition,
    toolAffectsChar,
    toolAffectsColor,
    toolAffectsBgColor,
    fillMatchChar,
    fillMatchColor,
    fillMatchBgColor
  } = useToolStore();
  const { fontMetrics } = useCanvasContext();

  // Helper function to create a cell respecting the tool toggles
  const createCellWithToggles = useCallback((x: number, y: number): Cell => {
    const existingCell = getCell(x, y);
    const newChar = toolAffectsChar ? selectedChar : (existingCell?.char || ' ');

    // Only apply color data if the cell will have a character (not just a space)
    const willHaveChar = newChar !== ' ';
    const hasExistingChar = existingCell?.char && existingCell.char !== ' ';
    const shouldApplyColors = willHaveChar || hasExistingChar;

    return {
      char: newChar,
      color: (toolAffectsColor && shouldApplyColors) ? selectedColor : (existingCell?.color || '#FFFFFF'),
      bgColor: (toolAffectsBgColor && shouldApplyColors) ? selectedBgColor : (existingCell?.bgColor || 'transparent')
    };
  }, [toolAffectsChar, toolAffectsColor, toolAffectsBgColor, selectedChar, selectedColor, selectedBgColor, getCell]);

  // Helper function to create a cell with all attributes (for shape tools)
  const createCellWithAllAttributes = useCallback((): Cell => {
    return {
      char: selectedChar,
      color: selectedColor,
      bgColor: selectedBgColor
    };
  }, [selectedChar, selectedColor, selectedBgColor]);

  // Bresenham line algorithm for drawing lines between two points
  const getLinePoints = useCallback((x0: number, y0: number, x1: number, y1: number) => {
    const points: { x: number; y: number }[] = [];

    const dx = Math.abs(x1 - x0);
    const dy = Math.abs(y1 - y0);
    const sx = x0 < x1 ? 1 : -1;
    const sy = y0 < y1 ? 1 : -1;
    let err = dx - dy;

    let x = x0;
    let y = y0;

    while (true) {
      points.push({ x, y });

      if (x === x1 && y === y1) break;

      const e2 = 2 * err;
      if (e2 > -dy) {
        err -= dy;
        x += sx;
      }
      if (e2 < dx) {
        err += dx;
        y += sy;
      }
    }

    return points;
  }, []);

  // Draw a line between two points using the line algorithm
  const drawLine = useCallback((x0: number, y0: number, x1: number, y1: number) => {
    const points = getLinePoints(x0, y0, x1, y1);
    points.forEach(({ x, y }) => {
      const newCell = createCellWithToggles(x, y);
      setCell(x, y, newCell);
    });
  }, [getLinePoints, setCell, createCellWithToggles]);

  const applyBrushStroke = useCallback((toolKey: 'pencil' | 'eraser', centerX: number, centerY: number) => {
    const brush = brushSettings[toolKey];
    const brushCells = calculateBrushCells(
      centerX,
      centerY,
      brush.size,
      brush.shape,
      fontMetrics.aspectRatio
    );

    if (toolKey === 'eraser') {
      brushCells.forEach(({ x, y }) => {
        clearCell(x, y);
      });
    } else {
      brushCells.forEach(({ x, y }) => {
        const newCell = createCellWithToggles(x, y);
        setCell(x, y, newCell);
      });
    }
  }, [brushSettings, fontMetrics.aspectRatio, clearCell, createCellWithToggles, setCell]);

  const applyBrushLine = useCallback((toolKey: 'pencil' | 'eraser', x0: number, y0: number, x1: number, y1: number) => {
    const points = getLinePoints(x0, y0, x1, y1);
    points.forEach(({ x, y }) => {
      applyBrushStroke(toolKey, x, y);
    });
  }, [getLinePoints, applyBrushStroke]);

  const drawBrushLine = useCallback((x0: number, y0: number, x1: number, y1: number) => {
    applyBrushLine('pencil', x0, y0, x1, y1);
  }, [applyBrushLine]);

  const eraseBrushLine = useCallback((x0: number, y0: number, x1: number, y1: number) => {
    applyBrushLine('eraser', x0, y0, x1, y1);
  }, [applyBrushLine]);

  const drawAtPosition = useCallback((x: number, y: number, isShiftClick = false, _isFirstStroke = false, toolOverride?: string) => {
    const toolToUse = toolOverride || activeTool;
    switch (toolToUse) {
      case 'pencil': {
        const brushTool: 'pencil' | 'eraser' = 'pencil';
        if (isShiftClick && pencilLastPosition) {
          applyBrushLine(brushTool, pencilLastPosition.x, pencilLastPosition.y, x, y);
        } else {
          applyBrushStroke(brushTool, x, y);
        }

        // Update position for potential shift+click line drawing
        setPencilLastPosition({ x, y });
        break;
      }
      case 'eraser': {
        const brushTool: 'pencil' | 'eraser' = 'eraser';
        if (isShiftClick && pencilLastPosition) {
          applyBrushLine(brushTool, pencilLastPosition.x, pencilLastPosition.y, x, y);
        } else {
          applyBrushStroke(brushTool, x, y);
        }
        // Update last position for eraser too
        setPencilLastPosition({ x, y });
        break;
      }
      case 'eyedropper': {
        const existingCell = getCell(x, y);
        if (existingCell) {
          pickFromCell(existingCell.char, existingCell.color, existingCell.bgColor);
        }
        break;
      }
      case 'paintbucket': {
        const newCell = createCellWithToggles(x, y);
        fillArea(x, y, newCell, paintBucketContiguous, { char: fillMatchChar, color: fillMatchColor, bgColor: fillMatchBgColor });
        break;
      }
    }
  }, [
    activeTool,
    paintBucketContiguous,
    getCell,
    fillArea,
    pickFromCell,
    pencilLastPosition,
    setPencilLastPosition,
    applyBrushStroke,
    applyBrushLine,
    createCellWithToggles,
    fillMatchChar,
    fillMatchColor,
    fillMatchBgColor
  ]);

  const drawRectangle = useCallback((startX: number, startY: number, endX: number, endY: number) => {
    const minX = Math.min(startX, endX);
    const maxX = Math.max(startX, endX);
    const minY = Math.min(startY, endY);
    const maxY = Math.max(startY, endY);

    for (let y = minY; y <= maxY; y++) {
      for (let x = minX; x <= maxX; x++) {
        // For hollow rectangles, only draw border
        if (!rectangleFilled) {
          if (x === minX || x === maxX || y === minY || y === maxY) {
            const newCell = createCellWithAllAttributes();
            setCell(x, y, newCell);
          }
        } else {
          // For filled rectangles, draw all cells
          const newCell = createCellWithAllAttributes();
          setCell(x, y, newCell);
        }
      }
    }
  }, [rectangleFilled, setCell, createCellWithAllAttributes]);

  // Helper function to get ellipse points using a simpler approach
  const getEllipsePoints = useCallback((centerX: number, centerY: number, radiusX: number, radiusY: number, filled: boolean = false) => {
    const points: Array<{ x: number; y: number }> = [];

    // Calculate bounding box
    const minX = Math.floor(centerX - radiusX);
    const maxX = Math.ceil(centerX + radiusX);
    const minY = Math.floor(centerY - radiusY);
    const maxY = Math.ceil(centerY + radiusY);

    if (filled) {
      // For filled ellipse, check each point within bounding box
      for (let y = minY; y <= maxY; y++) {
        for (let x = minX; x <= maxX; x++) {
          // Check if point is inside ellipse using ellipse equation
          const dx = x - centerX;
          const dy = y - centerY;
          const distance = (dx * dx) / (radiusX * radiusX) + (dy * dy) / (radiusY * radiusY);

          if (distance <= 1) {
            points.push({ x: Math.round(x), y: Math.round(y) });
          }
        }
      }
    } else {
      // For hollow ellipse, use a simple approach: check points around the perimeter
      const numPoints = Math.max(Math.ceil(2 * Math.PI * Math.max(radiusX, radiusY)), 20);

      for (let i = 0; i < numPoints; i++) {
        const angle = (2 * Math.PI * i) / numPoints;
        const x = centerX + radiusX * Math.cos(angle);
        const y = centerY + radiusY * Math.sin(angle);

        points.push({ x: Math.round(x), y: Math.round(y) });
      }

      // Remove duplicates by using a Set
      const uniquePoints = Array.from(
        new Set(points.map(p => `${p.x},${p.y}`))
      ).map(key => {
        const [x, y] = key.split(',').map(Number);
        return { x, y };
      });

      return uniquePoints;
    }

    return points;
  }, []);

  const drawEllipse = useCallback((startX: number, startY: number, endX: number, endY: number) => {
    const centerX = (startX + endX) / 2;
    const centerY = (startY + endY) / 2;
    const radiusX = Math.abs(endX - startX) / 2;
    const radiusY = Math.abs(endY - startY) / 2;

    const points = getEllipsePoints(centerX, centerY, radiusX, radiusY, rectangleFilled);

    // Draw all the ellipse points
    points.forEach(({ x, y }) => {
      if (x >= 0 && y >= 0) { // Basic bounds checking
        const newCell = createCellWithAllAttributes();
        setCell(x, y, newCell);
      }
    });
  }, [rectangleFilled, setCell, getEllipsePoints, createCellWithAllAttributes]);

  return {
    drawAtPosition,
    drawRectangle,
    drawEllipse,
    drawLine, // Export for gap-filling in drag operations
    drawBrushLine, // Export for brush-aware gap-filling
    eraseBrushLine, // Export for eraser gap-filling
    getEllipsePoints, // Export for preview rendering
    getLinePoints, // Export for line preview rendering
    activeTool
  };
};

import React, { useEffect, useCallback, useRef } from 'react';
import { useCanvasStore } from '../stores/canvasStore';
import { useAnimationStore } from '../stores/animationStore';
import { useToolStore } from '../stores/toolStore';
import type { Cell } from '../types';

/**
 * Hook that manages synchronization between canvas state and animation frames
 * - Auto-saves canvas changes to current frame
 * - Auto-loads frame data when switching frames
 * - Handles frame switching with proper data persistence
 */
export const useFrameSynchronization = (
  moveStateParam?: { 
    originalData: Map<string, Cell>;
    originalPositions: Set<string>;
    startPos: { x: number; y: number };
    baseOffset: { x: number; y: number };
    currentOffset: { x: number; y: number };
  } | null,
  setMoveStateParam?: React.Dispatch<React.SetStateAction<{ 
    originalData: Map<string, Cell>;
    originalPositions: Set<string>;
    startPos: { x: number; y: number };
    baseOffset: { x: number; y: number };
    currentOffset: { x: number; y: number };
  } | null>>
) => {
  const { cells, setCanvasData, width, height } = useCanvasStore();
  const { 
    currentFrameIndex, 
    setFrameData, 
    getFrameData, 
    getCurrentFrame,
    isPlaying,
    isDraggingFrame,
    isDeletingFrame
  } = useAnimationStore();

  // Get selection state and clearing functions  
  const { 
    selection,
    lassoSelection, 
    magicWandSelection,
    clearSelection,
    clearLassoSelection,
    clearMagicWandSelection
  } = useToolStore();

  const lastFrameIndexRef = useRef<number>(currentFrameIndex);
  const lastCellsRef = useRef<Map<string, Cell>>(new Map());
  const isLoadingFrameRef = useRef<boolean>(false);

  // Auto-save current canvas to current frame whenever canvas changes
  const saveCurrentCanvasToFrame = useCallback(() => {
    if (isLoadingFrameRef.current || isPlaying || isDraggingFrame || isDeletingFrame) return; // Don't save during frame loading, playback, dragging, or deletion

    // Add small delay to prevent race conditions during frame reordering
    setTimeout(() => {
      if (isLoadingFrameRef.current || isPlaying || isDraggingFrame || isDeletingFrame) return;

      const currentCells = new Map(cells);
      setFrameData(currentFrameIndex, currentCells);
      lastCellsRef.current = currentCells;
    }, 50);
  }, [cells, currentFrameIndex, setFrameData, isPlaying, isDraggingFrame, isDeletingFrame]);

  // Load frame data into canvas when frame changes
  const loadFrameToCanvas = useCallback((frameIndex: number) => {
    isLoadingFrameRef.current = true;

    const frameData = getFrameData(frameIndex);
    if (frameData) {
      setCanvasData(frameData);
    } else {
      // If frame has no data, clear canvas
      setCanvasData(new Map());
    }

    // Small delay to ensure canvas update completes
    setTimeout(() => {
      isLoadingFrameRef.current = false;
    }, 0);
  }, [getFrameData, setCanvasData]);

  // Handle frame switching
  useEffect(() => {
    const previousFrameIndex = lastFrameIndexRef.current;

    if (currentFrameIndex !== previousFrameIndex) {
      let currentCellsToSave = new Map(cells);

      // Commit any pending move operations to the original frame before clearing state
      if (moveStateParam && setMoveStateParam) {
        const totalOffset = {
          x: moveStateParam.baseOffset.x + moveStateParam.currentOffset.x,
          y: moveStateParam.baseOffset.y + moveStateParam.currentOffset.y
        };

        // Create a new canvas data map with the moved cells
        const newCells = new Map(cells);

        // Clear original positions
        const originalKeys = moveStateParam.originalPositions ?? new Set(moveStateParam.originalData.keys());
        originalKeys.forEach((key) => {
          newCells.delete(key);
        });

        // Place cells at new positions
        moveStateParam.originalData.forEach((cell, key) => {
          const [origX, origY] = key.split(',').map(Number);
          const newX = origX + totalOffset.x;
          const newY = origY + totalOffset.y;

          // Only place if within bounds
          if (newX >= 0 && newX < width && newY >= 0 && newY < height) {
            newCells.set(`${newX},${newY}`, cell);
          }
        });

        // Update the cells to save with the committed move
        currentCellsToSave = newCells;

        // Update canvas data with committed move
        setCanvasData(newCells);

        // Clear move state after committing
        setMoveStateParam(null);
      }

      // Clear all active selections when navigating between frames
      // This prevents selection content from being copied to the new frame
      if (selection.active) {
        clearSelection();
      }
      if (lassoSelection.active) {
        clearLassoSelection();
      }
      if (magicWandSelection.active) {
        clearMagicWandSelection();
      }

      // Save current canvas (with committed moves) to the frame we're leaving
      if (!isPlaying && !isLoadingFrameRef.current && !isDraggingFrame && !isDeletingFrame) {
        setFrameData(previousFrameIndex, currentCellsToSave);
      }

      // Load the new frame's data
      loadFrameToCanvas(currentFrameIndex);

      lastFrameIndexRef.current = currentFrameIndex;
    }
  }, [currentFrameIndex, cells, setFrameData, loadFrameToCanvas, isPlaying, isDraggingFrame, isDeletingFrame, moveStateParam, setMoveStateParam, selection.active, lassoSelection.active, magicWandSelection.active, clearSelection, clearLassoSelection, clearMagicWandSelection]);

  // Auto-save canvas changes to current frame (debounced)
  useEffect(() => {
    if (isLoadingFrameRef.current || isPlaying || isDraggingFrame || isDeletingFrame) return;

    // Check if cells actually changed to avoid unnecessary saves
    const currentCellsString = JSON.stringify(Array.from(cells.entries()).sort());
    const lastCellsString = JSON.stringify(Array.from(lastCellsRef.current.entries()).sort());

    if (currentCellsString !== lastCellsString) {
      // Longer delay to prevent interference with drag operations
      const timeoutId = setTimeout(() => {
        if (!isLoadingFrameRef.current && !isPlaying) {
          saveCurrentCanvasToFrame();
        }
      }, 150);

      return () => clearTimeout(timeoutId);
    }
  }, [cells, saveCurrentCanvasToFrame, isPlaying, isDraggingFrame, isDeletingFrame]);

  // Initialize first frame with current canvas data if empty
  useEffect(() => {
    const currentFrame = getCurrentFrame();
    if (currentFrame && currentFrame.data.size === 0 && cells.size > 0) {
      setFrameData(currentFrameIndex, new Map(cells));
    }
  }, []); // Only run once on mount

  return {
    saveCurrentCanvasToFrame,
    loadFrameToCanvas,
    isLoadingFrame: isLoadingFrameRef.current
  };
};

import { useCallback, useEffect } from 'react';
import { useCanvasStore } from '../stores/canvasStore';
import { useCanvasContext } from '../contexts/CanvasContext';
import { useGradientStore, initializeGradientWithCurrentValues } from '../stores/gradientStore';
import { useToolStore } from '../stores/toolStore';
import { useAnimationStore } from '../stores/animationStore';
import { calculateGradientCells } from '../utils/gradientEngine';
import { getGradientFillArea } from '../utils/fillArea';
import type { CanvasHistoryAction } from '../types';

/**
 * Custom hook for handling gradient fill tool operations
 * Integrates canvas interaction, fill area detection, gradient calculation, and undo/redo
 */
export const useGradientFillTool = () => {
  const { 
    cells,
    width: canvasWidth,
    height: canvasHeight,
    getCell, 
    setCanvasData 
  } = useCanvasStore();

  const { cellWidth, cellHeight } = useCanvasContext();

  const { currentFrameIndex } = useAnimationStore();

  const { 
    activeTool,
    selectedChar,
    selectedColor,
    selectedBgColor,
    pushToHistory
  } = useToolStore();

  const { 
    isApplying, 
    startPoint, 
    endPoint,
    ellipsePoint,
    hoverEndPoint,
    definition, 
    contiguous, 
    matchChar, 
    matchColor, 
    matchBgColor,
    previewData,
    setApplying, 
    setPoints, 
    setHoverEndPoint,
    setPreview,
    reset: resetGradient
  } = useGradientStore();

  const { isOpen } = useGradientStore();

  // Initialize gradient with current tool values when tool becomes active
  // Only initialize if there are no session settings (first time using the tool)
  // Wait for panel to be open to ensure session settings have been restored first
  useEffect(() => {
    if (activeTool === 'gradientfill' && isOpen) {
      const state = useGradientStore.getState();

      // Only initialize with current tool values if no session settings exist
      if (!state.sessionSettings) {
        initializeGradientWithCurrentValues(selectedChar, selectedColor, selectedBgColor);
      }
    }
  }, [activeTool, isOpen, selectedChar, selectedColor, selectedBgColor]);

  // Generate gradient preview
  const generatePreview = useCallback((start: { x: number; y: number }, end: { x: number; y: number }) => {
    try {
      // Find fill area using gradient matching criteria
      const fillArea = getGradientFillArea(
        start.x, 
        start.y,
        { width: canvasWidth, height: canvasHeight, getCell },
        { contiguous, matchChar, matchColor, matchBgColor }
      );

      if (fillArea.size === 0) {
        setPreview(null);
        return;
      }

      // Calculate gradient cells
      const cellAspectRatio = cellWidth / cellHeight;
      const gradientCells = calculateGradientCells({
        startPoint: start,
        endPoint: end,
        ellipsePoint: ellipsePoint || undefined,
        definition,
        fillArea,
        cellAspectRatio
      });

      setPreview(gradientCells);
    } catch (error) {
      console.error('Error generating gradient preview:', error);
      setPreview(null);
    }
  }, [
    canvasWidth,
    canvasHeight,
    cellWidth,
    cellHeight,
    getCell,
    contiguous,
    matchChar,
    matchColor,
    matchBgColor,
    definition,
    ellipsePoint,
    setPreview
  ]);

  // Apply gradient (Enter key or confirmation click)
  const applyGradient = useCallback(() => {
    if (!isApplying || !startPoint || !endPoint || !previewData) {
      console.warn('Cannot apply gradient: missing required state');
      return;
    }

    try {
      // Store current canvas state for undo
      const originalCells = new Map(cells);

      // Apply gradient to canvas
      const newCells = new Map(cells);
      previewData.forEach((cell, key) => {
        if (cell.char === ' ' && cell.color === '#FFFFFF' && cell.bgColor === 'transparent') {
          // Remove empty cells to save memory
          newCells.delete(key);
        } else {
          newCells.set(key, { ...cell });
        }
      });

      setCanvasData(newCells);

      // Add to history for undo/redo
      const historyAction: CanvasHistoryAction = {
        type: 'canvas_edit',
        timestamp: Date.now(),
        description: `Apply ${definition.type} gradient fill (${previewData.size} cells)`,
        data: {
          canvasData: originalCells,
          frameIndex: currentFrameIndex
        }
      };

      pushToHistory(historyAction);

      // Reset gradient state
      resetGradient();
    } catch (error) {
      console.error('Error applying gradient:', error);
      // Reset on error to prevent stuck state
      resetGradient();
    }
  }, [
    isApplying, 
    startPoint, 
    endPoint, 
    previewData, 
    cells, 
    currentFrameIndex,
    definition.type,
    setCanvasData, 
    pushToHistory, 
    resetGradient
  ]);

  // Handle canvas click during gradient application
  const handleCanvasClick = useCallback((x: number, y: number) => {
    if (activeTool !== 'gradientfill') return;

    // Ignore clicks on UI elements or outside canvas bounds
    if (x < 0 || x >= canvasWidth || y < 0 || y >= canvasHeight) return;

    if (!isApplying) {
      // First click - start applying gradient
      setApplying(true);
      const cellAspectRatio = cellWidth / cellHeight;
      setPoints({ x, y }, null, cellAspectRatio);
      setHoverEndPoint({ x, y });
      return;
    }

    if (startPoint && !endPoint) {
      // Second click - set end point and generate preview
      const newEndPoint = { x, y };
      const cellAspectRatio = cellWidth / cellHeight;
      setPoints(startPoint, newEndPoint, cellAspectRatio);
      setHoverEndPoint(null);
      generatePreview(startPoint, newEndPoint);
      return;
    }

    // If we already have both points, treat this as a confirmation click
    // (unless it's on a control point - that would be handled by drag logic)
    if (startPoint && endPoint && previewData) {
      applyGradient();
    }
  }, [
    activeTool, 
    isApplying, 
    startPoint, 
    endPoint, 
    previewData,
    canvasWidth,
    canvasHeight,
    setApplying, 
    setPoints,
    setHoverEndPoint,
    generatePreview,
    applyGradient
  ]);

  // Handle mouse move for interactive preview updates
  const handleCanvasMouseMove = useCallback((x: number, y: number) => {
    if (activeTool !== 'gradientfill' || !isApplying || !startPoint) return;

    if (!endPoint) {
      // Still dragging to set the end point - update preview with current mouse position
      const currentEndPoint = { x, y };
      setHoverEndPoint(currentEndPoint);
      generatePreview(startPoint, currentEndPoint);
    } else {
      // Both points are set - allow dragging to adjust them
      // For now, we'll regenerate preview if definition changes
      // TODO: Add drag detection for start/end point adjustment
    }
  }, [activeTool, isApplying, startPoint, endPoint, ellipsePoint, setHoverEndPoint, generatePreview]);

  // Cancel gradient (Escape key)
  const cancelGradient = useCallback(() => {
    if (!isApplying) return;
    resetGradient();
  }, [isApplying, resetGradient]);

  // Keyboard event handlers
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (activeTool !== 'gradientfill' || !isApplying) return;

      // Prevent default browser behavior for our handled keys
      if (event.key === 'Enter' || event.key === 'Escape') {
        event.preventDefault();
        event.stopPropagation();

        if (event.key === 'Enter') {
          applyGradient();
        } else if (event.key === 'Escape') {
          cancelGradient();
        }
      }
    };

    // Use capture phase to ensure we handle the event before other handlers
    window.addEventListener('keydown', handleKeyDown, { capture: true });

    return () => {
      window.removeEventListener('keydown', handleKeyDown, { capture: true });
    };
  }, [activeTool, isApplying, applyGradient, cancelGradient]);

  // Reset gradient state when switching tools
  useEffect(() => {
    if (activeTool !== 'gradientfill') {
      resetGradient();
    }
  }, [activeTool, resetGradient]);

  // Regenerate preview when gradient definition changes (while both points are set)
  useEffect(() => {
    const targetEndPoint = endPoint ?? hoverEndPoint;
    if (activeTool === 'gradientfill' && isApplying && startPoint && targetEndPoint) {
      generatePreview(startPoint, targetEndPoint);
    }
  }, [activeTool, isApplying, startPoint, endPoint, ellipsePoint, hoverEndPoint, definition, contiguous, matchChar, matchColor, matchBgColor, generatePreview]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      resetGradient();
    };
  }, [resetGradient]);

  return {
    // State
    isApplying,
    startPoint,
    endPoint,
  hoverEndPoint,
    previewData,

    // Actions
    handleCanvasClick,
    handleCanvasMouseMove,
    applyGradient,
    cancelGradient,

    // Computed properties
    canApply: isApplying && startPoint && endPoint && previewData && previewData.size > 0,
    fillAreaSize: previewData?.size || 0
  };
};

