import { useCallback, useEffect, useState } from 'react';
import { useToolStore } from '../stores/toolStore';
import { useCanvasStore } from '../stores/canvasStore';

export interface PastePreview {
  data: Map<string, any>;
  position: { x: number; y: number };
  bounds: {
    minX: number;
    maxX: number;
    minY: number;
    maxY: number;
  };
}

export interface PasteModeState {
  isActive: boolean;
  preview: PastePreview | null;
  isDragging: boolean;
  dragOffset?: { x: number; y: number };
  isPlaced: boolean; // Tracks if the preview has been "placed" by user interaction
}

/**
 * Hook for managing enhanced paste mode with visual preview and positioning
 */
export const usePasteMode = () => {
  const { 
    hasClipboard, 
    clipboard, 
    lassoClipboard, 
    hasLassoClipboard, 
    magicWandClipboard,
    hasMagicWandClipboard,
    clearSelection, 
    clearLassoSelection,
    clearMagicWandSelection,
    activeClipboardType
  } = useToolStore();
  const { cells, setCanvasData } = useCanvasStore();
  const [pasteMode, setPasteMode] = useState<PasteModeState>({
    isActive: false,
    preview: null,
    isDragging: false,
    isPlaced: false
  });

  // Get the active clipboard data (prioritize magic wand, then lasso, then regular clipboard)
  const getActiveClipboard = useCallback(() => {
    const priority: Array<'magicwand' | 'lasso' | 'rectangle'> = [];
    if (activeClipboardType) {
      priority.push(activeClipboardType);
    }
    priority.push('magicwand', 'lasso', 'rectangle');

    const seen = new Set<string>();

    for (const type of priority) {
      if (seen.has(type)) {
        continue;
      }
      seen.add(type);

      switch (type) {
        case 'magicwand':
          if (hasMagicWandClipboard() && magicWandClipboard) {
            return magicWandClipboard;
          }
          break;
        case 'lasso':
          if (hasLassoClipboard() && lassoClipboard) {
            return lassoClipboard;
          }
          break;
        case 'rectangle':
          if (clipboard) {
            return clipboard;
          }
          break;
      }
    }

    return null;
  }, [activeClipboardType, hasMagicWandClipboard, magicWandClipboard, hasLassoClipboard, lassoClipboard, clipboard]);

  /**
   * Calculate bounds of clipboard data
   */
  const calculateClipboardBounds = useCallback((clipboardData: Map<string, any>) => {
    if (!clipboardData || clipboardData.size === 0) {
      return { minX: 0, maxX: 0, minY: 0, maxY: 0 };
    }

    const coords = Array.from(clipboardData.keys()).map(key => {
      const [x, y] = key.split(',').map(Number);
      return { x, y };
    });

    const minX = Math.min(...coords.map(c => c.x));
    const maxX = Math.max(...coords.map(c => c.x));
    const minY = Math.min(...coords.map(c => c.y));
    const maxY = Math.max(...coords.map(c => c.y));

    return { minX, maxX, minY, maxY };
  }, []);

  /**
   * Start paste mode - show preview at specified position
   */
  const startPasteMode = useCallback((initialPosition: { x: number; y: number }) => {
    if (!hasClipboard() && !hasLassoClipboard() && !hasMagicWandClipboard()) {
      return false;
    }

    const activeClipboard = getActiveClipboard();
    if (!activeClipboard) {
      return false;
    }

    // Clear any existing selections when entering paste mode
    clearSelection();
    clearLassoSelection();
    clearMagicWandSelection();

    const bounds = calculateClipboardBounds(activeClipboard);

    setPasteMode({
      isActive: true,
      preview: {
        data: new Map(activeClipboard),
        position: initialPosition,
        bounds
      },
      isDragging: false,
      dragOffset: undefined,
      isPlaced: false
    });

    return true;
  }, [hasClipboard, hasLassoClipboard, hasMagicWandClipboard, getActiveClipboard, clearSelection, clearLassoSelection, clearMagicWandSelection, calculateClipboardBounds]);

  /**
   * Update paste preview position
   */
  const updatePastePosition = useCallback((mousePosition: { x: number; y: number }) => {
    setPasteMode(prev => {
      if (!prev.isActive || !prev.preview) return prev;

      // When dragging, apply the drag offset to maintain relative positioning
      if (prev.isDragging && prev.dragOffset) {
        const newPosition = {
          x: mousePosition.x - prev.dragOffset.x,
          y: mousePosition.y - prev.dragOffset.y
        };

        return {
          ...prev,
          preview: {
            ...prev.preview,
            position: newPosition
          }
        };
      }

      // When not dragging, don't update position (preview stays where it was placed)
      return prev;
    });
  }, []);

  /**
   * Start dragging the paste preview
   */
  const startPasteDrag = useCallback((clickPosition: { x: number; y: number }) => {
    setPasteMode(prev => {
      if (!prev.isActive || !prev.preview) return prev;

      // Calculate offset between click position and current preview position
      const dragOffset = {
        x: clickPosition.x - prev.preview.position.x,
        y: clickPosition.y - prev.preview.position.y
      };

      return {
        ...prev,
        isDragging: true,
        dragOffset
      };
    });
  }, []);

  /**
   * Stop dragging the paste preview
   */
  const stopPasteDrag = useCallback(() => {
    setPasteMode(prev => ({
      ...prev,
      isDragging: false,
      dragOffset: undefined
    }));
  }, []);

  /**
   * Cancel paste mode without committing
   */
  const cancelPasteMode = useCallback(() => {
    setPasteMode({
      isActive: false,
      preview: null,
      isDragging: false,
      dragOffset: undefined,
      isPlaced: false
    });
  }, []);

  /**
   * Commit paste at current preview position
   */
  const commitPaste = useCallback(() => {
    if (!pasteMode.isActive || !pasteMode.preview) {
      return null;
    }

    const { data, position } = pasteMode.preview;
    const pastedData = new Map<string, any>();

    // Transform clipboard data to absolute positions
    data.forEach((cell, relativeKey) => {
      const [relX, relY] = relativeKey.split(',').map(Number);
      const absoluteKey = `${position.x + relX},${position.y + relY}`;
      pastedData.set(absoluteKey, cell);
    });

    // Clear paste mode
    cancelPasteMode();

    return pastedData;
  }, [pasteMode, cancelPasteMode]);

  /**
   * Commit paste and apply to canvas - used for keyboard shortcuts
   */
  const commitPasteToCanvas = useCallback(() => {
    if (!pasteMode.isActive || !pasteMode.preview) {
      return false;
    }

    const pastedData = commitPaste();
    if (pastedData) {
      // Apply the paste to canvas
      const currentCells = new Map(cells);
      pastedData.forEach((cell, key) => {
        currentCells.set(key, cell);
      });
      setCanvasData(currentCells);
      return true;
    }
    return false;
  }, [pasteMode, commitPaste, cells, setCanvasData]);

  /**
   * Handle keyboard shortcuts for paste mode
   */
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (!pasteMode.isActive || !pasteMode.preview) return;

      switch (event.key) {
        case 'Escape':
          event.preventDefault();
          cancelPasteMode();
          break;
        case 'Enter':
          event.preventDefault();
          // Commit paste and apply to canvas
          commitPasteToCanvas();
          break;
        case 'ArrowUp':
        case 'ArrowDown':
        case 'ArrowLeft':
        case 'ArrowRight':
          event.preventDefault();
          event.stopPropagation();

          // Calculate arrow direction offset
          let offsetX = 0;
          let offsetY = 0;

          switch (event.key) {
            case 'ArrowUp':
              offsetY = -1;
              break;
            case 'ArrowDown':
              offsetY = 1;
              break;
            case 'ArrowLeft':
              offsetX = -1;
              break;
            case 'ArrowRight':
              offsetX = 1;
              break;
          }

          // Update paste preview position
          setPasteMode(prev => {
            if (!prev.preview) return prev;

            const newPosition = {
              x: prev.preview.position.x + offsetX,
              y: prev.preview.position.y + offsetY
            };

            return {
              ...prev,
              preview: {
                ...prev.preview,
                position: newPosition
              }
            };
          });
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [pasteMode.isActive, pasteMode.preview, cancelPasteMode, commitPasteToCanvas]);

  return {
    // State
    pasteMode,
    isInPasteMode: pasteMode.isActive,
    pastePreview: pasteMode.preview,
    isPasteDragging: pasteMode.isDragging,

    // Actions
    startPasteMode,
    updatePastePosition,
    startPasteDrag,
    stopPasteDrag,
    cancelPasteMode,
    commitPaste
  };
};

import { useCallback, useEffect, useRef } from 'react';
import { useToolStore } from '../stores/toolStore';
import { useCanvasStore } from '../stores/canvasStore';
import { useAnimationStore } from '../stores/animationStore';

/**
 * Text Tool Hook - Handles text input functionality
 * 
 * Features:
 * - Click to place cursor and start typing
 * - Arrow key navigation with boundary constraints
 * - Enter key for new lines (moves to line start)
 * - Backspace with line boundary stopping
 * - Word-based undo batching
 * - Purple blinking cursor animation
 * - Clipboard paste support with overwrite behavior
 */
export const useTextTool = () => {
  const { textToolState, startTyping, stopTyping, setCursorPosition, setCursorVisible, setTextBuffer, commitWord, pushCanvasHistory } = useToolStore();
  const { width, height, setCell, getCell, cells } = useCanvasStore();
  const { currentFrameIndex } = useAnimationStore();
  const { selectedColor, selectedBgColor } = useToolStore();

  const blinkTimerRef = useRef<NodeJS.Timeout | null>(null);
  const wordBoundaryChars = useRef(new Set([' ', '\t', '\n', '.', ',', ';', ':', '!', '?', '"', "'", '(', ')', '[', ']', '{', '}', '<', '>', '/', '\\', '|', '@', '#', '$', '%', '^', '&', '*', '+', '=', '-', '_', '~', '`']));

  // Helper function to create a cell with all attributes for text tool
  const createTextCellWithAllAttributes = useCallback((newChar: string): { char: string, color: string, bgColor: string } => {
    // Only apply color data if the character is not just a space
    const shouldApplyColors = newChar !== ' ';

    return {
      char: newChar,
      color: shouldApplyColors ? selectedColor : '#FFFFFF',
      bgColor: shouldApplyColors ? selectedBgColor : 'transparent'
    };
  }, [selectedColor, selectedBgColor]);

  // Cursor blink animation
  useEffect(() => {
    if (textToolState.isTyping && textToolState.cursorPosition) {
      // Clear any existing timer
      if (blinkTimerRef.current) {
        clearInterval(blinkTimerRef.current);
      }

      // Start blinking animation (500ms interval)
      blinkTimerRef.current = setInterval(() => {
        setCursorVisible(!textToolState.cursorVisible);
      }, 500);

      return () => {
        if (blinkTimerRef.current) {
          clearInterval(blinkTimerRef.current);
        }
      };
    }
  }, [textToolState.isTyping, textToolState.cursorPosition, setCursorVisible, textToolState.cursorVisible]);

  // Reset cursor to visible when moving
  const resetCursorBlink = useCallback(() => {
    setCursorVisible(true);
    if (blinkTimerRef.current) {
      clearInterval(blinkTimerRef.current);
      blinkTimerRef.current = setInterval(() => {
        setCursorVisible(!textToolState.cursorVisible);
      }, 500);
    }
  }, [setCursorVisible, textToolState.cursorVisible]);

  // Check if character is a word boundary
  const isWordBoundary = useCallback((char: string) => {
    return wordBoundaryChars.current.has(char);
  }, []);

  // Commit current word to undo stack
  const commitCurrentWord = useCallback(() => {
    if (textToolState.textBuffer.length > 0) {
      pushCanvasHistory(cells, currentFrameIndex);
      commitWord();
    }
  }, [textToolState.textBuffer.length, pushCanvasHistory, commitWord, cells, currentFrameIndex]);

  // Move cursor with boundary constraints
  const moveCursor = useCallback((deltaX: number, deltaY: number) => {
    if (!textToolState.cursorPosition) return;

    const { x, y } = textToolState.cursorPosition;
    let newX = x + deltaX;
    let newY = y + deltaY;

    // Boundary constraints - stop at edges
    newX = Math.max(0, Math.min(width - 1, newX));
    newY = Math.max(0, Math.min(height - 1, newY));

    // Don't move if we're at the boundary
    if (newX !== x + deltaX || newY !== y + deltaY) {
      return; // Hit boundary, don't move
    }

    setCursorPosition(newX, newY);
    resetCursorBlink();
  }, [textToolState.cursorPosition, width, height, setCursorPosition, resetCursorBlink]);

  // Insert character at cursor position
  const insertCharacter = useCallback((char: string) => {
    if (!textToolState.cursorPosition) return;

    const { x, y } = textToolState.cursorPosition;

    // Check if character causes word boundary - commit current word if so
    if (isWordBoundary(char)) {
      commitCurrentWord();
    }

    // Insert character using selected colors
    const newCell = createTextCellWithAllAttributes(char);
    setCell(x, y, newCell);

    // Add to text buffer for undo batching
    setTextBuffer(textToolState.textBuffer + char);

    // Move cursor right, respecting canvas boundaries
    const newX = x + 1;
    if (newX < width) {
      setCursorPosition(newX, y);
      resetCursorBlink();
    }
    // If at right edge, don't move cursor (content extends beyond canvas)
  }, [textToolState.cursorPosition, textToolState.textBuffer, isWordBoundary, commitCurrentWord, setCell, setTextBuffer, width, setCursorPosition, resetCursorBlink, createTextCellWithAllAttributes]);

  // Handle Enter key - move to next line at line start
  const handleEnter = useCallback(() => {
    if (!textToolState.cursorPosition) return;

    const { y } = textToolState.cursorPosition;
    const newY = y + 1;

    // Commit current word
    commitCurrentWord();

    // Move to next line at lineStartX, respecting boundaries
    if (newY < height) {
      setCursorPosition(textToolState.lineStartX, newY);
      resetCursorBlink();
    }
    // If at bottom edge, don't move cursor
  }, [textToolState.cursorPosition, textToolState.lineStartX, height, commitCurrentWord, setCursorPosition, resetCursorBlink]);

  // Handle Backspace - delete previous character with line boundary stopping
  const handleBackspace = useCallback(() => {
    if (!textToolState.cursorPosition) return;

    const { x, y } = textToolState.cursorPosition;

    // Can't backspace at position (0, 0)
    if (x === 0 && y === 0) return;

    let targetX = x - 1;
    let targetY = y;

    // If at beginning of line, stop (don't wrap to previous line)
    if (x === 0) {
      return;
    }

    // Get the character we're about to delete
    const cellToDelete = getCell(targetX, targetY);

    // If deleting a word boundary character, commit current word
    if (cellToDelete && isWordBoundary(cellToDelete.char)) {
      commitCurrentWord();
    }

    // Clear the cell
    const newCell = createTextCellWithAllAttributes(' ');
    setCell(targetX, targetY, newCell);

    // Move cursor to deleted position
    setCursorPosition(targetX, targetY);
    resetCursorBlink();

    // Update text buffer (remove last character)
    const newBuffer = textToolState.textBuffer.slice(0, -1);
    setTextBuffer(newBuffer);
  }, [textToolState.cursorPosition, textToolState.textBuffer, getCell, isWordBoundary, commitCurrentWord, setCell, setCursorPosition, resetCursorBlink, setTextBuffer, createTextCellWithAllAttributes]);

  // Handle clipboard paste
  const handlePaste = useCallback(async () => {
    if (!textToolState.cursorPosition) return;

    try {
      const clipboardText = await navigator.clipboard.readText();
      if (!clipboardText) return;

      const { x: startX, y: startY } = textToolState.cursorPosition;
      let currentX = startX;
      let currentY = startY;

      // Commit current word before pasting
      commitCurrentWord();

      // Process each character in clipboard
      for (const char of clipboardText) {
        if (char === '\n' || char === '\r') {
          // Handle line breaks - move to next line at lineStartX
          currentY++;
          currentX = textToolState.lineStartX;

          // Stop if we reach bottom boundary
          if (currentY >= height) break;
        } else {
          // Insert character if within bounds
          if (currentX < width && currentY < height) {
            const newCell = createTextCellWithAllAttributes(char);
            setCell(currentX, currentY, newCell);
            currentX++;
          }
          // Continue processing even if beyond width (content extends beyond canvas)
        }
      }

      // Position cursor at end of pasted content
      if (currentY < height) {
        const finalX = Math.min(currentX, width - 1);
        setCursorPosition(finalX, currentY);
        resetCursorBlink();
      }

      // Commit paste as single undo operation
      pushCanvasHistory(cells, currentFrameIndex);

    } catch (error) {
      console.error('Failed to read clipboard:', error);
    }
  }, [textToolState.cursorPosition, textToolState.lineStartX, width, height, commitCurrentWord, setCell, setCursorPosition, resetCursorBlink, pushCanvasHistory, cells, currentFrameIndex, createTextCellWithAllAttributes]);

  // Click to place cursor
  const handleTextToolClick = useCallback((x: number, y: number) => {
    // Commit current word if switching positions
    if (textToolState.isTyping) {
      commitCurrentWord();
    }

    startTyping(x, y);
    resetCursorBlink();
  }, [textToolState.isTyping, commitCurrentWord, startTyping, resetCursorBlink]);

  // Handle keyboard input
  const handleTextToolKeyDown = useCallback((event: KeyboardEvent) => {
    if (!textToolState.isTyping) return;

    // Prevent default for keys we handle
    const handledKeys = ['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', 'Enter', 'Backspace', 'Escape'];
    if (handledKeys.includes(event.key)) {
      event.preventDefault();
    }

    switch (event.key) {
      case 'ArrowLeft':
        moveCursor(-1, 0);
        break;
      case 'ArrowRight':
        moveCursor(1, 0);
        break;
      case 'ArrowUp':
        moveCursor(0, -1);
        break;
      case 'ArrowDown':
        moveCursor(0, 1);
        break;
      case 'Enter':
        handleEnter();
        break;
      case 'Backspace':
        handleBackspace();
        break;
      case 'Escape':
        commitCurrentWord();
        stopTyping();
        break;
      default:
        // Handle regular character input
        if (event.key.length === 1 && !event.ctrlKey && !event.metaKey) {
          event.preventDefault();
          insertCharacter(event.key);
        }
        // Handle Ctrl/Cmd+V for paste
        else if ((event.ctrlKey || event.metaKey) && event.key === 'v') {
          event.preventDefault();
          handlePaste();
        }
        break;
    }
  }, [textToolState.isTyping, moveCursor, handleEnter, handleBackspace, commitCurrentWord, stopTyping, insertCharacter, handlePaste]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (blinkTimerRef.current) {
        clearInterval(blinkTimerRef.current);
      }
    };
  }, []);

  return {
    // State
    isTyping: textToolState.isTyping,
    cursorPosition: textToolState.cursorPosition,
    cursorVisible: textToolState.cursorVisible,
    textBuffer: textToolState.textBuffer,

    // Actions
    handleTextToolClick,
    handleTextToolKeyDown,
    commitCurrentWord,

    // Utilities
    isWordBoundary
  };
};

/**
 * Effects Store - Zustand store for managing effects system state
 * 
 * Features:
 * - Effects panel state (open/closed, active effect)
 * - Effect settings for all supported effects
 * - Canvas analysis caching for performance
 * - Timeline targeting toggle
 * - Integration with existing stores for apply/preview operations
 */

import { create } from 'zustand';
import type { 
  EffectType, 
  LevelsEffectSettings, 
  HueSaturationEffectSettings, 
  RemapColorsEffectSettings, 
  RemapCharactersEffectSettings,
  CanvasAnalysis
} from '../types/effects';
import { 
  DEFAULT_LEVELS_SETTINGS,
  DEFAULT_HUE_SATURATION_SETTINGS,
  DEFAULT_REMAP_COLORS_SETTINGS,
  DEFAULT_REMAP_CHARACTERS_SETTINGS,
  CANVAS_ANALYSIS
} from '../constants/effectsDefaults';
import { useCanvasStore } from './canvasStore';
import { usePreviewStore } from './previewStore';
import { processEffect } from '../utils/effectsProcessing';

export interface EffectsState {
  // UI State
  isOpen: boolean;                           // Main effects panel visibility
  activeEffect: EffectType | null;           // Currently open effect panel
  applyToTimeline: boolean;                  // Timeline vs canvas targeting

  // Effect Settings State
  levelsSettings: LevelsEffectSettings;
  hueSaturationSettings: HueSaturationEffectSettings;
  remapColorsSettings: RemapColorsEffectSettings;
  remapCharactersSettings: RemapCharactersEffectSettings;

  // Canvas Analysis State
  canvasAnalysis: CanvasAnalysis | null;     // Cached analysis results
  isAnalyzing: boolean;                      // Analysis in progress

  // Preview State
  isPreviewActive: boolean;                  // Live preview enabled
  previewEffect: EffectType | null;          // Effect being previewed

  // Error State
  lastError: string | null;                  // Last error message

  // Actions - Panel Management
  openEffectPanel: (effect: EffectType) => void;
  closeEffectPanel: () => void;
  setApplyToTimeline: (apply: boolean) => void;

  // Actions - Effect Settings
  updateLevelsSettings: (settings: Partial<LevelsEffectSettings>) => void;
  updateHueSaturationSettings: (settings: Partial<HueSaturationEffectSettings>) => void;
  updateRemapColorsSettings: (settings: Partial<RemapColorsEffectSettings>) => void;
  updateRemapCharactersSettings: (settings: Partial<RemapCharactersEffectSettings>) => void;
  resetEffectSettings: (effect: EffectType) => void;

  // Actions - Canvas Analysis
  analyzeCanvas: () => Promise<void>;
  clearAnalysisCache: () => void;
  getUniqueColors: () => string[];
  getUniqueCharacters: () => string[];

  // Actions - Preview Management
  startPreview: (effect: EffectType) => void;
  stopPreview: () => void;
  updatePreview: () => Promise<void>;

  // Actions - Effect Application
  applyEffect: (effect: EffectType) => Promise<boolean>;

  // Actions - Error Management
  clearError: () => void;

  // Utility Actions
  reset: () => void;
}

// Canvas hash generation for cache invalidation
const generateCanvasHash = (cells: Map<string, any>, frameCount: number): string => {
  // Simple hash based on cell count, first few cells, and frame count
  const cellCount = cells.size;
  const firstCells = Array.from(cells.entries()).slice(0, 10);
  const hashData = `${cellCount}-${frameCount}-${JSON.stringify(firstCells)}`;

  // Simple hash function
  let hash = 0;
  for (let i = 0; i < hashData.length; i++) {
    const char = hashData.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash; // Convert to 32-bit integer
  }
  return Math.abs(hash).toString(36);
};

export const useEffectsStore = create<EffectsState>((set, get) => ({
  // Initial state
  isOpen: false,
  activeEffect: null,
  applyToTimeline: false,

  // Default effect settings
  levelsSettings: { ...DEFAULT_LEVELS_SETTINGS },
  hueSaturationSettings: { ...DEFAULT_HUE_SATURATION_SETTINGS },
  remapColorsSettings: { ...DEFAULT_REMAP_COLORS_SETTINGS },
  remapCharactersSettings: { ...DEFAULT_REMAP_CHARACTERS_SETTINGS },

  // Analysis state
  canvasAnalysis: null,
  isAnalyzing: false,

  // Preview state
  isPreviewActive: false,
  previewEffect: null,

  // Error state
  lastError: null,

  // Panel Management Actions
  openEffectPanel: (effect: EffectType) => {
    set({ 
      isOpen: true, 
      activeEffect: effect 
    });

    // Analyze canvas when opening any effect
    get().analyzeCanvas();
  },

  closeEffectPanel: () => {
    // Stop any active preview when closing
    if (get().isPreviewActive) {
      get().stopPreview();
    }

    set({ 
      isOpen: false, 
      activeEffect: null 
    });
  },

  setApplyToTimeline: (apply: boolean) => {
    set({ applyToTimeline: apply });
  },

  // Effect Settings Actions
  updateLevelsSettings: (settings: Partial<LevelsEffectSettings>) => {
    set(state => ({
      levelsSettings: { ...state.levelsSettings, ...settings }
    }));
  },

  updateHueSaturationSettings: (settings: Partial<HueSaturationEffectSettings>) => {
    set(state => ({
      hueSaturationSettings: { ...state.hueSaturationSettings, ...settings }
    }));
  },

  updateRemapColorsSettings: (settings: Partial<RemapColorsEffectSettings>) => {
    set(state => ({
      remapColorsSettings: { ...state.remapColorsSettings, ...settings }
    }));
  },

  updateRemapCharactersSettings: (settings: Partial<RemapCharactersEffectSettings>) => {
    set(state => ({
      remapCharactersSettings: { ...state.remapCharactersSettings, ...settings }
    }));
  },

  resetEffectSettings: (effect: EffectType) => {
    switch (effect) {
      case 'levels':
        set({ levelsSettings: { ...DEFAULT_LEVELS_SETTINGS } });
        break;
      case 'hue-saturation':
        set({ hueSaturationSettings: { ...DEFAULT_HUE_SATURATION_SETTINGS } });
        break;
      case 'remap-colors':
        set({ remapColorsSettings: { ...DEFAULT_REMAP_COLORS_SETTINGS } });
        break;
      case 'remap-characters':
        set({ remapCharactersSettings: { ...DEFAULT_REMAP_CHARACTERS_SETTINGS } });
        break;
    }
  },

  // Canvas Analysis Actions
  analyzeCanvas: async () => {
    // Avoid concurrent analysis
    if (get().isAnalyzing) return;

    set({ isAnalyzing: true });

    try {
      // Import stores dynamically to avoid circular dependencies
      const { useCanvasStore } = await import('../stores/canvasStore');
      const { useAnimationStore } = await import('../stores/animationStore');

      const canvasStore = useCanvasStore.getState();
      const animationStore = useAnimationStore.getState();

      const { cells } = canvasStore;
      const { frames } = animationStore;

      // Generate hash for cache invalidation
      const canvasHash = generateCanvasHash(cells, frames.length);

      // Check if analysis is still valid
      const currentAnalysis = get().canvasAnalysis;
      if (currentAnalysis && 
          currentAnalysis.canvasHash === canvasHash &&
          (Date.now() - currentAnalysis.analysisTimestamp) < CANVAS_ANALYSIS.CACHE_EXPIRY_MS) {
        set({ isAnalyzing: false });
        return;
      }

      // Analyze canvas for unique colors and characters
      const uniqueColors = new Set<string>();
      const uniqueCharacters = new Set<string>();
      const colorFrequency: Record<string, number> = {};
      const characterFrequency: Record<string, number> = {};

      // Analyze current canvas
      cells.forEach(cell => {
        // Track colors
        if (cell.color && cell.color !== 'transparent') {
          uniqueColors.add(cell.color);
          colorFrequency[cell.color] = (colorFrequency[cell.color] || 0) + 1;
        }
        if (cell.bgColor && cell.bgColor !== 'transparent') {
          uniqueColors.add(cell.bgColor);
          colorFrequency[cell.bgColor] = (colorFrequency[cell.bgColor] || 0) + 1;
        }

        // Track characters
        if (cell.char && cell.char.trim()) {
          uniqueCharacters.add(cell.char);
          characterFrequency[cell.char] = (characterFrequency[cell.char] || 0) + 1;
        }
      });

      // If applying to timeline, analyze all frames
      if (get().applyToTimeline) {
        frames.forEach(frame => {
          frame.data.forEach(cell => {
            // Same analysis for timeline frames
            if (cell.color && cell.color !== 'transparent') {
              uniqueColors.add(cell.color);
              colorFrequency[cell.color] = (colorFrequency[cell.color] || 0) + 1;
            }
            if (cell.bgColor && cell.bgColor !== 'transparent') {
              uniqueColors.add(cell.bgColor);
              colorFrequency[cell.bgColor] = (colorFrequency[cell.bgColor] || 0) + 1;
            }
            if (cell.char && cell.char.trim()) {
              uniqueCharacters.add(cell.char);
              characterFrequency[cell.char] = (characterFrequency[cell.char] || 0) + 1;
            }
          });
        });
      }

      // Create analysis results
      const analysis: CanvasAnalysis = {
        // Basic unique values
        uniqueColors: Array.from(uniqueColors).slice(0, CANVAS_ANALYSIS.MAX_UNIQUE_ITEMS),
        uniqueCharacters: Array.from(uniqueCharacters).slice(0, CANVAS_ANALYSIS.MAX_UNIQUE_ITEMS),

        // Frequency data (original simple format)
        colorFrequency,
        characterFrequency,

        // Extended frequency data (sorted arrays) - add these for compatibility
        colorsByFrequency: Object.entries(colorFrequency)
          .map(([color, count]) => ({ color, count }))
          .sort((a, b) => b.count - a.count),
        charactersByFrequency: Object.entries(characterFrequency)
          .map(([char, count]) => ({ char, count }))
          .sort((a, b) => b.count - a.count),

        // Distribution data with percentages
        colorDistribution: Object.entries(colorFrequency)
          .map(([color, count]) => ({ 
            color, 
            count, 
            percentage: cells.size > 0 ? (count / cells.size) * 100 : 0 
          }))
          .sort((a, b) => b.count - a.count),
        characterDistribution: Object.entries(characterFrequency)
          .map(([char, count]) => ({ 
            char, 
            count, 
            percentage: cells.size > 0 ? (count / cells.size) * 100 : 0 
          }))
          .sort((a, b) => b.count - a.count),

        // Cross-reference mappings - simplified for now
        colorToCharMap: {},
        charToColorMap: {},

        // Canvas statistics
        totalCells: cells.size,
        filledCells: cells.size, // Simplified - all cells with data are filled
        fillPercentage: 100,

        // Color analysis - simplified for now
        colorBrightnessStats: {
          brightest: '',
          darkest: '',
          averageBrightness: 0,
          brightColors: [],
          darkColors: []
        },

        // Metadata
        canvasHash,
        frameCount: frames.length,
        analysisTimestamp: Date.now()
      };

      set({ 
        canvasAnalysis: analysis,
        isAnalyzing: false 
      });

    } catch (error) {
      console.error('Canvas analysis failed:', error);
      set({ isAnalyzing: false });
    }
  },

  clearAnalysisCache: () => {
    set({ canvasAnalysis: null });
  },

  getUniqueColors: () => {
    return get().canvasAnalysis?.uniqueColors || [];
  },

  getUniqueCharacters: () => {
    return get().canvasAnalysis?.uniqueCharacters || [];
  },

  // Preview Management Actions
  startPreview: (effect: EffectType) => {
    const { updatePreview } = get();
    set({ 
      isPreviewActive: true, 
      previewEffect: effect 
    });

    // Generate and show preview immediately
    updatePreview().catch(error => {
      console.error('Initial preview generation failed:', error);
    });
  },

  stopPreview: () => {
    set({ 
      isPreviewActive: false, 
      previewEffect: null 
    });

    // Clear preview from previewStore
    const previewStore = usePreviewStore.getState();
    previewStore.clearPreview();
  },

  // Generate and update preview
  updatePreview: async () => {
    const state = get();
    if (!state.isPreviewActive || !state.previewEffect) return;

    try {
      const canvasStore = useCanvasStore.getState();
      const previewStore = usePreviewStore.getState();

      // Get current canvas data
      const currentCells = canvasStore.cells;

      // Get effect settings
      let effectSettings;
      switch (state.previewEffect) {
        case 'levels':
          effectSettings = state.levelsSettings;
          break;
        case 'hue-saturation':
          effectSettings = state.hueSaturationSettings;
          break;
        case 'remap-colors':
          effectSettings = state.remapColorsSettings;
          break;
        case 'remap-characters':
          effectSettings = state.remapCharactersSettings;
          break;
        default:
          return;
      }

      // Process effect on current canvas data (await the async function)
      const result = await processEffect(
        state.previewEffect,
        currentCells, 
        effectSettings,
        canvasStore.width,
        canvasStore.height
      );

      // Update preview store with processed cells if successful
      if (result.success && result.processedCells) {
        previewStore.setPreviewData(result.processedCells);
      } else {
        console.error('Preview processing failed:', result);
        previewStore.clearPreview();
      }

    } catch (error) {
      console.error('Preview generation error:', error);
      set({ lastError: error instanceof Error ? error.message : 'Preview generation failed' });
    }
  },

  // Effect Application Actions
  applyEffect: async (effect: EffectType): Promise<boolean> => {
    try {
      const state = get();

      // Clear any previous errors
      state.clearError();

      // Stop preview if active
      if (state.isPreviewActive) {
        state.stopPreview();
      }

      // Get effect settings
      const getEffectSettings = () => {
        switch (effect) {
          case 'levels':
            return state.levelsSettings;
          case 'hue-saturation':
            return state.hueSaturationSettings;
          case 'remap-colors':
            return state.remapColorsSettings;
          case 'remap-characters':
            return state.remapCharactersSettings;
          default:
            throw new Error(`Unknown effect type: ${effect}`);
        }
      };

      const settings = getEffectSettings();

      // Import processing engine dynamically
      const { processEffect, processEffectOnFrames } = await import('../utils/effectsProcessing');

      if (state.applyToTimeline) {
        // Apply to entire timeline
        const { useAnimationStore } = await import('./animationStore');
        const { useCanvasStore } = await import('./canvasStore');

        const animationStore = useAnimationStore.getState();
        const canvasStore = useCanvasStore.getState();

        console.log(`Applying ${effect} effect to ${animationStore.frames.length} frames...`);

        const result = await processEffectOnFrames(
          effect,
          animationStore.frames,
          settings,
          canvasStore.width,
          canvasStore.height,
          (frameIndex, totalFrames) => {
            console.log(`Processing frame ${frameIndex + 1}/${totalFrames}`);
          }
        );

        if (result.errors.length > 0) {
          console.warn('Effect processing had errors:', result.errors);
        }

        // Update animation store with processed frames
        // Use the set function directly to update frames
        useAnimationStore.setState((state) => ({
          ...state,
          frames: result.processedFrames
        }));

        // Sync the canvas with the processed current frame
        const updatedAnimationStore = useAnimationStore.getState();
        const currentFrame = updatedAnimationStore.frames[updatedAnimationStore.currentFrameIndex];
        if (currentFrame) {
          const { useCanvasStore } = await import('./canvasStore');
          const canvasStore = useCanvasStore.getState();
          canvasStore.setCanvasData(currentFrame.data);
        }

        console.log(`âœ… Applied ${effect} to timeline: ${result.totalAffectedCells} cells modified in ${result.processingTime.toFixed(2)}ms`);

      } else {
        // Apply to current canvas only
        const { useCanvasStore } = await import('./canvasStore');
        const canvasStore = useCanvasStore.getState();

        console.log(`Applying ${effect} effect to current canvas...`);

        const result = await processEffect(
          effect,
          canvasStore.cells,
          settings,
          canvasStore.width,
          canvasStore.height
        );

        if (result.success && result.processedCells) {
          // Update canvas store with processed cells
          const { setCanvasData } = canvasStore;
          setCanvasData(result.processedCells);

          console.log(`âœ… Applied ${effect} to canvas: ${result.affectedCells} cells modified in ${result.processingTime.toFixed(2)}ms`);
        } else {
          throw new Error(result.error || 'Effect processing failed');
        }
      }

      // Clear analysis cache since canvas changed
      state.clearAnalysisCache();

      // Close panel after successful application
      state.closeEffectPanel();

      return true;
    } catch (error) {
      console.error(`Failed to apply ${effect} effect:`, error);
      set(state => ({ ...state, lastError: `Failed to apply effect: ${error instanceof Error ? error.message : 'Unknown error'}` }));
      return false;
    }
  },

  // Utility Actions
  clearError: () => {
    set({ lastError: null });
  },

  reset: () => {
    set({
      isOpen: false,
      activeEffect: null,
      applyToTimeline: false,
      levelsSettings: { ...DEFAULT_LEVELS_SETTINGS },
      hueSaturationSettings: { ...DEFAULT_HUE_SATURATION_SETTINGS },
      remapColorsSettings: { ...DEFAULT_REMAP_COLORS_SETTINGS },
      remapCharactersSettings: { ...DEFAULT_REMAP_CHARACTERS_SETTINGS },
      canvasAnalysis: null,
      isAnalyzing: false,
      isPreviewActive: false,
      previewEffect: null,
      lastError: null
    });
  }
}));

/**
 * Time Effects Store
 * 
 * Zustand store for managing time-based effects state, dialog visibility,
 * and preview system. Follows the established effectsStore pattern.
 */

import { create } from 'zustand';
import type { 
  WaveWarpSettings, 
  WiggleSettings, 
  FrameRangeSettings,
  TimeEffectType 
} from '../types/timeEffects';
import { 
  DEFAULT_WAVE_WARP_SETTINGS, 
  DEFAULT_WIGGLE_SETTINGS 
} from '../constants/timeEffects';
import { useAnimationStore } from './animationStore';
import { useCanvasStore } from './canvasStore';
import { usePreviewStore } from './previewStore';
import { 
  applyWaveWarpToFrame, 
  applyWiggleToFrame, 
  calculateAccumulatedTime 
} from '../utils/timeEffectsProcessing';

interface TimeEffectsState {
  // ==========================================
  // Dialog Visibility State
  // ==========================================
  isWaveWarpDialogOpen: boolean;
  isWiggleDialogOpen: boolean;
  isSetDurationDialogOpen: boolean;
  isAddFramesDialogOpen: boolean;

  // ==========================================
  // Effect Settings (Persisted)
  // ==========================================
  waveWarpSettings: WaveWarpSettings;
  wiggleSettings: WiggleSettings;

  // ==========================================
  // Frame Range Control (Shared across effects)
  // ==========================================
  frameRange: FrameRangeSettings;

  // ==========================================
  // Preview System
  // ==========================================
  isPreviewActive: boolean;
  previewEffect: TimeEffectType | null;

  // ==========================================
  // Actions - Dialog Management
  // ==========================================
  openWaveWarpDialog: () => void;
  closeWaveWarpDialog: () => void;
  openWiggleDialog: () => void;
  closeWiggleDialog: () => void;
  openSetDurationDialog: () => void;
  closeSetDurationDialog: () => void;
  openAddFramesDialog: () => void;
  closeAddFramesDialog: () => void;

  // ==========================================
  // Actions - Settings Updates
  // ==========================================
  updateWaveWarpSettings: (settings: Partial<WaveWarpSettings>) => void;
  updateWiggleSettings: (settings: Partial<WiggleSettings>) => void;
  updateFrameRange: (range: Partial<FrameRangeSettings>) => void;
  resetWaveWarpSettings: () => void;
  resetWiggleSettings: () => void;

  // ==========================================
  // Actions - Preview Management
  // ==========================================
  startPreview: (effectType: TimeEffectType) => void;
  stopPreview: () => void;
  updatePreview: () => void; // Manually trigger preview update
}

export const useTimeEffectsStore = create<TimeEffectsState>((set, get) => ({
  // ==========================================
  // Initial State
  // ==========================================
  isWaveWarpDialogOpen: false,
  isWiggleDialogOpen: false,
  isSetDurationDialogOpen: false,
  isAddFramesDialogOpen: false,

  waveWarpSettings: { ...DEFAULT_WAVE_WARP_SETTINGS },
  wiggleSettings: { ...DEFAULT_WIGGLE_SETTINGS },

  frameRange: {
    applyToAll: true,
    startFrame: 0,
    endFrame: 0
  },

  isPreviewActive: false,
  previewEffect: null,

  // ==========================================
  // Dialog Management Actions
  // ==========================================

  openWaveWarpDialog: () => {
    // Initialize frame range when dialog opens
    const frames = useAnimationStore.getState().frames;

    set({ 
      isWaveWarpDialogOpen: true,
      frameRange: {
        applyToAll: true,
        startFrame: 0,
        endFrame: frames.length - 1
      }
    });
  },

  closeWaveWarpDialog: () => {
    const { stopPreview } = get();
    stopPreview();
    set({ isWaveWarpDialogOpen: false });
  },

  openWiggleDialog: () => {
    // Initialize frame range when dialog opens
    const frames = useAnimationStore.getState().frames;

    set({ 
      isWiggleDialogOpen: true,
      frameRange: {
        applyToAll: true,
        startFrame: 0,
        endFrame: frames.length - 1
      }
    });
  },

  closeWiggleDialog: () => {
    const { stopPreview } = get();
    stopPreview();
    set({ isWiggleDialogOpen: false });
  },

  openSetDurationDialog: () => set({ isSetDurationDialogOpen: true }),
  closeSetDurationDialog: () => set({ isSetDurationDialogOpen: false }),

  openAddFramesDialog: () => set({ isAddFramesDialogOpen: true }),
  closeAddFramesDialog: () => set({ isAddFramesDialogOpen: false }),

  // ==========================================
  // Settings Update Actions
  // ==========================================

  updateWaveWarpSettings: (settings) => {
    set((state) => ({
      waveWarpSettings: { ...state.waveWarpSettings, ...settings }
    }));

    // Auto-update preview if active
    if (get().isPreviewActive && get().previewEffect === 'wave-warp') {
      get().updatePreview();
    }
  },

  updateWiggleSettings: (settings) => {
    set((state) => ({
      wiggleSettings: { ...state.wiggleSettings, ...settings }
    }));

    // Auto-update preview if active
    if (get().isPreviewActive && get().previewEffect === 'wiggle') {
      get().updatePreview();
    }
  },

  updateFrameRange: (range) => {
    set((state) => ({
      frameRange: { ...state.frameRange, ...range }
    }));
  },

  resetWaveWarpSettings: () => {
    set({ waveWarpSettings: { ...DEFAULT_WAVE_WARP_SETTINGS } });

    // Update preview if active
    if (get().isPreviewActive && get().previewEffect === 'wave-warp') {
      get().updatePreview();
    }
  },

  resetWiggleSettings: () => {
    set({ wiggleSettings: { ...DEFAULT_WIGGLE_SETTINGS } });

    // Update preview if active
    if (get().isPreviewActive && get().previewEffect === 'wiggle') {
      get().updatePreview();
    }
  },

  // ==========================================
  // Preview Management Actions
  // ==========================================

  startPreview: (effectType) => {
    console.log('[TimeEffects Store] startPreview called with:', effectType);
    set({ 
      isPreviewActive: true, 
      previewEffect: effectType 
    });

    // Trigger initial preview
    get().updatePreview();
  },

  stopPreview: () => {
    console.log('[TimeEffects Store] stopPreview called');
    // Clear preview from canvas via previewStore
    try {
      usePreviewStore.getState().clearPreview();
    } catch (error) {
      console.warn('Preview store not available:', error);
    }

    set({ isPreviewActive: false, previewEffect: null });
  },

  updatePreview: () => {
    const state = get();

    console.log('[TimeEffects Store] updatePreview called, state:', {
      isPreviewActive: state.isPreviewActive,
      previewEffect: state.previewEffect
    });

    if (!state.isPreviewActive || !state.previewEffect) {
      console.log('[TimeEffects Store] Preview not active or no effect type');
      return;
    }

    try {
      // Use imported stores and utilities
      const animationStore = useAnimationStore.getState();
      const canvasStore = useCanvasStore.getState();
      const previewStore = usePreviewStore.getState();

      const { currentFrameIndex, frames } = animationStore;
      const { width: canvasWidth, height: canvasHeight } = canvasStore;

      // Get current frame data
      const currentFrameData = frames[currentFrameIndex]?.data;
      if (!currentFrameData) return;

      // Calculate accumulated time for current frame
      const accumulatedTime = calculateAccumulatedTime(frames, currentFrameIndex);

      // Apply effect based on type
      let previewData: Map<string, any> | null = null;

      console.log('[TimeEffects Store] Applying effect:', state.previewEffect, 'to frame', currentFrameIndex);
      console.log('[TimeEffects Store] Frame data size:', currentFrameData.size, 'accumulated time:', accumulatedTime);

      if (state.previewEffect === 'wave-warp') {
        previewData = applyWaveWarpToFrame(
          currentFrameData,
          canvasWidth,
          canvasHeight,
          state.waveWarpSettings,
          accumulatedTime
        );
        console.log('[TimeEffects Store] Wave warp result:', previewData?.size || 0, 'cells');
      } else if (state.previewEffect === 'wiggle') {
        previewData = applyWiggleToFrame(
          currentFrameData,
          canvasWidth,
          canvasHeight,
          state.wiggleSettings,
          accumulatedTime
        );
      }

      // Update preview on canvas
      if (previewData) {
        console.log('[TimeEffects Store] Setting preview data:', previewData.size, 'cells');
        previewStore.setPreviewData(previewData);
      } else {
        console.log('[TimeEffects Store] No preview data generated');
      }
    } catch (error) {
      console.error('Failed to update preview:', error);
    }
  }
}));

import { create } from 'zustand';
import type { Tool, ToolState, Selection, LassoSelection, MagicWandSelection, TextToolState, AnyHistoryAction, CanvasHistoryAction, BrushShape, BrushSettings } from '../types';
import { DEFAULT_COLORS } from '../constants';
import { 
  rectangularSelectionToText, 
  lassoSelectionToText, 
  magicWandSelectionToText, 
  writeToOSClipboard 
} from '../utils/clipboardUtils';
import { 
  createRectSelectionMask,
  updateSelectionFromMask,
  getBoundsFromMask
} from '../utils/selectionUtils';
import { useAsciiTypeStore } from './asciiTypeStore';

interface ToolStoreState extends ToolState {
  // Rectangular selection state
  selection: Selection;

  // Lasso selection state
  lassoSelection: LassoSelection;

  // Magic wand selection state
  magicWandSelection: MagicWandSelection;

  // Text tool state
  textToolState: TextToolState;

  // Pencil tool state for line drawing
  pencilLastPosition: { x: number; y: number } | null;

  // Shift+click line preview state
  linePreview: {
    active: boolean;
    points: { x: number; y: number }[];
  };

  // Clipboard for copy/paste
  clipboard: Map<string, any> | null;
  clipboardOriginalPosition: { x: number; y: number } | null;
  activeClipboardType: 'rectangle' | 'lasso' | 'magicwand' | null;

  // Lasso clipboard for copy/paste
  lassoClipboard: Map<string, any> | null;
  lassoClipboardOriginalPosition: { x: number; y: number } | null;

  // Magic wand clipboard for copy/paste
  magicWandClipboard: Map<string, any> | null;
  magicWandClipboardOriginalPosition: { x: number; y: number } | null;

  // Enhanced history for undo/redo
  historyStack: AnyHistoryAction[];
  historyPosition: number; // Current position in history stack (-1 = no history)
  maxHistorySize: number;

  // Animation playback state
  isPlaybackMode: boolean;

  // Actions
  setActiveTool: (tool: Tool) => void;
  setSelectedChar: (char: string) => void;
  setSelectedColor: (color: string) => void;
  setSelectedBgColor: (color: string) => void;
  setBrushSize: (size: number, tool?: 'pencil' | 'eraser') => void;
  setBrushShape: (shape: BrushShape, tool?: 'pencil' | 'eraser') => void;
  getBrushSettings: (tool?: 'pencil' | 'eraser') => BrushSettings;
  setRectangleFilled: (filled: boolean) => void;
  setPaintBucketContiguous: (contiguous: boolean) => void;
  setMagicWandContiguous: (contiguous: boolean) => void;

  // Tool behavior toggles
  toolAffectsChar: boolean;
  toolAffectsColor: boolean;
  toolAffectsBgColor: boolean;

  // Paint bucket match criteria (Selects same:)
  fillMatchChar: boolean;
  fillMatchColor: boolean;
  fillMatchBgColor: boolean;

  // Magic wand match criteria (Selects same:)
  magicMatchChar: boolean;
  magicMatchColor: boolean;
  magicMatchBgColor: boolean;

  // Eyedropper behavior toggles
  eyedropperPicksChar: boolean;
  eyedropperPicksColor: boolean;
  eyedropperPicksBgColor: boolean;

  // Actions for toggles
  setToolAffectsChar: (enabled: boolean) => void;
  setToolAffectsColor: (enabled: boolean) => void;
  setToolAffectsBgColor: (enabled: boolean) => void;
  setFillMatchChar: (enabled: boolean) => void;
  setFillMatchColor: (enabled: boolean) => void;
  setFillMatchBgColor: (enabled: boolean) => void;
  setMagicMatchChar: (enabled: boolean) => void;
  setMagicMatchColor: (enabled: boolean) => void;
  setMagicMatchBgColor: (enabled: boolean) => void;
  setEyedropperPicksChar: (enabled: boolean) => void;
  setEyedropperPicksColor: (enabled: boolean) => void;
  setEyedropperPicksBgColor: (enabled: boolean) => void;

  // Eyedropper functionality
  pickFromCell: (char: string, color: string, bgColor: string) => void;

  // Pencil tool actions
  setPencilLastPosition: (position: { x: number; y: number } | null) => void;
  setLinePreview: (points: { x: number; y: number }[]) => void;
  clearLinePreview: () => void;

  // Rectangular selection actions
  startSelection: (x: number, y: number) => void;
  updateSelection: (x: number, y: number) => void;
  clearSelection: () => void;
  setSelectionFromMask: (mask: Set<string>) => void;

  // Lasso selection actions
  startLassoSelection: () => void;
  addLassoPoint: (x: number, y: number) => void;
  updateLassoSelectedCells: (selectedCells: Set<string>) => void;
  setLassoPath: (path: { x: number; y: number }[]) => void;
  finalizeLassoSelection: () => void;
  clearLassoSelection: () => void;
  setLassoSelectionFromMask: (mask: Set<string>, path?: { x: number; y: number }[]) => void;

  // Magic wand selection actions
  startMagicWandSelection: (targetCell: any, selectedCells: Set<string>) => void;
  updateMagicWandSelectedCells: (selectedCells: Set<string>) => void;
  clearMagicWandSelection: () => void;
  setMagicWandSelectionFromMask: (mask: Set<string>, targetCell?: any) => void;

  // Clipboard actions
  copySelection: (canvasData: Map<string, any>) => void;
  pasteSelection: (x: number, y: number) => Map<string, any> | null;
  hasClipboard: () => boolean;
  getActiveClipboardType: () => 'rectangle' | 'lasso' | 'magicwand' | null;
  getActiveClipboardOriginalPosition: () => { x: number; y: number } | null;
  getClipboardOriginalPosition: () => { x: number; y: number } | null;

  // Lasso clipboard actions
  copyLassoSelection: (canvasData: Map<string, any>) => void;
  pasteLassoSelection: (offsetX: number, offsetY: number) => Map<string, any> | null;
  hasLassoClipboard: () => boolean;
  getLassoClipboardOriginalPosition: () => { x: number; y: number } | null;

  // Magic wand clipboard actions
  copyMagicWandSelection: (canvasData: Map<string, any>) => void;
  pasteMagicWandSelection: (offsetX: number, offsetY: number) => Map<string, any> | null;
  hasMagicWandClipboard: () => boolean;
  getMagicWandClipboardOriginalPosition: () => { x: number; y: number } | null;

  // Text tool actions
  startTyping: (x: number, y: number) => void;
  stopTyping: () => void;
  setCursorPosition: (x: number, y: number) => void;
  setCursorVisible: (visible: boolean) => void;
  setTextBuffer: (buffer: string) => void;
  setLineStartX: (x: number) => void;
  commitWord: () => void;

  // Enhanced history actions
  pushToHistory: (action: AnyHistoryAction) => void;
  pushCanvasHistory: (canvasData: Map<string, any>, frameIndex: number, description?: string) => void;
  undo: () => AnyHistoryAction | undefined;
  redo: () => AnyHistoryAction | undefined;
  clearHistory: () => void;
  canUndo: () => boolean;
  canRedo: () => boolean;

  // Playback mode actions
  setPlaybackMode: (enabled: boolean) => void;
}

const createEmptySelection = (): Selection => ({
  start: { x: 0, y: 0 },
  end: { x: 0, y: 0 },
  active: false,
  selectedCells: new Set<string>(),
  shape: 'rectangle'
});

const buildSelectionFromMask = (mask: Set<string>): Selection => {
  if (mask.size === 0) {
    return createEmptySelection();
  }

  const { start, end, selectedCells, shape } = updateSelectionFromMask(mask);
  return {
    start,
    end,
    selectedCells,
    shape,
    active: true
  };
};

export const useToolStore = create<ToolStoreState>((set, get) => ({
  // Initial state
  activeTool: 'pencil',
  selectedChar: '@',
  selectedColor: DEFAULT_COLORS[2], // White (moved from index 1 to 2)
  selectedBgColor: DEFAULT_COLORS[0], // Transparent
  brushSettings: {
    pencil: {
      size: 1,
      shape: 'circle' as BrushShape,
    },
    eraser: {
      size: 1,
      shape: 'circle' as BrushShape,
    }
  },
  rectangleFilled: false,
  paintBucketContiguous: true, // Default to contiguous fill
  magicWandContiguous: true, // Default to contiguous selection

  // Tool behavior toggles - all enabled by default
  toolAffectsChar: true,
  toolAffectsColor: true,
  toolAffectsBgColor: true,

  // Paint bucket matching criteria (Selects same:) - all enabled by default
  fillMatchChar: true,
  fillMatchColor: true,
  fillMatchBgColor: true,

  // Magic wand matching criteria (Selects same:) - all enabled by default
  magicMatchChar: true,
  magicMatchColor: true,
  magicMatchBgColor: true,

  // Eyedropper behavior toggles - all enabled by default
  eyedropperPicksChar: true,
  eyedropperPicksColor: true,
  eyedropperPicksBgColor: true,

  // Animation playback state
  isPlaybackMode: false,

  // Pencil tool state
  pencilLastPosition: null,

  // Shift+click line preview state
  linePreview: {
    active: false,
    points: []
  },

  // Rectangular selection state
  selection: createEmptySelection(),

  // Lasso selection state
  lassoSelection: {
    path: [],
    selectedCells: new Set<string>(),
    active: false,
    isDrawing: false
  },

  // Magic wand selection state
  magicWandSelection: {
    selectedCells: new Set<string>(),
    targetCell: null,
    active: false,
    contiguous: true
  },

  // Text tool state
  textToolState: {
    isTyping: false,
    cursorPosition: null,
    cursorVisible: true,
    textBuffer: '',
    lineStartX: 0
  },

  // Clipboard state
  clipboard: null,
  clipboardOriginalPosition: null,
  activeClipboardType: null,

  // Lasso clipboard state
  lassoClipboard: null,
  lassoClipboardOriginalPosition: null,

  // Magic wand clipboard state
  magicWandClipboard: null,
  magicWandClipboardOriginalPosition: null,

  // Enhanced history for undo/redo
  historyStack: [],
  historyPosition: -1,
  maxHistorySize: 50,

  // Tool actions
  setActiveTool: (tool: Tool) => {
    set({ activeTool: tool });
    const asciiStore = useAsciiTypeStore.getState();
    if (tool === 'asciitype') {
      asciiStore.openPanel();
    } else {
      asciiStore.closePanel();
    }
    // Clear line preview when switching tools
    get().clearLinePreview();
    // Clear selections when switching tools (except select/lasso/magicwand tools)
    if (tool !== 'select') {
      get().clearSelection();
    }
    if (tool !== 'lasso') {
      get().clearLassoSelection();
    }
    if (tool !== 'magicwand') {
      get().clearMagicWandSelection();
    }
    // Clear pencil last position when switching tools
    if (tool !== 'pencil') {
      get().setPencilLastPosition(null);
    }
    // Stop typing when switching away from text tool
    if (tool !== 'text') {
      get().stopTyping();
    }
  },

  setSelectedChar: (char: string) => set({ selectedChar: char }),
  setSelectedColor: (color: string) => set({ selectedColor: color }),
  setSelectedBgColor: (color: string) => set({ selectedBgColor: color }),
  setBrushSize: (size: number, tool?: 'pencil' | 'eraser') => {
    const clampedSize = Math.max(1, Math.min(20, size));
    const targetTool = tool ?? (get().activeTool === 'eraser' ? 'eraser' : 'pencil');

    set((state) => ({
      brushSettings: {
        ...state.brushSettings,
        [targetTool]: {
          ...state.brushSettings[targetTool],
          size: clampedSize,
        }
      }
    }));
  },
  setBrushShape: (shape: BrushShape, tool?: 'pencil' | 'eraser') => {
    const targetTool = tool ?? (get().activeTool === 'eraser' ? 'eraser' : 'pencil');

    set((state) => ({
      brushSettings: {
        ...state.brushSettings,
        [targetTool]: {
          ...state.brushSettings[targetTool],
          shape,
        }
      }
    }));
  },
  getBrushSettings: (tool?: 'pencil' | 'eraser') => {
    const targetTool = tool ?? (get().activeTool === 'eraser' ? 'eraser' : 'pencil');
    const { brushSettings } = get();
    return brushSettings[targetTool];
  },
  setRectangleFilled: (filled: boolean) => set({ rectangleFilled: filled }),
  setPaintBucketContiguous: (contiguous: boolean) => set({ paintBucketContiguous: contiguous }),
  setMagicWandContiguous: (contiguous: boolean) => set({ magicWandContiguous: contiguous }),

  // Tool behavior toggle actions
  setToolAffectsChar: (enabled: boolean) => set({ toolAffectsChar: enabled }),
  setToolAffectsColor: (enabled: boolean) => set({ toolAffectsColor: enabled }),
  setToolAffectsBgColor: (enabled: boolean) => set({ toolAffectsBgColor: enabled }),
  // Paint bucket matching criteria setters
  setFillMatchChar: (enabled: boolean) => set({ fillMatchChar: enabled }),
  setFillMatchColor: (enabled: boolean) => set({ fillMatchColor: enabled }),
  setFillMatchBgColor: (enabled: boolean) => set({ fillMatchBgColor: enabled }),
  // Magic wand matching criteria setters
  setMagicMatchChar: (enabled: boolean) => set({ magicMatchChar: enabled }),
  setMagicMatchColor: (enabled: boolean) => set({ magicMatchColor: enabled }),
  setMagicMatchBgColor: (enabled: boolean) => set({ magicMatchBgColor: enabled }),

  // Eyedropper behavior toggle actions
  setEyedropperPicksChar: (enabled: boolean) => set({ eyedropperPicksChar: enabled }),
  setEyedropperPicksColor: (enabled: boolean) => set({ eyedropperPicksColor: enabled }),
  setEyedropperPicksBgColor: (enabled: boolean) => set({ eyedropperPicksBgColor: enabled }),

  // Eyedropper functionality
  pickFromCell: (char: string, color: string, bgColor: string) => {
    const { eyedropperPicksChar, eyedropperPicksColor, eyedropperPicksBgColor } = get();

    const updates: Partial<ToolStoreState> = {};

    // Only pick character if toggle is enabled
    if (eyedropperPicksChar) {
      updates.selectedChar = char;
    }

    // Only pick color data if the cell has a character (not just a space) and toggle is enabled
    const hasChar = char !== ' ';
    if (eyedropperPicksColor && hasChar) {
      updates.selectedColor = color;
    }
    if (eyedropperPicksBgColor && hasChar) {
      updates.selectedBgColor = bgColor;
    }

    if (Object.keys(updates).length > 0) {
      set(updates);
    }
  },

  // Pencil tool actions
  setPencilLastPosition: (position: { x: number; y: number } | null) => {
    set({ pencilLastPosition: position });
  },

  setLinePreview: (points: { x: number; y: number }[]) => {
    set({ 
      linePreview: {
        active: points.length > 0,
        points
      }
    });
  },

  clearLinePreview: () => {
    set({ 
      linePreview: {
        active: false,
        points: []
      }
    });
  },

  // Selection actions
  startSelection: (x: number, y: number) => {
    const mask = createRectSelectionMask({ x, y }, { x, y });
    set({
      selection: {
        start: { x, y },
        end: { x, y },
        active: true,
        selectedCells: mask,
        shape: 'rectangle'
      }
    });
  },

  updateSelection: (x: number, y: number) => {
    set((state) => {
      if (!state.selection.active) {
        return {};
      }

      const mask = createRectSelectionMask(state.selection.start, { x, y });
      return {
        selection: {
          ...state.selection,
          end: { x, y },
          selectedCells: mask,
          shape: 'rectangle'
        }
      };
    });
  },

  clearSelection: () => {
    set({
      selection: createEmptySelection()
    });
  },

  setSelectionFromMask: (mask: Set<string>) => {
    set({
      selection: buildSelectionFromMask(mask)
    });
  },

  // Lasso selection actions
  startLassoSelection: () => {
    set({
      lassoSelection: {
        path: [],
        selectedCells: new Set<string>(),
        active: true,
        isDrawing: true
      }
    });
  },

  addLassoPoint: (x: number, y: number) => {
    set((state) => ({
      lassoSelection: {
        ...state.lassoSelection,
        path: [...state.lassoSelection.path, { x, y }]
      }
    }));
  },

  updateLassoSelectedCells: (selectedCells: Set<string>) => {
    set((state) => ({
      lassoSelection: {
        ...state.lassoSelection,
        selectedCells: new Set(selectedCells)
      }
    }));
  },

  setLassoPath: (path: { x: number; y: number }[]) => {
    set((state) => ({
      lassoSelection: {
        ...state.lassoSelection,
        path
      }
    }));
  },

  finalizeLassoSelection: () => {
    set((state) => ({
      lassoSelection: {
        ...state.lassoSelection,
        isDrawing: false
      }
    }));
  },

  clearLassoSelection: () => {
    set({
      lassoSelection: {
        path: [],
        selectedCells: new Set<string>(),
        active: false,
        isDrawing: false
      }
    });
  },

  setLassoSelectionFromMask: (mask: Set<string>, path: { x: number; y: number }[] = []) => {
    if (mask.size === 0) {
      set({
        lassoSelection: {
          path: [],
          selectedCells: new Set<string>(),
          active: false,
          isDrawing: false
        }
      });
      return;
    }

    set({
      lassoSelection: {
        path,
        selectedCells: new Set(mask),
        active: true,
        isDrawing: false
      }
    });
  },

  // Magic wand selection actions
  startMagicWandSelection: (targetCell: any, selectedCells: Set<string>) => {
    set({
      magicWandSelection: {
        selectedCells: new Set(selectedCells),
        targetCell: targetCell,
        active: true,
        contiguous: get().magicWandContiguous
      }
    });
  },

  updateMagicWandSelectedCells: (selectedCells: Set<string>) => {
    set((state) => ({
      magicWandSelection: {
        ...state.magicWandSelection,
        selectedCells: new Set(selectedCells)
      }
    }));
  },

  clearMagicWandSelection: () => {
    set({
      magicWandSelection: {
        selectedCells: new Set<string>(),
        targetCell: null,
        active: false,
        contiguous: get().magicWandContiguous
      }
    });
  },

  setMagicWandSelectionFromMask: (mask: Set<string>, targetCell: any = null) => {
    if (mask.size === 0) {
      set({
        magicWandSelection: {
          selectedCells: new Set<string>(),
          targetCell: null,
          active: false,
          contiguous: get().magicWandContiguous
        }
      });
      return;
    }

    set((state) => ({
      magicWandSelection: {
        selectedCells: new Set(mask),
        targetCell: targetCell ?? state.magicWandSelection.targetCell,
        active: true,
        contiguous: state.magicWandSelection.contiguous
      }
    }));
  },

  // Clipboard actions
  copySelection: (canvasData: Map<string, any>) => {
    const { selection } = get();
    if (!selection.active || selection.selectedCells.size === 0) {
      return;
    }

    const bounds = getBoundsFromMask(selection.selectedCells);
    if (!bounds) {
      return;
    }

    const copiedData = new Map<string, any>();
    selection.selectedCells.forEach((key) => {
      const cell = canvasData.get(key);
      if (!cell) {
        return;
      }
      const [x, y] = key.split(',').map(Number);
      const relativeKey = `${x - bounds.minX},${y - bounds.minY}`;
      copiedData.set(relativeKey, cell);
    });

    set({ 
      clipboard: copiedData,
      clipboardOriginalPosition: { x: bounds.minX, y: bounds.minY },
      activeClipboardType: 'rectangle'
    });

    // Also copy to OS clipboard as text
    const textForClipboard = rectangularSelectionToText(canvasData, selection.selectedCells);
    if (textForClipboard.trim() !== '') {
      writeToOSClipboard(textForClipboard).catch(error => {
        console.warn('Failed to copy to OS clipboard:', error);
      });
    }
  },

  pasteSelection: (x: number, y: number) => {
    const { activeClipboardType, clipboard, lassoClipboard, magicWandClipboard } = get();

    let sourceClipboard: Map<string, any> | null = null;
    switch (activeClipboardType) {
      case 'magicwand':
        sourceClipboard = magicWandClipboard ?? null;
        break;
      case 'lasso':
        sourceClipboard = lassoClipboard ?? null;
        break;
      case 'rectangle':
        sourceClipboard = clipboard ?? null;
        break;
    }

    if (!sourceClipboard) {
      if (magicWandClipboard && magicWandClipboard.size > 0) {
        sourceClipboard = magicWandClipboard;
      } else if (lassoClipboard && lassoClipboard.size > 0) {
        sourceClipboard = lassoClipboard;
      } else if (clipboard && clipboard.size > 0) {
        sourceClipboard = clipboard;
      }
    }

    if (!sourceClipboard) {
      return null;
    }

    const pastedData = new Map<string, any>();

    sourceClipboard.forEach((cell, relativeKey) => {
      const [relX, relY] = relativeKey.split(',').map(Number);
      const absoluteKey = `${x + relX},${y + relY}`;
      pastedData.set(absoluteKey, cell);
    });

    return pastedData;
  },

  hasClipboard: () => {
    const state = get();
    return (state.clipboard !== null && state.clipboard!.size > 0) || 
           (state.lassoClipboard !== null && state.lassoClipboard!.size > 0) ||
           (state.magicWandClipboard !== null && state.magicWandClipboard!.size > 0);
  },

  getActiveClipboardType: () => {
    return get().activeClipboardType;
  },

  getActiveClipboardOriginalPosition: () => {
    const state = get();
    switch (state.activeClipboardType) {
      case 'magicwand':
        return state.magicWandClipboardOriginalPosition;
      case 'lasso':
        return state.lassoClipboardOriginalPosition;
      case 'rectangle':
        return state.clipboardOriginalPosition;
      default:
        return null;
    }
  },

  getClipboardOriginalPosition: () => {
    return get().clipboardOriginalPosition;
  },

  // Lasso clipboard actions
  copyLassoSelection: (canvasData: Map<string, any>) => {
    const { lassoSelection } = get();

    if (!lassoSelection.active || lassoSelection.selectedCells.size === 0) {
      return;
    }

    const copiedData = new Map<string, any>();

    // Find bounds of the selected cells to create relative coordinates
    const cellCoords = Array.from(lassoSelection.selectedCells).map(key => {
      const [x, y] = key.split(',').map(Number);
      return { x, y };
    });

    const minX = Math.min(...cellCoords.map(c => c.x));
    const minY = Math.min(...cellCoords.map(c => c.y));

    // Copy only the selected cells with relative coordinates
    lassoSelection.selectedCells.forEach(key => {
      const [x, y] = key.split(',').map(Number);
      const relativeKey = `${x - minX},${y - minY}`;
      if (canvasData.has(key)) {
        copiedData.set(relativeKey, canvasData.get(key));
      }
    });

    set({ 
      lassoClipboard: copiedData,
      lassoClipboardOriginalPosition: { x: minX, y: minY },
      activeClipboardType: 'lasso'
    });

    // Also copy to OS clipboard as text
    const textForClipboard = lassoSelectionToText(canvasData, lassoSelection.selectedCells);
    if (textForClipboard.trim() !== '') {
      writeToOSClipboard(textForClipboard).catch(error => {
        console.warn('Failed to copy lasso selection to OS clipboard:', error);
      });
    }
  },

  pasteLassoSelection: (offsetX: number, offsetY: number) => {
    const { lassoClipboard } = get();
    if (!lassoClipboard) return null;

    const pastedData = new Map<string, any>();

    lassoClipboard.forEach((cell, relativeKey) => {
      const [relX, relY] = relativeKey.split(',').map(Number);
      const absoluteKey = `${offsetX + relX},${offsetY + relY}`;
      pastedData.set(absoluteKey, cell);
    });

    return pastedData;
  },

  hasLassoClipboard: () => {
    return get().lassoClipboard !== null && get().lassoClipboard!.size > 0;
  },

  getLassoClipboardOriginalPosition: () => {
    return get().lassoClipboardOriginalPosition;
  },

  // Magic wand clipboard actions
  copyMagicWandSelection: (canvasData: Map<string, any>) => {
    const { magicWandSelection } = get();
    if (!magicWandSelection.active || magicWandSelection.selectedCells.size === 0) {
      return;
    }

    const copiedData = new Map<string, any>();

    // Find bounds of the selected cells to create relative coordinates (consistent with other clipboard types)
    const cellCoords = Array.from(magicWandSelection.selectedCells).map(key => {
      const [x, y] = key.split(',').map(Number);
      return { x, y };
    });

    const minX = Math.min(...cellCoords.map(c => c.x));
    const minY = Math.min(...cellCoords.map(c => c.y));

    // Copy selected cells with relative coordinates
    const selectedArray = Array.from(magicWandSelection.selectedCells);
    for (const cellKey of selectedArray) {
      const [x, y] = cellKey.split(',').map(Number);
      const relativeKey = `${x - minX},${y - minY}`;
      const cell = canvasData.get(cellKey);
      if (cell) {
        copiedData.set(relativeKey, { ...cell });
      }
    }

    set({ 
      magicWandClipboard: copiedData,
      magicWandClipboardOriginalPosition: { x: minX, y: minY },
      activeClipboardType: 'magicwand'
    });

    // Also copy to OS clipboard as text
    const textForClipboard = magicWandSelectionToText(canvasData, magicWandSelection.selectedCells);
    if (textForClipboard.trim() !== '') {
      writeToOSClipboard(textForClipboard).catch(error => {
        console.warn('Failed to copy magic wand selection to OS clipboard:', error);
      });
    }
  },

  pasteMagicWandSelection: (offsetX: number, offsetY: number) => {
    const { magicWandClipboard } = get();
    if (!magicWandClipboard || magicWandClipboard.size === 0) {
      return null;
    }

    const pasteData = new Map<string, any>();

    // Apply offset to each cell position (now using relative coordinates like other clipboard types)
    magicWandClipboard.forEach((cell, relativeKey) => {
      const [relX, relY] = relativeKey.split(',').map(Number);
      const absoluteKey = `${offsetX + relX},${offsetY + relY}`;
      pasteData.set(absoluteKey, { ...cell });
    });

    return pasteData;
  },

  hasMagicWandClipboard: () => {
    return get().magicWandClipboard !== null && get().magicWandClipboard!.size > 0;
  },

  getMagicWandClipboardOriginalPosition: () => {
    return get().magicWandClipboardOriginalPosition;
  },

  // Enhanced history actions
  pushToHistory: (action: AnyHistoryAction) => {
    set((state) => {
      const newHistoryStack = [...state.historyStack];

      // If we're not at the end of history, truncate everything after current position
      if (state.historyPosition < newHistoryStack.length - 1) {
        newHistoryStack.splice(state.historyPosition + 1);
      }

      // Add new action to history
      newHistoryStack.push(action);

      // Limit history size
      if (newHistoryStack.length > state.maxHistorySize) {
        newHistoryStack.shift();
      }

      return {
        historyStack: newHistoryStack,
        historyPosition: newHistoryStack.length - 1
      };
    });
  },

  pushCanvasHistory: (canvasData: Map<string, any>, frameIndex: number, description: string = 'Canvas edit') => {
    const action: CanvasHistoryAction = {
      type: 'canvas_edit',
      timestamp: Date.now(),
      description,
      data: {
        canvasData: new Map(canvasData),
        frameIndex
      }
    };
    get().pushToHistory(action);
  },

  undo: () => {
    const { historyStack, historyPosition } = get();

    if (historyPosition < 0) return undefined;

    const action = historyStack[historyPosition];

    set({
      historyPosition: historyPosition - 1
    });

    return action;
  },

  redo: () => {
    const { historyStack, historyPosition } = get();

    if (historyPosition >= historyStack.length - 1) return undefined;

    const nextPosition = historyPosition + 1;
    const action = historyStack[nextPosition];

    set({
      historyPosition: nextPosition
    });

    return action;
  },

  clearHistory: () => {
    set({
      historyStack: [],
      historyPosition: -1
    });
  },

  canUndo: () => get().historyPosition >= 0,
  canRedo: () => {
    const { historyStack, historyPosition } = get();
    return historyPosition < historyStack.length - 1;
  },

  // Text tool actions
  startTyping: (x: number, y: number) => {
    set({
      textToolState: {
        ...get().textToolState,
        isTyping: true,
        cursorPosition: { x, y },
        cursorVisible: true,
        textBuffer: '',
        lineStartX: x
      }
    });
  },

  stopTyping: () => {
    set({
      textToolState: {
        ...get().textToolState,
        isTyping: false,
        cursorPosition: null,
        cursorVisible: true,
        textBuffer: ''
      }
    });
  },

  setCursorPosition: (x: number, y: number) => {
    set({
      textToolState: {
        ...get().textToolState,
        cursorPosition: { x, y },
        cursorVisible: true // Reset blink on move
      }
    });
  },

  setCursorVisible: (visible: boolean) => {
    set({
      textToolState: {
        ...get().textToolState,
        cursorVisible: visible
      }
    });
  },

  setTextBuffer: (buffer: string) => {
    set({
      textToolState: {
        ...get().textToolState,
        textBuffer: buffer
      }
    });
  },

  setLineStartX: (x: number) => {
    set({
      textToolState: {
        ...get().textToolState,
        lineStartX: x
      }
    });
  },

  commitWord: () => {
    // Clear the text buffer after committing a word for undo
    set({
      textToolState: {
        ...get().textToolState,
        textBuffer: ''
      }
    });
  },

  // Playback mode actions
  setPlaybackMode: (enabled: boolean) => {
    set({ isPlaybackMode: enabled });
  }
}));

