      componentLines.push('          >');
      componentLines.push('            Restart');
      componentLines.push('          </button>');
      componentLines.push('          <span');
      componentLines.push("            style={{ fontFamily: 'monospace', fontSize: '12px', color: '#475569' }}");
      componentLines.push('          >');
