      componentLines.push('          </span>');
      componentLines.push('        </div>');
      componentLines.push('      )}');
    }

    componentLines.push('    </div>');
    componentLines.push('  );');
    componentLines.push('};');

    const componentBlock = componentLines.join('\n');

    const lines: string[] = [];
    lines.push("'use client';");
    lines.push('');
