import { useCanvasStore } from '../stores/canvasStore';
import { useAnimationStore } from '../stores/animationStore';
import type { Cell } from '../types';
import { DEFAULT_FRAME_DURATION } from '../constants';

/**
 * JSON Import Utility
 * Handles loading and restoring data from human-readable JSON files exported by ASCII Motion
 */
export class JsonImporter {

  /**
   * Import project data from a JSON file
   */
  static async importJsonFile(
    file: File, 
    typographyCallbacks?: {
      setFontSize: (size: number) => void;
      setCharacterSpacing: (spacing: number) => void;
      setLineSpacing: (spacing: number) => void;
    }
  ): Promise<void> {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();

      reader.onload = (event) => {
        try {
          const content = event.target?.result as string;
          const jsonData = JSON.parse(content);

          // Validate JSON data structure
          if (!JsonImporter.validateJsonData(jsonData)) {
            throw new Error('Invalid JSON file format or structure');
          }

          // Import the JSON data
          JsonImporter.restoreJsonData(jsonData, typographyCallbacks);

          resolve();
        } catch (error) {
          reject(new Error(`Failed to import JSON: ${error instanceof Error ? error.message : 'Unknown error'}`));
        }
      };

      reader.onerror = () => {
        reject(new Error('Failed to read file'));
      };

      reader.readAsText(file);
    });
  }

    /**
   * Validate JSON data structure for the new text-based format
   */
  private static validateJsonData(data: any): boolean {
    try {
      // Check top-level structure
      if (typeof data !== 'object' || data === null) return false;
      if (!data.canvas || typeof data.canvas !== 'object') return false;
      if (!data.frames || !Array.isArray(data.frames)) return false;

      // Validate canvas settings
      if (typeof data.canvas.width !== 'number' || typeof data.canvas.height !== 'number') return false;

      // Validate typography (optional)
      if (data.typography) {
        if (typeof data.typography.fontSize !== 'number') return false;
        if (typeof data.typography.characterSpacing !== 'number') return false;
        if (typeof data.typography.lineSpacing !== 'number') return false;
      }

      // Validate frames
      for (const frame of data.frames) {
        if (typeof frame !== 'object' || frame === null) return false;
        if (typeof frame.title !== 'string') return false;
        if (typeof frame.duration !== 'number') return false;

        const contentField = frame.content ?? frame.contentString;
        const isStringContent = typeof contentField === 'string';
        const isArrayContent = Array.isArray(contentField) && contentField.every((line: unknown) => typeof line === 'string');
        if (!isStringContent && !isArrayContent) return false;

        // Validate colors (optional)
        if (frame.colors) {
          if (typeof frame.colors !== 'object') return false;

          const { foreground, background } = frame.colors;
          if (foreground && typeof foreground !== 'object' && typeof foreground !== 'string') return false;
          if (background && typeof background !== 'object' && typeof background !== 'string') return false;
        }
      }

      return true;
    } catch {
      return false;
    }
  }

  /**
   * Restore JSON data to application stores for text-based format
   */
  private static restoreJsonData(
    jsonData: any, 
    typographyCallbacks?: {
      setFontSize: (size: number) => void;
      setCharacterSpacing: (spacing: number) => void;
      setLineSpacing: (spacing: number) => void;
    }
  ): void {
    const canvasStore = useCanvasStore.getState();
    const animationStore = useAnimationStore.getState();

    // Restore canvas settings
    canvasStore.setCanvasSize(jsonData.canvas.width, jsonData.canvas.height);
    if (jsonData.canvas.backgroundColor) {
      canvasStore.setCanvasBackgroundColor(jsonData.canvas.backgroundColor);
    }

    // Clear current canvas
    canvasStore.clearCanvas();

    // Restore typography settings if available
    if (jsonData.typography && typographyCallbacks) {
      if (jsonData.typography.fontSize) {
        typographyCallbacks.setFontSize(jsonData.typography.fontSize);
      }
      if (jsonData.typography.characterSpacing !== undefined) {
        typographyCallbacks.setCharacterSpacing(jsonData.typography.characterSpacing);
      }
      if (jsonData.typography.lineSpacing !== undefined) {
        typographyCallbacks.setLineSpacing(jsonData.typography.lineSpacing);
      }
    }

    // Process frames from text content
    if (jsonData.frames && jsonData.frames.length > 0) {
      // Convert text-based frames to internal format
      const importedFrames = jsonData.frames.map((frameData: any, index: number) => {
        // Parse the text content into cells
        const contentField = frameData.content ?? frameData.contentString;
        const lines: string[] = Array.isArray(contentField)
          ? contentField
          : typeof contentField === 'string'
            ? contentField.split('\n')
            : [];

        const parseColorMap = (input: unknown): Record<string, string> => {
          if (!input) return {};
          if (typeof input === 'string') {
            try {
              const parsed = JSON.parse(input);
              return typeof parsed === 'object' && parsed !== null
                ? parsed as Record<string, string>
                : {};
            } catch {
              return {};
            }
          }
          if (typeof input === 'object' && input !== null) {
            return input as Record<string, string>;
          }
          return {};
        };

        const foregroundMap = parseColorMap(frameData.colors?.foreground);
        const backgroundMap = parseColorMap(frameData.colors?.background);

        const frameMap = new Map<string, Cell>();

        lines.forEach((line: string, y: number) => {
          [...line].forEach((character: string, x: number) => {
            if (character !== ' ' && character !== '') {
              const cellKey = `${x},${y}`;

              // Start with default colors
              let foregroundColor = '#FFFFFF';
              let backgroundColor = 'transparent';

              // Apply colors from color data
              if (foregroundMap[cellKey]) {
                foregroundColor = foregroundMap[cellKey];
              }
              if (backgroundMap[cellKey]) {
                backgroundColor = backgroundMap[cellKey];
              }

              frameMap.set(cellKey, {
                char: character,
                color: foregroundColor,
                bgColor: backgroundColor
              });
            }
          });
        });

        return {
          id: `frame-${index}`,
          name: frameData.title || `Frame ${index}`,
          duration: frameData.duration || DEFAULT_FRAME_DURATION,
          data: frameMap
        };
      });

      // Import the frames using the session import method
      animationStore.importSessionFrames(importedFrames);

      // Restore animation settings
      if (jsonData.animation) {
        if (jsonData.animation.frameRate) {
          animationStore.setFrameRate(jsonData.animation.frameRate);
        }
        if (jsonData.animation.looping !== undefined) {
          animationStore.setLooping(jsonData.animation.looping);
        }
        if (jsonData.animation.currentFrame !== undefined) {
          animationStore.setCurrentFrame(jsonData.animation.currentFrame);
        } else {
          animationStore.setCurrentFrame(0);
        }
      } else {
        // Default to first frame
        animationStore.setCurrentFrame(0);
      }

      // Load the current frame's data into the canvas
      const animationState = useAnimationStore.getState();
      const currentFrameIndex = animationState.currentFrameIndex;
      const currentFrame = animationState.frames[currentFrameIndex];
      if (currentFrame && currentFrame.data) {
        canvasStore.clearCanvas();
        currentFrame.data.forEach((cell, key) => {
          const [x, y] = key.split(',').map(Number);
          canvasStore.setCell(x, y, cell);
        });
      }
    }
  }
}

/**
 * Hook for JSON import functionality
 */
export const useJsonImporter = () => {
  const importJson = async (
    file: File, 
    typographyCallbacks?: {
      setFontSize: (size: number) => void;
      setCharacterSpacing: (spacing: number) => void;
      setLineSpacing: (spacing: number) => void;
    }
  ): Promise<void> => {
    try {
      await JsonImporter.importJsonFile(file, typographyCallbacks);
    } catch (error) {
      console.error('JSON import failed:', error);
      throw error;
    }
  };

  return { importJson };
};

/**
 * MediaProcessor - Core utility for processing image and video files for ASCII conversion
 * 
 * Handles:
 * - File loading and validation
 * - Image/video frame extraction
 * - Basic image processing operations (resize, crop)
 * - Canvas conversion for ASCII processing
 * - Error handling for unsupported formats
 */

// @ts-ignore - mp4box doesn't have proper TypeScript definitions
import * as MP4Box from 'mp4box';

export interface MediaFile {
  file: File;
  type: 'image' | 'video';
  name: string;
  size: number;
  duration?: number; // For video files
  frameCount?: number; // For video files
}

export interface ProcessingOptions {
  // Size controls
  targetWidth: number;  // Character width
  targetHeight: number; // Character height

  // Basic processing options
  maintainAspectRatio: boolean;
  cropMode: 'center' | 'top' | 'bottom' | 'left' | 'right' | 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right';

  // Quality settings
  quality: 'high' | 'medium' | 'low';
}

export interface ProcessedFrame {
  canvas: HTMLCanvasElement;
  imageData: ImageData;
  timestamp?: number; // For video frames
  frameIndex?: number; // For video frames
  frameDuration?: number; // Duration in milliseconds (for video frames)
}

export interface ProcessingResult {
  success: boolean;
  frames: ProcessedFrame[];
  metadata: {
    originalWidth: number;
    originalHeight: number;
    processedWidth: number;
    processedHeight: number;
    frameCount: number;
    duration?: number;
    frameRate?: number; // Original video frame rate
  };
  error?: string;
}

/**
 * Supported file formats for import
 */
export const SUPPORTED_IMAGE_FORMATS = [
  'image/jpeg',
  'image/jpg', 
  'image/png',
  'image/gif',
  'image/bmp',
  'image/webp',
  'image/svg+xml'
];

export const SUPPORTED_VIDEO_FORMATS = [
  'video/mp4',
  'video/webm',
  'video/ogg',
  'video/avi',
  'video/mov',
  'video/quicktime', // MOV files typically use this MIME type
  'video/wmv'
];

export const ALL_SUPPORTED_FORMATS = [
  ...SUPPORTED_IMAGE_FORMATS,
  ...SUPPORTED_VIDEO_FORMATS
];

export class MediaProcessor {
  private canvas: HTMLCanvasElement;
  private ctx: CanvasRenderingContext2D;

  constructor() {
    this.canvas = document.createElement('canvas');
    const ctx = this.canvas.getContext('2d');
    if (!ctx) {
      throw new Error('Failed to create canvas context for media processing');
    }
    this.ctx = ctx;
  }

  /**
   * Validate and classify uploaded file
   */
  validateFile(file: File): MediaFile | null {
    const isImage = SUPPORTED_IMAGE_FORMATS.includes(file.type);
    const isVideo = SUPPORTED_VIDEO_FORMATS.includes(file.type);

    if (!isImage && !isVideo) {
      return null;
    }

    return {
      file,
      type: isImage ? 'image' : 'video',
      name: file.name,
      size: file.size
    };
  }

  /**
   * Process image file and convert to canvas frames
   */
  async processImage(mediaFile: MediaFile, options: ProcessingOptions): Promise<ProcessingResult> {
    try {
      const img = await this.loadImage(mediaFile.file);
      const processedFrame = this.processImageToCanvas(img, options);

      return {
        success: true,
        frames: [processedFrame],
        metadata: {
          originalWidth: img.width,
          originalHeight: img.height,
          processedWidth: processedFrame.canvas.width,
          processedHeight: processedFrame.canvas.height,
          frameCount: 1
        }
      };
    } catch (error) {
      return {
        success: false,
        frames: [],
        metadata: {
          originalWidth: 0,
          originalHeight: 0,
          processedWidth: 0,
          processedHeight: 0,
          frameCount: 0
        },
        error: error instanceof Error ? error.message : 'Unknown error processing image'
      };
    }
  }

  /**
   * Process video file and extract frames
   */
  async processVideo(mediaFile: MediaFile, options: ProcessingOptions): Promise<ProcessingResult> {
    try {
      const video = await this.loadVideo(mediaFile.file);
      const { frames, detectedFrameRate } = await this.extractVideoFrames(video, options, mediaFile.file);

      return {
        success: true,
        frames,
        metadata: {
          originalWidth: video.videoWidth,
          originalHeight: video.videoHeight,
          processedWidth: frames[0]?.canvas.width || 0,
          processedHeight: frames[0]?.canvas.height || 0,
          frameCount: frames.length,
          duration: video.duration,
          frameRate: detectedFrameRate
        }
      };
    } catch (error) {
      return {
        success: false,
        frames: [],
        metadata: {
          originalWidth: 0,
          originalHeight: 0,
          processedWidth: 0,
          processedHeight: 0,
          frameCount: 0
        },
        error: error instanceof Error ? error.message : 'Unknown error processing video'
      };
    }
  }

  /**
   * Load image file into HTMLImageElement
   */
  private loadImage(file: File): Promise<HTMLImageElement> {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.onload = () => resolve(img);
      img.onerror = () => reject(new Error(`Failed to load image: ${file.name}`));
      img.src = URL.createObjectURL(file);
    });
  }

  /**
   * Load video file into HTMLVideoElement
   */
  private loadVideo(file: File): Promise<HTMLVideoElement> {
    return new Promise((resolve, reject) => {
      const video = document.createElement('video');
      video.preload = 'metadata';

      video.onloadedmetadata = () => {
        resolve(video);
      };

      video.onerror = () => {
        reject(new Error(`Failed to load video: ${file.name}`));
      };

      video.src = URL.createObjectURL(file);
    });
  }

  /**
   * Process image to canvas with resize and crop options
   */
  private processImageToCanvas(img: HTMLImageElement, options: ProcessingOptions): ProcessedFrame {
    const { targetWidth, targetHeight, maintainAspectRatio, cropMode } = options;

    // Set canvas to target size (this is what the user wants)
    this.canvas.width = targetWidth;
    this.canvas.height = targetHeight;

    // Clear canvas
    this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

    if (maintainAspectRatio) {
      // Apply crop settings to fill the canvas while maintaining aspect ratio
      const sourceRect = this.calculateSourceRect(
        img.width,
        img.height,
        targetWidth,
        targetHeight,
        cropMode,
        maintainAspectRatio
      );

      // Draw cropped image to fill the entire canvas
      this.ctx.drawImage(
        img,
        sourceRect.x,
        sourceRect.y,
        sourceRect.width,
        sourceRect.height,
        0,
        0,
        targetWidth,
        targetHeight
      );
    } else {
      // Stretch to fit without maintaining aspect ratio
      this.ctx.drawImage(
        img,
        0,
        0,
        img.width,
        img.height,
        0,
        0,
        targetWidth,
        targetHeight
      );
    }

    // Get image data
    const imageData = this.ctx.getImageData(0, 0, this.canvas.width, this.canvas.height);

    // Create result canvas (clone)
    const resultCanvas = document.createElement('canvas');
    resultCanvas.width = this.canvas.width;
    resultCanvas.height = this.canvas.height;
    const resultCtx = resultCanvas.getContext('2d')!;
    resultCtx.drawImage(this.canvas, 0, 0);

    return {
      canvas: resultCanvas,
      imageData
    };
  }

  /**
   * Extract frames from video using original frame rate
   */
  private async extractVideoFrames(video: HTMLVideoElement, options: ProcessingOptions, originalFile: File): Promise<{ frames: ProcessedFrame[], detectedFrameRate: number }> {
    const frames: ProcessedFrame[] = [];

    // Try to detect frame rate or use common defaults
    const estimatedFrameRate = await this.estimateVideoFrameRate(video, originalFile);
    const frameDuration = Math.round(1000 / estimatedFrameRate); // Convert to milliseconds

    // Extract all frames, but limit to reasonable maximum
    const maxFrames = Math.min(300, Math.floor(video.duration * estimatedFrameRate)); // Cap at 300 frames



    for (let i = 0; i < maxFrames; i++) {
      const timestamp = i / estimatedFrameRate;

      // Stop if we exceed video duration
      if (timestamp >= video.duration) break;

      video.currentTime = timestamp;

      // Wait for video to seek to the correct time with timeout
      await new Promise<void>((resolve) => {
        const timeout = setTimeout(() => resolve(), 200); // 200ms timeout
        video.onseeked = () => {
          clearTimeout(timeout);
          resolve();
        };
      });

      // Small delay to ensure frame is ready
      await new Promise(resolve => setTimeout(resolve, 10));

      // Process current frame
      const processedFrame = this.processVideoFrameToCanvas(video, options, timestamp, i, frameDuration);
      frames.push(processedFrame);
    }



    return { frames, detectedFrameRate: estimatedFrameRate };
  }

  /**
   * Extract frame rate from video file metadata
   */
  private async estimateVideoFrameRate(video: HTMLVideoElement, originalFile: File): Promise<number> {
    try {
      // Attempt to get frame rate from video metadata
      const frameRate = await this.extractFrameRateFromMetadata(video, originalFile);
      if (frameRate > 0) {

        return frameRate;
      }
    } catch (error) {

    }

    // Fallback to common frame rate

    return 30;
  }

  /**
   * Extract frame rate from video file metadata using MP4Box
   */
  private async extractFrameRateFromMetadata(_video: HTMLVideoElement, originalFile: File): Promise<number> {
    try {
      // Use the original file directly instead of fetching from blob URL
      const arrayBuffer = await originalFile.arrayBuffer();

      return await this.parseMP4FrameRate(arrayBuffer);
    } catch (error) {

      return 0;
    }
  }

  /**
   * Parse MP4 file to extract framerate using MP4Box
   */
  private parseMP4FrameRate(arrayBuffer: ArrayBuffer): Promise<number> {
    return new Promise((resolve) => {
      const mp4boxFile = (MP4Box as any).createFile();

      // Set up event handlers
      mp4boxFile.onReady = (info: any) => {


        // Look for video tracks
        const videoTrack = info.videoTracks?.[0];
        if (videoTrack) {
          // Calculate frame rate from track info
          let frameRate = 0;

          if (videoTrack.movie_timescale && videoTrack.movie_duration) {
            const durationInSeconds = videoTrack.movie_duration / videoTrack.movie_timescale;
            if (videoTrack.nb_samples && durationInSeconds > 0) {
              frameRate = videoTrack.nb_samples / durationInSeconds;
            }
          }

          // Alternative: use timescale if available
          if (!frameRate && videoTrack.timescale) {
            // Many videos store frame rate info in the timescale
            const commonRates = [23.976, 24, 25, 29.97, 30, 50, 59.94, 60];

            // Check if timescale matches common frame rates
            for (const rate of commonRates) {
              if (Math.abs(videoTrack.timescale / 1000 - rate) < 1) {
                frameRate = rate;
                break;
              }
            }

            // If no match, try direct calculation
            if (!frameRate && videoTrack.timescale > 1000) {
              frameRate = videoTrack.timescale / 1000;
            }
          }


          resolve(frameRate > 0 && frameRate <= 120 ? frameRate : 0);
        } else {

          resolve(0);
        }
      };

      mp4boxFile.onError = () => {
        // MP4Box parsing failed, use default framerate
        resolve(0);
      };

      // Convert ArrayBuffer to the format MP4Box expects
      const buffer = arrayBuffer as any;
      buffer.fileStart = 0;

      // Append data and flush
      mp4boxFile.appendBuffer(buffer);
      mp4boxFile.flush();
    });
  }

  /**
   * Process single video frame to canvas
   */
  private processVideoFrameToCanvas(
    video: HTMLVideoElement, 
    options: ProcessingOptions, 
    timestamp: number, 
    frameIndex: number,
    frameDuration: number
  ): ProcessedFrame {
    const { targetWidth, targetHeight, maintainAspectRatio, cropMode } = options;

    // Set canvas to target size (this is what the user wants)
    this.canvas.width = targetWidth;
    this.canvas.height = targetHeight;

    // Clear canvas
    this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

    if (maintainAspectRatio) {
      // Apply crop settings to fill the canvas while maintaining aspect ratio
      const sourceRect = this.calculateSourceRect(
        video.videoWidth,
        video.videoHeight,
        targetWidth,
        targetHeight,
        cropMode,
        maintainAspectRatio
      );

      // Draw cropped video frame to fill the entire canvas
      this.ctx.drawImage(
        video,
        sourceRect.x,
        sourceRect.y,
        sourceRect.width,
        sourceRect.height,
        0,
        0,
        targetWidth,
        targetHeight
      );
    } else {
      // Stretch to fit without maintaining aspect ratio
      this.ctx.drawImage(
        video,
        0,
        0,
        video.videoWidth,
        video.videoHeight,
        0,
        0,
        targetWidth,
        targetHeight
      );
    }

    // Get image data
    const imageData = this.ctx.getImageData(0, 0, this.canvas.width, this.canvas.height);

    // Create result canvas (clone)
    const resultCanvas = document.createElement('canvas');
    resultCanvas.width = this.canvas.width;
    resultCanvas.height = this.canvas.height;
    const resultCtx = resultCanvas.getContext('2d')!;
    resultCtx.drawImage(this.canvas, 0, 0);

    return {
      canvas: resultCanvas,
      imageData,
      timestamp,
      frameIndex,
      frameDuration
    };
  }

  /**
   * Calculate source rectangle for cropping
   */
  private calculateSourceRect(
    sourceWidth: number,
    sourceHeight: number,
    targetWidth: number,
    targetHeight: number,
    cropMode: ProcessingOptions['cropMode'],
    maintainAspectRatio: boolean
  ) {
    if (!maintainAspectRatio) {
      return {
        x: 0,
        y: 0,
        width: sourceWidth,
        height: sourceHeight
      };
    }

    const sourceAspectRatio = sourceWidth / sourceHeight;
    const targetAspectRatio = targetWidth / targetHeight;

    let cropWidth: number;
    let cropHeight: number;
    let cropX: number;
    let cropY: number;

    // Calculate crop dimensions to fill target while maintaining aspect ratio
    if (sourceAspectRatio > targetAspectRatio) {
      // Source is wider - crop width, fit height
      cropHeight = sourceHeight;
      cropWidth = sourceHeight * targetAspectRatio;
    } else {
      // Source is taller - crop height, fit width
      cropWidth = sourceWidth;
      cropHeight = sourceWidth / targetAspectRatio;
    }

    // Calculate crop position based on alignment mode
    switch (cropMode) {
      case 'top-left':
        cropX = 0;
        cropY = 0;
        break;
      case 'top':
        cropX = (sourceWidth - cropWidth) / 2;
        cropY = 0;
        break;
      case 'top-right':
        cropX = sourceWidth - cropWidth;
        cropY = 0;
        break;
      case 'left':
        cropX = 0;
        cropY = (sourceHeight - cropHeight) / 2;
        break;
      case 'center':
      default:
        cropX = (sourceWidth - cropWidth) / 2;
        cropY = (sourceHeight - cropHeight) / 2;
        break;
      case 'right':
        cropX = sourceWidth - cropWidth;
        cropY = (sourceHeight - cropHeight) / 2;
        break;
      case 'bottom-left':
        cropX = 0;
        cropY = sourceHeight - cropHeight;
        break;
      case 'bottom':
        cropX = (sourceWidth - cropWidth) / 2;
        cropY = sourceHeight - cropHeight;
        break;
      case 'bottom-right':
        cropX = sourceWidth - cropWidth;
        cropY = sourceHeight - cropHeight;
        break;
    }

    return {
      x: Math.max(0, cropX),
      y: Math.max(0, cropY),
      width: Math.min(cropWidth, sourceWidth),
      height: Math.min(cropHeight, sourceHeight)
    };
  }

  /**
   * Clean up resources
   */
  dispose(): void {
    // Clean up canvas and context
    this.canvas.remove();
  }
}

