// Palette validation utilities for import/export functionality

import type { PaletteExportFormat } from '../types/palette';
import { isValidHexColor, isPaletteExportFormat } from '../types/palette';

export interface ValidationResult {
  isValid: boolean;
  errors: string[];
  warnings: string[];
  data?: PaletteExportFormat;
}

/**
 * Validate JSON string as palette export format
 */
export const validatePaletteJSON = (jsonString: string): ValidationResult => {
  const result: ValidationResult = {
    isValid: false,
    errors: [],
    warnings: []
  };

  // Parse JSON
  let parsedData: any;
  try {
    parsedData = JSON.parse(jsonString);
  } catch (error) {
    result.errors.push('Invalid JSON format. Please check the file syntax.');
    return result;
  }

  // Check if it's an object
  if (typeof parsedData !== 'object' || parsedData === null) {
    result.errors.push('Palette data must be a JSON object.');
    return result;
  }

  // Validate required fields
  if (typeof parsedData.name !== 'string') {
    result.errors.push('Palette must have a "name" field of type string.');
  }

  if (!Array.isArray(parsedData.colors)) {
    result.errors.push('Palette must have a "colors" field of type array.');
  }

  // If basic structure is invalid, return early
  if (result.errors.length > 0) {
    return result;
  }

  // Validate palette name
  const name = parsedData.name.trim();
  if (name.length === 0) {
    result.errors.push('Palette name cannot be empty.');
  } else if (name.length > 100) {
    result.warnings.push('Palette name is very long (over 100 characters).');
  }

  // Validate colors array
  const colors = parsedData.colors;
  if (colors.length === 0) {
    result.errors.push('Palette must contain at least one color.');
  } else if (colors.length > 256) {
    result.warnings.push('Palette contains a very large number of colors (over 256). This may impact performance.');
  }

  // Validate each color
  const validColors: string[] = [];
  const invalidColors: string[] = [];
  const duplicateColors: string[] = [];
  const seenColors = new Set<string>();

  colors.forEach((color: any, index: number) => {
    if (typeof color !== 'string') {
      invalidColors.push(`Color at index ${index} is not a string.`);
      return;
    }

    const trimmedColor = color.trim();
    if (!isValidHexColor(trimmedColor)) {
      invalidColors.push(`Color "${color}" at index ${index} is not a valid hex color.`);
      return;
    }

    const normalizedColor = trimmedColor.toUpperCase();
    if (seenColors.has(normalizedColor)) {
      duplicateColors.push(normalizedColor);
    } else {
      seenColors.add(normalizedColor);
      validColors.push(normalizedColor);
    }
  });

  // Add validation errors
  if (invalidColors.length > 0) {
    result.errors.push(...invalidColors);
  }

  // Add warnings for duplicates
  if (duplicateColors.length > 0) {
    result.warnings.push(`Found ${duplicateColors.length} duplicate colors that will be removed: ${duplicateColors.slice(0, 3).join(', ')}${duplicateColors.length > 3 ? '...' : ''}`);
  }

  // Check if we have any valid colors after validation
  if (validColors.length === 0) {
    result.errors.push('No valid colors found in the palette.');
  }

  // If no errors, mark as valid and prepare cleaned data
  if (result.errors.length === 0) {
    result.isValid = true;
    result.data = {
      name: name,
      colors: validColors
    };
  }

  return result;
};

/**
 * Validate palette export format object
 */
export const validatePaletteObject = (data: any): ValidationResult => {
  const result: ValidationResult = {
    isValid: false,
    errors: [],
    warnings: []
  };

  if (!isPaletteExportFormat(data)) {
    result.errors.push('Invalid palette format. Expected object with "name" and "colors" fields.');
    return result;
  }

  // Additional validation beyond type checking
  if (data.name.trim().length === 0) {
    result.errors.push('Palette name cannot be empty.');
  }

  if (data.colors.length === 0) {
    result.errors.push('Palette must contain at least one color.');
  }

  if (result.errors.length === 0) {
    result.isValid = true;
    result.data = data;
  }

  return result;
};

/**
 * Sanitize and normalize palette data
 */
export const sanitizePaletteData = (data: PaletteExportFormat): PaletteExportFormat => {
  return {
    name: data.name.trim(),
    colors: [...new Set(data.colors.map(color => color.toUpperCase()))] // Remove duplicates and normalize case
  };
};

/**
 * Generate validation summary for user display
 */
export const getValidationSummary = (result: ValidationResult): string => {
  const parts: string[] = [];

  if (result.isValid) {
    parts.push('âœ… Valid palette format');
    if (result.data) {
      parts.push(`ðŸ“Š ${result.data.colors.length} colors found`);
    }
  } else {
    parts.push('âŒ Invalid palette format');
  }

  if (result.errors.length > 0) {
    parts.push(`ðŸš¨ ${result.errors.length} error${result.errors.length > 1 ? 's' : ''}`);
  }

  if (result.warnings.length > 0) {
    parts.push(`âš ï¸ ${result.warnings.length} warning${result.warnings.length > 1 ? 's' : ''}`);
  }

  return parts.join(' â€¢ ');
};

/**
 * Common palette file extensions for validation
 */
export const SUPPORTED_PALETTE_EXTENSIONS = ['.json', '.palette', '.pal'] as const;

/**
 * Check if filename has supported extension
 */
export const hasSupportedExtension = (filename: string): boolean => {
  const extension = filename.toLowerCase().slice(filename.lastIndexOf('.'));
  return SUPPORTED_PALETTE_EXTENSIONS.includes(extension as any);
};

/**
 * Performance measurement utilities for ASCII Motion
 * Development tools to track rendering performance and identify bottlenecks
 */

interface PerformanceMetric {
  name: string;
  startTime: number;
  endTime?: number;
  duration?: number;
}

interface RenderMetrics {
  renderTime: number;
  cellCount: number;
  fps: number;
  timestamp: number;
}

class PerformanceMonitor {
  private metrics: Map<string, PerformanceMetric> = new Map();
  private renderHistory: RenderMetrics[] = [];
  private frameCount = 0;
  private lastFrameTime = 0;
  private isEnabled = false;

  constructor() {
    // Only enable in development
    this.isEnabled = import.meta.env.DEV;
  }

  /**
   * Start measuring a performance metric
   */
  start(name: string): void {
    if (!this.isEnabled) return;

    this.metrics.set(name, {
      name,
      startTime: performance.now()
    });
  }

  /**
   * End measuring a performance metric and return duration
   */
  end(name: string): number {
    if (!this.isEnabled) return 0;

    const metric = this.metrics.get(name);
    if (!metric) {
      console.warn(`Performance metric "${name}" was not started`);
      return 0;
    }

    const endTime = performance.now();
    const duration = endTime - metric.startTime;

    metric.endTime = endTime;
    metric.duration = duration;

    return duration;
  }

  /**
   * Measure canvas render performance
   */
  measureCanvasRender(cellCount: number): { duration: number; fps: number } {
    if (!this.isEnabled) return { duration: 0, fps: 0 };

    const renderTime = this.end('canvas-render');
    const currentTime = performance.now();

    // Calculate FPS
    let fps = 0;
    if (this.lastFrameTime > 0) {
      const frameDelta = currentTime - this.lastFrameTime;
      fps = Math.round(1000 / frameDelta);
    }

    this.lastFrameTime = currentTime;
    this.frameCount++;

    // Store render metrics
    const metrics: RenderMetrics = {
      renderTime,
      cellCount,
      fps,
      timestamp: currentTime
    };

    this.renderHistory.push(metrics);

    // Keep only last 100 measurements
    if (this.renderHistory.length > 100) {
      this.renderHistory.shift();
    }

    return { duration: renderTime, fps };
  }

  /**
   * Get performance statistics
   */
  getStats(): {
    averageRenderTime: number;
    averageFPS: number;
    totalRenders: number;
    lastRenderTime: number;
    efficiency: string;
  } {
    if (!this.isEnabled || this.renderHistory.length === 0) {
      return {
        averageRenderTime: 0,
        averageFPS: 0,
        totalRenders: 0,
        lastRenderTime: 0,
        efficiency: 'N/A'
      };
    }

    const recent = this.renderHistory.slice(-10); // Last 10 renders
    const avgRenderTime = recent.reduce((sum, m) => sum + m.renderTime, 0) / recent.length;
    const avgFPS = recent.reduce((sum, m) => sum + m.fps, 0) / recent.length;
    const lastMetric = this.renderHistory[this.renderHistory.length - 1];

    let efficiency = 'Good';
    if (avgRenderTime > 16.67) { // > 60 FPS threshold
      efficiency = avgRenderTime > 33.33 ? 'Poor' : 'Fair'; // 30 FPS threshold
    }

    return {
      averageRenderTime: Math.round(avgRenderTime * 100) / 100,
      averageFPS: Math.round(avgFPS),
      totalRenders: this.frameCount,
      lastRenderTime: Math.round(lastMetric.renderTime * 100) / 100,
      efficiency
    };
  }

  /**
   * Log performance statistics to console
   */
  logStats(): void {
    if (!this.isEnabled) return;

    // Performance stats logging disabled for cleaner console
  }

  /**
   * Clear performance history
   */
  clear(): void {
    this.metrics.clear();
    this.renderHistory = [];
    this.frameCount = 0;
    this.lastFrameTime = 0;
  }

  /**
   * Test large grid performance
   */
  async testLargeGrid(width: number, height: number): Promise<{
    gridSize: string;
    avgRenderTime: number;
    recommendation: string;
  }> {
    if (!this.isEnabled) {
      return {
        gridSize: `${width}x${height}`,
        avgRenderTime: 0,
        recommendation: 'Performance testing disabled in production'
      };
    }

    // Testing grid performance (logging disabled)

    // Clear previous measurements
    this.clear();

    // Simulate multiple renders
    const cellCount = width * height;
    const testRenders = 10;

    for (let i = 0; i < testRenders; i++) {
      this.start('canvas-render');

      // Simulate render work (in real usage, this would be actual rendering)
      await new Promise(resolve => setTimeout(resolve, 1));

      this.measureCanvasRender(cellCount);
    }

    const stats = this.getStats();
    const recommendation = this.getRecommendation(stats.averageRenderTime, cellCount);

    const result = {
      gridSize: `${width}x${height}`,
      avgRenderTime: stats.averageRenderTime,
      recommendation
    };

    return result;
  }

  private getRecommendation(renderTime: number, cellCount: number): string {
    const cellsPerMs = cellCount / Math.max(renderTime, 0.1);

    if (renderTime < 8) return `Excellent performance (${Math.round(cellsPerMs)} cells/ms) - suitable for animation`;
    if (renderTime < 16.67) return `Good performance (${Math.round(cellsPerMs)} cells/ms) - smooth for static editing`;
    if (renderTime < 33.33) return `Fair performance (${Math.round(cellsPerMs)} cells/ms) - consider optimizations for large grids`;
    return `Poor performance (${Math.round(cellsPerMs)} cells/ms) - optimization required`;
  }
}

// Global performance monitor instance
export const performanceMonitor = new PerformanceMonitor();

// Convenience functions
